INSERT INTO `quest_offer_reward` VALUES (41651, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you for assisting us, jewelcrafter. We needed this necklace badly.', 0);
INSERT INTO `quest_offer_reward` VALUES (41652, 0, 0, 0, 0, 0, 0, 0, 0, 'Hail, jewelcrafter. I can take this jewelry off of your hands. Your gift will be put to good use.', 0);
