INSERT INTO `quest_offer_reward` VALUES (41654, 2, 0, 0, 0, 0, 0, 0, 0, 'Приятно познакомится $r. Валарьяры благодарят вас за то,что вы принесли это кольцо.', 0);
INSERT INTO `quest_offer_reward` VALUES (41655, 2, 0, 0, 0, 0, 0, 0, 0, 'Хорошо,что ты пришел сейчас, ювелир. Нашим людям как раз этого не хватало.', 0);
INSERT INTO `quest_offer_reward` VALUES (41656, 2, 0, 0, 0, 0, 0, 0, 0, 'Кирин-тор благодарит тебя!', 0);
INSERT INTO `quest_offer_reward` VALUES (41657, 2, 0, 0, 0, 0, 0, 0, 0, 'Спасибо, что помогли нам, алхимик. Нам очень нужны были эти зелья.', 0);
INSERT INTO `quest_offer_reward` VALUES (41658, 2, 0, 0, 0, 0, 0, 0, 0, 'Приветствую, алхимик. Я могу забрать эти эльксиры. Ваш подарок будет хорошо использован.', 0);
INSERT INTO `quest_offer_reward` VALUES (41659, 2, 0, 0, 0, 0, 0, 0, 0, 'Thank you very much, alchemist. These elixirs will not be wasted.', 0);
INSERT INTO `quest_offer_reward` VALUES (41660, 2, 0, 0, 0, 0, 0, 0, 0, 'Приятно познакомится, $R алхимик. Валарьяры благодарят вас за то, что вы принесли эти зелья.', 0);
INSERT INTO `quest_offer_reward` VALUES (41661, 2, 0, 0, 0, 0, 0, 0, 0, 'Ты как раз во-время Алхимик. Нашим людям как раз этого не хватало.', 0);
INSERT INTO `quest_offer_reward` VALUES (41662, 2, 0, 0, 0, 0, 0, 0, 0, 'Кирин-Тор благодарит тебя!', 0);
INSERT INTO `quest_offer_reward` VALUES (41663, 2, 0, 0, 0, 0, 0, 0, 0, 'Спасибо, что помог нам, начертататель. Мы нуждались в этой руне.', 0);
INSERT INTO `quest_offer_reward` VALUES (41664, 2, 0, 0, 0, 0, 0, 0, 0, 'Приветствую, начертатель. Я могу забрать эту вещь. Твой подарок будет хорошо использован.', 0);
INSERT INTO `quest_offer_reward` VALUES (41665, 2, 0, 0, 0, 0, 0, 0, 0, 'Thank you very much, sketch artist. These supplies will not be wasted.', 0);
INSERT INTO `quest_offer_reward` VALUES (41666, 2, 0, 0, 0, 0, 0, 0, 0, 'Приятно познакомится $r. Валарьяры благодарят вас за то,что вы принесли эту руну.', 0);
INSERT INTO `quest_offer_reward` VALUES (41667, 0, 0, 0, 0, 0, 0, 0, 0, 'You couldn\'t have arrived at a better time, scribe. Our people were running short on supplies.', 0);
INSERT INTO `quest_offer_reward` VALUES (41668, 2, 0, 0, 0, 0, 0, 0, 0, 'Кирин-Тор благодарит тебя!', 0);
INSERT INTO `quest_offer_reward` VALUES (41669, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you for assisting us, enchanter. We needed this enchantment badly.', 0);
INSERT INTO `quest_offer_reward` VALUES (41670, 0, 0, 0, 0, 0, 0, 0, 0, 'Приветствую тебя, $gзачаровыватель:зачаровывательница;. Я заберу у тебя эти чары. Твой дар найдет достойное применение.', 23877);
INSERT INTO `quest_offer_reward` VALUES (41671, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you very much, $gcharmer: charmer... This enchantment will serve a good cause.', 24015);
INSERT INTO `quest_offer_reward` VALUES (41672, 0, 0, 0, 0, 0, 0, 0, 0, 'Ты как нельзя более кстати, |3-6($r)-$gзачаровыватель:зачаровывательница;! Валарьяры благодарны тебе за эти чары.', 23222);
INSERT INTO `quest_offer_reward` VALUES (41673, 0, 0, 0, 0, 0, 0, 0, 0, 'Ты как нельзя более вовремя, $gзачаровыватель:зачаровывательница;. У нас почти кончились припасы.', 23222);
INSERT INTO `quest_offer_reward` VALUES (41674, 0, 0, 0, 0, 0, 0, 0, 0, 'Кирин-Тор благодарит тебя!', 0);
INSERT INTO `quest_offer_reward` VALUES (41675, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you for assisting us, engineer. We needed this device badly.', 0);
INSERT INTO `quest_offer_reward` VALUES (41676, 0, 0, 0, 0, 0, 0, 0, 0, 'Hail, engineer. I can take this device off of your hands. Your gift will be put to good use.', 0);
INSERT INTO `quest_offer_reward` VALUES (41677, 0, 0, 0, 0, 0, 0, 0, 0, 'Many thanks, engineer. This auto-hammer shall not be wasted.', 0);
INSERT INTO `quest_offer_reward` VALUES (41678, 0, 0, 0, 0, 0, 0, 0, 0, 'Хорошо познакомились, $R инженер. Валарьяры благодарят вас за то, что вы принесли этот рюкзак.', 0);
INSERT INTO `quest_offer_reward` VALUES (41679, 0, 0, 0, 0, 0, 0, 0, 0, 'You couldn\'t have arrived at a better time, engineer. Our people were running short on supplies.', 0);
INSERT INTO `quest_offer_reward` VALUES (41680, 0, 0, 0, 0, 0, 0, 0, 0, 'Кирин-Тор благодарит тебя!', 0);
INSERT INTO `quest_offer_reward` VALUES (41689, 2, 0, 0, 0, 0, 0, 0, 0, 'Ты $gнастоящий герой:настоящая героиня;, $n, и мы все в большом долгу перед тобой. Я очень многим тебе обязана.$B$BБоюсь только, что эта война продлится еще очень долго.', 21267);
INSERT INTO `quest_offer_reward` VALUES (41690, 1, 0, 0, 0, 0, 0, 0, 0, 'Не знаю, сколько бы еще я смогла продержаться, если бы не ты. Спасибо тебе.', 21267);
INSERT INTO `quest_offer_reward` VALUES (41702, 0, 0, 0, 0, 0, 0, 0, 0, '<There\'s a small tag attached to the sleeve that says, \"16 - Therin.\" >', 21531);
INSERT INTO `quest_offer_reward` VALUES (41704, 0, 0, 0, 0, 0, 0, 0, 0, 'Так, значит, Кел\'данат все же перекинулся...$B$BЖаль. Он заслуживал лучшей участи. Спасибо, что $gизбавил:избавила; его от страданий.', 21531);
INSERT INTO `quest_offer_reward` VALUES (41707, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (41708, 274, 0, 0, 0, 200, 0, 0, 0, 'Он жив. Остальное пока неясно.', 21287);
INSERT INTO `quest_offer_reward` VALUES (41709, 0, 0, 0, 0, 0, 0, 0, 0, 'Теперь у тебя есть все необходимые запчасти... некоторых даже по две.', 24015);
INSERT INTO `quest_offer_reward` VALUES (41724, 1, 0, 0, 0, 200, 0, 0, 0, 'Эта проклятая порча может забрать любого из нас.', 21287);
INSERT INTO `quest_offer_reward` VALUES (41728, 0, 0, 0, 0, 0, 0, 0, 0, 'Признаться, я не ожидал, что к нам прибудет Великий мастер собственной персоной. С тобой у нас есть шанс победить. Мы не позволим этим демонам одолеть нас!', 23171);
INSERT INTO `quest_offer_reward` VALUES (41729, 0, 0, 0, 0, 0, 0, 0, 0, '$gWorked: Worked hard; you\'re good, thank you. Together we are definitely a powerful force! The enemy wanted to spread the spoilage - we managed to stop it!', 23171);
INSERT INTO `quest_offer_reward` VALUES (41730, 0, 0, 0, 0, 0, 0, 0, 0, 'Тебе храбрости не занимать – $gсумел:сумела; демонам жару задать!', 23171);
INSERT INTO `quest_offer_reward` VALUES (41731, 0, 0, 0, 0, 0, 0, 0, 0, 'Теперь здесь безопасно – благодаря тебе! Жаль только меда нету ни капельки вот мне...$B$B<Король обезьян глубоко вздыхает.>', 23171);
INSERT INTO `quest_offer_reward` VALUES (41732, 0, 0, 0, 0, 0, 0, 0, 0, 'Монастырь Тянь снова в безопасности – благодаря тебе.', 23171);
INSERT INTO `quest_offer_reward` VALUES (41733, 0, 0, 0, 0, 0, 0, 0, 0, 'Thanks to you, Grand Master, our order will live on.', 23171);
INSERT INTO `quest_offer_reward` VALUES (41734, 1, 66, 0, 0, 0, 500, 0, 0, 'Great Master, now I know: everything that has been said about you is pure truth. I\'ve seen how valiant you are...I fought; in the monastery of Tian. My life and my heart belong to the Order of Monks, and there are many battles ahead of us. Let me be one of your breaking blades. It will be a great honor for me to fight for you.', 23171);
INSERT INTO `quest_offer_reward` VALUES (41735, 1, 66, 0, 0, 0, 500, 0, 0, 'Через многое вместе с тобою прошли, и солнечный храм отстоять мы смогли.$B$BВраги пред тобою от страха дрожат, и стать твоим другом буду я рад!$B$BЯ, торжествуя и шляпу снимая, своим командиром тебя называю!', 23171);
INSERT INTO `quest_offer_reward` VALUES (41736, 1, 66, 0, 0, 0, 500, 0, 0, 'Seeing you in action is a pleasure to the eye, Grand Master $n. I intend to put an end to Lady Keletress and the hordes of the Legion at all costs. I think we could borrow the Storm Honey recipe from the enemies and turn their own weapons against them. Will you let me fight with you?', 23360);
INSERT INTO `quest_offer_reward` VALUES (41737, 1, 66, 0, 0, 0, 500, 0, 0, 'Ох, как же здорово чувствовать себя в безопасности.$B$BПропустив пару кружек хмельного отвара, я чувствую, что могу горы свернуть!$B$BБлагодарю за спасение моей жизни, Великий мастер. Знай, что отныне я буду преданно служить тебе до гробовой доски. Только скажи, и я сделаю все, что ты пожелаешь.', 23360);
INSERT INTO `quest_offer_reward` VALUES (41738, 1, 66, 0, 0, 0, 500, 0, 0, '<Silara\'s looking out through the doorway. She seems to be deeply immersed in her thoughts, as if she has some very important but not yet finished business. She can barely hear herself sighing.<Then it translates the look of its steel eyes on you. >$B$BMy place is here. $B$BI fight for you.', 23360);
INSERT INTO `quest_offer_reward` VALUES (41739, 1, 66, 0, 0, 0, 500, 0, 0, 'Мы с тобой через многое вместе прошли, $n.$B$BБез тебя я не смогла бы отомстить за свою наставницу и не нашла бы в себе смелости взяться за ее легендарный рецепт.$B$BНо благодаря тебе у нас теперь полно штормового меда – хватит на весь храм.$B$BКстати, мне показалось, что у нас с тобой довольно похожие стили.$B$BТвоему ордену ведь наверняка нужны новобранцы?', 23420);
INSERT INTO `quest_offer_reward` VALUES (41740, 1, 66, 0, 0, 0, 500, 0, 0, 'Excellent choice, Predictor... Shamans from all over the world can learn a lot from each other.', 22810);
INSERT INTO `quest_offer_reward` VALUES (41741, 1, 66, 0, 0, 0, 500, 0, 0, 'Успех разведки превзошел самые смелые ожидания, $n. Твоим войскам удалось спасти подмастерье Клондацка, весьма... необычного маленького шамана.$B$BВ благодарность он согласился нам помочь.', 22810);
INSERT INTO `quest_offer_reward` VALUES (41742, 1, 66, 0, 0, 0, 500, 0, 0, 'The Lord of the Wind has ordered me to help the Servants of the Earth fight the Blazing Legion. Lord Gromorahan sees great potential in you, Predictor... In gratitude for your help in the struggle for power, he sent me to help mortals. When the time of battle comes, the armies of Heaven will support you.', 23420);
INSERT INTO `quest_offer_reward` VALUES (41743, 1, 66, 0, 0, 0, 500, 0, 0, 'In hard times you inspire us, $gPredictor:Foreteller; $n. It has been an honor traveling with you in Heavenly Ascension. Although there are many things here that require my attention, I will gladly rejoin you on the battlefield. If you need me again, I am at your complete disposal.', 23420);
INSERT INTO `quest_offer_reward` VALUES (41744, 1, 66, 0, 0, 0, 500, 0, 0, 'Я всегда уважал тебя за твою доблесть в бою, $gПредсказатель:Предсказательница;.$b$bОднако то, как ты $gпривлек:привлекла; к Служителям Земли защитников и $gобъединил:объединила; повелителей стихий... это было еще впечатляюще. Воистину, о лучшем предводителе нельзя и мечтать. Ты именно $gтот:та;, кто нужен нашему ордену!$b$bПусть против Пылающего Легиона объединились элементали, наша работа еще не закончена. Если я тебе еще понадоблюсь, только позови.$b$bЯ живу, чтобы помогать Служителям Земли.', 23420);
INSERT INTO `quest_offer_reward` VALUES (41745, 1, 66, 0, 0, 0, 500, 0, 0, 'Повелитель Огня прислал меня помочь смертным в битве с Пылающим Легионом.$b$bМой повелитель считает, что демонам нет места в этом мире. Я живу, чтобы уничтожать врагов моего повелителя.$b$bИ я буду исполнять твои приказы так, словно они исходят от него самого. Кого мне уничтожить первым?', 23420);
INSERT INTO `quest_offer_reward` VALUES (41746, 1, 66, 0, 0, 0, 500, 0, 0, 'It was an honor for me to fight with you shoulder to shoulder in the Underdark, Farseer ;. By your courage and determination you inspire hope in our order. But, I am afraid, the worst is yet to come. You should not bear this burden alone. Let me fight on your side again. I won’t let you down.', 23420);
INSERT INTO `quest_offer_reward` VALUES (41747, 1, 66, 0, 0, 0, 500, 0, 0, '< The deaf, roaring voice of Lavinion is rumbling slowly. >$b$bMother Rock to send me to help $gPredictor:The fortune teller;... $b$bI commanded... to destroy demons... to protect shamans.$b$b<Lavinion hits a giant fist against a fist, which makes the walls shake. >$b$bThe avalanche to go with $gA fortune teller: A fortune teller;... demons weeping.', 23420);
INSERT INTO `quest_offer_reward` VALUES (41748, 1, 0, 0, 0, 0, 0, 0, 0, 'Чтобы победить Легион, мы должны заново собрать наш Совет, но в одиночку с этим не справится никто.$B$BТебе нужен верный помощник, знающий, что такое бремя правления.$B$BЯ почту за честь стать твоим главным советником. Твоя сила и мудрость возродят Совет.', 22996);
INSERT INTO `quest_offer_reward` VALUES (41749, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (41750, 1, 0, 0, 0, 0, 0, 0, 0, 'Jagganot will no longer torment Kalid - thanks to a friend. Kalid can now choose whom to follow, and he swears allegiance to the new Lord: the new Ruler;! Will the friend accept Kalid\'s help?', 22996);
INSERT INTO `quest_offer_reward` VALUES (41751, 1, 0, 0, 0, 0, 0, 0, 0, 'Твоя решимость возродить Совет вызывает восхищение. Ты $gмог:могла; бы бросить Шинфель умирать и найти вместо нее кого-то еще, но ты заботишься о своих.$B$BКогда-то я была лишь слепой исполнительницей чужой воли. Я думала, мой наставник позаботится обо мне. А он оставил меня во главе Совета, который ни в чем со мной не соглашался.$B$BБольше я никогда не последую за ним. Я хочу стать твоей соратницей. Отныне моя сила в твоем полном распоряжении, если тебе есть в ней нужда.', 23360);
INSERT INTO `quest_offer_reward` VALUES (41752, 0, 0, 0, 0, 0, 0, 0, 0, '<Zinnin meets you with a look. Without taking his eyes away, he slowly brings his hand clenched in his fist to your chest, bringing you a vow of loyalty, understandable and without words.>', 22996);
INSERT INTO `quest_offer_reward` VALUES (41753, 1, 0, 0, 0, 0, 0, 0, 0, 'Пора бы нам с тобой прекратить встречаться при таких обстоятельствах, $n. Ты мне уже столько раз $gспасал:спасала; жизнь, что я теперь чувствую себя обязанной.$B$BТак что если когда-нибудь спасать придется уже тебя, я буду рядом и верну тебе долг.', 22996);
INSERT INTO `quest_offer_reward` VALUES (41754, 1, 0, 0, 0, 0, 0, 0, 0, 'Значит, Ритцун поставил тебя во главе Совета?$B$BПохоже, у вас многое поменялось, пока меня не было.$B$BИсходящая от Легиона угроза сильна как никогда. Я слишком хорошо знаю, на что способны демоны. Поодиночке нам их не одолеть.$B$BОбъедини нас, $n, и вместе мы уничтожим Легион!', 23360);
INSERT INTO `quest_offer_reward` VALUES (41755, 1, 0, 0, 0, 0, 0, 0, 0, 'Теперь мне хотя бы не нужно со стыда прятаться ото всех, и за это я очень тебе благодарна.$B$BНе знаю, смогу ли я когда-нибудь вернуть тебе этот долг, но постараюсь.', 23420);
INSERT INTO `quest_offer_reward` VALUES (41756, 1, 0, 0, 0, 0, 0, 0, 0, 'Мы живем, чтобы служить тебе, $n.$B$BМы с радостью выполним любой твой приказ.', 23420);
INSERT INTO `quest_offer_reward` VALUES (41759, 1, 0, 0, 0, 0, 0, 0, 0, 'Тебя Ритцун послал? Вижу, мой старый наставник так и не избавился от привычки заставлять других делать за него всю грязную работу.$B$BНет, я все понимаю – он не может допустить, чтобы его засекли вместе с кем-то, кто якобы состоит в \"Недремлющем Аргусе\"! Что же о нем подумают?$B$BНу, хоть мне и лестно, что Ритцун обратился ко мне за помощью, я не стану делать ему одолжений по доброте душевной.$B$BМоя монета – могущество и знание. И если тебе есть чем со мной расплатиться, то, может быть, мы и договоримся.', 22996);
INSERT INTO `quest_offer_reward` VALUES (41760, 0, 0, 0, 0, 0, 0, 0, 0, 'Потрясающе.$B$BБудем надеяться, заклинание продержится достаточно долго.', 21531);
INSERT INTO `quest_offer_reward` VALUES (41762, 0, 0, 0, 0, 0, 0, 0, 0, 'I think it\'s safe enough here and we can talk.', 21531);
INSERT INTO `quest_offer_reward` VALUES (41763, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (41764, 0, 0, 0, 0, 0, 0, 0, 0, 'Хорошо, что тебе удалось установить первый идол. Мы еще на шаг приблизились к спасению Смоляного Рога.\n\nНо упоминание о Мраке встревожило меня. Мне всегда казалось, что это всего лишь монстр из легенд и сказок, которым пугали непослушных малышей.\n\nВозможно ли, что это действительно правда?', 26654);
INSERT INTO `quest_offer_reward` VALUES (41767, 1, 0, 0, 0, 0, 0, 0, 0, 'Мы не можем допустить, чтобы Мефистрот сломил волю Шинфель. Вооружившись ее знаниями и могуществом, повелитель ужаса сумеет организовать массированную атаку на разлом Зловещего Шрама.$B$BС помощью ритуала мы призовем Шинфель к себе, но для этого нужно сначала создать духовные узы, которые будут служить для нее якорем в нашем мире.', 22996);
INSERT INTO `quest_offer_reward` VALUES (41768, 1, 0, 0, 0, 0, 0, 0, 0, 'Шинфель теперь с нами, но ее разум по-прежнему в руках демонов.$B$BЧто же это за такое древнее проклятие? Никогда раньше не приходилось сталкиваться ни с чем подобным.', 23360);
INSERT INTO `quest_offer_reward` VALUES (41769, 1, 0, 0, 0, 0, 0, 0, 0, 'И снова это имя... кто бы мог подумать.$B$BКогда-то мы работали вместе, но передай Ритцуну, что я стал совсем другим.$B$BМои заботы – Марла и эта ферма.', 23360);
INSERT INTO `quest_offer_reward` VALUES (41770, 0, 0, 0, 0, 0, 0, 0, 0, 'Предсказатель Нобундо сказал мне, что вы хотите спасти Азерот. Боюсь, я единственный из зефиров, кто разделяет ваше желание.', 23420);
INSERT INTO `quest_offer_reward` VALUES (41771, 0, 0, 0, 0, 0, 0, 0, 0, 'Наконец-то Искатель Ветра займет свое законное место – Трон Четырех Ветров. Это великий день для Небесной выси!', 23420);
INSERT INTO `quest_offer_reward` VALUES (41772, 0, 0, 0, 0, 0, 0, 0, 0, 'Наконец-то ты здесь, $gПредсказатель:Предсказательница; $n.$b$bПохоже, наше присутствие в Огненных Просторах не осталось незамеченным.', 23420);
INSERT INTO `quest_offer_reward` VALUES (41773, 0, 0, 0, 0, 0, 0, 0, 0, 'Сын Риолита убит простыми смертными? Какая позорная кончина!$b$bПревосходно, $gшаман:шаманка;. Я очень доволен.', 23420);
INSERT INTO `quest_offer_reward` VALUES (41775, 0, 0, 0, 0, 0, 0, 0, 0, 'Служители Земли хотят предложить помощь? Это неожиданно... но весьма уместно.', 23420);
INSERT INTO `quest_offer_reward` VALUES (41776, 0, 0, 0, 0, 0, 0, 0, 0, 'Наконец-то я вернулся в Небесную высь – и все благодаря помощи $gсмертного:смертной;. Я в большом долгу перед тобой, $gПредсказатель:Предсказательница;.', 23420);
INSERT INTO `quest_offer_reward` VALUES (41777, 0, 0, 0, 0, 0, 0, 0, 0, 'Сумеречный Молот нескоро оправится от понесенных сегодня потерь. Великая честь сражаться рядом с тобой, $gПредсказатель:Предсказательница; $n!', 23420);
INSERT INTO `quest_offer_reward` VALUES (41778, 1, 1, 0, 0, 0, 0, 0, 0, 'Получилось! Ну что ж, хоть у охотников на демонов несколько другой подход к украшению интерьеров, чем у даларанских архитекторов, должен признать, что горн у них вышел отменный.$b$bАннигилят можешь оставить себе, $n. Мне кажется, со временем ты найдешь ему применение.', 24015);
INSERT INTO `quest_offer_reward` VALUES (41780, 1, 0, 0, 0, 0, 0, 0, 0, 'Похоже, тут есть все, что мне нужно. Погоди минутку – сейчас я все смешаю...', 23360);
INSERT INTO `quest_offer_reward` VALUES (41781, 1, 0, 0, 0, 0, 0, 0, 0, 'Я смотрю, с моими козами все в порядке.$B$BДавай теперь подумаем, что можно сделать с проклятием.', 23360);
INSERT INTO `quest_offer_reward` VALUES (41782, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (41783, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (41784, 1, 0, 0, 0, 0, 0, 0, 0, 'Иногда убить демона недостаточно. В таком случае демона надо подчинить – или сразу двух, как в нашем случае.$B$BПризвав сестер и заставив их служить нам, мы нанесем Мефистроту сокрушительный удар и избавим Шинфель от проклятия.', 23360);
INSERT INTO `quest_offer_reward` VALUES (41785, 1, 0, 0, 0, 0, 0, 0, 0, 'What about you, the new Council leader? It\'s immediately clear from your weapons. I hope someday you\'ll tell me how it got to you - it must be an exciting story!$B$BMy name is Lulubella; for my own, just Lulu. And by the way... I\'d really appreciate it if you wouldn\'t tell everyone that you and I met, okay?', 23420);
INSERT INTO `quest_offer_reward` VALUES (41787, 1, 0, 0, 0, 0, 0, 0, 0, 'Спасибо тебе, $n.$B$BС твоей стороны помочь изгою было очень любезно; я этого не забуду.', 23420);
INSERT INTO `quest_offer_reward` VALUES (41788, 1, 0, 0, 0, 0, 0, 0, 0, 'Разобрать эти каракули не каждый сможет, но вроде вся важная информация тут есть.$B$BОтлично – теперь, когда у меня есть это признание, я смогу доказать свою невиновность и заняться более насущными проблемами.', 23420);
INSERT INTO `quest_offer_reward` VALUES (41790, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (41791, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (41792, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (41793, 1, 0, 0, 0, 0, 0, 0, 0, 'Теперь, заручившись помощью Люлюбеллы, мы сумеем призвать сестер.', 23420);
INSERT INTO `quest_offer_reward` VALUES (41795, 1, 0, 0, 0, 0, 0, 0, 0, 'Ты $gсправился:справилась;, $n. Демоны-близнецы подчинились тебе.$B$BПризнаюсь, я не видел столь могучей силы со времен падения Рагнароса.', 23420);
INSERT INTO `quest_offer_reward` VALUES (41796, 1, 0, 0, 0, 0, 0, 0, 0, 'Итак, выбор сделан.', 23420);
INSERT INTO `quest_offer_reward` VALUES (41797, 1, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно. Эти бойцы очень помогут нам в обороне Расколотых островов. Ты можешь отправлять их на задания так же, как и защитников.', 22996);
INSERT INTO `quest_offer_reward` VALUES (41798, 1, 0, 0, 0, 0, 0, 0, 0, 'Я помогу тебе собрать армию, $n.', 22996);
INSERT INTO `quest_offer_reward` VALUES (41799, 1, 0, 0, 0, 0, 0, 0, 0, 'Тени в мире духов становятся все сильнее. Я опасаюсь, что скоро они сумеют прорваться через границу, разделяющую наши миры.\n\nНадеюсь, мы сможем все исправить, если восстановим идолы.', 26654);
INSERT INTO `quest_offer_reward` VALUES (41800, 5, 1, 0, 0, 0, 0, 0, 0, 'Халн сражался с Культом Смерти?\n\nО Халне ходит множество легенд, и я всегда был уверен, что эта история с дрогбарами – лишь очередная небылица. Но теперь ясно, что они и правда служили Мраку.\n\nПечальная новость... но сейчас у нас нет времени сокрушаться об этом.', 26654);
INSERT INTO `quest_offer_reward` VALUES (41802, 0, 0, 0, 0, 0, 0, 0, 0, 'Alright, let\'s see what ye brought me.$B$B<Brann examines the disc pieces.>$B$BWell look at this? There\'s a piece o\' somethin\' fel here. I betcha that\'s what was causin\' all those imps ta show up \'round here.$B$BI\'ll give it to ya to dispose of, or do whatever it is ye do with things like that.', 0);
INSERT INTO `quest_offer_reward` VALUES (41803, 0, 0, 0, 0, 0, 0, 0, 0, '', 21287);
INSERT INTO `quest_offer_reward` VALUES (41804, 0, 0, 0, 0, 0, 0, 0, 0, '', 21287);
INSERT INTO `quest_offer_reward` VALUES (41806, 0, 0, 0, 0, 0, 0, 0, 0, '', 21287);
INSERT INTO `quest_offer_reward` VALUES (41807, 0, 0, 0, 0, 0, 0, 0, 0, '', 21287);
INSERT INTO `quest_offer_reward` VALUES (41815, 1, 0, 0, 0, 0, 0, 0, 0, 'Майла ушла вперед со Смоляным Рогом. Теперь они оба отрезаны от нас.$b$bПлохо дело, $n.', 26654);
INSERT INTO `quest_offer_reward` VALUES (41834, 0, 0, 0, 0, 0, 0, 0, 0, 'Эта маска вам к лицу.$B$BВ Сурамаре нередко бывает полезно какое-то время побыть кем-то другим...', 21531);
INSERT INTO `quest_offer_reward` VALUES (41840, 273, 0, 0, 0, 0, 0, 0, 0, 'Ты очень вовремя, $n. Эти осколки очень нужны для осуществления моего плана.', 26654);
INSERT INTO `quest_offer_reward` VALUES (41841, 1, 0, 0, 0, 0, 0, 0, 0, 'Мы потерпели поражение! Идол уничтожен.$b$bЯ чувствую, как восстает Мрак...', 26654);
INSERT INTO `quest_offer_reward` VALUES (41849, 1, 66, 0, 0, 0, 500, 0, 0, 'Так тебя, стало быть, произвели в Великие мастера, да? Ну, я все равно был занят.$B$BКороче, мне-то самому удалось улизнуть от этих мерзавцев, но наши товарищи все еще в беде.$B$BДемоны Легиона собираются принести их всех в жертву, чтобы получить души.$B$BМы должны спасти наших!', 23360);
INSERT INTO `quest_offer_reward` VALUES (41850, 1, 66, 0, 0, 0, 500, 0, 0, 'Ты $gсправился:справилась; с заданием. Отлично. Силара стоит десятка опытных бойцов.', 23360);
INSERT INTO `quest_offer_reward` VALUES (41851, 1, 66, 0, 0, 0, 500, 0, 0, '<Silara bows low to you. >$B$BWorthy fight.', 23360);
INSERT INTO `quest_offer_reward` VALUES (41852, 1, 66, 0, 0, 0, 500, 0, 0, 'Превосходно, Великий мастер!$B$BКажется, это все.', 23360);
INSERT INTO `quest_offer_reward` VALUES (41853, 1, 66, 0, 0, 0, 500, 0, 0, 'Thank you for releasing me; me. I thought everybody was gonna eat me, and I\'d be dancing on my bones.', 23360);
INSERT INTO `quest_offer_reward` VALUES (41854, 1, 66, 0, 0, 0, 500, 0, 0, 'Это просто потрясающие новости, $n!$B$BТы не только $gнарушил:нарушила; планы Легиона, но и $gспас:спасла; множество жизней.$B$BИ это не все – ты еще и $gвызволил:вызволила; из беды трех наших великих монахов.$B$BОрден снова в большом долгу перед тобой.', 23360);
INSERT INTO `quest_offer_reward` VALUES (41863, 0, 0, 0, 0, 0, 0, 0, 0, '', 21287);
INSERT INTO `quest_offer_reward` VALUES (41877, 1, 0, 0, 0, 0, 0, 0, 0, 'Надеюсь, ты $gготов:готова; войти в город. Время пришло.', 24015);
INSERT INTO `quest_offer_reward` VALUES (41878, 0, 0, 0, 0, 0, 0, 0, 0, 'Ну что, за дело?', 24015);
INSERT INTO `quest_offer_reward` VALUES (41882, 1, 0, 0, 0, 0, 0, 0, 0, 'Спасибо, $n. Если бы ты не $gвмешался:вмешалась;, этот шепот смог бы перессорить даже лучших друзей.', 26654);
INSERT INTO `quest_offer_reward` VALUES (41884, 1, 0, 0, 0, 0, 0, 0, 0, 'Spirits are aware of many truths, $n.$b$bWhen we follow in their footsteps, we may find out where the Smolyan Horn has placed its idols.', 26654);
INSERT INTO `quest_offer_reward` VALUES (41888, 0, 0, 0, 0, 0, 0, 0, 0, 'Я и не надеялся дожить до того дня, когда четыре великих повелителя элементалей объединят силы со смертными народами Азерота.$b$bТы $gсделал:сделала; то, что не удавалось еще ни одному предсказателю за всю историю существования Служителей Земли.$b$bВоистину, этот день останется в памяти поколений!', 23420);
INSERT INTO `quest_offer_reward` VALUES (41889, 1, 1, 0, 0, 0, 0, 0, 0, 'Какие же мы молодцы! Мне кажется, нет ничего такого, с чем мы бы с тобой не справились!$b$bЯ позабочусь о том, чтобы Мей получила этот доспех как можно скорее. Выкройку можешь оставить себе – наверняка она тебе еще пригодится.', 23222);
INSERT INTO `quest_offer_reward` VALUES (41890, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (41891, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (41893, 0, 0, 0, 0, 0, 0, 0, 0, 'There\'s a fog in my eyes, but I\'m glad he finally found peace.', 21287);
INSERT INTO `quest_offer_reward` VALUES (41897, 0, 0, 0, 0, 0, 0, 0, 0, 'If the Mistress is not stopped, the Twilight Hammer will have enough power to conquer the Underground and enslave the elements! This cannot be allowed.', 23420);
INSERT INTO `quest_offer_reward` VALUES (41898, 0, 0, 0, 0, 0, 0, 0, 0, 'Слуги Теразан свободны, темные ритуалы их подчинения прекращены... по крайней мере, пока.', 23420);
INSERT INTO `quest_offer_reward` VALUES (41899, 0, 0, 0, 0, 0, 0, 0, 0, 'Пленники вырвались на свободу, но нам еще многое предстоит сделать, чтобы уничтожить культ Сумеречного Молота.', 23420);
INSERT INTO `quest_offer_reward` VALUES (41900, 0, 0, 0, 0, 0, 0, 0, 0, 'Отрадно видеть тебя в целости и сохранности, $gПредсказатель:Предсказательница;. Вместе с Теразан у нас теперь есть три союзника среди повелителей элементалей.$b$bОстался еще один...', 23420);
INSERT INTO `quest_offer_reward` VALUES (41901, 0, 0, 0, 0, 0, 0, 0, 0, 'The Lord of the Wind promised to help the Servants of the Earth in the war with the Burning Legion? What a pleasant surprise! But there\'s no time to celebrate, we still have a lot to do!', 23420);
INSERT INTO `quest_offer_reward` VALUES (41905, 0, 0, 0, 0, 0, 0, 0, 0, 'Твой защитник обернулся быстрее ветра, $n! В своем донесении он сообщает, что монастырь Тянь подвергся нападению!', 23171);
INSERT INTO `quest_offer_reward` VALUES (41907, 0, 0, 0, 0, 0, 0, 0, 0, 'Oh, yeah, yeah! Supreme Elder Rainy Cloud did the right thing sending you to me.', 23171);
INSERT INTO `quest_offer_reward` VALUES (41909, 0, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно. Предстоит исключительно рискованная операция, но теперь мы по крайней мере знаем точную численность противника и его местоположение.', 23360);
INSERT INTO `quest_offer_reward` VALUES (41910, 0, 0, 0, 0, 0, 0, 0, 0, 'Эта вода образуется из чистейшего свежевыпавшего снега – это важный ингредиент для нашего меда!', 23360);
INSERT INTO `quest_offer_reward` VALUES (41911, 0, 0, 0, 0, 0, 0, 0, 0, 'Мм-м... какой запах! Сразу видно: хорошая партия.', 23360);
INSERT INTO `quest_offer_reward` VALUES (41915, 574, 0, 0, 0, 0, 0, 0, 0, 'Ну что, старый дурак наконец-то встретил достойного противника?$b$bНу что ж, значит, Центральной набережной теперь понадобится новый ювелир – а я в любом случае уже узнал от своего прежнего хозяина все, чему он мог меня научить.$b$bВот тебе за твои труды.', 21737);
INSERT INTO `quest_offer_reward` VALUES (41916, 1, 0, 0, 0, 0, 0, 0, 0, 'Наша сила растет, а враги слабеют. Мы победим в этой войне, $n!', 23171);
INSERT INTO `quest_offer_reward` VALUES (41918, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (41919, 0, 0, 0, 0, 0, 0, 0, 0, '', 21215);
INSERT INTO `quest_offer_reward` VALUES (41920, 0, 0, 0, 0, 0, 0, 0, 0, '', 21215);
INSERT INTO `quest_offer_reward` VALUES (41921, 0, 0, 0, 0, 0, 0, 0, 0, '', 21215);
INSERT INTO `quest_offer_reward` VALUES (41922, 0, 0, 0, 0, 0, 0, 0, 0, '', 21215);
INSERT INTO `quest_offer_reward` VALUES (41924, 0, 0, 0, 0, 0, 0, 0, 0, '', 21215);
INSERT INTO `quest_offer_reward` VALUES (41934, 0, 0, 0, 0, 0, 0, 0, 0, 'I see you\'ve successfully awakened; Google, and he\'s not hungry. I seem to have underestimated you...', 23420);
INSERT INTO `quest_offer_reward` VALUES (41945, 0, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно, Великий мастер! Задание успешно выполнено. Наша новая наставница, вербовщица Тяндзи, как раз сейчас обустраивается в храме.', 23171);
INSERT INTO `quest_offer_reward` VALUES (41946, 0, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно. Эти бойцы очень помогут нам в обороне Расколотых островов. Ты можешь отправлять их на задания так же, как и защитников.', 23171);
INSERT INTO `quest_offer_reward` VALUES (41957, 0, 0, 0, 0, 0, 0, 0, 0, 'Спасибо, что $gоткликнулся на зов так быстро, брат:откликнулась на зов так быстро, сестра;.', 21287);
INSERT INTO `quest_offer_reward` VALUES (41966, 396, 0, 0, 0, 0, 0, 0, 0, 'Я... Я обязан тебе жизнью, $gбрат:сестра;, но... ты же здесь не только из-за меня, верно?$b$b<Вы рассказываете Баррему, как он в бреду упоминал Т\'ууре.>$b$bАга... это, наверное, мне приснилась Алора, моя сокамерница в том проклятом мире, куда меня утащили демоны. Она говорила мне, что видела его, но не более того.$b$bНадеюсь, она еще жива.', 21287);
INSERT INTO `quest_offer_reward` VALUES (41967, 0, 0, 0, 0, 0, 0, 0, 0, 'У меня было такое чувство, будто некая сила пытается отделить душу от тела. Если бы ты не $gподоспел:подоспела; вовремя... Нет, я даже думать об этом не хочу!$b$bСкажи, чем я могу тебе отплатить? Ты ведь $gспас:спасла; мне жизнь!$b$b<Вы пересказываете ей историю Баррема.>$b$bТак Баррем жив? Хвала Свету! Конечно, я расскажу тебе все, что мне известно.', 21287);
INSERT INTO `quest_offer_reward` VALUES (41989, 0, 0, 0, 0, 0, 0, 0, 0, 'Спасибо тебе. Теперь мы можем взяться за работу.', 21531);
INSERT INTO `quest_offer_reward` VALUES (41993, 1, 0, 0, 0, 0, 0, 0, 0, 'Ты ищешь демона, владеющего хрустальным посохом? Эредар, называющая себя Леди Калиндрия, полностью соответствует твоему описанию.$b$bНо она МОЯ! Я гонялся за ее прислужником по всем островам – и не упущу своего шанса.$b$bНо если ты хочешь отправиться со мной, давай – я не против. Помоги мне в этой битве – и можешь забрать посох себе. Ее оружие меня не интересует; мне нужна только ее черная душа.$b$bНу, что скажешь?', 21287);
INSERT INTO `quest_offer_reward` VALUES (42000, 1, 0, 0, 0, 0, 0, 0, 0, 'Здорово, что ты присоединишься к нам. Я ждал этого долгие годы. Теперь, когда мы заручились помощью провидцев, успех уже не за горами!', 21287);
INSERT INTO `quest_offer_reward` VALUES (42001, 1, 0, 0, 0, 0, 0, 0, 0, 'Свитки Митры у нас. Прекрасно.\n\nОстается только отыскать сам посох и с помощью этих свитков создать с ним связь. Похоже, и то, и другое сделать будет непросто.', 21267);
INSERT INTO `quest_offer_reward` VALUES (42002, 1, 0, 0, 0, 0, 0, 0, 0, 'Надеюсь, путешествие было не слишком утомительным.$b$bТебе, верно, любопытно, что это мы делаем на врайкульском кладбище. Все очень просто – дело в том, что автор этой саги давно помер! И вот тут-то нам как раз и поможет Таху!', 21287);
INSERT INTO `quest_offer_reward` VALUES (42003, 0, 0, 0, 0, 0, 0, 0, 0, 'Стало быть, ты ищешь Алунет? Опасное, очень опасное оружие.$B$BДавным-давно маги Кирин-Тора отдали его на хранение синим драконам. Посох был помещен в единственное место, в котором его необузданная мощь не вырвалась бы на волю, – Хранилище Нексуса.$B$BОднако я давно уже не бывал там. После того как синих драконов практически истребили, Нексус был заброшен. Так что теперь никто не знает, уцелело ли хранилище и что ', 21267);
INSERT INTO `quest_offer_reward` VALUES (42005, 603, 0, 0, 0, 0, 0, 0, 0, 'Я уже слыхал про тот остров. В таверне ходили слухи, что некий великий воин сумел проникнуть в гробницу короля врайкулов на острове в Штормхейме.$b$bГолову даю на отсечение – это как раз то, что мы ищем!', 21287);
INSERT INTO `quest_offer_reward` VALUES (42006, 0, 0, 0, 0, 0, 0, 0, 0, 'Похоже, это устройство связи эфириалов.$B$BВозможно, оно поможет разобраться, кто стоит за происходящим в святилище драконов.', 21267);
INSERT INTO `quest_offer_reward` VALUES (42007, 0, 0, 0, 0, 0, 0, 0, 0, 'So the Nexus is captured by the Etherium Brotherhood... ...although that\'s not the worst. But if they really intend to expand the window to the Circle of the Void, it\'s a serious threat to all of Azeroth. Apparently, fate itself has guided you this way, $n. Now, not only will you find your artifact, but you will also upset the plans of the Etherium Brotherhood! Unfortunately, I will not be able to help you with your business, but my advice is that you will not be able to help me...', 21267);
INSERT INTO `quest_offer_reward` VALUES (42008, 0, 0, 0, 0, 0, 0, 0, 0, 'Похоже, братство Эфириум сообразило, как с помощью волноловов Малигоса погрузиться глубже в Круговерть Пустоты.$B$BНо у меня есть идея, как обратить эти их приспособления против них же самих.', 21655);
INSERT INTO `quest_offer_reward` VALUES (42009, 0, 0, 0, 0, 0, 0, 0, 0, 'Я вижу, тебе удалось обуздать энергию Алунет. Я уж и не вспомню, когда последний раз кому-либо такое удавалось.\n\nНу что, ты $gготов:готова; высвободить эту силу?', 21655);
INSERT INTO `quest_offer_reward` VALUES (42010, 0, 0, 0, 0, 0, 0, 0, 0, 'Azuregos!$B$BApparently, he went back to the Nexus to protect the vault. He was the one Maligos trusted to look after the magical artifacts of the dragon family.$B$BI\'m afraid I\'m going to have to ask you one more favor, $n.', 21655);
INSERT INTO `quest_offer_reward` VALUES (42011, 0, 0, 0, 0, 0, 0, 0, 0, 'Я очень благодарен тебе за спасение Азурегоса, $n. Теперь хоть смогу спать спокойно, зная, что он приглядит за Нексусом.$B$BПризнаться, ты очень $gудивил:удивила; меня. Я за всю свою жизнь не встречал мага, способного контролировать хаотическую энергию Алунет. $B$BСпасибо за все, что ты сегодня $gсделал:сделала; для меня и моих собратьев. Воистину, ты $gтот самый герой, который так нужен:та самая героиня, которая так нужна; нам в битве с Легионом.', 21655);
INSERT INTO `quest_offer_reward` VALUES (42017, 603, 1, 0, 0, 0, 2000, 0, 0, 'I am heartened that your quest was a success, $n. We have been awaiting your return.$b$bIt is time we said goodbye to an old friend.', 21287);
INSERT INTO `quest_offer_reward` VALUES (42031, 0, 0, 0, 0, 0, 0, 0, 0, 'Ты от Наралекса? Каковы результаты исследования?', 22996);
INSERT INTO `quest_offer_reward` VALUES (42032, 0, 0, 0, 0, 0, 0, 0, 0, 'Наши защитники только что вернулись из похода к Шаладрассилу, Верховный друид. Экспедиция оказалась успешной!', 22996);
INSERT INTO `quest_offer_reward` VALUES (42033, 0, 0, 0, 0, 0, 0, 0, 0, 'Очень рад тебя видеть, $n, но, кажется, мы не успели! Когда я прибыл сюда, тут уже царил полный хаос.$B$BВыжившие говорят, что на убежище напали сатиры. Но разве это возможно? В этих лесах с незапамятных времен не бывало сатиров.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42034, 0, 0, 0, 0, 0, 0, 0, 0, 'Никогда еще сатиры не были столь сильны и не вели себя настолько дерзко. Они застали нас врасплох, и наша магия оказалась бессильна перед их натиском. Если бы не ты, мне бы точно пришел конец.$B$BУвы, спасти Идол так и не удалось...', 22996);
INSERT INTO `quest_offer_reward` VALUES (42035, 0, 0, 0, 0, 0, 0, 0, 0, 'Смотри, что мы нашли.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42036, 0, 0, 0, 0, 0, 0, 0, 0, 'Как я счастлив видеть его в твоих руках, $n.$B$BУ нас в Роще Снов он будет в безопасности.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42037, 0, 0, 0, 0, 0, 0, 0, 0, 'Хорошо, друиды уже собираются вместе. Прямо сейчас они стекаются к Нордрассилу, чтобы подготовиться к ритуалу.', 23171);
INSERT INTO `quest_offer_reward` VALUES (42038, 1, 66, 0, 0, 0, 500, 0, 0, 'Мне известно о Кошмаре достаточно, чтобы понимать степень исходящей от него угрозы. Под действием Кошмара сила героев обращается против них самих.$B$BНо ты... ты сохраняешь стойкость и отвагу даже перед лицом этой страшной опасности. Я верю, что ты сумеешь привести нас к победе.$B$B$n, я хочу принести клятву верности тебе и всему ордену друидов.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42039, 1, 66, 0, 0, 0, 500, 0, 0, '$n, my life belongs to you. $gYou\'ve given me:you\'ve given me a second chance, and I\'ll use it with dignity. $B$B Thanks to you, the Idol of the Forest has returned to us.  I\'m afraid to think that this sacred relic could fall into the hands of our enemies. $B$BNow I want to finish the job. $B$BLet me fight with you. If anyone is meant to defeat the Nightmare, it\'s only you.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42040, 0, 0, 0, 0, 0, 0, 0, 0, 'Тут что-то не так, $n. Это не Изумрудный Сон. Неужели нашу священную землю наводнили твари Ксавия? Как им удалось пробиться через нашу защиту?', 23222);
INSERT INTO `quest_offer_reward` VALUES (42041, 0, 0, 0, 0, 0, 0, 0, 0, 'Хорошо. Я уже чувствую, как уходит сила Кошмара.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42042, 0, 0, 0, 0, 0, 0, 0, 0, 'Ура, они спасены! Спасибо!', 23222);
INSERT INTO `quest_offer_reward` VALUES (42043, 0, 0, 0, 0, 0, 0, 0, 0, 'Превосходно, $n. Я знала, что сила Элуны способна исцелить заразу Кошмара. Ее благословение здесь сильно.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42044, 0, 0, 0, 0, 0, 0, 0, 0, 'Сегодня я оплакиваю павшего друга – того, кто ступал по этим лесам многие века.$B$BТеперь он обретет покой, зная, что отдал жизнь ради защиты этого священного места.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42045, 0, 0, 0, 0, 0, 0, 0, 0, 'Мой дед – Малорн, сам древний бог дикой природы. Как долго я ждал этого дня... и лишь узнал, что ему грозит великая опасность.$B$BЕсли он в плену Кошмара, разве осталась у нас надежда?', 23222);
INSERT INTO `quest_offer_reward` VALUES (42046, 0, 0, 0, 0, 0, 0, 0, 0, 'Спасибо за твои героические деяния, |3-6($c). Без твоей помощи мы бы не смогли отыскать идола и поговорить с самим Малорном.$b$bТеперь, когда мы знаем, что он не в силах выбраться из Кошмара, мы можем разработать план.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42047, 1, 66, 0, 0, 0, 500, 0, 0, 'Сколько себя помню, я всегда служил дикой природе. Обучал молодых друидов, наблюдал за тем, как они набираются знаний и опыта.$B$BМало кто удостаивался звания верховного друида, но, видится мне, ты можешь стать $gлучшим:лучшей; из нас.$B$BЯ почту за честь следовать твоим наставлениям.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42048, 1, 66, 0, 0, 0, 500, 0, 0, 'Привет, $n! После твоего ухода в лесу только и было разговоров, что о тебе. Мои друзья убедили меня прийти к тебе сюда!$B$BТакого замечательного, доброго и храброго друида не каждый день встретишь!$B$BЭто будет просто здорово, правда?', 23222);
INSERT INTO `quest_offer_reward` VALUES (42049, 0, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно. В этих осколках так и бурлит злая энергия. Осторожнее с ними, $n.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42050, 0, 0, 0, 0, 0, 0, 0, 0, 'Прекрасный выбор. Остальные помогут мне держать оборону. Нам надо спешить.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42051, 0, 0, 0, 0, 0, 0, 0, 0, 'Не обманывайся, $n. То, что кажется тебе горой Хиджал, на самом деле лишь наведенный врагом морок.$B$BЭто образ самого страшного кошмара из головы Малорна. Война древних, случившаяся тысячи лет назад.$B$BИменно во время этой войны Азерот впервые столкнулся с Пылающим Легионом. Именно в этой войне погиб Малорн и Дикие Боги.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42053, 0, 0, 0, 0, 0, 0, 0, 0, 'Сегодня ты $gпривел:привела; нас к триумфу, $n.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42054, 0, 0, 0, 0, 0, 0, 0, 0, '<Malorn is looking right at you. Warm radiant energy comes from him. You are suddenly enveloped in peace and a sense of gratitude.>', 23420);
INSERT INTO `quest_offer_reward` VALUES (42055, 0, 0, 0, 0, 0, 0, 0, 0, '$n, we Dream Grove Elders have spoken and agreed unanimously. We want you at the head of the Grove of Dreams. $B$BAnd today we give you the title of Supreme Druid of the Grove of Dreams, the highest title in our order. $B$BThe duty of our order is to protect nature, and the responsibility for that from now on falls on your shoulders! Be $gworthy; be worthy of your high rank, $n.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42056, 1, 66, 0, 0, 0, 500, 0, 0, 'Мы с тобой не первый день знакомы, $n. Не раз и не два твои решения приносили Роще Снов победу.$B$BЯ готов отдать свою жизнь, чтобы защитить Азерот, и знаю, что если придется, ты поступишь точно так же.$B$BВпереди нас по-прежнему ждет великая опасность. Прими мою клятву, $n. Мы должны защитить природу!', 23420);
INSERT INTO `quest_offer_reward` VALUES (42065, 0, 0, 0, 0, 0, 0, 0, 0, 'Так значит, Владычица Сумерек была драконицей... Мне казалось, с родом Синтарии покончено.$b$bНе удивлюсь, если после Зериксии еще кто-нибудь появится.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42068, 0, 0, 0, 0, 0, 0, 0, 0, 'Как хорошо, что ты здесь, $gПредсказатель:Предсказательница; $n. Сумеречный Молот выстроил в Подземье укрепленную цитадель.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42074, 1, 603, 0, 0, 0, 0, 0, 0, 'Это Т\'ууре! А мы-то думали, что потеряли его навсегда!$b$bВ последний раз мне доводилось видеть этот посох, когда мы спасались бегством из маленького мирка, именуемого Нискарой. Легион застал нас врасплох, нам удалось спастись лишь благодаря горстке смелых воздаятелей, которые вместе с Т\'ууре остались там, чтобы задержать врага. $b$bКто бы мог подумать, что тебе и правда удастся вернуть Т\'ууре. Воистину, тебя озар', 21287);
INSERT INTO `quest_offer_reward` VALUES (42079, 0, 0, 0, 0, 0, 0, 0, 0, '$gMy little brother:my little sister; he\'s about to turn a new page in his life, like $ghim:her; this or not.', 21531);
INSERT INTO `quest_offer_reward` VALUES (42081, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'re finished? Let\'s see it!$B$B<Deucus takes the conduit from you and places it against the wall.>$B$BNow that is a good looking decoction conduit. I\'m glad I upgraded its strength from that old hunk of junk I had before.$B$BYou might even say Svena did me a favor by destroying that old one.', 0);
INSERT INTO `quest_offer_reward` VALUES (42088, 5, 0, 0, 0, 0, 0, 0, 0, 'Ты как раз вовремя. Разведка доложила, что появился сам Король подземелий.', 21384);
INSERT INTO `quest_offer_reward` VALUES (42096, 1, 0, 0, 0, 0, 0, 0, 0, 'I\'ve watched you rise up the ranks for a long time, $n. With you leading our order, I know we are in good hands.$B$BCount me as one of your champions, Arch Druid.', 21249);
INSERT INTO `quest_offer_reward` VALUES (42097, 0, 0, 0, 0, 0, 0, 0, 0, 'There is a folio in the archives, which details the process of creating demonic gates. Through such a gate you can get into any world of demons - you just need to know where to take them.We could use that gate to find the remaining members of the Council and bring them back here.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42098, 1, 0, 0, 0, 0, 0, 0, 0, 'Отлично, $n.$B$BЭта потерянная душа послужит великой цели.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42100, 1, 0, 0, 0, 0, 0, 0, 0, 'Твои бойцы потрудились на славу. Теперь у нас достаточно осколков для создания якоря.', 23171);
INSERT INTO `quest_offer_reward` VALUES (42102, 1, 0, 0, 0, 0, 0, 0, 0, 'Наша разведка наконец-то выдала результат. Мы вышли на чернокнижника, который, судя по всему, обладает всеми необходимыми навыками и опытом для проведения ритуала призыва.$B$BТак не будем терять зря времени.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42103, 1, 0, 0, 0, 0, 0, 0, 0, 'Этого должно быть достаточно, чтобы кровавый камень насытился.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42104, 0, 0, 0, 0, 0, 0, 0, 0, 'The Underking is coming. Best keep that weapon of yours handy in case this does not go well.', 21108);
INSERT INTO `quest_offer_reward` VALUES (42107, 0, 0, 0, 0, 0, 0, 0, 0, 'Truly, the glory has shone on us! The great worm cannot hide from us!', 22996);
INSERT INTO `quest_offer_reward` VALUES (42110, 0, 0, 0, 0, 0, 0, 0, 0, 'Ты $gпришел:пришла; за великим червем? Это либо великая глупость, либо великая храбрость.$B$BЯ знаю эти горы вдоль и поперек. Помоги отомстить за мою деревню, и я приведу тебя к мерзкой твари.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42114, 0, 0, 0, 0, 0, 0, 0, 0, 'Я уже видел тебя, $gПредсказатель:Предсказательница;.$b$b<Некоторое время владыка Нептулон оценивающе разглядывает вас.>$b$bЯ чувствую, что в тебе дремлет великая сила. Посмотрим, истинно ли пророчество...', 23420);
INSERT INTO `quest_offer_reward` VALUES (42120, 603, 1, 0, 0, 0, 2000, 0, 0, 'I am heartened at your successful quest. We have been waiting for you to return. It is time we said goodbye to an old friend.', 21287);
INSERT INTO `quest_offer_reward` VALUES (42125, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (42126, 0, 0, 0, 0, 0, 0, 0, 0, 'Защитник справился с заданием! Верховный маг Омниара присоединилась к нам и уже устраивается в новых покоях.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42127, 0, 0, 0, 0, 0, 0, 0, 0, 'Отлично. Эти бойцы очень помогут нам в борьбе с Пылающим Легионом. Ты можешь отправлять их на задания так же, как и наших защитников.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42128, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (42129, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes, it\'s her ... the pendant of the guardians! Hurrah! Revenge is so nice!', 23222);
INSERT INTO `quest_offer_reward` VALUES (42131, 1, 0, 0, 0, 0, 0, 0, 0, 'Эти марионетки Кил\'джедена – настоящее проклятье для нас. Хорошо, что ты $gзавладел:завладела; их боевыми клинками.\n\nКогда мы окажемся в мире демонов, с таким оружием убить этих двоих будет куда проще.\n\nКстати, у тебя найдется время? Нужно обсудить один важный вопрос.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42132, 1, 0, 0, 0, 0, 0, 0, 0, 'Осталось сделать еще одну вещь.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42133, 1, 0, 0, 0, 0, 0, 0, 0, 'Это оружие отлично послужит против Хаккара и его прислужников. Я позабочусь, чтобы оно попало в руки наших друзей.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42134, 1, 0, 0, 0, 0, 0, 0, 0, 'Это честь для меня – помочь Незримому пути. Я сделаю все, что в моих силах.', 23171);
INSERT INTO `quest_offer_reward` VALUES (42137, 0, 0, 0, 0, 0, 0, 0, 0, 'То, что ты $gготов:готова; прийти на помощь, – это прекрасно. У тебя задатки хорошего лидера.$B$BЯ знаю, что ты хочешь собрать жрецов из разных фракций и объединить их для борьбы с Пылающим Легионом. Это невероятно сложное задание, но тебе не придется заниматься этим в одиночку.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42138, 1, 0, 0, 0, 0, 0, 0, 0, 'Ты далеко пойдешь, $n. С тех пор как ты $gпоявился:появилась; здесь, все стало быстро меняться к лучшему.$B$BКто-то скажет – повезло, но мне кажется, это судьба. Наши пути не просто так пересеклись.$B$BКак знать? Может, нас ждут великие дела.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42139, 6, 273, 0, 0, 0, 0, 0, 0, 'Ты, говорят, с Ванессой $gзакорешился:закорешилась;? Ничего, все путем\'.\n\nМы с ней поладим.\n\nСлушай, у тебя минутка времени найдется?', 21916);
INSERT INTO `quest_offer_reward` VALUES (42140, 4, 0, 0, 0, 0, 0, 0, 0, 'Золото! Скоро от разбойников, воров и головорезов отбоя не будет.\n\n<Никки переглядывается с Ванессой и шепотом добавляет.>\n\nМожет, даже из Братства Справедливости кто подтянется. Слишком рано?', 21916);
INSERT INTO `quest_offer_reward` VALUES (42141, 0, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно, Милра отлично справилась с заданием! Наши старания не прошли даром.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42142, 0, 0, 0, 0, 0, 0, 0, 0, 'Превосходно. Эти бойцы очень помогут нам в обороне Расколотых островов. Ты можешь отправлять их на задания так же, как и своих защитников.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42147, 0, 0, 0, 0, 0, 0, 0, 0, 'У семьи Лунархов обширные связи. Придет время – и они нам очень пригодятся.', 21737);
INSERT INTO `quest_offer_reward` VALUES (42149, 1, 0, 0, 0, 0, 0, 0, 0, 'Лучше отнеси их в Оплот Хранителя, $n. Там о них позаботятся.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42158, 1, 0, 0, 0, 0, 0, 0, 0, 'The Temple of Storms is close by, so you should get there before that thieving vrykul.', 21287);
INSERT INTO `quest_offer_reward` VALUES (42166, 1, 0, 0, 0, 0, 0, 0, 0, 'Они практикуют магию Скверны. Нет, было в них что-то очень странное, но такое мне даже в голову не приходило!', 22566);
INSERT INTO `quest_offer_reward` VALUES (42168, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (42170, 0, 0, 0, 0, 0, 0, 0, 0, 'Ткачи Снов благодарят вас.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42171, 1, 0, 0, 0, 0, 0, 0, 0, 'Спасибо. Теперь мы знаем правду о Небесной академии, $n!', 22566);
INSERT INTO `quest_offer_reward` VALUES (42175, 1, 0, 0, 0, 0, 0, 0, 0, 'Хорошо, что $gпришел:пришла;, $n. У нас много работы!', 22566);
INSERT INTO `quest_offer_reward` VALUES (42184, 0, 0, 0, 0, 0, 0, 0, 0, 'Защитники Служителей Земли, отправленные в Небесную высь, вернулись в оплот, добившись успеха.$b$bВ обители воздуха сейчас царит смута, но наши защитники нашли могущественного элементаля, желающего к нам присоединиться.', 22810);
INSERT INTO `quest_offer_reward` VALUES (42185, 1, 0, 0, 0, 0, 0, 0, 0, 'Brann won\'t believe it when I tell him. What an adventure!', 21287);
INSERT INTO `quest_offer_reward` VALUES (42186, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you for coming, Grandmaster.', 0);
INSERT INTO `quest_offer_reward` VALUES (42187, 1, 0, 0, 0, 0, 0, 0, 0, 'Чэнь и Ли Ли – одни из лучших монахов нашего времени.$B$BЕсли они не будут постоянно ввязываться во всякие истории, то станут прекрасными представителями нашего ордена.', 23171);
INSERT INTO `quest_offer_reward` VALUES (42188, 1, 0, 0, 0, 0, 0, 0, 0, 'They say you\'re making progress in the Cracked Islands. That\'s great. We didn\'t sit idly by the Whirlpool either. You and I have important things to do...', 23420);
INSERT INTO `quest_offer_reward` VALUES (42191, 0, 0, 0, 0, 0, 0, 0, 0, 'Отлично, $n! Я вижу, что орден Павшего Храма в надежных руках.', 23171);
INSERT INTO `quest_offer_reward` VALUES (42193, 0, 0, 0, 0, 0, 0, 0, 0, 'Что ты ищешь? Тоже хочешь поживиться фамильными реликвиями, как другие?', 22996);
INSERT INTO `quest_offer_reward` VALUES (42194, 0, 0, 0, 0, 0, 0, 0, 0, 'Truly, Odin himself guides your hand, $n.', 22522);
INSERT INTO `quest_offer_reward` VALUES (42197, 1, 66, 0, 0, 0, 500, 0, 0, 'Lord Neptulon sent me to help the Servants of the Earth protect Azeroth. I am commanded to serve $gPredictor:Foreteller; the Servants of the Earth are as faithful and dedicated as I serve the Master of the Tides. I will stay with you until the Blazing Legion is cast out of this world, and until Azeroth is saved. I am at your disposal, $gPredictor:Foreteller.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42198, 1, 66, 0, 0, 0, 500, 0, 0, 'Объединение стихий – непростая задача, |3-6($ct) $n. Тебе пригодится любая помощь!$b$bВоистину, темные времена настали... но не бойся, я буду с тобой!$b$bСтарушка Милра тебя поддержит, $gПредсказатель:Предсказательница;!$b$bА ну-ка, за работу! Нельзя терять время!', 23420);
INSERT INTO `quest_offer_reward` VALUES (42200, 0, 0, 0, 0, 0, 0, 0, 0, 'Разведка в Подземье прошла удачно. Защитники Служителей Земли связались с Теразан, но дело оказалось еще серьезнее, чем мы предполагали…', 23420);
INSERT INTO `quest_offer_reward` VALUES (42202, 1, 273, 0, 0, 0, 1500, 0, 0, 'Thank you, $n.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42204, 1, 0, 0, 0, 0, 0, 0, 0, 'Свершилось! Теперь, когда в наших руках Гьяллархорн, армии Ульдуара исполнят свой долг и присоединятся к нам!', 22996);
INSERT INTO `quest_offer_reward` VALUES (42206, 1, 0, 0, 0, 0, 0, 0, 0, 'Ты мне нравишься, $n. Не боишься перейти к решительным действиям, когда это необходимо.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42208, 0, 0, 0, 0, 0, 0, 0, 0, 'Наша операция в Огненных Просторах прошла успешно, $gПредсказатель:Предсказательница;. Нам нужно многое обсудить.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42210, 0, 0, 0, 0, 0, 0, 0, 0, 'Хорошо, что нам удалось добыть эти свитки. Помощь Девятой Цзя нашему ордену очень пригодится.', 23171);
INSERT INTO `quest_offer_reward` VALUES (42213, 0, 0, 0, 0, 0, 0, 0, 0, '<The top magician Kadgar left room here for the Golgannet Tidal Stone, as did the other Pillars of Creation. >', 21384);
INSERT INTO `quest_offer_reward` VALUES (42214, 1, 1, 0, 0, 0, 0, 0, 0, '', 23420);
INSERT INTO `quest_offer_reward` VALUES (42220, 0, 0, 0, 0, 0, 0, 0, 0, '< In the end, it was done for their own good. >', 21491);
INSERT INTO `quest_offer_reward` VALUES (42222, 1, 0, 0, 0, 0, 0, 0, 0, 'Равандвир очень впечатлен твоими действиями в Небесной академии, $n.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42223, 1, 0, 0, 0, 0, 0, 0, 0, 'Ты как нельзя кстати, |3-6($r).', 22908);
INSERT INTO `quest_offer_reward` VALUES (42224, 0, 0, 0, 0, 0, 0, 0, 0, '< You\'ll know the relic from the Talisra vision. >', 22810);
INSERT INTO `quest_offer_reward` VALUES (42225, 0, 0, 0, 0, 0, 0, 0, 0, '< As only the magic barrier begins to melt, you hear the rustle of the leaves and the claws chew. >', 22810);
INSERT INTO `quest_offer_reward` VALUES (42226, 0, 0, 0, 0, 0, 0, 0, 0, 'Ты хоть понимаешь, что $gнаделал:наделала;?!', 22810);
INSERT INTO `quest_offer_reward` VALUES (42227, 0, 0, 0, 0, 0, 0, 0, 0, 'Нам нужно действовать как можно быстрее.', 22810);
INSERT INTO `quest_offer_reward` VALUES (42228, 0, 0, 0, 0, 0, 0, 0, 0, '<Energy is pulsating around the ancient seed. >', 22810);
INSERT INTO `quest_offer_reward` VALUES (42229, 1, 0, 0, 0, 0, 0, 0, 0, 'Такое временное пристанище нам пригодится.', 21737);
INSERT INTO `quest_offer_reward` VALUES (42230, 0, 0, 0, 0, 0, 0, 0, 0, '<Semia fits perfectly on the stand and fits into the slot with a nice click. >', 22810);
INSERT INTO `quest_offer_reward` VALUES (42231, 1, 0, 0, 0, 0, 0, 0, 0, 'Я много слышал о твоих подвигах. Если уж и объединять силы с чужаком, то ты – лучший выбор. Мой орден хранил важный секрет, который теперь раскрыт. Мы – хранители последнего пристанища самого Тира!$b$bГлядя на то, что творится в мире, мы поняли: нельзя больше прятать нашу священную реликвию. Нельзя скрывать Серебряную Длань!', 21287);
INSERT INTO `quest_offer_reward` VALUES (42233, 0, 0, 0, 0, 0, 0, 0, 0, 'Племена Крутогорья благодарны вам.', 22124);
INSERT INTO `quest_offer_reward` VALUES (42234, 0, 0, 0, 0, 0, 0, 0, 0, 'The Valariars thank you.', 22624);
INSERT INTO `quest_offer_reward` VALUES (42238, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes, yes, my new master sent you. I understand. But first I need to solve all the issues with my former owner.', 21384);
INSERT INTO `quest_offer_reward` VALUES (42244, 1, 0, 0, 0, 1000, 0, 0, 0, '<Councilman glances over the magazine and quickly looks around, then turns to you and gives you a curve smile. She almost whispers.\n\nDo you have any idea that $gheld: held; in your hands? It\'s just incredible luck that such valuable information hasn\'t gotten into the Alliance\'s paws.\n\nWell, at least now it\'s in a safe place.', 21384);
INSERT INTO `quest_offer_reward` VALUES (42268, 0, 0, 0, 0, 0, 0, 0, 0, '<You\'ve done all you can and your conscience can be calm. >', 21384);
INSERT INTO `quest_offer_reward` VALUES (42271, 0, 0, 0, 0, 0, 0, 0, 0, 'Ну разумеется, они решили напасть сейчас.$b$bЭти иссохшие твари выбрали самое подходящее время для того, чтобы ударить по нам. Мой выводок уже понес первые потери.', 21384);
INSERT INTO `quest_offer_reward` VALUES (42345, 0, 0, 0, 0, 0, 0, 0, 0, 'You have successfully cured fever refugees. In gratitude you were promised that Olisarra in Dalaran will definitely learn about your selfless deed and skill of a healer.', 24015);
INSERT INTO `quest_offer_reward` VALUES (42365, 0, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно, Верховный друид. Это то, что надо.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42366, 0, 0, 0, 0, 0, 0, 0, 0, 'Kyrtos, you old fool! You should have known the risks...', 0);
INSERT INTO `quest_offer_reward` VALUES (42367, 0, 0, 0, 0, 0, 0, 0, 0, 'Теперь, когда с Аркетраксом и его прислужниками покончено, над Аванпостом иллидари больше не нависает угроза окружения. Ты молодчина.', 21384);
INSERT INTO `quest_offer_reward` VALUES (42368, 0, 0, 0, 0, 0, 0, 0, 0, 'Это задержит их на какое-то время. В подходящий момент нам нужно будет вернуться туда снова, потому что со временем они восстановят силы.', 21384);
INSERT INTO `quest_offer_reward` VALUES (42369, 11, 0, 0, 0, 0, 0, 0, 0, 'На нашей стороне портала рвануло знатно – но это ты еще не $gвидел:видела;, что творилось по ту сторону. Ха!', 21384);
INSERT INTO `quest_offer_reward` VALUES (42370, 1, 1, 0, 0, 0, 0, 0, 0, 'Так... время, отведенное на тренировку с жезлом, подошло к концу. Пора двигаться дальше. Тебя ждет следующий урок!', 21384);
INSERT INTO `quest_offer_reward` VALUES (42371, 1, 1, 0, 0, 0, 0, 0, 0, 'У меня от всей этой писанины уже рука отваливается. Думаю, пора прерваться.', 21384);
INSERT INTO `quest_offer_reward` VALUES (42372, 1, 0, 0, 0, 0, 0, 0, 0, 'Keep your voice down if you don\'t want to get a bad meal.', 21384);
INSERT INTO `quest_offer_reward` VALUES (42375, 0, 0, 0, 0, 0, 0, 0, 0, 'Is that how it can be? Why are demons so worthlessly profane at calling their own kind?', 21384);
INSERT INTO `quest_offer_reward` VALUES (42377, 1, 0, 0, 0, 0, 0, 0, 0, 'Galford... a terrible loss. You have his Spark, though, and I have another. We must press on.', 21287);
INSERT INTO `quest_offer_reward` VALUES (42383, 0, 0, 0, 0, 0, 0, 0, 0, 'Превосходно. Буревестница Милра и герцог Гидраксис – достойные воины. Им можно доверить задание любой сложности.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42384, 1, 0, 0, 0, 0, 0, 0, 0, 'Разведчики только что сообщили мне последние новости. Похоже, как раз вовремя. Сейчас наша помощь необходима действительно многим.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42385, 1, 0, 0, 0, 0, 0, 0, 0, 'Не знаю, кто прислал тебя к нам, но очень надеюсь, что ты $gпришел:пришла;, чтобы помочь.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42386, 1, 0, 0, 0, 0, 0, 0, 0, 'Не знаю, что ты $gсделал:сделала;, но все получилось. Гончих стало гораздо меньше.$B$BНадеюсь, что теперь жители смогут немного отдохнуть. Все-таки для них это не последняя битва.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42387, 1, 0, 0, 0, 0, 0, 0, 0, 'Спасибо, $n. Теперь дети наконец смогут спать спокойно, зная, что за дверью их не поджидают подлые убийцы.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42388, 1, 0, 0, 0, 0, 0, 0, 0, 'Я очень рада, что у нас снова появилась возможность оказать помощь тем, кто в ней нуждается. Твоя работа в Браденсбруке – очередное доказательство того, насколько мир нуждается в помощи Незримого пути.$B$BПока тебя не было, нас буквально завалили рапортами со всего Азерота. Нам очень не хватает людей, и я не уверена, что мы сможем помочь всем.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42389, 1, 0, 0, 0, 0, 0, 0, 0, 'I haven’t met anyone from the order for a long time.$B$BIf you want to offer me a return, I’m afraid now it’s impossible.', 23171);
INSERT INTO `quest_offer_reward` VALUES (42390, 1, 0, 0, 0, 0, 0, 0, 0, 'Ты из того приюта на горе, верно? Наслышан о вашем ордене, дело благородное, хотя, по мне, довольно нудное.', 23171);
INSERT INTO `quest_offer_reward` VALUES (42391, 1, 0, 0, 0, 0, 0, 0, 0, 'Спасибо, что $gпомог:помогла; спасти Кору. Она пока что очень напугана, но с ней все будет в порядке.', 23171);
INSERT INTO `quest_offer_reward` VALUES (42392, 1, 0, 0, 0, 0, 0, 0, 0, 'Yes, you $gsurprised me:surprised me ;, $n. Few hunters can survive the night in harsh conditions among the mountains.', 23171);
INSERT INTO `quest_offer_reward` VALUES (42393, 1, 0, 0, 0, 0, 0, 0, 0, 'Хорошо, что Хилейр вернулась в Приют. Ее опыт и преданность делу очень скоро нам пригодятся.', 23171);
INSERT INTO `quest_offer_reward` VALUES (42394, 1, 0, 0, 0, 0, 0, 0, 0, 'Незримый путь охраняет покой жителей Азерота, даже если сами они об этом не подозревают.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42395, 1, 0, 0, 0, 0, 0, 0, 0, 'I\'m afraid the Burning Legion is preparing an invasion of Dalaran. On my orders, the mages of Kirin Tor have gone on a reconnaissance mission to the demon camps - perhaps they can find out something. And Dalaran could really use some help to protect the Invisible Way. I\'m sure that\'s how the people of the city will be safe.', 23171);
INSERT INTO `quest_offer_reward` VALUES (42396, 5, 0, 0, 0, 0, 0, 0, 0, '<Fin grabs you by the shoulders and jumps joyfully. >$b$bMrgl! Grlgrgrmrgl.', 24015);
INSERT INTO `quest_offer_reward` VALUES (42397, 1, 0, 0, 0, 0, 0, 0, 0, 'Ты $gпроделал:проделала; такой путь, чтобы найти Барона? Давненько к нам не захаживали гости.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42398, 5, 0, 0, 0, 0, 0, 0, 0, 'Не знаю, что за кости вы нашли, но в последний раз я видел Барона таким бодрым, когда он был щенком!', 23222);
INSERT INTO `quest_offer_reward` VALUES (42399, 1, 0, 0, 0, 0, 0, 0, 0, 'Хорошо, что ловчий Блейк теперь с нами. С его ищейкой мы сможем без труда разыскать магов Кадгара.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42400, 1, 0, 0, 0, 0, 0, 0, 0, 'Спасибо, что $gпришел:пришла;, $n!', 23222);
INSERT INTO `quest_offer_reward` VALUES (42401, 1, 0, 0, 0, 0, 0, 0, 0, 'Псарь – древний могущественный демон, который однажды уже вторгался в наш мир.$B$BЕго возвращение может обернуться настоящей катастрофой для магов всего Азерота.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42402, 1, 0, 0, 0, 0, 0, 0, 0, 'Наши друзья собрались здесь, в Приюте Стрелка, и хотят узнать последние новости.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42403, 1, 0, 0, 0, 0, 0, 0, 0, 'Ты из того приюта на горе, верно?', 23222);
INSERT INTO `quest_offer_reward` VALUES (42404, 1, 0, 0, 0, 0, 0, 0, 0, 'If we can help, it\'s our duty to do so. Hakkaru has no place in our world, and we will not let him stay here.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42405, 1, 0, 0, 0, 0, 0, 0, 0, 'У нас действительно могущественные союзники, а в бой нас поведешь ты! У Хаккара нет ни единого шанса.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42406, 1, 0, 0, 0, 0, 0, 0, 0, 'Любопытную породу гончих Скверны вывел псарь.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42407, 1, 0, 0, 0, 0, 0, 0, 0, 'Тревожные вести. Как нам сражаться с Хаккаром, если наше оружие бессильно против его прислужников?$B$BНадо позвать на помощь наших союзников и решить, как мы можем исправить ситуацию.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42408, 1, 0, 0, 0, 0, 0, 0, 0, 'Yes, this is just what you need.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42409, 1, 0, 0, 0, 0, 0, 0, 0, 'Я как раз тебя поджидаю, $n. Присутствие Он\'ары говорит о том, что грядут перемены.$B$BПлемена Крутогорья с давних пор были надежными союзниками Незримого пути.$B$BМои предки гостеприимно встретили Намурию и ее охотников на своей земле, и вместе они выстроили этот приют, в котором ты сейчас и находишься.$B$BИ для меня большая честь служить тебе.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42410, 1, 0, 0, 0, 0, 0, 0, 0, 'Над нами снова нависла древняя угроза, сейчас враг опаснее, чем когда-либо. Азероту нужны охотники, обладающие достаточно сильной волей, чтобы выживать в любых условиях.$B$BТы возглавляешь Незримый путь, а потому многие будут искать твоего покровительства. Покажи им, что выжить возможно.$B$BЯ буду рядом. Вместе мы поможем им сохранить волю к борьбе даже перед лицом такого опасного врага, как Пылающий Легион.', 23171);
INSERT INTO `quest_offer_reward` VALUES (42411, 1, 0, 0, 0, 0, 0, 0, 0, 'Yes, this is only a matter of time - when the scouts need to return home. $B$BOn my territory all these years have been calm. But recently, some clans decided to betray their own traditions and begin serving a king who sided with the Legion. $B$BThose who were under my protection were in the camp of the enemy. I will not allow anyone else to repeat their mistake.', 23171);
INSERT INTO `quest_offer_reward` VALUES (42412, 1, 0, 0, 0, 0, 0, 0, 0, 'Ты $gвернул:вернула; Барона к жизни – старик даже прослезился. А раньше мне и представить было сложно, что я буду скучать по его пронзительному вою. Это просто чудесно, что Барон снова в отличной форме.$B$BМы будем рады прийти на помощь, если понадобится.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42413, 1, 0, 0, 0, 0, 0, 0, 0, 'Ловко же ты $gсправился:справилась; с этой птицей.$B$BМожет, я и ошибался насчет охотников из Приюта. Свое дело вы знаете от и до!$B$BЯ бы с радостью охотился вместе с вами, если, конечно, это охота на крупную дичь!', 23222);
INSERT INTO `quest_offer_reward` VALUES (42414, 1, 0, 0, 0, 0, 0, 0, 0, 'I think even Heming himself couldn\'t have taught me better, although I\'m closer to a real hunt than chasing a big game. I\'ll stick close to you to figure out what it means to be a real hunter. We\'ve got a lot of things to do ahead of us!', 23222);
INSERT INTO `quest_offer_reward` VALUES (42415, 1, 0, 0, 0, 0, 0, 0, 0, 'In the battle with Hackar, you had so much strength and courage - it\'s been a long time since I\'ve seen such a thing.But, as Emmarel said, the battle with the Legion has just begun. If you need help, you can count on the Wanderers.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42416, 1, 0, 0, 0, 0, 0, 0, 0, 'If Supreme Mage Vargot really does practice the magic of Badgers, I\'m just shocked! Thank you for bringing this to our attention.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42418, 1, 0, 0, 0, 0, 0, 0, 0, 'Мы раньше не встречались? Твое лицо мне кажется знакомым.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42420, 0, 0, 0, 0, 0, 0, 0, 0, 'The courtyard of Farondis is very grateful to you.', 22248);
INSERT INTO `quest_offer_reward` VALUES (42421, 0, 0, 0, 0, 0, 0, 0, 0, 'Помраченные благодарят вас.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42422, 0, 0, 0, 0, 0, 0, 0, 0, 'Стражи благодарят вас.', 22248);
INSERT INTO `quest_offer_reward` VALUES (42423, 1, 0, 0, 0, 0, 0, 0, 0, 'Хм... Похоже, это записи верховного мага Варгота о камне души ночнорожденных. Предположительно, энергия плененных в нем душ может быть направлена прямо из камня! Если так, это мощное оружие.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42424, 1, 0, 0, 0, 0, 0, 0, 0, 'Хорошие новости! Один из защитников обнаружил верховного мага Варгота, когда тот входил в хранилища катакомб Сурамара.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42425, 0, 0, 0, 0, 0, 0, 0, 0, '', 21414);
INSERT INTO `quest_offer_reward` VALUES (42428, 1, 0, 0, 0, 0, 0, 0, 0, 'Rensar sent you? As much as I\'d like to help, whatever you need is going to have to wait. As you can see, we have bigger problems.$b$bActually, we could really use your help. If you could lend us your aid then I\'ll do all that I can to help you with whatever it is you\'re here for.$b$bWould this be acceptable?', 21287);
INSERT INTO `quest_offer_reward` VALUES (42429, 273, 1, 0, 0, 0, 0, 0, 0, 'Я вижу, твои глаза горят огнем новообретенного знания, $n.$b$bПосох, что ты держишь в руках, повидал великое множество смертных жизней. <Прокашливается> Он очень многому сможет тебя научить, если ты захочешь прислушаться к нему.$b$bЧто же до воспоминаний... это штука зыбкая и ненадежная. Каждый день на улицах Даларана появляется новый образ. Тебе нужно будет отыскать их все.', 21287);
INSERT INTO `quest_offer_reward` VALUES (42430, 1, 0, 0, 0, 0, 0, 0, 0, 'I\'ve heard from Delandros about how you rescued the Ashen and protected the shrine.$b$bWe all owe you a great deal for your heroism, $n. And it seems that you were able to recover the fangs as well!$B$BI suspect this war is far from over.', 21287);
INSERT INTO `quest_offer_reward` VALUES (42432, 0, 0, 0, 0, 0, 0, 0, 0, 'Finally, we are triumphant, $n! With Xavius\'s defeat, the Nightmare\'s presence will slowly recede from the Emerald Dream.$B$BThe tranquil realm of the sleepers is safe once again. Our druids will work on its restoration and recovery.$B$BThank you, Archdruid $n. Azeroth will see many more serene days and tranquil nights because of you.', 0);
INSERT INTO `quest_offer_reward` VALUES (42433, 1, 0, 0, 0, 0, 0, 0, 0, '$n, your defenders managed to find some very powerful artifacts!', 22566);
INSERT INTO `quest_offer_reward` VALUES (42434, 1, 0, 0, 0, 0, 0, 0, 0, 'Хорошо, что ты $gпришел:пришла;, $n.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42435, 1, 0, 0, 0, 0, 0, 0, 0, 'Хорошо. Теперь мы сможем проникнуть в анклав Небесной академии и посмотреть, чем они там занимаются.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42436, 1, 0, 0, 0, 0, 0, 0, 0, 'Наша дружба с племенем Небесного Рога стала крепкой как никогда – и все благодаря тебе.', 23171);
INSERT INTO `quest_offer_reward` VALUES (42438, 1, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $n.$b$bThe shrine isn\'t safe yet, but at least now it won\'t burn down.', 21287);
INSERT INTO `quest_offer_reward` VALUES (42439, 1, 2, 0, 0, 0, 0, 0, 0, '<Delandros nods.>$b$bThank you, hero. Now that my people are safe we might be able to turn the tide of this battle.', 21287);
INSERT INTO `quest_offer_reward` VALUES (42440, 1, 0, 0, 0, 0, 0, 0, 0, 'What? Verstok fled with the Fangs? That fool, he\'s going to get himself killed!', 21287);
INSERT INTO `quest_offer_reward` VALUES (42444, 0, 0, 0, 0, 0, 0, 0, 0, 'Caw?$B$B<The ravenbear chieftan seems preoccupied with the gathering of reagents for some sort of ritual. Perhaps you could help him?>', 21491);
INSERT INTO `quest_offer_reward` VALUES (42445, 0, 0, 0, 0, 0, 0, 0, 0, 'Caw! Caw! $B$B<The ravenbear chieftan flaps his wings excitedly when he sees the reagents you\'ve brought.>', 21491);
INSERT INTO `quest_offer_reward` VALUES (42446, 0, 0, 0, 0, 0, 0, 0, 0, 'The Blackfeather chieftan bows respectfully to you. $B$BYou have helped ensure the survival of his tribe.', 21491);
INSERT INTO `quest_offer_reward` VALUES (42447, 0, 0, 0, 0, 0, 0, 0, 0, 'It seems that Nithogg wasn\'t interested in the ravenbears\' tribute.$B$BIt\'s probably a good idea to get out of here before he comes back...', 21491);
INSERT INTO `quest_offer_reward` VALUES (42449, 0, 0, 0, 0, 0, 0, 0, 0, 'Мир живых будет вечно проклинать нас за черное дело, которое мы начинаем сегодня...', 23420);
INSERT INTO `quest_offer_reward` VALUES (42451, 0, 0, 0, 0, 0, 0, 0, 0, 'Приятно снова видеть тебя, $n.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42452, 1, 0, 0, 0, 0, 0, 0, 0, 'From the texts you have found it is clear that Ebonchill is in the hands of Balaadur. There is no way he would not claim such a powerful staff. That is a problem, but not an impossible one. All we need to do is get access to Balaadur.', 21267);
INSERT INTO `quest_offer_reward` VALUES (42454, 0, 0, 0, 0, 0, 0, 0, 0, '<Archmage Khadgar has set aside a space here for the Hammer of Khaz\'goroth, as well as the rest of the Pillars of Creation.>', 21414);
INSERT INTO `quest_offer_reward` VALUES (42455, 1, 0, 0, 0, 0, 0, 0, 0, 'Excellent! Honestly I\'m surprised you mages no longer use Mana Gems, I found them terribly useful.$b$bAt any rate these will make up for the lack of additional mages, at least once anyway.', 21267);
INSERT INTO `quest_offer_reward` VALUES (42476, 1, 0, 0, 0, 0, 0, 0, 0, 'This is better than I hoped. A working ward prototype will make it easier to replicate and the ritual notes as well!$b$bI\'m beginning to believe this is possible!', 21267);
INSERT INTO `quest_offer_reward` VALUES (42477, 1, 0, 0, 0, 0, 0, 0, 0, 'Our order suffered a great loss that day, it is a shame we chose to hide it instead of study it.$b$bYou have another critical element to the ritual, and we are one step closer.', 21267);
INSERT INTO `quest_offer_reward` VALUES (42479, 1, 0, 0, 0, 0, 0, 0, 0, 'Your ability continues to astound me. Balaadur lies dead in his own realm and you have Ebonchill. I look forward to seeing what you will do next.', 21287);
INSERT INTO `quest_offer_reward` VALUES (42483, 0, 0, 0, 0, 0, 0, 0, 0, '<A note from the goblins is left behind, along with an old magnifying glass>$B$BI wish I coulda been there to see the look on your face, but Raz and I got bigger fish to fry!$B$BThanks for the trophies and meat. I\'m sure they\'ll fetch a nice sum on the black market, along with all the goodies we got from those stupid tauren! I\'m sure they won\'t be needing them any more!$B$BSo long sucker!', 21287);
INSERT INTO `quest_offer_reward` VALUES (42484, 0, 0, 0, 0, 0, 0, 0, 0, 'Отлично, $gВладыка смерти:Владычица смерти;. При жизни он был одним из величайших генералов Орды, а после смерти стал твоим первым Всадником!', 23420);
INSERT INTO `quest_offer_reward` VALUES (42486, 273, 0, 0, 0, 0, 0, 0, 0, 'О, моя дорогая Корина! Спасибо тебе, спасибо!', 24015);
INSERT INTO `quest_offer_reward` VALUES (42487, 0, 0, 0, 0, 0, 0, 0, 0, 'The Ventire is so kind.', 24015);
INSERT INTO `quest_offer_reward` VALUES (42488, 0, 0, 0, 0, 0, 0, 0, 0, 'Как нам повезло, что усилитель удалось собрать! Спасибо тебе!', 24015);
INSERT INTO `quest_offer_reward` VALUES (42489, 0, 0, 0, 0, 0, 0, 0, 0, 'It is impossible to express gratitude for that in words.', 24015);
INSERT INTO `quest_offer_reward` VALUES (42490, 0, 0, 0, 0, 0, 0, 0, 0, 'Спасибо. Хоть об этом мне можно больше не беспокоиться.', 24015);
INSERT INTO `quest_offer_reward` VALUES (42491, 0, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно! Я немедленно ее изучу.', 24015);
INSERT INTO `quest_offer_reward` VALUES (42493, 1, 0, 0, 0, 0, 0, 0, 0, 'Так значит, это правда. Верховный маг Варгот поддался воздействию Легиона.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42494, 1, 0, 0, 0, 0, 0, 0, 0, 'Молодец, $n.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42501, 0, 0, 0, 0, 0, 0, 0, 0, '<The coded message you found is sure to contain a lead on the cult\'s activities. You need only a way to decipher its contents.>', 21287);
INSERT INTO `quest_offer_reward` VALUES (42502, 0, 0, 0, 0, 0, 0, 0, 0, '<The cipher you found will allow you to intercept the cult\'s communications, bringing you one step closer to Garona.>', 21287);
INSERT INTO `quest_offer_reward` VALUES (42503, 0, 0, 0, 0, 0, 0, 0, 0, '<This does not bode well for the citizens of Darkshire.>', 21287);
INSERT INTO `quest_offer_reward` VALUES (42504, 396, 0, 0, 0, 0, 0, 0, 0, 'Снова держать эти клинки было настоящей пыткой... Теперь они твои.', 22624);
INSERT INTO `quest_offer_reward` VALUES (42508, 0, 0, 0, 0, 0, 0, 0, 0, 'Я задержу демонов у входа, а ты иди внутрь.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42510, 1, 0, 0, 0, 0, 0, 0, 0, 'Мы должны подготовиться. Впереди еще много дел.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42512, 0, 0, 0, 0, 0, 0, 0, 0, 'The village. It... it is just... gone. They told me what happened to Riverbend but I figured that was an exageration.', 21384);
INSERT INTO `quest_offer_reward` VALUES (42516, 1, 0, 0, 0, 0, 0, 0, 0, 'Good. Your weapon is growing in power, as are you.', 21249);
INSERT INTO `quest_offer_reward` VALUES (42517, 1, 0, 0, 0, 0, 0, 0, 0, 'Значит, все прошло хорошо?$B$BПосле такого ритуала нужно время, чтобы восстановить силы. Нельзя проводить его чаще одного раза в день.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42519, 0, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно. Эммарель Стражница Тени и Лорен Штормовое Копыто – достойные защитники. Им можно доверить задание любой сложности.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42520, 1, 0, 0, 0, 0, 0, 0, 0, 'Supreme Magician Vargot is a terrible loss to our order, $n. Truly, it\'s dark times.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42521, 1, 0, 0, 0, 0, 0, 0, 0, 'Надеюсь, тебе удастся найти упорству Миллхауса достойное применение!', 22566);
INSERT INTO `quest_offer_reward` VALUES (42522, 6, 0, 0, 0, 0, 0, 0, 0, '$gReady:Ready; $n?', 23222);
INSERT INTO `quest_offer_reward` VALUES (42523, 1, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно, твои защитники добились успеха! Готовься встречать новичков.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42524, 1, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно. Эти бойцы очень помогут нам в обороне Расколотых островов. Ты можешь отправлять их на задания так же, как и наших защитников.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42525, 1, 0, 0, 0, 0, 0, 0, 0, 'Успех этого разведывательного задания превзошел наши самые смелые ожидания, $n. Твоим войскам удалось спасти блестящего стратега, мастера выживания Бана.$B$BВ благодарность он согласился нам помочь.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42526, 1, 0, 0, 0, 0, 0, 0, 0, 'Отлично, $n. Мастер выживания Бан решил здесь поселиться, так что ты сможешь обратиться к нему в любое время, когда захочешь заняться улучшениями.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42533, 0, 0, 0, 0, 0, 0, 0, 0, 'Что заставило тебя вторгнуться в мое королевство, рыцарь смерти? Отвечай быстро, я не привык долго ждать...', 23420);
INSERT INTO `quest_offer_reward` VALUES (42534, 0, 0, 0, 0, 0, 0, 0, 0, 'Скоро головы моих врагов украсят стены этого королевства! И эти тролли – только начало.$b$bДаже Сильвана не посмеет ступить на земли свободного королевства Стромгард!', 23420);
INSERT INTO `quest_offer_reward` VALUES (42535, 0, 0, 0, 0, 0, 0, 0, 0, 'Ты очень $gпомог:помогла; моему королевству, рыцарь смерти. Стромгард не забудет того, что ты $gсделал:сделала; для нас.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42536, 0, 0, 0, 0, 0, 0, 0, 0, 'Мы и так уже задержались здесь, $gВладыка смерти:Владычица смерти;. Пришло время открыть гробницу и воскресить нашего защитника.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42537, 0, 0, 0, 0, 0, 0, 0, 0, 'Сегодня легендарный король восстал из мертвых, чтобы снова поднять свой меч и защищать свободу.$b$bМы почтем за честь сражаться с ним плечом к плечу против демонических полчищ.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42539, 0, 0, 0, 0, 0, 0, 0, 0, '<Your next destination is clear: Stormwind. But where is Garona?>', 21287);
INSERT INTO `quest_offer_reward` VALUES (42567, 0, 0, 0, 0, 0, 0, 0, 0, '$n? Is that you, my friend?$b$b<Runas coughs violently.>', 21384);
INSERT INTO `quest_offer_reward` VALUES (42568, 396, 0, 0, 0, 0, 0, 0, 0, 'If you\'re with me, coat your blades in poison now. This isn\'t going to be easy.', 21287);
INSERT INTO `quest_offer_reward` VALUES (42583, 0, 0, 0, 0, 0, 0, 0, 0, 'Good. I know Zen\'tabra and Naralex, and they will serve the Dreamgrove bravely.', 21249);
INSERT INTO `quest_offer_reward` VALUES (42584, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent, $n. Druids are already beginning to arrive. Zen\'tabra and Naralex have done well.', 21384);
INSERT INTO `quest_offer_reward` VALUES (42585, 0, 0, 0, 0, 0, 0, 0, 0, 'Отлично. Я знал, что сестра Лилит нас не подведет.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42586, 0, 0, 0, 0, 0, 0, 0, 0, 'Наши защитники не только справились с заданием, но и нашли лист, на котором записаны слова древнего знания. Возможно, он заинтересует Листоборода Сказителя – древо мудрости, которое обитает к северу отсюда.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42588, 0, 0, 0, 0, 0, 0, 0, 0, 'Отлично! Вижу, советы Листоборода нам уже помогают.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42590, 0, 0, 0, 0, 0, 0, 0, 0, 'Your kindness will not be forgotten. You are a friend to my family, $n.', 21249);
INSERT INTO `quest_offer_reward` VALUES (42593, 6, 0, 0, 0, 0, 0, 0, 0, 'Supreme Mother?\n\n< The supreme magician blushes, coughs up and sneaks around. >\n\nYes, yes, of course. For Evil, anything.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42594, 1, 0, 0, 0, 0, 0, 0, 0, 'Я позабочусь, чтобы гримуар попал к Злобе, – она сможет использовать сокрытые в нем знания.\n\nКстати, пора провести еще одну разведывательную операцию в Черном храме.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42596, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, friend to Highmountain.', 21249);
INSERT INTO `quest_offer_reward` VALUES (42597, 0, 0, 0, 0, 0, 0, 0, 0, 'It is as I predicted. You continue to grow in power and prove your worth.\r\n\r\nThe time has come for you to take command of my armies, $n.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42598, 0, 0, 0, 0, 0, 0, 0, 0, 'Ты $gготов:готова; вести свои силы в битву. Итак, начнем!', 22996);
INSERT INTO `quest_offer_reward` VALUES (42601, 1, 0, 0, 0, 0, 0, 0, 0, 'Превосходный выбор, $n. Архивариус Мелинда решила остаться в разломе Зловещего Шрама, поэтому ты сможешь поговорить с ней, когда захочешь заняться улучшениями оплота класса.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42602, 1, 66, 0, 0, 0, 500, 0, 0, 'Успех этого разведывательного задания превзошел наши самые смелые ожидания, $n. Твоим войскам удалось спасти блестящего стратега, архивариуса Мелинду.$B$BВ благодарность она согласилась нам помочь.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42603, 1, 0, 0, 0, 0, 0, 0, 0, 'Ритцун и Калид смогли кое-что выяснить, но кто знает, можно ли доверять этим данным, учитывая то, как они были получены.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42605, 1, 0, 0, 0, 0, 0, 0, 0, 'Один поставил тебя командовать нами? Я с тобой. Всегда говорил, что спорить с богами – последнее дело.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42606, 1, 0, 0, 0, 0, 0, 0, 0, 'Я буду сражаться в твоей армии, $n. Я видела, на что ты $gспособен:способна;, ты не боишься заглянуть в лицо смерти.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42607, 0, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно. Хьялмар быстро приведет валарьяров в боевую форму.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42608, 1, 0, 0, 0, 0, 0, 0, 0, 'То, что Ритцун отказался от своего положения, уступив первенство тебе, говорит о многом. Похоже, он верит, что именно ты сумеешь возродить Совет.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42609, 0, 0, 0, 0, 0, 0, 0, 0, 'The case is moving! Soon you will be at the head of a powerful army.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42610, 1, 0, 0, 0, 0, 0, 0, 0, 'Отлично! Мастерство заклинателя рун сделает наши армии еще сильнее.', 22522);
INSERT INTO `quest_offer_reward` VALUES (42611, 1, 0, 0, 0, 0, 0, 0, 0, 'Мудрый выбор, $n.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42614, 1, 0, 0, 0, 0, 0, 0, 0, 'Мне надоело торчать в мире мертвых. Покажи мне врагов, и я с ними расправлюсь!', 22996);
INSERT INTO `quest_offer_reward` VALUES (42616, 1, 0, 0, 0, 0, 0, 0, 0, 'If you hadn\'t helped; I, my vow of defender Uldoor, would have been broken. I will not let you down!', 23420);
INSERT INTO `quest_offer_reward` VALUES (42618, 1, 0, 0, 0, 0, 0, 0, 0, 'Ты $gпроявил:проявила; удивительную храбрость в Ульдуаре, $n. Я сдержу клятву и отвечу на зов Гьяллархорна.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42619, 1, 0, 0, 0, 0, 0, 0, 0, 'Thank you for your help, $n. It would be an honor to fight with you in Odin\'s army.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42622, 0, 0, 0, 0, 0, 0, 0, 0, 'Nice! Got \'em! Feel free to play the drums, but leave them down here, will you? You\'re alright by me, $n.', 21287);
INSERT INTO `quest_offer_reward` VALUES (42627, 396, 0, 0, 0, 0, 0, 0, 0, 'Every second I held those blades again was torture. They\'re all yours.', 21287);
INSERT INTO `quest_offer_reward` VALUES (42630, 0, 0, 0, 0, 0, 0, 0, 0, 'That was some nice work you did there, $n. Thanks for the assist.', 21249);
INSERT INTO `quest_offer_reward` VALUES (42634, 0, 0, 0, 0, 0, 0, 0, 0, 'Who would have thought he would be working with us?$B$BWe are now one step closer to taking the fight directly to the Burning Legion on their own worlds.', 0);
INSERT INTO `quest_offer_reward` VALUES (42635, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent! Luckily I brought a copy of the vrykul cipher that the League procured from Northrend.$B$BI\'ll get to work on this straightforth! Let us hope the dialects are not too dissimilar.', 21287);
INSERT INTO `quest_offer_reward` VALUES (42639, 0, 0, 0, 0, 0, 0, 0, 0, 'You have the amulet! Most excellent!$B$BOf course, that probably won\'t help you with the pirates that are already mad as hatters, but small victories, eh chap?', 21287);
INSERT INTO `quest_offer_reward` VALUES (42640, 0, 0, 0, 0, 0, 0, 0, 0, 'Well that was most noble of you to save these ancient relics from a water-logged fate.$B$BI will be sure that the Explorer\'s League puts these to good use!', 21287);
INSERT INTO `quest_offer_reward` VALUES (42641, 0, 0, 0, 0, 0, 0, 0, 0, 'Oh, the stake didn\'t work you say?  My apologies, I must\'ve had my immortal evil undead mixed up.$B$BYou\'re all in one piece though, and the evil has seen its end. Hopefully, at least.$B$BVampyr can die, can\'t they?', 21287);
INSERT INTO `quest_offer_reward` VALUES (42645, 0, 0, 0, 0, 0, 0, 0, 0, 'Good riddance to bad rubbish, I say. $B$BThose louts were ne\'er-do-wells before they went crazy. Just imagine what they would do if we let them run rampant!$B$BCheers to your bravery, sir.', 21287);
INSERT INTO `quest_offer_reward` VALUES (42650, 0, 0, 0, 0, 0, 0, 0, 0, 'Я чувствую, что моя душа снова принадлежит мне!', 22522);
INSERT INTO `quest_offer_reward` VALUES (42651, 0, 0, 0, 0, 0, 0, 0, 0, 'Йорхуттам! Нужно немедленно выследить великого червя. Битва обещает быть грандиозной.', 22522);
INSERT INTO `quest_offer_reward` VALUES (42653, 0, 0, 0, 0, 0, 0, 0, 0, 'I am encouraged that we have come to an understanding. I admired Altruis when he opposed the direction Illidan was taking all those years ago. While the annihilation of the Burning Legion is of paramount importance, in the process, we must not become that which we hope to destroy.$B$BIf you would have me as a champion, I will bring all of my knowledge to bear on the task at hand. With time, I believe that we might even come to trust each other.', 0);
INSERT INTO `quest_offer_reward` VALUES (42654, 1, 0, 0, 0, 0, 0, 0, 0, 'Из этого дуба получатся ладные стрелы.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42655, 1, 0, 0, 0, 0, 0, 0, 0, 'Похоже, здесь предостаточно руды, чтобы сделать оружие и боеприпасы для наших друзей.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42656, 1, 0, 0, 0, 0, 0, 0, 0, 'Кузнец Халдарона знает свое дело. Но это оружие нужно еще зачаровать, чтобы оно работало против гончих Хаккара.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42657, 1, 0, 0, 0, 0, 0, 0, 0, 'Часовые предупредили, что ты придешь.$B$BПрошу прощения за беспорядок. Мне пришлось разбираться с непрошеными гостями.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42658, 1, 0, 0, 0, 0, 0, 0, 0, 'Останься ты здесь, со мной, я могла бы сделать гораздо больше. Но я знаю, что у тебя много других срочных дел.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42659, 1, 0, 0, 0, 0, 0, 0, 0, 'Ты даже не представляешь, какое это облегчение – видеть тебя $gживым:живой;!', 23420);
INSERT INTO `quest_offer_reward` VALUES (42660, 1, 0, 0, 0, 0, 0, 0, 0, 'Пожрав сердце и получив его силу, кровавый камень станет достаточно могущественным, чтобы подчинить себе эредарских сестер.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42662, 1, 66, 0, 0, 0, 500, 0, 0, 'Синие драконы всегда были воплощением магии Азерота, и сейчас как никогда нам нужно объединить свои силы против Пылающего Легиона. Для меня будет честью сражаться на твоей стороне в эти темные времена.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42663, 1, 0, 0, 0, 0, 0, 0, 0, 'Supreme Wizard Keileck and Supreme Wizard Modera are the greatest wizards of all time in Azeroth. They will be honorary representatives of our order.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42664, 1, 15, 0, 0, 0, 0, 0, 0, 'Меня всегда восхищала своеобразная идеология Иллидана. В конце концов, первоочередная задача – уничтожить Пылающий Легион. Все остальное не важно. Ничто не должно стоять на нашем пути.\n\nЯ встану на вашу сторону. Все мои знания и сила будут брошены на выполнение нашей общей задачи. Придет время, и мы уничтожим всех наших врагов. А возможно, и Акаму.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42665, 1, 661, 0, 0, 0, 0, 0, 0, 'Until today, the Illidari have been in close circle - only demon hunters. But you managed to prove to everyone that if we want to move on and trust each other, then accepting Akama into our ranks will be an important step.\r\n\r\nIt gives hope.\r\n\r\nAnd as for Kor\'vas, all the more so no one doubts. In battle, she has no equal.\r\n\r\nWhat is it?..', 23222);
INSERT INTO `quest_offer_reward` VALUES (42666, 0, 0, 0, 0, 0, 0, 0, 0, 'Commander. I am glad to see that Asha was able to find you so quickly.$B$BThere is something important that you must attend to as soon as possible.', 0);
INSERT INTO `quest_offer_reward` VALUES (42669, 1, 0, 0, 0, 0, 0, 0, 0, 'Теперь мы знаем, что Кария и Варедис скрываются в демоническом мире, который называется Нискара.\n\nПора с ними покончить.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42670, 1, 0, 0, 0, 0, 0, 0, 0, 'Тебе удалось завербовать первых двух защитников. Впереди нас ждут великие дела, $n.\n\n<Кор\'вас бормочет себе под нос, что-то ругательное и связанное с тем, что ее не выбрали.>', 23222);
INSERT INTO `quest_offer_reward` VALUES (42671, 1, 0, 0, 0, 0, 0, 0, 0, 'Тебе удалось завербовать первых двух защитников. Впереди нас ждут великие дела, $n.\n\n<Кор\'вас бормочет себе под нос, что-то ругательное и связанное с тем, что ее не выбрали.>', 23222);
INSERT INTO `quest_offer_reward` VALUES (42673, 1, 4, 0, 0, 0, 0, 0, 0, 'Самое время, $gдурачок:глупышка;. Когда ты $gвыбрал:выбрала; эту Ашу Воронью Песнь, я уж начала сомневаться, все ли у тебя в порядке с головой.\n\nНичего не имею против Аши. Но разве не ясно, кто из нас лучший боец?\n\nДа?\n\nЛадно. Конечно, я стану твоим защитником. Предложение принято.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42677, 1, 0, 0, 0, 0, 0, 0, 0, 'Ну, теперь хотя бы увидим новые лица. Если так можно сказать про сломленных.\n\n<Кор\'вас ухмыляется.>', 23222);
INSERT INTO `quest_offer_reward` VALUES (42678, 0, 0, 0, 0, 0, 0, 0, 0, 'У нас все получилось. Наконец-то мы сможем прочитать зашифрованное письмо ШРУ.', 22248);
INSERT INTO `quest_offer_reward` VALUES (42679, 273, 0, 0, 0, 0, 0, 0, 0, 'Превосходно. С этими Пеплоустами ты сможешь размещать войска там, где сочтешь нужным.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42680, 1, 273, 0, 0, 0, 0, 0, 0, 'Надо признаться, все это неожиданно. Матиас Шоу взят в плен Легионом.\n\nПо крайней мере, это объясняет странные действия, которые недавно предприняло ШРУ. Ими сейчас командует повелитель ужаса.', 22248);
INSERT INTO `quest_offer_reward` VALUES (42681, 1, 0, 0, 0, 0, 0, 0, 0, 'Они вернулись и привели его. Это Лорам, да. Но он заперт в теле повелителя ужаса Разелиха Осквернителя!\n\nПока мы не убедимся, что он не представляет опасности, по моему приказу его будут держать в ловушке для демонов.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42682, 6, 0, 0, 0, 0, 0, 0, 0, 'Нам правда нужно все это, чтобы установить контакт с душой Иллидана?', 23222);
INSERT INTO `quest_offer_reward` VALUES (42683, 1, 274, 0, 0, 0, 0, 0, 0, 'Это добрые вести. Он начинает вносить вклад в общее дело, и мы с его помощью становимся гораздо сильнее.\n\nОднако он не может полностью контролировать Разелиха. Думаю, будет лучше оставить его в ловушке, пока не подыщем для Лорама новое тело. Как считаешь?\n\nИ если он будет доставлять слишком много хлопот, мы примем другие меры.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42684, 1, 273, 0, 0, 0, 0, 0, 0, 'Прекрасно сработано. Наши разведчики докладывают, что диверсии проходят весьма успешно.\n\nПомимо этого мои подозрения насчет одного предмета, который понадобится при расшифровке письма ШРУ, прояснились в Штормхейме.', 22124);
INSERT INTO `quest_offer_reward` VALUES (42685, 1, 66, 0, 0, 0, 500, 0, 0, 'Отрадно видеть, что орден Стражей Тирисфаля возрождается, $n. В Совете Шестерых не все со мной согласны, но я считаю, что мы просто обязаны работать вместе, чтобы покончить с Пылающим Легионом раз и навсегда!', 22566);
INSERT INTO `quest_offer_reward` VALUES (42687, 0, 0, 0, 0, 0, 0, 0, 0, 'Нам повезло, что среди выживших оказалась летописец Элрианна. Это уважаемый архивариус из Кирин-Тора. Несколько месяцев она провела вместе с пандаренами, изучая труды, собранные в библиотеке монастыря Тянь.$B$BЕе знания о древней магии очень ценны для нашего ордена.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42688, 54, 0, 0, 0, 0, 0, 0, 0, 'Млрглрм! Рглрлглр, грмлмрлг лмрглрг мрлгмрлг! Млрлг грмлгр.', 24015);
INSERT INTO `quest_offer_reward` VALUES (42689, 1, 0, 0, 0, 0, 0, 0, 0, 'Фаронаар кишит демонами – виданными и невиданными.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42690, 0, 0, 0, 0, 0, 0, 0, 0, 'You honor me, $n. Of course I will serve as your champion and right-hand man. How could I refuse?$B$BWe have only just begun to change the way in which the Illidari operate, but already, you have made great strides. If we are to defeat the Burning Legion, it must be done in concert with everyone working together. Not just the Illidari standing alone.$B$BAnd not standing upon the smoking husk of a world that we have carelessly destroyed while trying to save it.', 0);
INSERT INTO `quest_offer_reward` VALUES (42691, 1, 0, 0, 0, 0, 0, 0, 0, 'Эти внутренности буквально излучают магию. Они станут отличной приманкой.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42692, 1, 1, 0, 0, 0, 0, 0, 0, 'This Prince Farondis opposed Queen Azshara, so she got mad and blew everything up? That\'s awesome!$b$bHey Olothil, listen to this!', 21384);
INSERT INTO `quest_offer_reward` VALUES (42693, 1, 1, 0, 0, 0, 0, 0, 0, 'Perfect. Let\'s get this ritual started, shall we?', 21384);
INSERT INTO `quest_offer_reward` VALUES (42694, 1, 1, 0, 0, 0, 0, 0, 0, 'After over ten thousand years... to see mother and father again...$b$b<The elven mage is at a loss for words.>$b$bThank you, $n. Thank you.', 21384);
INSERT INTO `quest_offer_reward` VALUES (42695, 1, 273, 0, 0, 0, 0, 0, 0, 'Это честь для меня, $n. Конечно, я стану твоим защитником и помощником. Разве может быть иначе?\n\nМы только начали воплощать в жизнь разработанный план, но тебе уже удалось сделать большие шаги вперед. Если мы хотим одолеть Пылающий Легион, то не должны терять бдительность. Все прочие цели меркнут в сравнении с этой. На чаше весов – судьба всего сущего.\n\nВ конце концов победа будет нашей... и неважно, какой ценой она нам достанется!', 23222);
INSERT INTO `quest_offer_reward` VALUES (42696, 0, 0, 0, 0, 0, 0, 0, 0, 'Отлично, $n! Я вижу, что Оплот Хранителя в надежных руках.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42697, 6, 1, 0, 0, 0, 1000, 0, 0, 'Ты хочешь, чтобы я стала одним из твоих защитников?\n\n<Аша какое-то время раздумывает.>\n\nЯ соглашусь, но при одном условии. Я хочу на поле боя. Хоть от меня и есть польза здесь, на \"Молоте Скверны\", мои таланты больше пригодились бы в Азероте.\n\nКроме того, мы ведь по-прежнему собираемся поставить Пылающий Легион на колени?', 23222);
INSERT INTO `quest_offer_reward` VALUES (42701, 1, 66, 0, 0, 0, 0, 0, 0, 'Редко выпадает такой шанс – стать не просто слугой, а одним из иллидари. Должна признаться, я беспокоилась, что ты меня не выберешь в защитники.\n\nНо, как оказалось, напрасно – я так тебе благодарна!\n\nМне всегда казалось странным, почему по всем мирам обязательно нужно проходиться огнем и мечом, когда можно жить в свое удовольствие? Саргерас бывает таким занудой. Вместе мы поставим Пылающий Легион на колени, и они будут ползать в грязи.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42702, 1, 66, 0, 0, 0, 500, 0, 0, 'What, a bad bulwark, $n. I\'m sure there\'s a couple of funny secrets to dig up. I think I\'m gonna stay! Uh... Unless, of course, you don\'t mind.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42703, 1, 0, 0, 0, 0, 0, 0, 0, 'Приветствую, $n. Огни Азерота дают нам возможность заглядывать вперед.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42704, 1, 66, 0, 0, 0, 500, 0, 0, 'Чародейский разрушитель издает высокий звук и замирает в ожидании.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42705, 1, 66, 0, 0, 0, 500, 0, 0, 'Мне слишком долго пришлось ходить в учениках у верховного мага Варгота. Наверное, пора сменить наставника. Почту за честь стать твоим защитником, $n.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42706, 1, 66, 0, 0, 0, 500, 0, 0, 'Из нас получилась отличная команда. Я с радостью предоставлю тебе мои клинки и заклинания для борьбы с Пылающим Легионом, $n.', 22566);
INSERT INTO `quest_offer_reward` VALUES (42707, 1, 66, 0, 0, 0, 500, 0, 0, 'Хорошо, ты как раз вовремя! Это не даст Мерилу умереть, пока мы ищем того, кто наслал заклинание.', 22594);
INSERT INTO `quest_offer_reward` VALUES (42708, 0, 0, 0, 0, 0, 0, 0, 0, 'Это разозлит Сильвану, но у нас есть более важные дела, чем беспокоиться о настроении Темной Госпожи.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42719, 1, 0, 0, 0, 0, 0, 0, 0, 'Ох! Хорошая вода, такая чистая и волшебная! Спасибо тебе, $gгерой:воительница;!', 23360);
INSERT INTO `quest_offer_reward` VALUES (42722, 1, 0, 0, 0, 0, 0, 0, 0, 'Ты их $gосвободил:освободила;? Здорово. Мне тоже не нравилось сидеть в клетке...', 24015);
INSERT INTO `quest_offer_reward` VALUES (42728, 0, 0, 0, 0, 0, 0, 0, 0, 'Мрргмл... млрглмлрг. Млрмглрг гмрлр!', 24015);
INSERT INTO `quest_offer_reward` VALUES (42730, 1, 0, 0, 0, 0, 0, 0, 0, 'Никаких камней за пазухой, так ведь? Дело есть дело, и дела идут неплохо. Когда я вижу возможность, я ее использую, как и ты.\n\nЧтобы показать свою лояльность твоей маленькой организации, а также в знак моего расположения, я даю тебе образец продукта. Он обладает волшебными свойствами. Пей до дна!', 22124);
INSERT INTO `quest_offer_reward` VALUES (42731, 1, 25, 0, 0, 0, 0, 0, 0, 'Yes, we did everything that Syrah the Moonguard ordered, but this is not enough for her.\n\nShe asks for one more service, and she wants you to do $g:do it; it personally.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42732, 1, 6, 0, 0, 0, 0, 0, 0, 'It\'s so hard to get, and it\'s worth a lot. That\'s impressive! I can\'t wait to see what happens.\r\n\r\nWell, how about we get on with our new assignment? Let the villains know no peace.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42733, 1, 6, 0, 0, 0, 0, 0, 0, 'Конечно. Как и другие сооружения Легиона, \"Молот Скверны\" подпитывается энергией душ. Ты $gсобрал:собрала; действительно могущественные души.\n\nИх энергии должно хватить, чтобы отправить нас куда угодно.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42734, 1, 66, 0, 0, 0, 500, 0, 0, 'Тебе удалось совершить невозможное, $n. Мы все в долгу перед тобой. Я с радостью объявляю тебя Верховным магом!', 23360);
INSERT INTO `quest_offer_reward` VALUES (42735, 6, 1, 0, 0, 0, 0, 0, 0, '$n, is that you? Who\'d have thought that would happen?\n\nNow you\'re out there, and I\'m in a cage.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42736, 1, 6, 0, 0, 0, 0, 0, 0, 'Ты превосходишь мои ожидания. Отлично. Всегда приятно учиться на собственных ошибках.\n\nПоможешь мне с еще одним небольшим дельцем?', 23360);
INSERT INTO `quest_offer_reward` VALUES (42737, 0, 0, 0, 0, 0, 0, 0, 0, '<Rune stone began to vibrate as you approached it, holding the rune in your hand.>', 23360);
INSERT INTO `quest_offer_reward` VALUES (42738, 0, 0, 0, 0, 0, 0, 0, 0, '< Ragnar\'s rune gets warmer as you approach the runes stone. >', 23360);
INSERT INTO `quest_offer_reward` VALUES (42739, 0, 0, 0, 0, 0, 0, 0, 0, '< The moon and the runes stone begin to glow and buzz with growing power.>', 23360);
INSERT INTO `quest_offer_reward` VALUES (42740, 1, 1, 0, 0, 0, 0, 0, 0, 'Ah, $n! You\'re here! Well, at least you got out of this trap alive... We lost so much out there... $b$b<Genn Sedogriv clenches his teeth. >$b$bDamn the Horde for leaving us alone with the Legion!', 21863);
INSERT INTO `quest_offer_reward` VALUES (42747, 0, 0, 0, 0, 0, 0, 0, 0, '', 21491);
INSERT INTO `quest_offer_reward` VALUES (42748, 0, 0, 0, 0, 0, 0, 0, 0, '', 21491);
INSERT INTO `quest_offer_reward` VALUES (42749, 0, 0, 0, 0, 0, 0, 0, 0, '<Malassa whispers in your ear as she walks away >\n\nOkay, illidari, a deal is a deal. The sargerite key was always kept in the Kazemat Sentinels next to the body of Illidan - it was hidden in the platform itself.\n\n<Satem Malassa tells you which stones you need to touch on the platform in Sentinels Kazemata to access the sargerite key. After that, it disappears into the clubs of smoke.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42750, 0, 0, 0, 0, 0, 0, 0, 0, 'I knew Leirana... she was one of Remulos\' favored, and kindhearted soul. If he has been captured as she believes, we have no time to mourn her passing.', 21491);
INSERT INTO `quest_offer_reward` VALUES (42751, 0, 0, 0, 0, 0, 0, 0, 0, 'n. May your path be blessed.Boneflux', 21491);
INSERT INTO `quest_offer_reward` VALUES (42752, 5, 0, 0, 0, 0, 0, 0, 0, 'Наконец-то мы отомстили Кордане Оскверненной Песни за всех охотников на демонов, которые из-за нее стали скверноподданными. Циана, Тиратон, Глевианна, Кейлин, Иллисанна и другие...\n\nСмерть уже давно должна была настигнуть ее.\n\nСаргеритовый ключ снова у нас. Теперь мы можем смотреть в будущее с надеждой.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42753, 0, 0, 0, 0, 0, 0, 0, 0, 'Finally, revenge against Cordana Felsong for all of the demon hunters whom she tempted into becoming Felsworn. Cyana, Tirathon, Glayvianna, Cailyn, Illysanna, and more.$B$BHer death has been a long time coming.$B$BThe Sargerite Keystone is once more in our possession. Now, to look to the future.', 0);
INSERT INTO `quest_offer_reward` VALUES (42754, 1, 0, 0, 0, 0, 0, 0, 0, 'Это то, что надо. Лучше и не найти.\n\nСкажи, демоны сильно злились, когда у них стащили эти ценные детали?', 23420);
INSERT INTO `quest_offer_reward` VALUES (42756, 0, 0, 0, 0, 0, 0, 0, 0, 'Hah! Ha ha ha haaaa!$b$bI haven\'t felt this alive in YEARS!$b$bWhat you\'ve done, $n - not only to replenish my mana pools, but also to save my brood - will not be forgotten. The blues of Azsuna are ever in your debt.', 21384);
INSERT INTO `quest_offer_reward` VALUES (42758, 0, 0, 0, 0, 0, 0, 0, 0, 'Blimey! This one still has blood on it! Looks like he got a good bite on ye good, lad!', 0);
INSERT INTO `quest_offer_reward` VALUES (42762, 1, 0, 0, 0, 0, 0, 0, 0, 'You seek my staff? You make me laugh!$bYou say you want my help and time?$bOkay then, wikket, hear this rhyme:$b$bThree new riddles will I ask,$bThree true answers are your task.$bProve you\'re smart, show you\'re worth it.$bAnswer the King and we\'ll unearth it!', 21491);
INSERT INTO `quest_offer_reward` VALUES (42765, 0, 0, 0, 0, 0, 0, 0, 0, 'I knew you\'d succeed, $n. I\'m honored to be at your service.', 21491);
INSERT INTO `quest_offer_reward` VALUES (42766, 0, 0, 0, 0, 0, 0, 0, 0, 'Them greenies were really curious about this smelly brew. It\'s powerful and smelly, I wonder if it\'s delicious too?', 21491);
INSERT INTO `quest_offer_reward` VALUES (42767, 0, 0, 0, 0, 0, 0, 0, 0, 'For this grain, the wikkets work real hard. They toil and struggle so they do not starve.$b$bWith these bags safe from the greenies, we should do the right thing and give them back.$b$bOkay, maybe we take a bit for ourselves, probably just one sack.', 21491);
INSERT INTO `quest_offer_reward` VALUES (42768, 396, 11, 0, 0, 0, 0, 0, 0, 'This splashie spirit core holds far more water than you might think. Enough to fill a big bowl or a small sink!$b$bI bet we can find some good use for it, like squeezing it out to make ookin\' excellent slerp!', 21491);
INSERT INTO `quest_offer_reward` VALUES (42770, 1, 0, 0, 0, 0, 0, 0, 0, 'These brave watchers require our aid. The Light may well ignore our prayers if we do not help our brethren.', 21287);
INSERT INTO `quest_offer_reward` VALUES (42771, 396, 0, 0, 0, 0, 0, 0, 0, 'Those poor souls are finally at peace now. Thank you.', 21287);
INSERT INTO `quest_offer_reward` VALUES (42772, 0, 0, 0, 0, 0, 0, 0, 0, 'Good. With some time the last few lingering souls will make their way home.', 21287);
INSERT INTO `quest_offer_reward` VALUES (42773, 0, 0, 0, 0, 0, 0, 0, 0, 'Tirion... could it be?', 21287);
INSERT INTO `quest_offer_reward` VALUES (42774, 1, 0, 0, 0, 0, 0, 0, 0, 'We\'re ready to depart when you are.', 21287);
INSERT INTO `quest_offer_reward` VALUES (42775, 1, 0, 0, 0, 0, 0, 0, 0, 'Теперь, когда саргеритовый ключ у нас в руках, нам остается только приготовить войска и разработать план вторжения.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42776, 1, 0, 0, 0, 0, 0, 0, 0, 'Теперь у тебя шесть защитников, на твоей стороне все силы иллидари, а также растущая армия, где каждый готов принести себя в жертву ради победы над Пылающим Легионом.\n\nПора приготовиться к завершающей фазе.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42777, 6, 2, 0, 0, 0, 0, 0, 0, 'Ты хочешь, чтобы я стал одним из твоих защитников?\n\n<Белат выглядит удивленным и довольным.>\n\nЭто большая честь. Я готов посвятить себя нашей общей цели, $n. Я буду бороться с врагами иллидари под твоим знаменем.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42782, 1, 1, 0, 0, 0, 0, 0, 0, 'Хорошо, что ты $gподготовился:подготовилась; к плаванию. Невозможно предугадать, что нас ждет на Расколотых островах.$b$bЭти кольца изготовлены специально для высокопоставленных представителей Альянса. Надеюсь, твое кольцо защитит тебя в грядущей битве.', 21863);
INSERT INTO `quest_offer_reward` VALUES (42786, 0, 0, 0, 0, 0, 0, 0, 0, 'These remains... this is horrible! They are turning dryads into mindless piles of ooze!', 21491);
INSERT INTO `quest_offer_reward` VALUES (42787, 1, 0, 0, 0, 0, 0, 0, 0, 'Стражи могли бы пойти нам навстречу и пораньше. По крайней мере мы теперь знаем, что нам нужно найти Стража Малассу Тень в Штормхейме. Именно ей доверили охранять саргеритовый ключ.\n\nЭто, а также то, что мне удалось разведать самостоятельно, укажет тебе нужное направление.\n\nТы и глазом моргнуть не успеешь, а ключ будет уже у нас, $n.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42792, 1, 0, 0, 0, 0, 0, 0, 0, 'Теперь они будут считаться с Сумеречной лилией. Отличная работа.', 24015);
INSERT INTO `quest_offer_reward` VALUES (42800, 1, 25, 0, 0, 0, 0, 0, 0, 'Конечно, я стану твоим защитником. Что бы ни случилось.\n\nМы будем работать вместе. И я сделаю все, что в моих силах.', 22248);
INSERT INTO `quest_offer_reward` VALUES (42801, 1, 1, 0, 0, 0, 0, 0, 0, 'Yes, Belat tried. Conducting reconnaissance is a really good plan.\n\nI only hope that when trying to find the information, not so many broken ones died.\n\nOne way or another, now I\'m done. There was a suitable place in the Black Temple, where you can go.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42802, 6, 273, 0, 0, 0, 0, 0, 0, 'Ariana Heart of Fire?! I thought she was dead the first time we were here. It\'s terrible that she stayed here the whole time. But how great is it that she\'s alive!\n\nI remember she used to be a great mentor to demon hunters. Her talents will be of great use to us. Let\'s not waste any more time.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42808, 1, 6, 0, 0, 0, 0, 0, 0, 'Здорово, что Ариана поправилась и готова обучать новых охотников на демонов.\n\nДавненько в наших рядах не было свежей крови.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42809, 0, 0, 0, 0, 0, 0, 0, 0, 'We\'ve won the day! Caria and Varedis are no more, and we\'ve sent a clear message to the Burning Legion that they are vulnerable, even on their own worlds.$B$BIt\'s about time that we celebrated, but first...', 0);
INSERT INTO `quest_offer_reward` VALUES (42810, 274, 0, 0, 0, 0, 0, 0, 0, 'Кил\'джеден?! Это нельзя рассматривать иначе, чем как открытый вызов.\n\nНадеюсь, мы сможем это пережить, $n?', 23420);
INSERT INTO `quest_offer_reward` VALUES (42811, 1, 0, 0, 0, 0, 0, 0, 0, 'It is fitting that you are here.$b$bTime and again, you have fought to save our world. Now, I fear I must ask you to undertake an even greater act of service.', 21287);
INSERT INTO `quest_offer_reward` VALUES (42812, 0, 0, 0, 0, 0, 0, 0, 0, 'It is fitting that you are here.$B$BTime and again, you have fought to save our world. Now, I fear I must ask you to undertake an even greater act of service.', 0);
INSERT INTO `quest_offer_reward` VALUES (42814, 1, 0, 0, 0, 0, 0, 0, 0, 'I am relieved you survived the Broken Shore when so many brave heroes were lost.$b$b<Danath hangs his head in a moment of silence.>$b$bI have been recalled from Honor Hold to aid the Alliance in its darkest hour. It is crucial that we learn from our mistakes.$b$bDo you have time to speak?', 21384);
INSERT INTO `quest_offer_reward` VALUES (42815, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome to Devils of Valor, $gwarrior:warrior;! Before only the dead could enter here, but times change.', 21384);
INSERT INTO `quest_offer_reward` VALUES (42818, 0, 0, 0, 0, 0, 0, 0, 0, 'Хорошо, что ты здесь, $gВладыка смерти:Владычица смерти; $n. Бой уже начался.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42821, 0, 0, 0, 0, 0, 0, 0, 0, 'Скоро Алый орден будет разбит, $gВладыка смерти:Владычица смерти; $n. Битва уже подходит к концу...', 23420);
INSERT INTO `quest_offer_reward` VALUES (42823, 0, 0, 0, 0, 0, 0, 0, 0, 'Теперь путь свободен, $gВладыка смерти:Владычица смерти;. Пришло время предложить Салли Вайтмейн возможность искупить свои грехи.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42824, 0, 0, 0, 0, 0, 0, 0, 0, 'Мне сообщили, что битва в монастыре Алого ордена увенчалась успехом – благодаря тебе.$b$bПослушница Вайтмейн уже начала свое обучение. Она подает большие надежды и, несомненно, станет превосходным Всадником.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42828, 1, 0, 0, 0, 0, 0, 0, 0, 'Хм, да. Все именно так, как я преполагал.', 24015);
INSERT INTO `quest_offer_reward` VALUES (42829, 1, 0, 0, 0, 0, 0, 0, 0, 'Ну что ж, пойдем, покажу тебе тут все.', 24015);
INSERT INTO `quest_offer_reward` VALUES (42832, 11, 0, 0, 0, 0, 0, 0, 0, 'Советую прополоскать рот – наши сорта отличаются ярко выраженным вкусом!', 24015);
INSERT INTO `quest_offer_reward` VALUES (42833, 1, 0, 0, 0, 0, 0, 0, 0, 'Welcome! So, how was the tour?', 24015);
INSERT INTO `quest_offer_reward` VALUES (42834, 1, 0, 0, 0, 0, 0, 0, 0, 'It\'s simple, isn\'t it?', 24015);
INSERT INTO `quest_offer_reward` VALUES (42835, 1, 0, 0, 0, 0, 0, 0, 0, 'На выходе я непременно вручу тебе бутылочку чародейского вина твоего собственного производства.', 24015);
INSERT INTO `quest_offer_reward` VALUES (42836, 66, 0, 0, 0, 0, 0, 0, 0, 'Так-то лучше. Спасибо.', 24015);
INSERT INTO `quest_offer_reward` VALUES (42837, 603, 0, 0, 0, 0, 0, 0, 0, 'Я упакую эти... хм... сувениры.', 24015);
INSERT INTO `quest_offer_reward` VALUES (42838, 18, 0, 0, 0, 0, 0, 0, 0, 'Марго... о нет!', 24015);
INSERT INTO `quest_offer_reward` VALUES (42839, 1, 0, 0, 0, 0, 0, 0, 0, 'Крепись, $n.$b$bПотеря Марго – настоящий удар для нас, но вряд ли это наша последняя потеря. Боюсь, далеко нет.', 24015);
INSERT INTO `quest_offer_reward` VALUES (42840, 11, 0, 0, 0, 0, 0, 0, 0, 'Хорошо, что ты осознаешь ценность моих услуг, партнер.', 24015);
INSERT INTO `quest_offer_reward` VALUES (42841, 1, 0, 0, 0, 0, 0, 0, 0, 'Приятно иметь с тобой дело.', 24015);
INSERT INTO `quest_offer_reward` VALUES (42844, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you for coming so quickly, Highlord.', 0);
INSERT INTO `quest_offer_reward` VALUES (42846, 1, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно. И лорд Тиросс, и леди Лиадрин будут служить тебе верой и правдой.', 23171);
INSERT INTO `quest_offer_reward` VALUES (42847, 1, 0, 0, 0, 0, 0, 0, 0, 'Максвелл Тиросс принес важные вести. В первую очередь, он нашел выживших и забрал их с собой. Сейчас о них заботятся в часовне. Среди них – командир Ансела, закаленная боями воительница, обучившая немало паладинов.$B$BВпрочем, пусть лучше сэр Тиросс расскажет все сам.', 23171);
INSERT INTO `quest_offer_reward` VALUES (42848, 1, 0, 0, 0, 0, 0, 0, 0, 'Отлично. Эти оруженосцы не подведут тебя.', 23171);
INSERT INTO `quest_offer_reward` VALUES (42849, 1, 0, 0, 0, 0, 0, 0, 0, 'Мы успешно выполнили задание: не только одержали победу, но и завербовали Аламанда Грейторна, известного исследователя из архивов Дольного Очага.', 23171);
INSERT INTO `quest_offer_reward` VALUES (42850, 1, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно. На сэра Грейторна действительно можно положиться.', 23171);
INSERT INTO `quest_offer_reward` VALUES (42851, 66, 0, 0, 0, 0, 0, 0, 0, '$g Supreme Lord:Supreme Lady;, my gratitude knows no bounds: you $gspas:saved; my life and $g opened:opened; my eyes. $B$BI have a deep hatred for the Blazing Legion, it is true, but you $gnapped:remembered; to me, my way depends on me. $B$BI choose the way of righteousness and will continue to follow you.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42852, 66, 0, 0, 0, 0, 0, 0, 0, 'Мне много приходилось слышать о твоих деяниях, $gВерховный лорд:Верховная леди;. А теперь, когда мне выпала честь сражаться на твоей стороне, могу сказать, что слухи не лгут.$B$BПозволь мне присоединиться к ордену Серебряной Длани и стать воителем Света, $n. Я буду сражаться справедливо и храбро и выполнять твои приказы до конца.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42857, 396, 0, 0, 0, 0, 0, 0, 0, 'О да, это излюбленное лакомство греллей. Попробую с его помощью выманить их отсюда. Спасибо.', 22124);
INSERT INTO `quest_offer_reward` VALUES (42865, 1, 0, 0, 0, 0, 0, 0, 0, '', 22624);
INSERT INTO `quest_offer_reward` VALUES (42866, 1, 0, 0, 0, 0, 0, 0, 0, 'I see you have an urgent message for me. What is it?', 23171);
INSERT INTO `quest_offer_reward` VALUES (42867, 1, 0, 0, 0, 0, 0, 0, 0, 'Спасибо, что $gсобрал:собрала; наших предводителей.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42868, 0, 0, 0, 0, 0, 0, 0, 0, 'This brew be excellent! It\'s taste be the best!$b$bYou\'ve proved yourself well, now I\'ll tell you where my staff rests.', 21491);
INSERT INTO `quest_offer_reward` VALUES (42869, 1, 0, 0, 0, 0, 0, 0, 0, 'У меня для тебя потрясающая новость, $n.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42872, 1, 0, 0, 0, 0, 0, 0, 0, 'Я надеюсь снова встать плечом к плечу с остальными нашими бойцами.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42881, 66, 0, 0, 0, 0, 0, 0, 0, 'Long days to you, $n. They say you want to end demonic atrocities. Is that right? $B$BThe Legion has chased us through countless worlds... ...took so many lives. With every paladin we die, we feel the pain of the Light itself. I will fight with you.', 23171);
INSERT INTO `quest_offer_reward` VALUES (42882, 0, 0, 0, 0, 0, 0, 0, 0, 'The Scarlet Order will pay full price for its crimes. Soon the monastery will fall, and with it the Scarlet Order\'s last hope for salvation.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42883, 603, 0, 0, 0, 0, 0, 0, 0, '', 22624);
INSERT INTO `quest_offer_reward` VALUES (42884, 1, 0, 0, 0, 0, 0, 0, 0, '', 22624);
INSERT INTO `quest_offer_reward` VALUES (42885, 0, 0, 0, 0, 0, 0, 0, 0, 'Good. This intel will prove vital to our operations.', 0);
INSERT INTO `quest_offer_reward` VALUES (42886, 1, 0, 0, 0, 0, 0, 0, 0, 'Отлично, $gВерховный лорд:Верховная леди;. Ты здесь.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42887, 1, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42888, 1, 0, 0, 0, 0, 0, 0, 0, 'Нет... мы недооценили противника.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42889, 1, 1, 0, 0, 0, 0, 0, 0, 'Когда ты внезапно $gисчез:исчезла;, мы уже не надеялись снова тебя увидеть.$b$bНе знаю, что и сказать... Мне нужно время, чтобы осмыслить эту трагедию.$b$bТак или иначе, я рада, что ты в порядке.', 22996);
INSERT INTO `quest_offer_reward` VALUES (42890, 1, 0, 0, 0, 0, 0, 0, 0, 'Задание в Фаронааре выполнено успешно, хотя моей заслуги в этом нет.$B$BМне не удалось сдержать гнев, и, если бы не ты, пришлось бы дорого за это заплатить.$B$BСпасибо тебе, $gВерховный лорд:Верховная леди;.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42911, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes, that\'s her! This is what you need. Please bring as much as possible!', 24015);
INSERT INTO `quest_offer_reward` VALUES (42914, 1, 66, 0, 0, 0, 500, 0, 0, 'Я уже целую вечность сражаюсь против Пылающего Легиона, $n. Буду рад поделиться своими знаниями с тобой, когда ты поведешь нас в новую битву. Я выучил пару новых трюков и смогу застать демонов врасплох!', 23360);
INSERT INTO `quest_offer_reward` VALUES (42917, 1, 66, 0, 0, 0, 500, 0, 0, 'Ты $gспас:спасла; мою репутацию, не говоря уже о моей жизни! Если бы не твое упорство, $n, висеть бы мне до сих пор над Нексусом. Прими мою бесконечную благодарность.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42918, 0, 0, 0, 0, 0, 0, 0, 0, 'Отлично! Демоны пожалеют о том дне, когда задумали захватить Азерот!', 23420);
INSERT INTO `quest_offer_reward` VALUES (42919, 1, 0, 0, 0, 0, 0, 0, 0, 'Наша цель ясна, но как ее достичь?', 23360);
INSERT INTO `quest_offer_reward` VALUES (42920, 4, 0, 0, 0, 0, 0, 0, 0, 'Мы победили! Кария и Варедис больше не представляют угрозы, и мы ясно дали демонам Легиона понять, что они уязвимы, даже в собственных мирах.\n\nМожно было бы отпраздновать, но сначала...', 23420);
INSERT INTO `quest_offer_reward` VALUES (42921, 1, 0, 0, 0, 0, 0, 0, 0, 'Кто бы мог подумать, что он станет действовать с нами заодно?\n\nМы еще на шаг приблизились к тому, чтобы дать бой Пылающему Легиону в собственных мирах демонов.', 23222);
INSERT INTO `quest_offer_reward` VALUES (42928, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $n.$B$BThese bandages will help heal our injured allies.', 0);
INSERT INTO `quest_offer_reward` VALUES (42929, 1, 0, 0, 0, 0, 0, 0, 0, 'Спасибо, $n.$B$BЭтот лосось накормит наше племя.', 23171);
INSERT INTO `quest_offer_reward` VALUES (42930, 1, 0, 0, 0, 0, 0, 0, 0, 'Спасибо, $n.$B$BЭто мясо придаст сил нашим воинам.', 23171);
INSERT INTO `quest_offer_reward` VALUES (42931, 0, 0, 0, 0, 0, 0, 0, 0, 'The Doomhammer could be anywhere here. And that demon is still alive.', 0);
INSERT INTO `quest_offer_reward` VALUES (42932, 0, 0, 0, 0, 0, 0, 0, 0, 'You there! What is this nonsense falling into my domain? Green fire raining from the Maelstrom! Are you surface dwellers behind this?$B$B<You explain everything to the Stonemother.>$B$BA missing artifact, hm? I have not seen it, but I know where to start. You help the Stonemother, yes, and she will help you.', 0);
INSERT INTO `quest_offer_reward` VALUES (42933, 0, 0, 0, 0, 0, 0, 0, 0, 'Troggs. So tiresome. Pebbles for brains. Easily swayed by any cult, demon, or dragon that tries to interfere with my rule. Thank you for giving them a solid whomping.', 0);
INSERT INTO `quest_offer_reward` VALUES (42935, 0, 0, 0, 0, 0, 0, 0, 0, 'Very very good, $C. The cavern is once more filled with their delightful wails.', 0);
INSERT INTO `quest_offer_reward` VALUES (42936, 0, 0, 0, 0, 0, 0, 0, 0, 'How delightful! Aeosera is very pleased with you, $C. As am I.$B$BNow then, it\'s time to find that artifact of yours. Thanks to your efforts, the stone drakes will help us.', 0);
INSERT INTO `quest_offer_reward` VALUES (42937, 0, 0, 0, 0, 0, 0, 0, 0, 'Oh ho ho ho! I heard the cavern shake from here, $n! It\'s good to rattle some stones and remind everyone who\'s the boss down here, I must say.$B$BNow about that hammer of yours. Your companions have come up empty, but I think you know where to go next, hm?', 0);
INSERT INTO `quest_offer_reward` VALUES (42940, 1, 66, 0, 0, 0, 500, 0, 0, 'Твои защитники потрудились на славу. Думаю, теперь я смогу тебе помочь попасть в Окулус.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42954, 1, 0, 0, 0, 0, 0, 0, 0, 'Книг о магических болезнях? Возможно, я знаю, где их можно найти...', 22566);
INSERT INTO `quest_offer_reward` VALUES (42955, 1, 0, 0, 0, 0, 0, 0, 0, 'Это чистейшая энергия. Красивый цвет, правда? Подходящий дар для книги исцеления. Выйди же к нам, о книга. Ты больше не будешь потерянной и забытой.', 22624);
INSERT INTO `quest_offer_reward` VALUES (42956, 1, 0, 0, 0, 0, 0, 0, 0, 'Ari found that book! And she gave you the ring.', 22624);
INSERT INTO `quest_offer_reward` VALUES (42957, 396, 11, 0, 0, 0, 0, 0, 0, 'Ah, there the wikket is. Now, we see how well you can brew!', 21491);
INSERT INTO `quest_offer_reward` VALUES (42959, 1, 0, 0, 0, 0, 0, 0, 0, 'Они прекрасны! Конечно, я не могу оставить их себе. Но... Ты слышишь гудение пчел в медовом напитке? Они поют так сладко... Ох! Какая чудесная мысль! Спасибо, мои маленькие друзья.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42966, 1, 0, 0, 0, 0, 0, 0, 0, 'That\'s great.', 23360);
INSERT INTO `quest_offer_reward` VALUES (42967, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done. That should halt the Legion attacks for a while.', 0);
INSERT INTO `quest_offer_reward` VALUES (42968, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent. Our paladins will be well equipped for battle.', 0);
INSERT INTO `quest_offer_reward` VALUES (42970, 0, 0, 0, 0, 0, 0, 0, 0, 'Magni\'s information was crucial to our efforts against the Legion. And it was good to see him again.$B$BNow to find that tome about the Pillars of Creation...', 0);
INSERT INTO `quest_offer_reward` VALUES (42971, 0, 0, 0, 0, 0, 0, 0, 0, 'Now the other elements will truly fear you.', 0);
INSERT INTO `quest_offer_reward` VALUES (42974, 0, 0, 0, 0, 0, 0, 0, 0, 'Спасибо тебе, $n. Если бы не ты, один из величайших стражей Азерота оказался бы во власти Легиона.$B$BТы $gдоказал, что способен:доказала, что способна; вести мои войска на битву и побеждать Легион на его собственной территории.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42977, 0, 0, 0, 0, 0, 0, 0, 0, 'Что же Служителям Земли нужно от старого слуги Искателя Ветра?', 23420);
INSERT INTO `quest_offer_reward` VALUES (42978, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s an honor, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (42982, 0, 0, 0, 0, 0, 0, 0, 0, 'Well-earned, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (42983, 0, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно. Такого количества сущности мистралей мне как раз хватит, чтобы восстановить Столп!', 23420);
INSERT INTO `quest_offer_reward` VALUES (42984, 0, 0, 0, 0, 0, 0, 0, 0, 'Это точно он! С этим скипетром я смогу связать Столп Водоворота со стихией воздуха. А еще – показать несколько потрясающих трюков на каком-нибудь фестивале.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42985, 2, 0, 0, 0, 0, 0, 0, 0, '', 24742);
INSERT INTO `quest_offer_reward` VALUES (42986, 0, 0, 0, 0, 0, 0, 0, 0, 'Отлично сработано, $gПредсказатель:Предсказательница;. Теперь, когда во владениях Громораана все спокойно, он сможет отдать все свои силы и внимание войне.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42987, 113, 0, 0, 0, 0, 0, 0, 0, '', 24742);
INSERT INTO `quest_offer_reward` VALUES (42988, 0, 0, 0, 0, 0, 0, 0, 0, 'Твои успехи очень впечатляют, $gПредсказатель:Предсказательница;! Слухи о твоем мастерстве оказались чистой правдой.$B$BТы $gпомог:помогла; залатать разлом, который наши враги создали на Расколотых островах. Теперь мы можем вздохнуть с облегчением.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42989, 0, 0, 0, 0, 0, 0, 0, 0, '<Ma\'haat Hardy speaks to you in a low roaring voice. >$b$bSo... you\'re asking... from the ground?', 23420);
INSERT INTO `quest_offer_reward` VALUES (42990, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes, this is just what we need! I will immediately get down to business.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42995, 0, 0, 0, 0, 0, 0, 0, 0, '<Ma\'haat The Stubborn eagerly swallows Sargheras\' blood as we speak. >$b$bYes... It\'s like rush to-- I\'m a choffel. Here\'s the eiffo, it\'s yours.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42996, 0, 0, 0, 0, 0, 0, 0, 0, 'Рада тебя видеть, $gПредсказатель:Предсказательница;. Нам нужно многое обсудить.', 23420);
INSERT INTO `quest_offer_reward` VALUES (42997, 0, 0, 0, 0, 0, 0, 0, 0, 'Oh, you\'re just in time, $gPredictor: The Predictor;! I was hoping you\'d come.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43002, 0, 0, 0, 0, 0, 0, 0, 0, 'Этот клинок действительно может помочь нам воскресить Искателя Ветра. Но он поврежден, и душа элементаля в нем ослабла.$b$bОчень может быть, что у нас мало времени...', 23420);
INSERT INTO `quest_offer_reward` VALUES (43003, 0, 0, 0, 0, 0, 0, 0, 0, 'Так это правда! Яйцо Гайата существует... Глазам своим не верю!', 23420);
INSERT INTO `quest_offer_reward` VALUES (43007, 1, 0, 0, 0, 0, 0, 0, 0, 'Хорошо, что Валире удалось так быстро тебя найти.\n\nУ меня к тебе очень срочное дело.', 21916);
INSERT INTO `quest_offer_reward` VALUES (43013, 274, 6, 0, 0, 0, 0, 0, 0, 'Ну да. Выглядят они так себе, но в общем и целом сойдет.\n\nДа и вообще. Они пойдут на дело с Ванессой и Гароной, какие могут быть проблемы?', 21916);
INSERT INTO `quest_offer_reward` VALUES (43014, 274, 273, 0, 0, 0, 1000, 0, 0, '', 21952);
INSERT INTO `quest_offer_reward` VALUES (43015, 1, 0, 0, 0, 0, 0, 0, 0, '', 21952);
INSERT INTO `quest_offer_reward` VALUES (43054, 0, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно, да! Да! Этого хватит на несколько недель. Огромное тебе спасибо, Великий мастер.', 23222);
INSERT INTO `quest_offer_reward` VALUES (43055, 0, 0, 0, 0, 0, 0, 0, 0, 'Candy buckets like this are located in inns throughout the realms. Go ahead... take some!', 0);
INSERT INTO `quest_offer_reward` VALUES (43056, 0, 0, 0, 0, 0, 0, 0, 0, 'Candy buckets like this are located in inns throughout the realms. Go ahead... take some!', 0);
INSERT INTO `quest_offer_reward` VALUES (43057, 0, 0, 0, 0, 0, 0, 0, 0, 'Candy buckets like this are located in inns throughout the realms. Go ahead... take some!', 0);
INSERT INTO `quest_offer_reward` VALUES (43058, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes, these will do nicely. I\'ll get them salted and cured right away!', 0);
INSERT INTO `quest_offer_reward` VALUES (43060, 0, 0, 0, 0, 0, 0, 0, 0, 'Отличный улов! Никогда не видел рыбу таких размеров! Спасибо, Великий мастер.', 23222);
INSERT INTO `quest_offer_reward` VALUES (43061, 0, 0, 0, 0, 0, 0, 0, 0, 'These bandages will help save many lives. Thank you.', 0);
INSERT INTO `quest_offer_reward` VALUES (43062, 0, 0, 0, 0, 0, 0, 0, 0, 'Impressive, $n. No doubt you\'re in great shape.', 23171);
INSERT INTO `quest_offer_reward` VALUES (43090, 1, 0, 0, 0, 0, 0, 0, 0, 'Спасибо, $n. Мы потеряли Ходира, и это настоящая трагедия, но нам удалось предотвратить самое страшное. Сегодня Ульдуар не достался Легиону, и все благодаря тебе.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43100, 0, 0, 0, 0, 0, 0, 0, 0, '', 21491);
INSERT INTO `quest_offer_reward` VALUES (43106, 0, 0, 0, 0, 0, 0, 0, 0, '<You stretch the mana crystals out one by one, they blink in your hands and disappear. >', 21737);
INSERT INTO `quest_offer_reward` VALUES (43151, 0, 0, 0, 0, 0, 0, 0, 0, 'Спасибо, |3-6($c). Это мне пригодится.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43153, 0, 0, 0, 0, 0, 0, 0, 0, '', 21491);
INSERT INTO `quest_offer_reward` VALUES (43162, 0, 0, 0, 0, 0, 0, 0, 0, 'My dear sister laid to rest,$BA tortured hero proves their best,$BToday I do lift this curse,$BAnd offer you a token, a small purse,$BMy other sisters are still gone,$BThere comes a day to right their wrong...', 0);
INSERT INTO `quest_offer_reward` VALUES (43179, 0, 0, 0, 0, 0, 0, 0, 0, 'Маги Кирин-Тора благодарят тебя.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43182, 1, 0, 0, 0, 0, 0, 0, 0, 'Какое облегчение – это орудие разрушения больше не во власти наших врагов.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43184, 11, 1, 0, 0, 0, 1000, 0, 0, 'Очень дальновидно с твоей стороны – оставить лучших напоследок.\n\n<Аллари игриво ухмыляется.>\n\nА если серьезно, я польщена, $n. Что-то мне подсказывает, что худшее еще впереди. Мы должны сохранять бдительность и наконец разделаться с Пылающим Легионом.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43185, 0, 0, 0, 0, 0, 0, 0, 0, 'Я? Ты хочешь видеть меня в рядах своих защитников?\n\nДа, конечно! Могу повторить это миллион раз. Только скажи, куда мне пойти и что сделать – и можешь считать, что это уже сделано.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43186, 1, 2, 0, 0, 0, 0, 0, 0, 'Под твоим руководством мы одерживали победу за победой. Мы с радостью служим тебе и принесем ради тебя любые жертвы.\n\nСлавься, $gИстребитель:Истребительница; $n!', 23420);
INSERT INTO `quest_offer_reward` VALUES (43249, 6, 0, 0, 0, 0, 0, 0, 0, 'Как насчет того, чтобы на пару со мной наведаться в Валь\'шару и добыть самый полезный самоцвет во всем мире?', 22248);
INSERT INTO `quest_offer_reward` VALUES (43250, 0, 0, 0, 0, 0, 0, 0, 0, 'Наконец-то ты здесь. Можно начинать.\n\nЧто-то поднимает эльфов из могил. Они не понимают, что происходит. Для них мы – Пылающий Легион, явившийся уничтожить крепость Черной Ладьи.', 22248);
INSERT INTO `quest_offer_reward` VALUES (43251, 0, 0, 0, 0, 0, 0, 0, 0, 'Учетная книга? Дай взглянуть.\n\n<Валира забирает у вас книгу и начинает листать древние страницы.>\n\n Вот оно!', 22248);
INSERT INTO `quest_offer_reward` VALUES (43252, 0, 0, 0, 0, 0, 0, 0, 0, 'Спасибо, $n. То, что ты $gподарил:подарила; моим предкам заслуженный покой, для меня многое значит.', 22248);
INSERT INTO `quest_offer_reward` VALUES (43253, 273, 274, 0, 0, 0, 1000, 0, 0, 'Легенда не врет! Я была права в своих подозрениях. Говорят, во время Войны древних у Гребня Ворона была идеальная команда разведчиков. Теперь мы знаем, как они собирали информацию.\n\nРассказать – никто не поверит. Можно, конечно, оставить эту находку себе...\n\n<На мгновенье Валира задумывается.>\n\nНо нет. Так жадные глупцы сворачивают себе шеи. Мы разделим этот риск с другими.', 22248);
INSERT INTO `quest_offer_reward` VALUES (43254, 0, 0, 0, 0, 0, 0, 0, 0, '', 21491);
INSERT INTO `quest_offer_reward` VALUES (43259, 0, 0, 0, 0, 0, 0, 0, 0, 'So you\'ve come to the crooked tree,$B$BTo name a foe you ask of me,$B$BBut listen first to my tragic tale,$B$BThen you will hear my sister\'s wail...', 0);
INSERT INTO `quest_offer_reward` VALUES (43261, 0, 0, 0, 0, 0, 0, 0, 0, 'И теперь ты хочешь, чтобы я была твоей защитницей?\n\n<Ванесса недоверчиво смотрит на вас и ненадолго задумывается.>\n\nХорошо! Но я буду действовать, как считаю нужным. Договорились?', 21916);
INSERT INTO `quest_offer_reward` VALUES (43262, 1, 0, 0, 0, 1000, 0, 0, 0, '<Garon skillfully spins a knife as sharp as a razor in his hands, then hides it in the scabbard on his wrist.>\n\nIf you need to take out someone dangerous, you\'re welcome. I\'m ready to run your errands.', 21916);
INSERT INTO `quest_offer_reward` VALUES (43264, 1, 0, 0, 0, 0, 0, 0, 0, 'Отлично, $gВладыка смерти:Владычица смерти;. Назгрим и Тассариан – прекрасные защитники. Они будут вершить твою волю, не останавливаясь ни перед чем.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43265, 1, 0, 0, 0, 0, 0, 0, 0, 'Судя по всему, Назргим и Тассариан добились успехов. Мы нашли нескольких перспективных кандидатов, которые могут помочь нам в войне с Пылающим Легионом.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43266, 1, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно, $gВладыка смерти:Владычица смерти;.$b$bЭти вурдалаки станут нашим козырем в войне за Расколотые острова. Ты можешь отправлять их на задания точно так же, как защитников.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43267, 1, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно, $gВладыка смерти:Владычица смерти;. Задание выполнено.$b$bЕсли военная удача и дальше не отвернется от нас, Пылающий Легион будет разбит.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43268, 1, 0, 0, 0, 0, 0, 0, 0, 'Превосходный выбор, $gВладыка смерти:Владычица смерти;. Если надо будет еще что-нибудь улучшить, обращайся к Цзубаши.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43270, 1, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно. Калия Менетил и верховная жрица Ишана – достойные защитники. Им можно доверить задание любой сложности.', 23171);
INSERT INTO `quest_offer_reward` VALUES (43271, 1, 0, 0, 0, 0, 0, 0, 0, 'Рада встрече, $n. Сражаться на твоей стороне – большая честь для меня.$B$BВсегда рада прийти на помощь.', 23171);
INSERT INTO `quest_offer_reward` VALUES (43272, 1, 0, 0, 0, 0, 0, 0, 0, 'От имени Ша\'тар я благодарю тебя за восстановление Саа\'ры.$B$BКак глава Алдоров я поклялась оберегать все то, что для нас свято. Этот храм – важный бастион Света, и он нуждается в нашей защите.$B$BМожешь рассчитывать на меня. Я сделаю все, чтобы уничтожить Пылающий Легион.', 23171);
INSERT INTO `quest_offer_reward` VALUES (43273, 1, 0, 0, 0, 0, 0, 0, 0, 'Калия успешно выполнила задание! Готовься встречать новичков.', 22908);
INSERT INTO `quest_offer_reward` VALUES (43275, 1, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно. Эти бойцы очень помогут нам в обороне Расколотых островов. Ты можешь отправлять их на задания так же, как и наших защитников.', 22908);
INSERT INTO `quest_offer_reward` VALUES (43276, 1, 0, 0, 0, 0, 0, 0, 0, 'Успех этого разведывательного задания превзошел наши самые смелые ожидания, $n.$B$BТвоим войскам удалось спасти архонта Ториаса – опытного исследователя, чьи познания выходят за пределы, обозначенные Светом и Тьмой.$B$BВ благодарность он согласился нам помочь.', 22908);
INSERT INTO `quest_offer_reward` VALUES (43277, 1, 0, 0, 0, 0, 0, 0, 0, 'Прекрасный выбор, $n. Архонт Ториас продолжит исследования в Храме света Пустоты. Поговори с ним, когда придет время снова улучшать оплот.', 22908);
INSERT INTO `quest_offer_reward` VALUES (43303, 0, 0, 0, 0, 0, 0, 0, 0, '', 21491);
INSERT INTO `quest_offer_reward` VALUES (43309, 1, 0, 0, 0, 0, 0, 0, 0, 'Oh, it\'s you, $n.$b$bIt\'s nice to see you again. So, you\'re $gready; ready to help me with the campaign?', 24015);
INSERT INTO `quest_offer_reward` VALUES (43310, 1, 0, 0, 0, 0, 0, 0, 0, 'Леди Лунарх мне как сестра, но, боюсь, сейчас у меня связаны руки.', 24015);
INSERT INTO `quest_offer_reward` VALUES (43311, 1, 0, 0, 0, 0, 0, 0, 0, 'Ну ладно. Давай обсудим стратегию.', 24015);
INSERT INTO `quest_offer_reward` VALUES (43312, 5, 66, 0, 0, 0, 0, 0, 0, 'Он цел и невредим! Я в огромном долгу перед тобой – и перед Ли\'лет.', 24015);
INSERT INTO `quest_offer_reward` VALUES (43313, 1, 0, 0, 0, 0, 0, 0, 0, 'The city is full of the most interesting rumors, $n. Our victory has come closer.', 24015);
INSERT INTO `quest_offer_reward` VALUES (43314, 1, 0, 0, 0, 0, 0, 0, 0, '<Talisra tightens the stone char. >$b$bHe brought it on himself. $b$bI don\'t have to live in the past. Without it, Alicanda has become weaker, and we are one step closer to correcting the evil caused to the Shal\'dorai people.', 24015);
INSERT INTO `quest_offer_reward` VALUES (43315, 1, 0, 0, 0, 0, 0, 0, 0, 'У тебя прекрасно все получилось. Мне не было смысла вмешиваться.$b$bОдним жадным до власти аристократом меньше. Хорошая работа, $n.', 24015);
INSERT INTO `quest_offer_reward` VALUES (43317, 0, 0, 0, 0, 0, 0, 0, 0, 'Я надеялась, что все пройдет более гладко.', 24015);
INSERT INTO `quest_offer_reward` VALUES (43318, 66, 0, 0, 0, 0, 0, 0, 0, 'Сегодня мы сделали важное дело. Я этого не забуду.', 24015);
INSERT INTO `quest_offer_reward` VALUES (43319, 0, 0, 0, 0, 0, 0, 0, 0, 'Я передам эти знания ученикам, Великий мастер. Они научатся следить за движениями противника и наносить удары в нужный момент. Скоро твои послушники будут готовы к битве.', 23171);
INSERT INTO `quest_offer_reward` VALUES (43323, 0, 0, 0, 0, 0, 0, 0, 0, 'Момент, чтобы погрузиться в воспоминания и оценить то, чего мы достигли. День, чтобы заглянуть в будущее и обдумать возможные пути. Год на размышления о том, что значит прогресс, и целая жизнь, полная воспоминаний о смельчаках, которые умеют ловить момент.', 22908);
INSERT INTO `quest_offer_reward` VALUES (43331, 0, 0, 0, 0, 0, 0, 0, 0, 'With the Sixtrigger brothers ended, you\'re finally able to collect your payment for services rendered.$B$BYou rummage through their most prized possessions and take your pick.', 0);
INSERT INTO `quest_offer_reward` VALUES (43334, 1, 0, 0, 0, 0, 0, 0, 0, 'Welcome, $gshaman: shamanka;! Do you want to get the weapon of the formidable god Ra-den?$b$bMany have been led to Pandaria by this cherished dream, but so far no one has been able to prove that he is worthy to own it.', 22747);
INSERT INTO `quest_offer_reward` VALUES (43335, 1, 0, 0, 0, 0, 0, 0, 0, 'Костер получился что надо, стейк прожарен до румяной корочки. Впечатляет... давай поедим.', 23171);
INSERT INTO `quest_offer_reward` VALUES (43338, 1, 6, 0, 0, 0, 0, 0, 0, 'Тигр, похоже, настроен серьезно, да?$b$bПока ты $gбыл:была; здесь, к нему уже кое-кто пришел с дарами. Нас, похоже, ждет состязание.', 22747);
INSERT INTO `quest_offer_reward` VALUES (43341, 0, 0, 0, 0, 0, 0, 0, 0, 'Превосходно, $N. Нам все еще многое предстоит сделать, чтобы изгнать Легион из этого мира, но теперь мы на шаг ближе к цели.', 22566);
INSERT INTO `quest_offer_reward` VALUES (43349, 0, 0, 0, 0, 0, 0, 0, 0, 'Эгида Агграмара будет в безопасности в таком хорошо защищенном городе, как Даларан.$B$BВы сделали еще один важный шаг на пути к изгнанию Легиона.', 22566);
INSERT INTO `quest_offer_reward` VALUES (43352, 1, 0, 0, 0, 0, 0, 0, 0, 'And you become a reliable companion.', 24015);
INSERT INTO `quest_offer_reward` VALUES (43359, 2, 0, 0, 0, 0, 0, 0, 0, 'Надеюсь, новая сила твоего оружия послужит тебе верой и правдой.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43360, 2, 0, 0, 0, 0, 0, 0, 0, 'Спасибо. Это место никогда не будет прежним, но их безумие не повредит остальным.', 24015);
INSERT INTO `quest_offer_reward` VALUES (43361, 1, 0, 0, 0, 0, 0, 0, 0, 'Я изучу образцы со всем тщанием. Если удача нам улыбнется, мы получим ответы.', 24015);
INSERT INTO `quest_offer_reward` VALUES (43362, 1, 2, 0, 0, 0, 0, 0, 0, 'Не знаю, как тебе удалось ее добыть, но понимаю, что задача была непростая. Обстоятельства против нас, но ты $gдобился:добилась; успеха. Идем. Восстановим аркан\'дор.', 24015);
INSERT INTO `quest_offer_reward` VALUES (43363, 0, 0, 0, 0, 0, 0, 0, 0, 'A small tag on this present reads:$B$BHappy holidays, $n!', 0);
INSERT INTO `quest_offer_reward` VALUES (43364, 0, 0, 0, 0, 0, 0, 0, 0, 'This present looks like it has been shaken a few times. The tag on it reads:$B$BTo a very special $R $C.', 0);
INSERT INTO `quest_offer_reward` VALUES (43365, 1, 0, 0, 0, 0, 0, 0, 0, 'Спасибо... Светику легче от того, что жизнь продолжается.', 23222);
INSERT INTO `quest_offer_reward` VALUES (43368, 1, 66, 0, 0, 0, 500, 0, 0, 'Твои подвиги в Изумрудном Сне стали примером для многих, $n. Опасность миновала – но угроза осталась, и так будет до тех пор, пока жив Ксавий.$B$BЯ помогу тебе. Вместе мы защитим Азерот от тех, кто хочет его погубить.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43370, 0, 0, 0, 0, 0, 0, 0, 0, 'Here you go, $n. Take care of Hati and the Titanstrike and be safe in your adventures.', 0);
INSERT INTO `quest_offer_reward` VALUES (43371, 0, 0, 0, 0, 0, 0, 0, 0, 'Hopefully our priests have a chance to rest before heading back into battle.', 0);
INSERT INTO `quest_offer_reward` VALUES (43372, 1, 0, 0, 0, 0, 0, 0, 0, 'Gilner is seeing voices, right? He\'s been flirting with dark power for too long, and it doesn\'t go away.', 23222);
INSERT INTO `quest_offer_reward` VALUES (43373, 1, 0, 0, 0, 0, 0, 0, 0, 'Ты как раз вовремя.$B$BЗначит, Велен хочет явить видение? Это непросто, но мы с Ялией справимся.', 23222);
INSERT INTO `quest_offer_reward` VALUES (43374, 1, 0, 0, 0, 0, 0, 0, 0, 'Твоя смекалка выручила меня в сложной ситуации. Спасибо тебе, $n.', 23222);
INSERT INTO `quest_offer_reward` VALUES (43375, 1, 0, 0, 0, 0, 0, 0, 0, 'It’s hard to imagine how many lives you $gsaved: saved; by collecting these vials.', 23222);
INSERT INTO `quest_offer_reward` VALUES (43376, 1, 0, 0, 0, 0, 0, 0, 0, 'Мы собрали все компоненты для эликсира Велена.', 23222);
INSERT INTO `quest_offer_reward` VALUES (43377, 0, 0, 0, 0, 0, 0, 0, 0, '< Sa\'ha\'s soft voice sounds in your head. >$B$BThank you for the gift of light, $ga priest:priestess.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43378, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve returned, and just in time it would seem.$B$BYalia and Zabra have just finished crafting the elixir. This lens will allow you to see what I see when the vision begins.', 0);
INSERT INTO `quest_offer_reward` VALUES (43379, 1, 0, 0, 0, 0, 0, 0, 0, 'Мы не позволим демону решать нашу судьбу. Изменить будущее в твоих силах.$B$BЯ знаю путь к спасению, и ты станешь нашим проводником.$B$BА пока мне придется тебя покинуть. Бремя этого видения оказалось тяжким и для моего тела, и для моего сознания.', 23222);
INSERT INTO `quest_offer_reward` VALUES (43380, 1, 0, 0, 0, 0, 0, 0, 0, 'Рожденный Светом сражается за Свет!', 23360);
INSERT INTO `quest_offer_reward` VALUES (43381, 1, 0, 0, 0, 0, 0, 0, 0, 'Ты $gспас:спасла; мою жизнь и душу.$B$B$gУвидел:Увидела;, что во мне осталось что-то хорошее, и $gзаставил:заставила; меня тоже в это поверить.$B$BПозволь мне сделать что-то стоящее, $n. Я хочу сражаться вместе с тобой.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43382, 1, 0, 0, 0, 0, 0, 0, 0, 'Ты $gсумел:сумела; вернуть мой дух из Бездны, а это задача не из простых. Не буду скрывать, что какая-то часть меня хотела остаться там, в объятиях теней, навсегда.$B$BБездна пронизана множеством голосов, $n. То, что они говорят, внушает тревогу.$B$BНельзя допустить, чтобы убеждения разобщили нас. Мы должны действовать заодно, как жрецы Света и Тьмы, и вместе противостоять Пылающему Легиону.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43383, 0, 0, 0, 0, 0, 0, 0, 0, '< The light is getting brighter and it\'s getting warmer in the room. >', 23360);
INSERT INTO `quest_offer_reward` VALUES (43384, 0, 0, 0, 0, 0, 0, 0, 0, 'Они в отличном состоянии! Спасибо, $n.', 23222);
INSERT INTO `quest_offer_reward` VALUES (43385, 1, 0, 0, 0, 0, 0, 0, 0, 'Твоим защитникам удалось добыть ценную информацию.$B$BМы узнали, что несколько бойцов Алого Натиска готовы перейти на нашу сторону.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43386, 1, 0, 0, 0, 0, 0, 0, 0, 'Зачем ты здесь? Зачем ты меня $gспас:спасла;? Я хочу умереть. Это будет расплата за все мои преступления...', 23360);
INSERT INTO `quest_offer_reward` VALUES (43387, 1, 0, 0, 0, 0, 0, 0, 0, 'Свет не оставил меня, $n. Это он привел тебя сюда!', 23360);
INSERT INTO `quest_offer_reward` VALUES (43388, 1, 0, 0, 0, 0, 0, 0, 0, 'Твое милосердие дарит мне новую надежду.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43389, 1, 0, 0, 0, 0, 0, 0, 0, 'Это и есть враг, который станет нашим союзником?', 23360);
INSERT INTO `quest_offer_reward` VALUES (43390, 1, 0, 0, 0, 0, 0, 0, 0, 'Тебя Алонсий послал? Раньше он нами особо не интересовался.$B$BХотя мы и не являемся служителями Света... в обычном понимании этого слова.$B$BМы служим не только Свету, но и Тьме. Верим, что обе силы достойны поклонения.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43391, 1, 0, 0, 0, 0, 0, 0, 0, 'Это он! Дневник, где записано заклинание, которое поможет нам найти дух Натали.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43392, 1, 0, 0, 0, 0, 0, 0, 0, 'The abyss attracts. Sometimes I thought I\'d stay here forever.$B$BThank you for the $grelease: releasing me; $n.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43393, 1, 0, 0, 0, 0, 0, 0, 0, 'Узнав о возвращении Натали Селин, Отрекшиеся будут ликовать, как и мы, – увидев, что пророчество сбывается.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43394, 1, 0, 0, 0, 0, 0, 0, 0, 'Хвала Свету, $gжрец:жрица;... Я сохраняла надежду из последних сил.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43395, 1, 0, 0, 0, 0, 0, 0, 0, 'Delas Moonfang has already told me about your heroism. Thank you - you $gsaved:saved; many lives.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43396, 1, 0, 0, 0, 0, 0, 0, 0, 'Сведения, которые ты $gдобыл:добыла;, в очередной раз доказывают, что пророчество нельзя оставлять без внимания. Вторжение Легиона неминуемо, и нам не выстоять без паладинов – наших друзей, с которыми нас объединяет Свет.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43397, 1, 0, 0, 0, 0, 0, 0, 0, 'Мы поможем защитить Храм света Пустоты и устроим ловушку для Бальназара.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43398, 1, 0, 0, 0, 0, 0, 0, 0, 'When we first met in the fields of Tirisfal, it became clear to me that it is you who will be able to unite all the priests. I would like to take credit for the fact that we have found you - our leader, but I think that in fact you have found us; we have a lot to do together, $n.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43399, 1, 0, 0, 0, 0, 0, 0, 0, 'Этого должно хватить, чтобы укрепить нашу оборону.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43400, 1, 0, 0, 0, 0, 0, 0, 0, 'Этих светоносных камней хватит, чтобы вооружить целую армию. Прекрасно, $n.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43401, 1, 0, 0, 0, 0, 0, 0, 0, 'Ты храбро $gсражался, Верховный жрец:сражалась, Верховная жрица;.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43402, 1, 0, 0, 0, 0, 0, 0, 0, 'Ты по праву $gстал нашим предводителем и заслужил эти почести, Верховный жрец:стала нашей предводительницей и заслужила эти почести, Верховная жрица;.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43403, 0, 0, 0, 0, 0, 0, 0, 0, 'Thanks to you, Supreme Druid, the Grove of Dreams has given a worthy fight to the enemy. You\'re saved; many lives, and your victories will be remembered for many years.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43407, 0, 0, 0, 0, 0, 0, 0, 0, 'Пусть новая сила твоего оружия послужит тебе верой и правдой.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43409, 4, 0, 0, 0, 0, 0, 0, 0, 'С этой новой силой мы сразим Легион!', 23420);
INSERT INTO `quest_offer_reward` VALUES (43412, 0, 0, 0, 0, 0, 0, 0, 0, 'Пусть новая сила твоего оружия послужит тебе верой и правдой.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43414, 1, 0, 0, 0, 0, 0, 0, 0, 'Эта новая сила делает нас непобедимыми!', 23420);
INSERT INTO `quest_offer_reward` VALUES (43415, 0, 0, 0, 0, 0, 0, 0, 0, 'Пусть эта сила поможет тебе в грядущие времена.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43418, 0, 0, 0, 0, 0, 0, 0, 0, 'Пусть эта сила поможет тебе в грядущие времена.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43420, 0, 0, 0, 0, 0, 0, 0, 0, 'Пусть новая сила твоего оружия послужит тебе верой и правдой.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43422, 0, 0, 0, 0, 0, 0, 0, 0, 'Пусть твое новое оружие хорошо тебе послужит.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43423, 0, 0, 0, 0, 0, 0, 0, 0, 'Пусть новая сила твоего оружия послужит тебе верой и правдой.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43424, 0, 0, 0, 0, 0, 0, 0, 0, 'May this blessed power aid you in the times ahead.', 0);
INSERT INTO `quest_offer_reward` VALUES (43425, 0, 0, 0, 0, 0, 0, 0, 0, 'May your new weapon serve you well.', 0);
INSERT INTO `quest_offer_reward` VALUES (43441, 1, 0, 0, 0, 0, 0, 0, 0, 'Значит, решено.', 22566);
INSERT INTO `quest_offer_reward` VALUES (43461, 0, 0, 0, 0, 0, 0, 0, 0, 'A moment to reminisce on how far we have come. A day to look to the future and where we might go. A year of reflection on what it means to grow and a lifetime of memories made by those brave enough to seize it.', 0);
INSERT INTO `quest_offer_reward` VALUES (43462, 1, 0, 0, 0, 0, 0, 0, 0, 'Чисто сработано.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43468, 273, 0, 0, 0, 0, 0, 0, 0, 'Этого хватит. Я отправлю Вульфу все до последней капли.\n\nСпасибо, $n.', 22101);
INSERT INTO `quest_offer_reward` VALUES (43469, 1, 6, 0, 0, 0, 0, 0, 0, 'Ну что же. Теперь мы знаем, что нашего друга из ШРУ удерживают в оплоте Оскверненной Души в Сурамаре. Надо было первым делом туда заглянуть...\n\nТы ведь уже $gпридумал:придумала;, как его оттуда вытащить? У тоже меня есть кое-какие мысли на этот счет. Давай объединим наши усилия.', 22290);
INSERT INTO `quest_offer_reward` VALUES (43470, 273, 0, 0, 0, 0, 0, 0, 0, 'Ну вот, так гораздо лучше. Наш сад стал куда красивее.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43471, 0, 0, 0, 0, 0, 0, 0, 0, '$n, finally. I guess you $ggot:got it; my letter.', 22908);
INSERT INTO `quest_offer_reward` VALUES (43472, 0, 0, 0, 0, 0, 0, 0, 0, '$n, you\'re here. You must have received my letter.', 0);
INSERT INTO `quest_offer_reward` VALUES (43473, 0, 0, 0, 0, 0, 0, 0, 0, 'Want to earn a few quick coins? I need subjects to try one of my potions.$B$BThis one gets its unique aroma from frog eyes, frog hearts... wait a minute. Let me check the ingredient list here.$B$BMm-hmm, mm-hmm, OK, yeah.$B$BThis is pretty much just liquid frogs. Bottoms up!', 0);
INSERT INTO `quest_offer_reward` VALUES (43474, 1, 1, 5, 0, 0, 0, 0, 0, 'Хочешь быстро подзаработать? Я ищу подопытных, чтобы испытать мое новое зелье.$b$bК сожалению, мне его пробовать никак нельзя: на некоторые ингредиенты у меня аллергия. Оно для меня невероятно токсично. Для меня оно, фактически, просто большая бутыль яда.$b$bНу, пей же смелей!', 23911);
INSERT INTO `quest_offer_reward` VALUES (43475, 0, 0, 0, 0, 0, 0, 0, 0, 'Want to earn a few quick coins? I need subjects to try this potion.$B$BI spent most of my development time focusing on the potion\'s effects, and not a lot on its... pleasantness. You may have to ignore the taste, smell, mouthfeel, and appearance.$B$BActually, I think I heard a few SOUNDS coming from this particular potion... you should ignore those, too.$B$BCheers!', 0);
INSERT INTO `quest_offer_reward` VALUES (43476, 1, 1, 5, 0, 0, 0, 0, 0, 'Хочешь быстро подзаработать? Я ищу нового подопытного, чтобы испытать один из своих любимых рецептов.$b$bПризнаюсь, последние несколько раз с зельем, приготовленным по этому рецепту, возникли кое-какие, скажем так, сложности – я имею в виду у подопытных. Поэтому-то мне и приходится теперь платить тем, кто пробует мои зелья.$b$bИтак, приятного аппетита.', 22566);
INSERT INTO `quest_offer_reward` VALUES (43477, 0, 0, 0, 0, 0, 0, 0, 0, 'Want to earn a few quick coins? I need test subjects to try my newest potion.$B$BI\'ve had several requests for vegetarian potions. You\'d be surprised how many potions have meat in them!$B$BTo compensate for the complete lack of carcasses in this potion, I\'ve had to make a few substitutions. I hope you like crushed-up bits of glass and rotten fish guts!$B$BFish aren\'t animals, right?', 0);
INSERT INTO `quest_offer_reward` VALUES (43478, 0, 0, 0, 0, 0, 0, 0, 0, 'Want to earn a few quick coins? I need test subjects to drink a potion while I watch them.$B$BI\'ve had a lot of trouble getting this potion to perform its desired effect. Mostly it just seems to make people mad at me for making them drink it.$B$BGo on, then. Drink up.', 0);
INSERT INTO `quest_offer_reward` VALUES (43479, 273, 0, 0, 0, 0, 0, 0, 0, 'Прекрасный выбор целей. Некоронованным повезло с тобой $n.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43485, 0, 0, 0, 0, 0, 0, 0, 0, 'Отвлекающий маневр удался!', 23420);
INSERT INTO `quest_offer_reward` VALUES (43486, 1, 0, 0, 0, 0, 0, 0, 0, 'Тебя прислала Апони? Как у нее дела? В такие времена непросто поддерживать связь с друзьями.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43487, 1, 0, 0, 0, 0, 0, 0, 0, 'Yes, of course, this is what you need! Awesome book, $n. This knowledge will serve a good cause.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43488, 1, 0, 0, 0, 0, 0, 0, 0, 'Yes, that’s enough.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43489, 1, 0, 0, 0, 0, 0, 0, 0, '< This book is written in Aponi\'s handwriting. What happened here?', 23360);
INSERT INTO `quest_offer_reward` VALUES (43490, 1, 0, 0, 0, 0, 0, 0, 0, 'Хвала Свету, $gВерховный лорд:Верховная леди;. Я не теряла надежду на спасение.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43491, 1, 0, 0, 0, 0, 0, 0, 0, 'Aponi Lightyear Griva already told me about your heroism, $gSupreme Lord:Supreme Lady... You\'re $gsaved: saved many lives.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43492, 66, 0, 0, 0, 0, 0, 0, 0, 'Я в неоплатном долгу перед тобой, $gВерховный лорд:Верховная леди;. Ты живешь, открыв сердце Свету, – так, как учат служители солнца.$B$BДля меня будет честью сражаться на твоей стороне. Позволь мне продолжить борьбу в рядах ордена Серебряной Длани.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43493, 1, 0, 0, 0, 0, 0, 0, 0, 'Невероятно, $gВерховный лорд:Верховная леди;. Угроза со стороны крепости устранена. Теперь мы можем сосредоточиться на других областях.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43494, 1, 0, 0, 0, 0, 0, 0, 0, 'Beautiful! These knights will serve the Order well.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43496, 1, 0, 0, 0, 0, 0, 0, 0, 'Сенегос любезно предложил нам свою помощь на время предстоящего пути.$B$BКакая чуткость!', 24015);
INSERT INTO `quest_offer_reward` VALUES (43501, 0, 0, 0, 0, 0, 0, 0, 0, 'Senegos has generously offered us his assistance in the journey ahead.$B$BA truly kind soul.', 0);
INSERT INTO `quest_offer_reward` VALUES (43502, 0, 0, 0, 0, 0, 0, 0, 0, 'У нашего деревца изрядный аппетит.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43503, 1, 0, 0, 0, 0, 0, 0, 0, '', 22747);
INSERT INTO `quest_offer_reward` VALUES (43505, 0, 0, 0, 0, 0, 0, 0, 0, 'Senegos has generously offered us his assistance in the journey ahead.$B$BA truly kind soul.', 0);
INSERT INTO `quest_offer_reward` VALUES (43506, 0, 0, 0, 0, 0, 0, 0, 0, 'It is done. Let’s collect the Valarjars for a glorious battle!', 23420);
INSERT INTO `quest_offer_reward` VALUES (43508, 1, 0, 0, 0, 0, 0, 0, 0, 'Hurry up, $n. We gotta get him out of here before the demons raise the alarm.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43509, 0, 0, 0, 0, 0, 0, 0, 0, 'Were you able to overcome Odyn\'s challenge?', 0);
INSERT INTO `quest_offer_reward` VALUES (43514, 1, 0, 0, 0, 0, 0, 0, 0, 'Отлично. Это поможет нам развеять магию, которая защищает Сердце.', 22810);
INSERT INTO `quest_offer_reward` VALUES (43517, 1, 0, 0, 0, 0, 0, 0, 0, 'The Ancient has found peace again. I hope this sacrifice is not in vain.', 22996);
INSERT INTO `quest_offer_reward` VALUES (43518, 1, 0, 0, 0, 0, 0, 0, 0, 'Невероятно! Это то, что нам нужно.', 22810);
INSERT INTO `quest_offer_reward` VALUES (43519, 1, 0, 0, 0, 0, 0, 0, 0, 'Отлично! Все прошло, как задумано.', 22996);
INSERT INTO `quest_offer_reward` VALUES (43520, 0, 0, 0, 0, 0, 0, 0, 0, 'That is... far more than we needed.$B$BThank you, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (43521, 0, 0, 0, 0, 0, 0, 0, 0, 'Your bravery rivals that of my own kind.$B$BWell done.', 0);
INSERT INTO `quest_offer_reward` VALUES (43522, 0, 0, 0, 0, 0, 0, 0, 0, 'That is all I can do for you, little one. If you seek more power, you must look elsewhere.', 0);
INSERT INTO `quest_offer_reward` VALUES (43523, 0, 0, 0, 0, 0, 0, 0, 0, 'Let us speak with Thalrenus and discuss our plan of action.', 0);
INSERT INTO `quest_offer_reward` VALUES (43524, 0, 0, 0, 0, 0, 0, 0, 0, 'That\'s the one! Oh, thank you, $n. Thank you!', 0);
INSERT INTO `quest_offer_reward` VALUES (43525, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent. This will work quite well.', 0);
INSERT INTO `quest_offer_reward` VALUES (43526, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'m... so sorry, $n. I was so sure that would work...', 0);
INSERT INTO `quest_offer_reward` VALUES (43527, 0, 0, 0, 0, 0, 0, 0, 0, 'It seems we have gained new allies. Let us figure out a plan of action.', 0);
INSERT INTO `quest_offer_reward` VALUES (43528, 0, 0, 0, 0, 0, 0, 0, 0, 'We\'ve found a back entrance into the Nighthold that should provide some relief from the armies at the main gate.$B$B\'Some\' being key.', 0);
INSERT INTO `quest_offer_reward` VALUES (43530, 0, 0, 0, 0, 0, 0, 0, 0, '<Thalrenus\' eyes widen at the sight of the Millenia Tome.>$B$BIf Elisande has seen reason... perhaps there is still hope for the Guard after all.', 0);
INSERT INTO `quest_offer_reward` VALUES (43531, 1, 0, 0, 0, 0, 0, 0, 0, '<The keycard is digging into a pile of shrapnel >$B$BYes, they\'re just perfect! Now there\'s only one thing missing.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43532, 0, 0, 0, 0, 0, 0, 0, 0, 'Let us not waste another moment.', 0);
INSERT INTO `quest_offer_reward` VALUES (43533, 0, 0, 0, 0, 0, 0, 0, 0, 'Your artifact\'s new form is even more fantastic than I imagined.$B$BPlease, take what remains of the Heart as a token of my gratitude for such a marvelous adventure - there is still a fair bit of power left inside it.', 0);
INSERT INTO `quest_offer_reward` VALUES (43534, 1, 0, 0, 0, 0, 0, 0, 0, 'Yes, this is just what we need to complete the preparations. It must have been difficult to get her. Thank you, $ gHighlord:High Lady ;.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43535, 1, 0, 0, 0, 0, 0, 0, 0, 'Спасибо, что $gпривел:привела; Делас сюда, $n. Я знала, что она нам поможет.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43539, 0, 0, 0, 0, 0, 0, 0, 0, 'The Black Klink warriors sent to the Land of Shadows have returned to Akerus, having achieved success. Meet him when he arrives.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43540, 1, 0, 0, 0, 0, 0, 0, 0, 'Легион планирует атаку на главный штаб жрецов? То, что тебе удалось вовремя добыть эту информацию... это бесценно, $gВерховный лорд:Верховная леди;.$B$BРазумеется, Легион хочет устранить пророка Велена и его последователей. Мы должны помешать Бальназару.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43541, 1, 0, 0, 0, 0, 0, 0, 0, 'Что ж, выбор сделан. Мы поможем защитить Храм света Пустоты и поймаем Бальназара в ловушку.$B$BЖрецы Конклава – прекрасные союзники. Может, вместе мы сумеем справиться с Бальназаром.', 23360);
INSERT INTO `quest_offer_reward` VALUES (43545, 0, 0, 0, 0, 0, 0, 0, 0, 'Were you able to defeat Lord Ravencrest?', 0);
INSERT INTO `quest_offer_reward` VALUES (43546, 0, 0, 0, 0, 0, 0, 0, 0, 'Were you able to overcome the Emerald Nightmare?', 0);
INSERT INTO `quest_offer_reward` VALUES (43547, 0, 0, 0, 0, 0, 0, 0, 0, 'Were you able to overcome the Emerald Nightmare?', 0);
INSERT INTO `quest_offer_reward` VALUES (43548, 0, 0, 0, 0, 0, 0, 0, 0, 'Were you able to overcome the Emerald Nightmare?', 0);
INSERT INTO `quest_offer_reward` VALUES (43549, 0, 0, 0, 0, 0, 0, 0, 0, 'Were you able to overcome the Emerald Nightmare?', 0);
INSERT INTO `quest_offer_reward` VALUES (43551, 0, 0, 0, 0, 0, 0, 0, 0, 'Were you able to overcome Darkheart Thicket?', 0);
INSERT INTO `quest_offer_reward` VALUES (43552, 0, 0, 0, 0, 0, 0, 0, 0, 'Were you able to overcome Eye of Azshara', 0);
INSERT INTO `quest_offer_reward` VALUES (43553, 0, 0, 0, 0, 0, 0, 0, 0, 'Were you able to overcome Maw of Souls?', 0);
INSERT INTO `quest_offer_reward` VALUES (43554, 0, 0, 0, 0, 0, 0, 0, 0, 'Were you able to overcome Neltharion\'s Lair?', 0);
INSERT INTO `quest_offer_reward` VALUES (43556, 0, 0, 0, 0, 0, 0, 0, 0, 'Were you able to overcome Vault of the Wardens?', 0);
INSERT INTO `quest_offer_reward` VALUES (43557, 0, 0, 0, 0, 0, 0, 0, 0, 'Were you able to overcome the Assault on the Violet Hold?', 0);
INSERT INTO `quest_offer_reward` VALUES (43558, 0, 0, 0, 0, 0, 0, 0, 0, 'And so you return with the bindings in hand. The Firelord shall be very displeased to have lost these.', 0);
INSERT INTO `quest_offer_reward` VALUES (43560, 0, 0, 0, 0, 0, 0, 0, 0, 'The bindings now belong to me. When the time comes, the Firelord shall be imprisoned as I once was. But that time is not now.$B$BYour reward is this, a small fragment of my power. Unleash it upon your blades, $R, and you shall have what you seek.', 0);
INSERT INTO `quest_offer_reward` VALUES (43562, 0, 0, 0, 0, 0, 0, 0, 0, 'Надо срочно действовать.', 24015);
INSERT INTO `quest_offer_reward` VALUES (43563, 0, 0, 0, 0, 0, 0, 0, 0, 'Это то, что надо.', 24015);
INSERT INTO `quest_offer_reward` VALUES (43564, 0, 0, 0, 0, 0, 0, 0, 0, 'If I have an accurate map of the catacombs, these are all the exits we need to worry about.', 24015);
INSERT INTO `quest_offer_reward` VALUES (43565, 0, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно. Теперь мои шансы телепортировать вас на дно моря существенно ниже!', 24015);
INSERT INTO `quest_offer_reward` VALUES (43567, 0, 0, 0, 0, 0, 0, 0, 0, 'Узри же. Искупление помраченных...', 24015);
INSERT INTO `quest_offer_reward` VALUES (43568, 5, 0, 0, 0, 0, 0, 0, 0, 'Наконец-то мы свободны.', 24015);
INSERT INTO `quest_offer_reward` VALUES (43569, 5, 18, 0, 0, 0, 0, 0, 0, '', 24015);
INSERT INTO `quest_offer_reward` VALUES (43571, 0, 0, 0, 0, 0, 0, 0, 0, 'Отлично, $gВладыка смерти:Владычица смерти;. Из ремешков этого ожерелья получатся отличные поводья.$b$bПолагаю, Даргрул был счастлив, когда расставался со своей игрушкой?$b$b<Саланар злорадно усмехается.>', 23420);
INSERT INTO `quest_offer_reward` VALUES (43572, 0, 0, 0, 0, 0, 0, 0, 0, 'Наконец-то! Кошмарная плеть! Держу ее в руках и понимаю, что легенды не врут.$b$bСтолько мощи... столько тьмы... столько страданий!$b$bДа, прекрасно подойдет.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43573, 0, 0, 0, 0, 0, 0, 0, 0, 'Рыцари Черного Клинка сражаются весьма успешно, и это привлекло особое внимание Пылающего Легиона.$b$bТеперь за нашими действиями пристально следят силы Саргераса...', 23420);
INSERT INTO `quest_offer_reward` VALUES (43574, 0, 0, 0, 0, 0, 0, 0, 0, 'Легендарная Кувалда мертвецов... Она излучает ненависть и отчаяние бесчисленных истерзанных душ. Воплощенное страдание, само совершенство.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43576, 1, 0, 0, 0, 0, 0, 0, 0, 'Ясность рассудка возвращается ко мне.', 22522);
INSERT INTO `quest_offer_reward` VALUES (43577, 0, 0, 0, 0, 0, 0, 0, 0, 'The Valariars have taken the Legion\'s portal to the Flames of Square. It\'s time to strike a decisive blow. We will not wait for the demons to attack first!', 23420);
INSERT INTO `quest_offer_reward` VALUES (43581, 0, 0, 0, 0, 0, 0, 0, 0, 'Your next steps will be difficult, young one, and more dangerous than any threat you\'ve faced, but they must be taken.$B$BFor the good of Azeroth.', 0);
INSERT INTO `quest_offer_reward` VALUES (43582, 0, 0, 0, 0, 0, 0, 0, 0, 'That was quite the spectacle, $n.$B$BSu\'esh will be a valuable ally in the days to come. I will keep an eye on her for the time being.', 0);
INSERT INTO `quest_offer_reward` VALUES (43585, 0, 0, 0, 0, 0, 0, 0, 0, 'Ты и твои воины многого добились. Ты отличный командир. Один не ошибся с выбором.', 23171);
INSERT INTO `quest_offer_reward` VALUES (43586, 0, 0, 0, 0, 0, 0, 0, 0, 'Славная победа! Жаль только, что Хелия выжила. Но когда ты поведешь в бой армии Небесной Цитадели, она не осмелится выступить против нас.', 23420);
INSERT INTO `quest_offer_reward` VALUES (43587, 0, 0, 0, 0, 0, 0, 0, 0, '<You fill the channel with mana, and it\'s like it\'s coming back to life. \" You literally feel the air around you charging with mysterious magic', 23420);
INSERT INTO `quest_offer_reward` VALUES (43588, 0, 0, 0, 0, 0, 0, 0, 0, '<You fill the channel with mana, and it\'s like it\'s coming back to life. \" You literally feel the air around you charging with mysterious magic', 24015);
INSERT INTO `quest_offer_reward` VALUES (43590, 0, 0, 0, 0, 0, 0, 0, 0, '<You place the missing part on the column and start to saturate it with mana. The sorcerer\'s energy seals the rift, returning the mechanism to its original state. It starts to rotate slowly - the power line is activated.', 24015);
INSERT INTO `quest_offer_reward` VALUES (43591, 0, 0, 0, 0, 0, 0, 0, 0, '<You put the wreckage in the column, and the sorcerer\'s energy begins to seal the cracks. You fill the channel with ancient mana, and it\'s like it\'s coming alive.>', 24015);
INSERT INTO `quest_offer_reward` VALUES (43592, 0, 0, 0, 0, 0, 0, 0, 0, '<You fill the power line with mana, and it\'s like it\'s coming back to life. \" You literally feel the air around you charging with mysterious magic', 24015);
