INSERT INTO `quest_offer_reward` VALUES (43926, 1, 1, 0, 0, 0, 0, 0, 0, 'The ships are leaving from the dock in Bladefist Bay.$B$BBefore you leave, prepare yourself. I have the feeling that this fight won\'t be an easy one.', 21737);
INSERT INTO `quest_offer_reward` VALUES (43928, 0, 0, 0, 0, 0, 0, 0, 0, 'Теперь, когда у нас есть частицы страдания, работа близится к завершению. Жеребцы проклятых восстанут... а Пылающий Легион падет!', 23420);
INSERT INTO `quest_offer_reward` VALUES (43929, 0, 0, 0, 0, 0, 0, 0, 0, 'Greetings, $n.$B$BIt is my understanding that you have been one of the foremost scribes responsible for equipping our forces with Vantus runes. Do not think that your efforts as an artisan have gone unnoticed.$B$BWe noticed.$B$BI would like to present you with this technique. Now go, and use it to help us win this war.', 0);
INSERT INTO `quest_offer_reward` VALUES (43933, 66, 0, 0, 0, 0, 0, 0, 0, '$gsupreme lord: Supreme lady; I\'m taking the first step on a new path. $B$BThank you for the honor. I swear to follow our principles of honor and righteousness, to eradicate injustice. I gain new brothers and sisters from the Order of Silver Plani. $B$BUnder the banner of Light, we are one!', 23360);
