INSERT INTO `quest_offer_reward` VALUES (43942, 273, 0, 0, 0, 0, 0, 0, 0, 'Just what I\'ve been looking for! <King Mrgl Mrgl studies the artifact> Ah! Yes. But there must be more of these Timeworn Artifacts still on the shore. Any more you can bring back would be greatly appreciated!', 21570);
INSERT INTO `quest_offer_reward` VALUES (43945, 1, 0, 0, 0, 0, 0, 0, 0, 'Очень хорошо. Твои союзники собрались здесь, на утесе. Они знают, что делать дальше.$b$bПусть ветер всегда благоволит тебе, |3-6($ct).', 22810);
INSERT INTO `quest_offer_reward` VALUES (43949, 0, 0, 0, 0, 0, 0, 0, 0, 'Это грозное оружие под стать $gмогучему защитнику:могучей защитнице; наших чертогов. Приступим же к его поискам.', 22996);
