INSERT INTO `quest_offer_reward` VALUES (45028, 1, 0, 0, 0, 0, 0, 0, 0, '', 24015);
INSERT INTO `quest_offer_reward` VALUES (45044, 0, 0, 0, 0, 0, 0, 0, 0, 'Ay mon, whatcha got der in your hands?$B$BDat be da Darkspear! Ya found it?!', 0);
INSERT INTO `quest_offer_reward` VALUES (45051, 0, 0, 0, 0, 0, 0, 0, 0, 'Dem\'s some mighty fine materials dat was made of. No wonda\' da boss wants it so bad.', 0);
INSERT INTO `quest_offer_reward` VALUES (45055, 0, 0, 0, 0, 0, 0, 0, 0, 'Oh mon, I wish I could\'a seen da look on their faces when you pummled dem!$B$BNow dat we have all da materials, we just need to put it together.', 0);
INSERT INTO `quest_offer_reward` VALUES (45056, 0, 0, 0, 0, 0, 0, 0, 0, 'I cannot possibly thank you enough. We will be reunited, and I will see we are never separated again.', 0);
INSERT INTO `quest_offer_reward` VALUES (45057, 0, 0, 0, 0, 0, 0, 0, 0, 'Dat\'s it, mon! Ya did it!$B$BI tink before I head off back to da Isles, I\'ll be making a few extra copies of dees plans, just in case.$B$BI\'ll make ya a copy too, in case I be needing your help wit\' making another one.', 0);
INSERT INTO `quest_offer_reward` VALUES (45062, 0, 0, 0, 0, 0, 0, 0, 0, 'Твои находки порадуют Окулета. Как тебе уже известно, он усердно работает над новым устройством для иссохших.', 23222);
INSERT INTO `quest_offer_reward` VALUES (45063, 0, 0, 0, 0, 0, 0, 0, 0, 'Иссохшие вполне неплохо приспособились.', 23222);
INSERT INTO `quest_offer_reward` VALUES (45064, 1, 0, 0, 0, 0, 0, 0, 0, 'Наши усилия принесли плоды. Думаю, мы вполне готовы оказать сопротивление.', 23222);
INSERT INTO `quest_offer_reward` VALUES (45065, 1, 1, 0, 0, 0, 0, 0, 0, 'Я немедленно приступлю к анализу полученных данных.$B$BА пока обратим внимание на более... <Окулет складывает пальцы домиком.>$B$B...интересные достижения технического прогресса.', 23222);
INSERT INTO `quest_offer_reward` VALUES (45066, 5, 0, 0, 0, 0, 0, 0, 0, 'Beautiful! The fun is about to begin! Talk to Talisroy, I think she\'s ready for new... trials.', 23222);
INSERT INTO `quest_offer_reward` VALUES (45067, 25, 1, 0, 0, 0, 0, 0, 0, 'Приступим.', 23222);
INSERT INTO `quest_offer_reward` VALUES (45073, 1, 0, 0, 0, 0, 0, 0, 0, 'Спасибо тебе. Теперь я знаю, что отмщен, и мне станет легче.', 24015);
INSERT INTO `quest_offer_reward` VALUES (45079, 0, 0, 0, 0, 0, 0, 0, 0, '<Annie looks at you, her eyes hollow and empty. Her clothes are stained with blood, though she seems to be mostly unscathed. She stutters, but her words are unintelligible.>', 0);
INSERT INTO `quest_offer_reward` VALUES (45083, 0, 0, 0, 0, 0, 0, 0, 0, 'There are feathers everywhere! What a mess.', 0);
INSERT INTO `quest_offer_reward` VALUES (45088, 0, 0, 0, 0, 0, 0, 0, 0, 'With these souls returned to the Halls of Valor my forces return to their former strength. As promised, the bounty is yours.', 0);
INSERT INTO `quest_offer_reward` VALUES (45103, 0, 0, 0, 0, 0, 0, 0, 0, '', 24015);
INSERT INTO `quest_offer_reward` VALUES (45111, 0, 0, 0, 0, 0, 0, 0, 0, 'Хорошо, я позволю твоим защитникам выступить на ринге. Если они смогут победить, вернись ко мне, и мы попробуем подобрать для них соперников посильнее.', 24015);
INSERT INTO `quest_offer_reward` VALUES (45118, 0, 0, 0, 0, 0, 0, 0, 0, '', 24015);
INSERT INTO `quest_offer_reward` VALUES (45123, 396, 0, 0, 0, 0, 0, 0, 0, '', 24015);
INSERT INTO `quest_offer_reward` VALUES (45125, 0, 0, 0, 0, 0, 0, 0, 0, 'Я приятно удивлен, |3-6($c). Если бы не твоя находчивость, у твоей приятельницы не было бы шансов бежать из плена Агаты.\n\nЧто ж, портал стабилизирован, и он должен привести тебя к Левии.', 23835);
INSERT INTO `quest_offer_reward` VALUES (45126, 0, 0, 0, 0, 0, 0, 0, 0, 'Омерзительно! Поверить не могу, что ее чары сработали! \n\nЧто за глупость! Она заставила меня думать, что заботится обо мне. Но все оказалось ложью!', 23835);
INSERT INTO `quest_offer_reward` VALUES (45127, 0, 0, 0, 0, 0, 0, 0, 0, 'Значит, Левия в безопасности? Отрадно знать, хоть нам и стоило связаться с ней раньше. Мать бесов – это и так плохо, а уж на что Агата будет способна с силой Левии... Да и кто знает, какие тайны Левия выдала ей в плену?\n\nБоюсь, с этой проблемой еще не покончено.\n\nНо пока мы можем порадоваться твоему успеху. Спасибо за помощь, $n.', 23835);
INSERT INTO `quest_offer_reward` VALUES (45128, 0, 0, 0, 0, 0, 0, 0, 0, '', 24015);
INSERT INTO `quest_offer_reward` VALUES (45143, 1, 0, 0, 0, 0, 0, 0, 0, '', 24015);
INSERT INTO `quest_offer_reward` VALUES (45145, 1, 0, 0, 0, 0, 0, 0, 0, '', 24015);
