INSERT INTO `quest_offer_reward` VALUES (46785, 0, 0, 0, 0, 0, 0, 0, 0, 'Our power grows.', 0);
INSERT INTO `quest_offer_reward` VALUES (46786, 0, 0, 0, 0, 0, 0, 0, 0, '', 24015);
INSERT INTO `quest_offer_reward` VALUES (46788, 0, 0, 0, 0, 0, 0, 0, 0, '', 24015);
INSERT INTO `quest_offer_reward` VALUES (46789, 0, 0, 0, 0, 0, 0, 0, 0, '', 24015);
INSERT INTO `quest_offer_reward` VALUES (46791, 0, 0, 0, 0, 0, 0, 0, 0, 'Спасибо, что $gоткликнулся:откликнулась; на зов, $gпредсказатель:предсказательница;.', 24742);
INSERT INTO `quest_offer_reward` VALUES (46792, 0, 0, 0, 0, 0, 0, 0, 0, 'Теперь, когда в конклаве воцарилось согласие, буря обойдет обитель воздуха стороной!$b$bВладыка Громораан желает вознаградить твои усилия.', 24742);
INSERT INTO `quest_offer_reward` VALUES (46794, 0, 0, 0, 0, 0, 0, 0, 0, 'Greetings Deathlord $n.$B$BFor some time now you have been a Paragon to our prince and to our people. Our gratitude goes beyond anything mere words can define.$B$BTake these trophies as a token of our gratitude and a share in the spoils of war. The more you aid us, the more we can provide you.', 0);
INSERT INTO `quest_offer_reward` VALUES (46796, 0, 0, 0, 0, 0, 0, 0, 0, 'Greetings Huntmaster $n.$B$BFor some time now you have been a Paragon to our people. Our gratitude goes beyond anything mere words can define.$B$BTake these trophies as a token of our gratitude and a share in the spoils of war. The more you aid us, the more we can provide you.', 0);
INSERT INTO `quest_offer_reward` VALUES (46797, 0, 0, 0, 0, 0, 0, 0, 0, 'Greetings Highlord $n.$B$BFor some time now you have been a Paragon to Nature and to our people. Our gratitude goes beyond anything mere words can define.$B$BTake these trophies as a token of our gratitude and a share in the spoils of war. The more you aid us, the more we can provide you.', 0);
INSERT INTO `quest_offer_reward` VALUES (46798, 0, 0, 0, 0, 0, 0, 0, 0, 'Greetings Highlord $n.$B$BFor some time now you have been a Paragon to Odyn and a hero to the Valarjar. Our gratitude goes beyond anything mere words can define.$B$BTake these trophies as a token of our gratitude and a share in the spoils of war. The more you aid us, the more we can provide you.', 0);
INSERT INTO `quest_offer_reward` VALUES (46799, 0, 0, 0, 0, 0, 0, 0, 0, 'Приветствую тебя, |3-6($ct), $gчужестранец:чужестранка;, $gосвободитель:освободительница;, друг.$b$bНаша благодарность так велика, что ее невозможно выразить словами.$b$bПрими от нас эти боевые трофеи. Продолжай помогать нам, и мы продолжим делиться с тобой добычей.', 23877);
INSERT INTO `quest_offer_reward` VALUES (46800, 0, 0, 0, 0, 0, 0, 0, 0, 'Greetings Huntmaster.$B$BFor some time now you have been a Paragon in your efforts against the Legion and your aid of the Wardens. Our gratitude goes beyond anything mere words can define.$B$BTake these trophies as a token of our gratitude and a share in the spoils of war. The more you aid us, the more we can provide you.', 0);
INSERT INTO `quest_offer_reward` VALUES (46802, 0, 0, 0, 0, 0, 0, 0, 0, 'Greetings, Deathlord.$B$BFor some time now you have been a paragon in your efforts against the Legion, the beacon that unites our armies together. Our gratitude goes beyond anything mere words can define.$B$BTake these trophies as a token of our gratitude and a share in the spoils of war. The more you aid us, the more we can provide you.', 0);
INSERT INTO `quest_offer_reward` VALUES (46804, 0, 0, 0, 0, 0, 0, 0, 0, 'Glad I could share the Hammerbeard System of Style and Taste with you. Are you ready to work for your legacy in your chosen profession?', 0);
INSERT INTO `quest_offer_reward` VALUES (46805, 0, 0, 0, 0, 0, 0, 0, 0, '', 26365);
INSERT INTO `quest_offer_reward` VALUES (46809, 0, 0, 0, 0, 0, 0, 0, 0, 'What have you brought today, Deathlord?$B$B<Illanna examines the ancient tome.>$B$BThis book... it changes everything!', 0);
INSERT INTO `quest_offer_reward` VALUES (46810, 0, 0, 0, 0, 0, 0, 0, 0, 'If you find any other unwanted spoils of war, obliterate them at the forge and I can help you find something more desirable.', 0);
INSERT INTO `quest_offer_reward` VALUES (46812, 0, 0, 0, 0, 0, 0, 0, 0, '< The codex contains information about many of the great red dragons of the past. >$B$B<blank page, you find a note about an ancient red dragon named Kiranastraz who fell in battle with \"terrible damage\" on an iceberg north of here. >$B$B<Fields marked where this iceberg was last seen. >', 24742);
INSERT INTO `quest_offer_reward` VALUES (46813, 0, 0, 0, 0, 0, 0, 0, 0, '< There\'s a crack in the ice. It\'s barely visible, but it\'s deep enough that you can sink your weapon into it and fill the bones with impious energy.', 24742);
INSERT INTO `quest_offer_reward` VALUES (46815, 6, 0, 0, 0, 0, 0, 0, 0, 'Такое ощущение, что битва не прекращалась ни на миг. Сколько же им пришлось так страдать?', 25549);
INSERT INTO `quest_offer_reward` VALUES (46816, 1, 0, 0, 0, 0, 0, 0, 0, 'Лотраксион – давний друг и благородный воин. Нам очень повезло, что он с нами.', 25549);
INSERT INTO `quest_offer_reward` VALUES (46818, 1, 0, 0, 0, 0, 0, 0, 0, 'Ситуация меняется на глазах. Мы должны двигаться дальше.', 25549);
INSERT INTO `quest_offer_reward` VALUES (46827, 1, 0, 0, 0, 0, 0, 0, 0, 'Мы как раз обсуждали твои недавние успехи, $n. Мы действуем тайно, и наша работа на первый взгляд не видна. Но если бы нас не было, Армии погибели Легиона противостояло бы куда больше могущественных демонов.$b$bСлава нам не нужна. Нам достаточно результатов.', 24015);
INSERT INTO `quest_offer_reward` VALUES (46832, 0, 0, 0, 0, 0, 0, 0, 0, 'Тебя прислал Чеймберс? Да, перепить его мало кто может, надо отдать ему должное. Но он говорит, что я ему что-то там должен, это неправда!$b$bМы с моими драконами удержим здесь позиции – можешь не волноваться. Но чтобы твоя кампания увенчалась успехом, нужно укрепиться на всем побережье.', 23835);
INSERT INTO `quest_offer_reward` VALUES (46834, 1, 0, 0, 0, 0, 0, 0, 0, 'Я не могу исправить того, что здесь уже произошло, но все-таки на душе у меня стало спокойнее.$b$bСпасибо тебе, $n.', 25549);
INSERT INTO `quest_offer_reward` VALUES (46839, 0, 0, 0, 0, 0, 0, 0, 0, 'Итак, начнем.', 25549);
INSERT INTO `quest_offer_reward` VALUES (46840, 0, 0, 0, 0, 0, 0, 0, 0, 'Собратья Хатууна будут очень благодарны тебе.', 25549);
INSERT INTO `quest_offer_reward` VALUES (46841, 0, 0, 0, 0, 0, 0, 0, 0, 'Теперь, когда командиры демонов обращены в прах, мы можем нанести последний решительный удар.', 25549);
INSERT INTO `quest_offer_reward` VALUES (46842, 1, 1, 0, 0, 0, 0, 0, 0, 'Эта победа – лишь первое звено в цепи предстоящих сражений, |3-6($ct).', 25549);
INSERT INTO `quest_offer_reward` VALUES (46843, 0, 0, 0, 0, 0, 0, 0, 0, 'It was very difficult for Arthur to bear separation from us. We expected it. But we only wanted him to be safe while we fight the Legion.$b$bNow we\'re back together and we\'re not breaking up anymore.', 25549);
INSERT INTO `quest_offer_reward` VALUES (46845, 397, 0, 0, 0, 0, 0, 0, 0, 'У иллидари не принято тратить время на поздравления. Еще многое нужно сделать – и сделать это предстоит тебе.$B$BПоторопись.', 23835);
INSERT INTO `quest_offer_reward` VALUES (46904, 0, 0, 0, 0, 0, 0, 0, 0, 'Отлично. Теперь ты получишь преимущество, которое будет действовать, пока ставка командования активна.', 24015);
INSERT INTO `quest_offer_reward` VALUES (46924, 1, 0, 0, 0, 0, 0, 0, 0, '', 24015);
INSERT INTO `quest_offer_reward` VALUES (46935, 1, 1, 0, 0, 0, 0, 0, 0, 'С помощью этих маяков Легион поддерживает связь со своим флотом. Было бы неплохо найти побольше образцов для исследования...', 24015);
INSERT INTO `quest_offer_reward` VALUES (46938, 396, 0, 0, 0, 0, 0, 0, 0, 'Welcome to Argus, the home world of the Blazing Legion.', 25549);
INSERT INTO `quest_offer_reward` VALUES (46941, 1, 0, 0, 0, 0, 0, 0, 0, 'Путь вперед расчищен, и с нами Армия Света.', 25549);
INSERT INTO `quest_offer_reward` VALUES (46946, 0, 0, 0, 0, 0, 0, 0, 0, 'If you find any other unwanted spoils of war, obliterate them at the forge and I can help you find something more desirable.', 0);
INSERT INTO `quest_offer_reward` VALUES (46997, 0, 0, 0, 0, 0, 0, 0, 0, '', 24461);
INSERT INTO `quest_offer_reward` VALUES (46998, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, I can now provide you with a boon that will grant you a benefit while the Command Center is activated.', 0);
INSERT INTO `quest_offer_reward` VALUES (46999, 0, 0, 0, 0, 0, 0, 0, 0, 'Отлично. Теперь ты получишь преимущество, которое будет действовать, пока ставка командования активна.', 24015);
INSERT INTO `quest_offer_reward` VALUES (47000, 273, 0, 0, 0, 0, 0, 0, 0, '<Cadgar nods welcome to you >$B$BI\'m glad you came; |3-6($ct).', 23835);
INSERT INTO `quest_offer_reward` VALUES (47003, 25, 0, 0, 0, 0, 0, 0, 0, '', 24015);
INSERT INTO `quest_offer_reward` VALUES (47004, 25, 0, 0, 0, 0, 0, 0, 0, 'Поздновато для предостережения! Но все равно спасибо, что не забыли про нас.', 26365);
INSERT INTO `quest_offer_reward` VALUES (47005, 0, 0, 0, 0, 0, 0, 0, 0, 'A bit late for the warning, eh? Still, I appreciate that we haven\'t been forgotten.', 0);
INSERT INTO `quest_offer_reward` VALUES (47006, 25, 0, 0, 0, 0, 0, 0, 0, 'Поздновато для предостережения! Но все равно спасибо, что не забыли про нас.', 23937);
INSERT INTO `quest_offer_reward` VALUES (47007, 0, 0, 0, 0, 0, 0, 0, 0, 'Отлично. Теперь ты получишь преимущество, которое будет действовать, пока башня магов активна.', 23857);
