INSERT INTO `quest_offer_reward` VALUES (47102, 396, 396, 0, 0, 0, 0, 0, 0, 'Ты $gнашел:нашла; один из отчетов нашей разведки? Мы должны были получить его раньше; я уже стала беспокоиться, что он попал в руки врага.$b$b<Капитан быстро просматривает письмо.>$b$bДа, это как раз то, чего я и ожидала. Легион что-то затевает, и эта информация очень нам поможет!', 25549);
INSERT INTO `quest_offer_reward` VALUES (47104, 669, 0, 0, 0, 0, 0, 0, 0, 'Supreme Inquisitor Ralgar? He\'s a mighty enemy, but he\'s long been held at Arkatraz in the Storm Fortress. If the Legion intends to free him, then the demons believe he poses great danger to us.', 25549);
INSERT INTO `quest_offer_reward` VALUES (47112, 0, 0, 0, 0, 0, 0, 0, 0, 'Спасибо, $n. Это единственное, что мы можем сделать для тех, кто пожертвовал своей жизнью во имя Альянса... во имя Азерота.', 23877);
