INSERT INTO `quest_offer_reward` VALUES (48323, 2, 0, 0, 0, 0, 0, 0, 0, 'Спасибо за помощь, алхимик. Нам очень нужны были эти зелья.', 25961);
INSERT INTO `quest_offer_reward` VALUES (48337, 2, 0, 0, 0, 0, 0, 0, 0, 'Спасибо за помощь, травник. Нам очень нужен был этот астральный вьюнок.', 25961);
