INSERT INTO `quest_offer_reward` VALUES (48363, 2, 0, 0, 0, 0, 0, 0, 0, 'Спасибо за помощь, ювелир. Нам очень нужен был этот цветистый малахит.', 25961);
INSERT INTO `quest_offer_reward` VALUES (48364, 2, 0, 0, 0, 0, 0, 0, 0, 'Спасибо за помощь, ювелир. Нам очень нужен был этот эсселиан.', 25961);
INSERT INTO `quest_offer_reward` VALUES (48373, 2, 0, 0, 0, 0, 0, 0, 0, 'Спасибо за помощь, портной. Нам очень нужна была эта светоткань.', 25961);
INSERT INTO `quest_offer_reward` VALUES (48374, 2, 0, 0, 0, 0, 0, 0, 0, '', 25549);
INSERT INTO `quest_offer_reward` VALUES (48375, 0, 0, 0, 0, 0, 0, 0, 0, '<As Caelen takes the Sargerite, you see him fusing it with the Obliterum near the forge. The power radiating from it is quite impressive.>$B$BHere you are, lad. Say, ya don\'t think this stuff is toxic, right?', 0);
INSERT INTO `quest_offer_reward` VALUES (48376, 0, 0, 0, 0, 0, 0, 0, 0, '<As Caelen takes the stack of Sargerite, you see him fusing it with the stack of Obliterum near the forge. The power radiating from it is quite impressive.>$B$BHere you are, lad. Say, ya don\'t think this stuff is toxic, right?', 0);
INSERT INTO `quest_offer_reward` VALUES (48403, 1, 0, 0, 0, 0, 0, 0, 0, 'У меня нет слов, чтобы выразить благодарность за все, что ты $gсделал:сделала; для нас, и как я благодарна судьбе за то, что она ниспослала нам столь $gценного союзника:ценную союзницу;.$b$bСпасибо тебе. Спасибо от всех нас и от меня лично.$b$bЯ в огромном долгу перед тобой.', 26654);
INSERT INTO `quest_offer_reward` VALUES (48433, 1, 0, 0, 0, 0, 0, 0, 0, 'Кажется, у нас появились новые союзники. Спасибо тебе.$b$bТаурены Крутогорья славятся воинской доблестью. Я с радостью посмотрю, какую взбучку они зададут Альянсу.', 26654);
INSERT INTO `quest_offer_reward` VALUES (48434, 1, 0, 0, 0, 0, 0, 0, 0, 'Я снова стал самим собой, $n.\n\nВы с Серым Небом освободили меня от удушающей хватки Древних богов. Если бы я провел в таком состоянии еще какое-то время, то уже не смог бы вернуться.', 26654);
INSERT INTO `quest_offer_reward` VALUES (48440, 603, 0, 0, 0, 0, 0, 0, 0, 'Мы должны действовать быстро!', 25549);
INSERT INTO `quest_offer_reward` VALUES (48441, 0, 0, 0, 0, 0, 0, 0, 0, 'Ты вернул наши припасы. Как и было обещано, мы окажем твоим бойцам любую помощь на Аргусе.', 25549);
INSERT INTO `quest_offer_reward` VALUES (48442, 0, 0, 0, 0, 0, 0, 0, 0, '', 25961);
INSERT INTO `quest_offer_reward` VALUES (48443, 0, 0, 0, 0, 0, 0, 0, 0, '', 25961);
INSERT INTO `quest_offer_reward` VALUES (48445, 0, 0, 0, 0, 0, 0, 0, 0, '', 25961);
INSERT INTO `quest_offer_reward` VALUES (48446, 0, 0, 0, 0, 0, 0, 0, 0, '', 25996);
INSERT INTO `quest_offer_reward` VALUES (48447, 0, 0, 0, 0, 0, 0, 0, 0, '', 25996);
INSERT INTO `quest_offer_reward` VALUES (48448, 0, 0, 0, 0, 0, 0, 0, 0, '', 25996);
INSERT INTO `quest_offer_reward` VALUES (48453, 0, 0, 0, 0, 0, 0, 0, 0, 'Твоя помощь для нас просто неоценима. Прими это в знак благодарности.', 25549);
INSERT INTO `quest_offer_reward` VALUES (48455, 0, 0, 0, 0, 0, 0, 0, 0, 'Мы поддерживаем в лесу равновесие и тем самым продлеваем дни его благоденствия.', 25549);
INSERT INTO `quest_offer_reward` VALUES (48460, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'m afraid this forest can no longer be saved.', 25549);
INSERT INTO `quest_offer_reward` VALUES (48461, 1, 0, 0, 0, 0, 0, 0, 0, 'Столько миров Легиона только и ждут, чтобы их захватили...', 25549);
INSERT INTO `quest_offer_reward` VALUES (48483, 0, 0, 0, 0, 0, 0, 0, 0, 'Незнакомец в капюшоне, говоришь? И он не пытался напасть на тебя?$B$BЛюбопытно.', 25549);
INSERT INTO `quest_offer_reward` VALUES (48500, 0, 0, 0, 0, 0, 0, 0, 0, '$gReady:Ready; ready to go?', 25549);
INSERT INTO `quest_offer_reward` VALUES (48506, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'m glad you were among those called for the expedition to Argus. There will be plenty of fighting ahead.', 0);
INSERT INTO `quest_offer_reward` VALUES (48507, 0, 0, 0, 0, 0, 0, 0, 0, 'I knew you would be among those called to fight. It will be an honor to fight by your side.', 0);
INSERT INTO `quest_offer_reward` VALUES (48513, 1, 0, 0, 0, 0, 0, 0, 0, 'Наша победа над Легионом уже близка.', 25549);
INSERT INTO `quest_offer_reward` VALUES (48542, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve been a great asset to my wranglers here. Please take this as a token of our gratitude.', 0);
INSERT INTO `quest_offer_reward` VALUES (48544, 0, 0, 0, 0, 0, 0, 0, 0, 'We can\'t survive here at all without shepherds. So your help won\'t be without a reward.', 25549);
INSERT INTO `quest_offer_reward` VALUES (48559, 1, 0, 0, 0, 0, 0, 0, 0, 'Это первый и очень важный шаг, $n.$b$bЕсли уравновесить энергии в тигле света Пустоты, мы сможем еще больше усилить твое оружие.', 25549);
INSERT INTO `quest_offer_reward` VALUES (48560, 1, 1, 0, 0, 0, 0, 0, 0, 'О да! Равновесие – ключ ко всему, как не раз говорил Пророк.', 25549);
INSERT INTO `quest_offer_reward` VALUES (48600, 0, 0, 0, 0, 0, 0, 0, 0, '', 26124);
INSERT INTO `quest_offer_reward` VALUES (48601, 0, 0, 0, 0, 0, 0, 0, 0, '', 26124);
INSERT INTO `quest_offer_reward` VALUES (48605, 1, 0, 0, 0, 0, 0, 0, 0, 'Мы сразили их командира!', 25549);
