INSERT INTO `quest_offer_reward` VALUES (48654, 0, 0, 0, 0, 0, 0, 0, 0, '', 25961);
INSERT INTO `quest_offer_reward` VALUES (48668, 0, 0, 0, 0, 0, 0, 0, 0, 'This beacon will be added to the Navigation Console.$B$BA contingent of the Army of the Light will remain to hold this location for future use.', 0);
INSERT INTO `quest_offer_reward` VALUES (48799, 396, 0, 0, 0, 0, 0, 0, 0, 'Хорошее начало, $gгерой:воительница;. Наверняка там его много.', 25549);
INSERT INTO `quest_offer_reward` VALUES (48803, 0, 0, 0, 0, 0, 0, 0, 0, 'Получилось! Руки-то – они помнят!\r\n\r\nДержи, $n. Как новенький.', 0);
INSERT INTO `quest_offer_reward` VALUES (48864, 0, 0, 0, 0, 0, 0, 0, 0, 'Рад, что у Фарии все хорошо.\r\n\r\nСовершенно верно... Это фрагмент ключа от арсенала, который я выковал для нее в ту давнюю пору.', 0);
INSERT INTO `quest_offer_reward` VALUES (48910, 0, 0, 0, 0, 0, 0, 0, 0, '', 25549);
INSERT INTO `quest_offer_reward` VALUES (48911, 0, 0, 0, 0, 0, 0, 0, 0, '', 25961);
INSERT INTO `quest_offer_reward` VALUES (48912, 0, 0, 0, 0, 0, 0, 0, 0, '', 25996);
INSERT INTO `quest_offer_reward` VALUES (48929, 0, 0, 0, 0, 0, 0, 0, 0, '<Turalyon listens in grim silence.>', 0);
INSERT INTO `quest_offer_reward` VALUES (48954, 0, 0, 0, 0, 0, 0, 0, 0, 'Champion, you have returned... and not a moment too soon.$B$BThe object you hold in your hands is but one of many mysteries unfolding before us.', 0);
INSERT INTO `quest_offer_reward` VALUES (48962, 1, 0, 0, 0, 0, 0, 0, 0, 'Former elves of blood want to help the Alliance. It exceeds my wildest expectations.', 26822);
INSERT INTO `quest_offer_reward` VALUES (48965, 0, 0, 0, 0, 0, 0, 0, 0, '<Caleb\'s body is lifeless, still warm. His wounds claimed him while you were gone.$B$BHis face seems content, as though he knew you would finish the job.>', 0);
INSERT INTO `quest_offer_reward` VALUES (48973, 0, 0, 0, 0, 0, 0, 0, 0, 'Greetings, $n.$B$BLong have we awaited this day. Argus was all but lost, its true denizens forced to remain hidden lest we perish. Your efforts on our behalf have allowed us to once again live free from fear. Please accept these trophies as a symbol of our gratitude.', 0);
INSERT INTO `quest_offer_reward` VALUES (48974, 0, 0, 0, 0, 0, 0, 0, 0, 'Krona ki cristorr, champion!$B$BYour presence among the Army of the Light has been a singular boon to our cause. Gratitude is not all that we may offer. Please accept these trophies as a tribute to your unwavering support.$B$BTogether we will bring about the Legion\'s end.', 0);
INSERT INTO `quest_offer_reward` VALUES (48976, 0, 0, 0, 0, 0, 0, 0, 0, 'Highlord, thanks to your efforts, our forces have been able to secure additional resources from the battlefield.$B$BThe more you continue to dominate the battlefield the more resources our forces can spare.', 0);
INSERT INTO `quest_offer_reward` VALUES (48977, 0, 0, 0, 0, 0, 0, 0, 0, 'Archenon poros, champion.$B$BThe Army of the Light wishes to thank you for your aid against the Legion\'s forces on Argus.$B$BPlease accept these supplies with our gratitude. Your continued support brings us ever closer to our ultimate goal of eradicating the Legion once and for all.', 0);
INSERT INTO `quest_offer_reward` VALUES (49014, 1, 0, 0, 0, 0, 0, 0, 0, 'После стольких лет... стольких жертв... наконец-то мы в шаге от победы.', 25549);
INSERT INTO `quest_offer_reward` VALUES (49015, 1, 0, 0, 0, 0, 0, 0, 0, '', 25961);
