INSERT INTO `quest_offer_reward` VALUES (49135, 0, 0, 0, 0, 0, 0, 0, 0, 'With these sigils, the Vindicaar can bypass the Burning Throne\'s defenses.$B$BThe time has come to press our advantage and end this war.', 0);
INSERT INTO `quest_offer_reward` VALUES (49143, 1, 0, 0, 0, 0, 0, 0, 0, 'О да, я чувствую силу Света в этих крупицах. Не теряй надежду, $n!', 25549);
INSERT INTO `quest_offer_reward` VALUES (49224, 1, 0, 0, 0, 0, 0, 0, 0, 'По мере того как твое оружие набирает силу, мы сможем наделить его еще большей мощью, но пока и этого более чем достаточно.', 25549);
INSERT INTO `quest_offer_reward` VALUES (49266, 1, 0, 0, 0, 0, 0, 0, 0, 'Т\'паартос... полон рвения. Он жаждет творить добро, а ярости, с которой пылает огонь в его сердце, могут позавидовать многие даже среди озаренных. Спасибо, что $gуберег:уберегла:r; его, |3-6($r).', 26822);
INSERT INTO `quest_offer_reward` VALUES (49293, 0, 0, 0, 0, 0, 0, 0, 0, 'Наша победа над Легионом уже близка.', 25549);
INSERT INTO `quest_offer_reward` VALUES (49354, 1, 0, 0, 0, 0, 0, 0, 0, 'Это было несколько неожиданно.', 26654);
INSERT INTO `quest_offer_reward` VALUES (49445, 1, 1, 0, 0, 0, 0, 0, 0, 'Ты ведь знаешь, что изначальный саргерит – невероятно ценный материал, который придает огромную силу предметам. В войне с Легионом нам понадобится любое преимущество.$b$bПринеси столько изначального саргерита, сколько сможешь, и я буду рад его у тебя купить.', 25549);
INSERT INTO `quest_offer_reward` VALUES (49472, 396, 396, 0, 0, 0, 0, 0, 0, '', 25549);
INSERT INTO `quest_offer_reward` VALUES (49473, 0, 0, 0, 0, 0, 0, 0, 0, 'Take this. May it aid your efforts to stop the Legion... and to save our world.', 0);
INSERT INTO `quest_offer_reward` VALUES (49474, 396, 396, 0, 0, 0, 0, 0, 0, '', 25549);
INSERT INTO `quest_offer_reward` VALUES (49532, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome to the battlefield, grunt!', 0);
INSERT INTO `quest_offer_reward` VALUES (49533, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, $n, you have arrived!$B$BLet\'s get to work.', 0);
INSERT INTO `quest_offer_reward` VALUES (49534, 0, 0, 0, 0, 0, 0, 0, 0, 'Death and acts of sacrilege pervert this land.$B$B<Lantinga lets out a drawn-out sigh.>$B$BThankfully, we\'re here to stop it.', 0);
INSERT INTO `quest_offer_reward` VALUES (49535, 0, 0, 0, 0, 0, 0, 0, 0, 'I am glad you\'re here, $R. Hemet has not been heard from for quite some time, and I could really use your help finding him.', 0);
INSERT INTO `quest_offer_reward` VALUES (49536, 0, 0, 0, 0, 0, 0, 0, 0, 'Yo! Do I have an explosive proposition for you, pal....', 0);
INSERT INTO `quest_offer_reward` VALUES (49537, 0, 0, 0, 0, 0, 0, 0, 0, 'The Warsong Offensive is ready to strike at the heart of the Scourge, $C. Are you up to the challenge?', 0);
INSERT INTO `quest_offer_reward` VALUES (49538, 0, 0, 0, 0, 0, 0, 0, 0, 'Lok\'tar ogar, $n. You\'ve arrived not a moment too soon....', 0);
INSERT INTO `quest_offer_reward` VALUES (49539, 0, 0, 0, 0, 0, 0, 0, 0, 'You seem like someone who needs some excitement in their life. Excitement... and beer!', 0);
INSERT INTO `quest_offer_reward` VALUES (49540, 0, 0, 0, 0, 0, 0, 0, 0, 'What the dook is going on here, $R?!$B$BWhy are pandaren so sad?!', 0);
INSERT INTO `quest_offer_reward` VALUES (49541, 0, 0, 0, 0, 0, 0, 0, 0, 'Traveling to Kun-Lai? Right this way, wanderer, right this way!', 0);
INSERT INTO `quest_offer_reward` VALUES (49542, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome to Townlong Steppes, outsider.$B$BIf you insist on wandering in to the darkest places of Pandaria then far be it from me to not put you to good use.', 0);
INSERT INTO `quest_offer_reward` VALUES (49543, 0, 0, 0, 0, 0, 0, 0, 0, 'A volunteer for Dread Wastes duty? I think you might be the first.$B$BPleased to meet you.', 0);
INSERT INTO `quest_offer_reward` VALUES (49545, 0, 0, 0, 0, 0, 0, 0, 0, 'I am glad to see you are well after Tanaan Jungle.$B$BWe have much to do.', 0);
INSERT INTO `quest_offer_reward` VALUES (49546, 0, 0, 0, 0, 0, 0, 0, 0, 'Gorgrond is no place for armies. We are now stepping into the domain of legends.', 0);
INSERT INTO `quest_offer_reward` VALUES (49547, 0, 0, 0, 0, 0, 0, 0, 0, 'It is good to see you, Commander $n. I require your advisement.', 0);
INSERT INTO `quest_offer_reward` VALUES (49548, 0, 0, 0, 0, 0, 0, 0, 0, '<Azik looks you up and down.>$B$BAzik is betting you are someone of importance to your people.$B$BCome closer, friend. Azik could use your help.', 0);
INSERT INTO `quest_offer_reward` VALUES (49549, 0, 0, 0, 0, 0, 0, 0, 0, 'They came at us... from... nowhere.$B$B<The stone guard nearly passes out.>', 0);
INSERT INTO `quest_offer_reward` VALUES (49550, 0, 0, 0, 0, 0, 0, 0, 0, 'We\'re gettin\' hammered out here, lad.$B$BI hope you\'re here to help.', 0);
INSERT INTO `quest_offer_reward` VALUES (49551, 0, 0, 0, 0, 0, 0, 0, 0, 'What is it?!$B$BCan\'t you see I\'m already busy trying to get this port ready, $R?', 0);
INSERT INTO `quest_offer_reward` VALUES (49552, 0, 0, 0, 0, 0, 0, 0, 0, 'Death and acts of sacrilege pervert this land.$B$B<Lantinga lets out a drawn-out sigh.>$B$BThankfully, we\'re here to stop it.', 0);
INSERT INTO `quest_offer_reward` VALUES (49553, 0, 0, 0, 0, 0, 0, 0, 0, 'I am glad you\'re here, $R. Hemet has not been heard from for quite some time, and I could really use your help finding him.', 0);
INSERT INTO `quest_offer_reward` VALUES (49554, 0, 0, 0, 0, 0, 0, 0, 0, 'Yo! Do I have an explosive proposition for you, pal....', 0);
INSERT INTO `quest_offer_reward` VALUES (49555, 0, 0, 0, 0, 0, 0, 0, 0, 'The Valiance Expedition is ready to strike at the heart of the Scourge, $C. Are you up to the challenge?', 0);
INSERT INTO `quest_offer_reward` VALUES (49556, 0, 0, 0, 0, 0, 0, 0, 0, 'Our role is of utmost importance, $n.$B$BAdmiral Taylor and his flagship have gone missing.... More importantly, the young Prince was with him, and it is our primary objective to bring him back home safely.', 0);
INSERT INTO `quest_offer_reward` VALUES (49557, 0, 0, 0, 0, 0, 0, 0, 0, 'You seem like someone who needs some excitement in their life. Excitement... and beer!', 0);
INSERT INTO `quest_offer_reward` VALUES (49558, 0, 0, 0, 0, 0, 0, 0, 0, 'What the dook is going on here, $R?!$B$BWhy are pandaren so sad?!', 0);
INSERT INTO `quest_offer_reward` VALUES (49559, 0, 0, 0, 0, 0, 0, 0, 0, 'Traveling to Kun-Lai? Right this way, wanderer, right this way!', 0);
INSERT INTO `quest_offer_reward` VALUES (49560, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome to Townlong Steppes, outsider.$B$BIf you insist on wandering in to the darkest places of Pandaria then far be it from me to not put you to good use.', 0);
INSERT INTO `quest_offer_reward` VALUES (49561, 0, 0, 0, 0, 0, 0, 0, 0, 'A volunteer for Dread Wastes duty? I think you might be the first.$B$BPleased to meet you.', 0);
INSERT INTO `quest_offer_reward` VALUES (49564, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome to Gorgrond, commander.$B$BWe have just arrived ourselves.', 0);
INSERT INTO `quest_offer_reward` VALUES (49565, 0, 0, 0, 0, 0, 0, 0, 0, 'It is good to see you, Commander $n. I require your advisement.', 0);
INSERT INTO `quest_offer_reward` VALUES (49566, 0, 0, 0, 0, 0, 0, 0, 0, '<Azik looks you up and down.>$B$BAzik is betting you are someone of importance to your people.$B$BCome closer, friend. Azik could use your help.', 0);
INSERT INTO `quest_offer_reward` VALUES (49567, 0, 0, 0, 0, 0, 0, 0, 0, 'They came at us... from... nowhere.$B$B<The lieutenant nearly passes out.>', 0);
INSERT INTO `quest_offer_reward` VALUES (49613, 0, 0, 0, 0, 0, 0, 0, 0, 'Отлично, все в сборе. Скоро должны прибыть и наши гости.', 26654);
INSERT INTO `quest_offer_reward` VALUES (49614, 0, 0, 0, 0, 0, 0, 0, 0, 'I see you\'re back; with good news, $n.', 26654);
INSERT INTO `quest_offer_reward` VALUES (49698, 1, 0, 0, 0, 0, 0, 0, 0, 'Приветствую, $n. Скоро ты станешь $gсвидетелем:свидетельницей; ритуала, ранее не виданного никем, кроме солдат Армии Света.', 26822);
INSERT INTO `quest_offer_reward` VALUES (49756, 1, 6, 0, 0, 0, 0, 0, 0, 'Это была славная битва, $n. Об остальном мы позаботимся.', 26654);
INSERT INTO `quest_offer_reward` VALUES (49772, 0, 0, 0, 0, 0, 0, 0, 0, 'What have we here? One of the famed Lightforged draenei! Welcome, welcome.$B$BThank you for your service against the Legion, $n. Truly an inspiration!', 0);
INSERT INTO `quest_offer_reward` VALUES (49773, 0, 0, 0, 0, 0, 0, 0, 0, 'Are you the new one? I\'m supposed to welcome you. Here, take this letter.$B$B<Blackguard lets out an exasperated sigh.>', 0);
INSERT INTO `quest_offer_reward` VALUES (49782, 0, 0, 0, 0, 0, 0, 0, 0, 'Duty. Honor. Sacrifice. The Lightforged strive to embody these qualities. You have done so with aplomb.$B$BIt is my pleasure to award you with a symbol of your service, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (49783, 0, 0, 0, 0, 0, 0, 0, 0, 'You have brought great honor to our tribes, $n. It is fitting that we give you a symbol of our thanks.$B$BHighmountain stands... because you have the courage to fight for it.', 0);
INSERT INTO `quest_offer_reward` VALUES (49784, 0, 0, 0, 0, 0, 0, 0, 0, 'When we were liberated from the Legion, some among the shal\'dorei believed we should remain isolated. Alone.$B$BYour accomplishments in Azeroth have shown that we made the right decision in reclaiming our place in this world. It pleases me to honor you with this symbol of Suramar\'s gratitude.', 0);
INSERT INTO `quest_offer_reward` VALUES (49787, 1, 0, 0, 0, 0, 0, 0, 0, 'Умбрий чересчур беспечен, но, похоже, знает свое дело. Мы должны найти его, прежде чем он зайдет слишком далеко.', 26822);
INSERT INTO `quest_offer_reward` VALUES (49788, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, there you are, $n! We\'ve been inspecting--err--expecting you!', 0);
INSERT INTO `quest_offer_reward` VALUES (49812, 0, 0, 0, 0, 0, 0, 0, 0, 'Goddess be praised that you are here, $n. I\'m hoping that you can aid me.', 0);
INSERT INTO `quest_offer_reward` VALUES (49851, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, $n. Thank you for responding to my request.', 0);
INSERT INTO `quest_offer_reward` VALUES (49852, 0, 0, 0, 0, 0, 0, 0, 0, 'Lok\'tar, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (49855, 0, 0, 0, 0, 0, 0, 0, 0, 'We knew the Horde would send help and not a moment too soon!', 0);
INSERT INTO `quest_offer_reward` VALUES (49865, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, $n. Thank you for responding to my request.', 0);
INSERT INTO `quest_offer_reward` VALUES (49866, 0, 0, 0, 0, 0, 0, 0, 0, 'Greetings, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (49928, 0, 0, 0, 0, 0, 0, 0, 0, 'Though some continue to doubt us, your deeds have proven that the ren\'dorei serve the Alliance with honor.$B$BYour will is strong, $n. Accept this gift as a symbol of the powers you command.', 0);
INSERT INTO `quest_offer_reward` VALUES (49929, 1, 1, 0, 0, 0, 0, 0, 0, 'Приветствую, $n. Отрадно видеть, что ты $gоткликнулся:откликнулась; на зов.', 26755);
INSERT INTO `quest_offer_reward` VALUES (49930, 1, 1, 0, 0, 0, 0, 0, 0, 'Ah, $n, there you are. Now we\'re good to go.', 26654);
INSERT INTO `quest_offer_reward` VALUES (49933, 0, 0, 0, 0, 0, 0, 0, 0, 'Are you the new one? I\'m supposed to welcome you. Here, take this letter.$B$B<Blackguard lets out an exasperated sigh.>', 0);
INSERT INTO `quest_offer_reward` VALUES (49973, 0, 0, 0, 0, 0, 0, 0, 0, 'Сурамар – очень красивый город. Надеюсь, Луносвет их не разочарует.', 26654);
INSERT INTO `quest_offer_reward` VALUES (49976, 0, 0, 0, 0, 0, 0, 0, 0, 'If this strange material really has the strength we think it has, we cannot let it fall into the wrong hands. You should go to Seilitus.', 25961);
INSERT INTO `quest_offer_reward` VALUES (49977, 1, 0, 0, 0, 0, 0, 0, 0, '', 25961);
INSERT INTO `quest_offer_reward` VALUES (49981, 0, 0, 0, 0, 0, 0, 0, 0, 'Здравствуй, $n. Хорошо, что в Силитус прислали $gтакого опытного воителя:такую опытную воительницу;, как ты.', 25961);
INSERT INTO `quest_offer_reward` VALUES (49982, 0, 0, 0, 0, 0, 0, 0, 0, '', 25961);
INSERT INTO `quest_offer_reward` VALUES (50046, 0, 0, 0, 0, 0, 0, 0, 0, 'Отлично, $n! Теперь, когда крошшеры выведены из строя, картелю Трюмных Вод придется сделать перерыв!', 25961);
INSERT INTO `quest_offer_reward` VALUES (50047, 0, 0, 0, 0, 0, 0, 0, 0, 'Никогда не видел ничего подобного... Я тут же приступлю к изучению!', 25961);
INSERT INTO `quest_offer_reward` VALUES (50048, 0, 0, 0, 0, 0, 0, 0, 0, 'You made it back alive. That\'s good news, indeed! I trust the expedition went well?', 0);
INSERT INTO `quest_offer_reward` VALUES (50049, 0, 0, 0, 0, 0, 0, 0, 0, 'Спасибо, что $gдонес:донесла; до меня письмо Кадгара. Мне понадобится время, чтобы обдумать его слова. Если он прав, то, может быть, у нас есть шанс спасти Азерот... но за это придется дорого заплатить.', 25961);
INSERT INTO `quest_offer_reward` VALUES (50052, 0, 0, 0, 0, 0, 0, 0, 0, '', 25961);
INSERT INTO `quest_offer_reward` VALUES (50053, 0, 0, 0, 0, 0, 0, 0, 0, '', 25961);
INSERT INTO `quest_offer_reward` VALUES (50054, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'m pleased to see that you made it back alive. I\'m sure you fought with honor!', 0);
INSERT INTO `quest_offer_reward` VALUES (50055, 0, 0, 0, 0, 0, 0, 0, 0, '', 25961);
INSERT INTO `quest_offer_reward` VALUES (50056, 1, 1, 0, 0, 0, 0, 0, 0, 'Я знал, что на тебя можно положиться, $p. Ты всегда приходишь на помощь, когда Азерот в беде.$b$bНу, за дело.', 25961);
INSERT INTO `quest_offer_reward` VALUES (50057, 603, 1, 0, 0, 0, 0, 0, 0, 'Меч темнеет! Я чувствую, как страдания Азерота ослабевают... хотя и ненамного.$b$bТы хорошо $gпостарался. Выиграл:постаралась. Выиграла; немного времени.$b$bНо мы должны найти способ полностью исцелить эту рану. Неважно, что происходит между Ордой и Альянсом, главное – наш долг перед Азеротом!', 25961);
INSERT INTO `quest_offer_reward` VALUES (50071, 1, 0, 0, 0, 0, 0, 0, 0, 'Об озаренных в Штормграде говорят на каждом углу. Элитные солдаты, посвятившие всю свою жизнь борьбе с Легионом... они станут одними из наших сильнейших союзников.', 26822);
INSERT INTO `quest_offer_reward` VALUES (50130, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you for helping the people of Sporeggar, $R. You are always welcomed here.', 0);
INSERT INTO `quest_offer_reward` VALUES (50131, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $n! Thank you.', 0);
INSERT INTO `quest_offer_reward` VALUES (50226, 0, 0, 0, 0, 0, 0, 0, 0, 'О! Ты только посмотри на эти большие, прекрасные мозги! Мне уже не терпится в них покопаться... Спасибо за помощь, $n! Можешь остаться и посмотреть, если не брезгуешь!', 25961);
INSERT INTO `quest_offer_reward` VALUES (50227, 0, 0, 0, 0, 0, 0, 0, 0, 'I see you\'re larval specimens! That\'s great. Do you want to stay and watch me dissect them thoroughly and catalogue all the details of their anatomy?', 25961);
INSERT INTO `quest_offer_reward` VALUES (50228, 0, 0, 0, 0, 0, 0, 0, 0, 'I see you brought it; Michael Skitaltz\'s head is the Abyss. The task is unpleasant, but necessary. The Alliance thanks you for your service, $n.', 25961);
INSERT INTO `quest_offer_reward` VALUES (50229, 0, 0, 0, 0, 0, 0, 0, 0, '', 25961);
INSERT INTO `quest_offer_reward` VALUES (50230, 0, 0, 0, 0, 0, 0, 0, 0, '', 25961);
INSERT INTO `quest_offer_reward` VALUES (50231, 0, 0, 0, 0, 0, 0, 0, 0, '', 25961);
INSERT INTO `quest_offer_reward` VALUES (50232, 0, 0, 0, 0, 0, 0, 0, 0, '', 25961);
INSERT INTO `quest_offer_reward` VALUES (50239, 1, 0, 0, 0, 0, 0, 0, 0, 'Я ценю твою мудрость, $n. Выбор, сделанный тобой сегодня, укрепит силу Альянса.', 26755);
INSERT INTO `quest_offer_reward` VALUES (50242, 1, 0, 0, 0, 0, 0, 0, 0, 'Я ценю твою мудрость, $n. Выбор, сделанный тобой сегодня, сделает Орду сильнее.', 26654);
INSERT INTO `quest_offer_reward` VALUES (50247, 0, 0, 0, 0, 0, 0, 0, 0, '', 26124);
INSERT INTO `quest_offer_reward` VALUES (50248, 1, 0, 0, 0, 0, 0, 0, 0, 'Приступай, $n. Штомград благодарит тебя за службу.', 26822);
INSERT INTO `quest_offer_reward` VALUES (50303, 0, 0, 0, 0, 0, 0, 0, 0, 'So this is Orgrimmar. The orcs have made some... interesting aesthetic choices. Be careful of the spikes, $n.$B$BAnd the smells. So many smells.', 0);
INSERT INTO `quest_offer_reward` VALUES (50305, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome to Stormwind. Though it is no Silvermoon, the city does have its charms.$B$BIf you came here expecting a warm welcome, I cannot say I have received one myself. But most folk are polite enough.$B$BNo matter. I am off to seek adventure! And perhaps a stiff drink.', 0);
INSERT INTO `quest_offer_reward` VALUES (50313, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome to this beautiful city, $n. I am sure you will feel as welcome by the other members of the Alliance as I do.$B$BI have met a number of the city guards. Good people, though there are a few things they could learn from us.', 0);
INSERT INTO `quest_offer_reward` VALUES (50318, 0, 0, 0, 0, 0, 0, 0, 0, 'We\'re behind schedule - let\'s get moving.', 0);
INSERT INTO `quest_offer_reward` VALUES (50319, 0, 0, 0, 0, 0, 0, 0, 0, 'So many tauren! I think I\'ve seen more tauren since I got here than in my entire life in Highmountain.$B$BThey all ask to touch my antlers. It\'s weird at first, but now I kind of like it.', 0);
INSERT INTO `quest_offer_reward` VALUES (50335, 0, 0, 0, 0, 0, 0, 0, 0, 'The uprising quelled, the elements calmed, and Hogger slain... I cannot thank you enough, $n. My people have been avenged.$B$BThank you.', 0);
INSERT INTO `quest_offer_reward` VALUES (50336, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'d love for you to come aboard as a full G-Team Commando, but our bylaws specifically prohibit Horde team members; however, I CAN enroll you in the G-Team Fan Club!$B$BHere\'s something for your trouble.', 0);
INSERT INTO `quest_offer_reward` VALUES (50337, 0, 0, 0, 0, 0, 0, 0, 0, 'Took you long enough. Get behind the pillar before you blow our cover!', 0);
INSERT INTO `quest_offer_reward` VALUES (50338, 0, 0, 0, 0, 0, 0, 0, 0, 'The mission is far from over!', 0);
INSERT INTO `quest_offer_reward` VALUES (50341, 0, 0, 0, 0, 0, 0, 0, 0, '', 25961);
INSERT INTO `quest_offer_reward` VALUES (50358, 0, 0, 0, 0, 0, 0, 0, 0, '', 25961);
INSERT INTO `quest_offer_reward` VALUES (50360, 0, 0, 0, 0, 0, 0, 0, 0, '', 25961);
INSERT INTO `quest_offer_reward` VALUES (50364, 1, 0, 0, 0, 0, 0, 0, 0, '', 25961);
INSERT INTO `quest_offer_reward` VALUES (50371, 0, 0, 0, 0, 0, 0, 0, 0, 'Наконец-то ты $gприбыл:прибыла;, $n. От того, что я тебе расскажу, может зависеть будущее Альянса...', 25961);
INSERT INTO `quest_offer_reward` VALUES (50372, 0, 0, 0, 0, 0, 0, 0, 0, 'Ты здесь, чтобы помочь мне с образцами? Давай же начнем!', 25961);
INSERT INTO `quest_offer_reward` VALUES (50373, 0, 0, 0, 0, 0, 0, 0, 0, 'Рад тебя видеть, $n. Жаль, мы не встретились при более благоприятных обстоятельствах...', 25961);
INSERT INTO `quest_offer_reward` VALUES (50374, 1, 1, 0, 0, 0, 0, 0, 0, 'Кровь Азерот? Я позабочусь о том, чтобы донести эту весть до Штормграда, но операцию нужно продолжать до получения других приказов.', 25961);
INSERT INTO `quest_offer_reward` VALUES (50432, 0, 0, 0, 0, 0, 0, 0, 0, 'Потрясающе! Невероятно! Наконец-то я все понимаю и со мной говорят сами титаны! Они научили меня с помощью пробуждающей сущности создавать лучшие доспехи в мире... Хотя, думаю, результаты могут быть слегка непредсказуемыми.\r\n\r\n<Вридиэль сияет от гордости.>\r\n\r\nНу, чего ты ждешь? Принеси мне столько пробуждающей сущности, сколько сможешь найти!', 0);
INSERT INTO `quest_offer_reward` VALUES (316930, 0, 0, 0, 0, 0, 0, 0, 0, 'Ouch, you really are a good pet trainer! I guess you\'re the exception.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5, 'enUS', 'You need some Crab Cakes, do you? Well I might be able to cook some up for you...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (6, 'enUS', 'Hah - you caught him! You\'ve done Elwynn a great service, and earned a nice bounty!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (7, 'enUS', 'Well done, citizen. Those kobolds are thieves and cowards, but in large numbers they pose a threat to us. And the humans of Stormwind do not need another threat.$B$BFor defeating them, you have my gratitude.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (8, 'enUS', 'Oh? A letter? Wonderful news! I\'ve been waiting on word from my dear old mother in Deathknell. Such a saint, she is. I wonder how she\'s holding up.$B$BWhat? Too cheery? Not buying it, are you?$B$BWell, get lost then... it\'s really no business who the letter\'s from anyway. Go on! Get lost!$B$BCome back when you\'ve got some coin to spend... loafer.$B$BNext thing you know, you\'ll be coming back to rest after a long days adventure or some stupid thing like that.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (9, 'enUS', 'Good work, friend. You have earned your pay well. Who knows, perhaps Westfall will prosper once again.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (10, 'enUS', 'He was a good goblin, young Scrimshank. Let\'s hope that his demise was not in vain by what he was able to record with his surveying equipment.$B$BIt seems as though we have quite the situation on our hands here with these bugs. They\'re way too cunning for their own good, and the research we\'ve done so far is shaping up to indicate that they might even be under the controlling influence of a third party! If this is the case, then it bodes ill for not only Gadgetzan, but the whole desert!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (11, 'enUS', 'I see you\'ve been busy! You have our thanks, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (12, 'enUS', 'Well done, $n. My scout witnessed your valiant acts. You are proving yourself quite well so far.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (13, 'enUS', 'Your worth to The People\'s Militia has been validated by your brave acts thus far.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (14, 'enUS', 'When I left the tainted lands of Lordaeron I returned to a grim state of affairs here in my homeland. But there is hope for Westfall yet. As proven by your valor in battle, it is obvious to me that you serve our cause with honor. It is with great pride that I induct thee into The People\'s Militia. May the Light shine upon you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (15, 'enUS', 'I don\'t like hearing of all these kobolds in our mine. No good can come of this. Here, take this as payment, and when you\'re ready, speak to me again. I would like you to go back to the mines one more time...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (16, 'enUS', 'Thanks, $n! And come back if you want to trade again.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (17, 'enUS', 'Well done $n, you\'ve proven your worth once again! The caps will be an interesting study, yon verily!$B$BAnd now, as promised, enjoy a taste of one of my more potent restorative brews from my personal stock...$B$BIf you are a skilled alchemist, then perhaps we might have some additional business to conduct. Regardless, I thank you for your assistance!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (18, 'enUS', 'Back with some bandanas, I see. The Stormwind Army appreciates your help.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (19, 'enUS', 'Tharil\'zun was a vicious, crafty orc. Well done--I\'m sure he didn\'t go down easy. Here is your reward, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (20, 'enUS', 'That makes for a lot fewer orcs to worry about, thanks.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (21, 'enUS', 'Once again, you have earned my respect, and the gratitude of the Stormwind Army. There may yet be kobolds in the mine, but I will marshal others against them. We have further tasks for you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (22, 'enUS', 'These are just perfect, $n! Thank you so much. Farmer Saldean and I are going to feast tonight. And here is a little something for you, for the hard work. You didn\'t think I was going to let a $C like you go hungry, did you?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (23, 'enUS', 'The paw of Ursangous!$B$BHave you any idea what trouble this beast has caused us over the years? Those days are over now, thanks to you.$B$BI shall forward this trophy on to Orgrimmar to be put on display, along with your account of its death.$B$BCongratulations!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (24, 'enUS', 'Shadumbra\'s Head?$B$BNone can say how many of our Horde brethren have fallen to this beast over the years. Your prowess has ensured that no more shall suffer this fate.$B$BWarchief Hellscream shall hear of your deeds, $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (25, 'enUS', 'Well done, $C.$B$BA fitting end to a textbook assault.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (26, 'enUS', 'Salutations, young $C; the time has come for you to know our aquatic form. It is one that lets you move freely and indefinitely through water. You will find this aspect a useful tool and a blessing... but it is one that must be earned before it is mastered.$B$BYou will face two trials to overcome. The first will test your ability to work in water under pressure. The second will test your resolve to understand the aspects of the form itself.$B$BStand ready, as the time of testing is now upon us.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (27, 'enUS', 'Salutations, young $C; the time has come for you to know our aquatic form. It is one that lets you move freely and indefinitely through water. You will find this aspect a useful tool and a blessing... but it is one that must be earned before it is mastered.$B$BYou will face two trials to overcome. The first will test your ability to work in water under pressure. The second will test your resolve to understand the aspects of the form itself.$B$BStand ready, as the time of testing is now upon us.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (28, 'enUS', 'Well now, it looks like yet another $C has successfully completed the Trial of the Lake! Well done, my young friend - well done.$B$BAs I\'ve said, my name is Tajarri. It is my duty and my privilege to serve as a warden for Moonglade, and specifically this shrine. Keeper Remulos guides the Cenarion Circle in preservation of nature and balance, and his shrine serves as a lasting tribute to such. By using the bauble here, it serves as a nod to the importance of this place within the Circle.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (29, 'enUS', 'Well now, it looks like yet another $C has successfully completed the Trial of the Lake! Well done, my young friend - well done.$B$BAs I\'ve said, my name is Tajarri. It is my duty and my privilege to serve as a warden for Moonglade, and specifically this shrine. Keeper Remulos guides the Cenarion Circle in preservation of nature and balance, and his shrine serves as a lasting tribute to such. By using the bauble here, it serves as a nod to the importance of this place within the Circle.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (30, 'enUS', 'You\'ve completed the Trial of the Sea Lion, $n - congratulations.$B$BEach task in acquiring the pendant shows that both agility and endurance are necessary to act in harmony with what you desire to do underwater. Neither can exist without the other, and both cannot be done without your willingness to embrace the aspect of the sea lion.$B$BRemember these lessons well, and count on them once you have gained your aquatic form.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (32, 'enUS', 'This report... I\'ve dealt with these beasts before. Fizzledowser was wise to send this, and you, to me.$B$BThese creatures will consume the Tanaris desert rapidly; they are nothing short of one of the greatest menaces we all will ever face. Familiarize yourself with this name, $n: silithid. The insects mentioned here are the silithid, and they are the tools of one of the greatest threats that both the Horde and Alliance face.$B$BIf only we were smart enough to see that before.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (33, 'enUS', '\'Twas a grim task, friend, but you held up your part of the bargain.$B$BI have some things here you might want - take your pick!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (34, 'enUS', 'Finally the menace is laid to rest! Thank you, $n, you have done me a great service. The garden shall be in full bloom this season!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (35, 'enUS', 'Yes, murlocs have settled in and around the streams of eastern Elwynn. We don\'t know why they are here, but they are aggressive and at least semi-intelligent.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (36, 'enUS', 'That Verna was always such a sweet lass. We\'ll miss her here in Westfall but between you and me, she\'s a city girl at heart and Stormwind will suit her just fine. But enough gossip! Now we can make Westfall Stew!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (37, 'enUS', 'Although much has been stripped from the corpse, strewn nearby is a medallion with the words: \"Footman Malakai Stone\" etched upon it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (38, 'enUS', 'That Okra will thicken up this broth nicely! Now we just add the Stringy Vulture Meat, a few Murloc Eyes and those delicious Goretusk Snouts. And we\'re done! For all your help, $n, I want you to take today\'s first batch of Westfall Stew!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (39, 'enUS', 'Hmm, this news is troubling. Already our defenses are stretched thin, and losing Rolf and Malakai to those murlocs put us in an even worse position.$B$BIf things don\'t improve, there will be fighting in Goldshire by the week\'s end!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (40, 'enUS', 'Yes, I spoke with Remy. I respect him as a merchant, though all reports of Murlocs to the east have been sketchy at best.$B$BYour concerns are noted, but unless I receive a military report of a murloc threat, we can\'t afford to send more troops east.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (45, 'enUS', 'You find around the neck of the corpse a metal medallion inscribed with the words: \"Footman Rolf Hartford.\"', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (46, 'enUS', 'You have the fins? Great! Marshal Dughan is anxious about the Murloc situation in eastern Elwynn, and I\'d like to tell him that it\'s becoming under control.$B$BYour actions have helped realize that.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (47, 'enUS', 'Thanks for the dust, $n. Here\'s your cash, and...here is a token from associates of mine. You might find it useful...useful.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (48, 'enUS', 'Hiccup! Ho Ho!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (49, 'enUS', 'Hiccup! Ho ho!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (50, 'enUS', 'Hiccup! Ho ho!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (51, 'enUS', 'Hiccup! Ho ho!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (52, 'enUS', 'Thanks a lot for the help, $n. Something in the forest must be making these animals so bold.$B$BWhatever it is, I hope it stays there.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (53, 'enUS', 'Aged in the barrel, filtered through coal$BIt passes your lips and right through your soul.$BSo lets us drink now, a toast to you$BAnd me while we\'re at it, Grimbooze Thunderbrew!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (54, 'enUS', 'Well, it says here that you\'ve been awarded Acting Deputy Status with the Stormwind Marshals. Congratulations.$B$BAnd good luck - keeping Elwynn safe is no picnic... what with most the army busy doing who knows what for who knows which noble!$B$BIt\'s hard to keep track of politics in these dark days...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (55, 'enUS', 'Morbent Fel has been defeated. The dark riders are still at large, but a small piece of that which I had hoped beyond hope has come to pass.$B$BYou have done the impossible, and your deeds will forever live in the legends of Duskwood.$B$BGo, $n. And perhaps, one day, fate may bring you back to me...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (56, 'enUS', 'Splendid, $n. For your service to the people of Darkshire you shall be rewarded.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (57, 'enUS', 'The people of Darkshire thank you, $n. You have proven yourself to be a great ally of The Night Watch.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (58, 'enUS', 'On behalf of the people of Darkshire and as the Leader of the Night Watch, I thank you, $n, for your bravery and dedication. May the Light shine upon your ways.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (59, 'enUS', 'Ah, thank you for the marker. Please feel free to pick your choice of armor.$B$BLuck to you, brave $C. And may this armor serve you well.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (60, 'enUS', 'You were busy hunting kobolds, were you? Thanks for the candles, $n, and here\'s your reward...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (61, 'enUS', 'Here is your payment ... and while you\'re here, take a look around! I\'m sure we have a potion or other trinket you\'d find useful.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (62, 'enUS', 'This is bad news. What\'s next, dragons?!? We\'ll have to increase our patrols near that mine. Thanks for your efforts, $n. And hold a moment... I might have another task for you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (64, 'enUS', 'My watch! Thank you so much, kind sir! $B$BWe are but poor farmers and we have lost our land but please accept this reward as a token of our appreciation.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (65, 'enUS', 'So, Stoutmantle sent you? Well, I owe him.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (66, 'enUS', 'Ah so Madame Eva sent you. . . .', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (67, 'enUS', 'The old footlocker creaks open. . . .', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (68, 'enUS', 'Interesting. So the chap did spend some time in Moonbrook after all. It is rather odd the letter never got delivered. Nonetheless, I shall update the registry.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (69, 'enUS', 'Stalvan? Sure sounds familiar.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (70, 'enUS', 'You\'ve got some nerve bringing this here.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (71, 'enUS', 'You have confirmed my fears, $n. The murlocs are a threat we cannot ignore.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (72, 'enUS', 'You slowly slide the lid of the crate off. . . .', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (74, 'enUS', 'I can barely make out those letters. But that writing style reminds me of something I once saw before my vision became so poor.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (75, 'enUS', 'You found it!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (76, 'enUS', 'Kobolds at the Jasperlode Mine, you say? Curses! The situation is worsening by the minute!$B$BThank you for the report, $n. But I wish that the news you brought was good news.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (77, 'enUS', 'Finely executed, $n. I am glad you had enough foresight not to bruise the delicacy of the ripple. There is nothing worse than the bouquet of ripple that has been jostled about needlessly.$B$BIt will take a moment for the magic to be wrought over this precious cargo. Actually, no magic is involved; I merely have some empty packaging for Hinterlands Honey Ripple. Our chicanery shall turn these bottles into an alcoholic treasure of the ages.$B$BOnward - for great booze!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (78, 'enUS', 'By the Light! You bet I recognize that handwriting.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (79, 'enUS', 'My, you have proved yourself to be quite a detective, $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (80, 'enUS', 'Why yes! The writing on this page does in fact match that of the registry signatures.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (81, 'enUS', 'Well, let\'s see what\'s in this barrel!$B$BYes, yes, it looks as though it is all here. Here\'s your fee for the package, good job on that. I stand to make five times that fee when I sell it to the tavern! Har har!$B$BThese extra bottles of Hinterlands Honey Ripple are going to be set for a special occasion. I think I\'ll drink some with champagne. I\'ll call it... champipple.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (82, 'enUS', 'Good work - I\'ll start my analysis on these now! I can tell you right from the start though... these are nothing like any insect form I have ever seen. It\'s almost as if these bugs are being controlled by an outside force or something.$B$BYou need to go talk to Fizzledowser right away; apparently there is an emergency that he needs your help with on the double! I\'m not sure, but I think it involves more of the bugs in the Tanaris desert!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (83, 'enUS', 'Ah, these are nice scraps, if a little rough...$B$BHere you are!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (84, 'enUS', 'Mm, yum! This pie is the best!$B$BI think my memory is coming back to me...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (85, 'enUS', 'You lost a what? Well I didn\'t take no necklace, because I ain\'t no thief!$B$BI might know who did but...<grin>...I\'m too hungry to remember.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (86, 'enUS', 'Though this wild boar meat is tough, simmer it enough and it sure does make for some tasty pie!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (87, 'enUS', 'Oh, you found it! Thank you, thank you dear!$B$BHere, take this. It was my husband\'s and he always said it was lucky. If only he didn\'t forget it on his last campaign! *sniff*', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (88, 'enUS', 'Thank goodness! That pig was getting so big she\'d have eaten our whole crop! Thank you, $n.$B$BNow, do any of these suit you?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (89, 'enUS', 'Great work, $n. These supplies will help the bridge effort immensely. We\'ll get this beauty shored up in no time.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (90, 'enUS', 'Very nice work, $n! Enjoy the delicacy known as Seasoned Wolf Kabob!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (91, 'enUS', 'The Court of Lakeshire recognizes your commitment to justice, $n. By enforcing the laws of the Kingdom you have helped uphold the justice of the land.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (92, 'enUS', 'Well done, $n. Such fine specimens too! And here is your delicious delicacy known as Redridge Goulash!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (93, 'enUS', 'Ah yes, a nice clutch of legs you have there! Let me just get these seasoned with my secret spices (no looking!) and get them to skittering on a skillet for a while...$B$BAnd, although Dusky Crab Cakes are my specialty and I won\'t give out the recipe, here\'s the recipe for a dish that\'s almost as good.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (94, 'enUS', 'You place the Glyph of Azora on the stone. It adheres itself ... and you hear from it Theocritus\' disembodied voice:$B$BYou have my gratitude, young $n. You may now speak with me through this Glyph, and I might have future tasks for you ... after I have probed the thoughts of my rival.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (95, 'enUS', 'A mound of dirt is here, hastily piled up as if someone had need of hiding something...without much time to do it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (97, 'enUS', 'I knew it! Fine work, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (98, 'enUS', 'I know, $n: Stalvan is dead. I sensed a wave of hope ripple through the tainted forest. Once and for all, Duskwood is free from his blood thirst. My joy is hampered by thoughts of those who fell prey to the horrible beast. Nonetheless, you are a brave and cunning $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (99, 'enUS', 'Your persistence is commendable. With the knowledge you helped me gather, Arugal will fall and his reckless magic shall be undone. Cheers to you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (101, 'enUS', 'Very wise of you, $n. This Totem of Infliction will help to protect you against the evil which lurks throughout Azeroth and beyond. When you feel threatened, hold this totem and take comfort in its magical aura. Be brave, fair $C. You will need all the courage you can muster for I see much bloodshed in your future.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (102, 'enUS', 'Well done, $n. With valiant adventurers such as yourself fighting alongside The People\'s Militia, Westfall just might return to the prosperous breadbasket it once was. Please accept this in recognition of your tireless efforts.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (103, 'enUS', 'Praise you, brave $C. The rocks of the Westfall Coast shall be lit thanks to your hard work. Many lives will be spared so long as the torch is kept lit.$B$BI died needlessly on this very shore. My afterlife plight is to see that no others follow my destiny.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (104, 'enUS', 'So the foul heathen, Murk-Eye is dead. Well done, $n. By your hands one life was laid to rest but perhaps many more were saved. The Great Sea, wrought with danger as it is, shall be a wee bit safer this night thanks to your heroics.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (105, 'enUS', 'You\'ve done it! Araj... the lich is finally defeated!$B$BYou\'ve done a tremendous service to the Horde on this day, $n. Your bravery and perseverance are a testament to what it means to be a true hero. Please - accept this as a token for all to see and know that you have delivered the Horde a victory for the ages!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (106, 'enUS', 'Ah! I can\'t stand us being apart. I have to see her!!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (107, 'enUS', 'My heart goes out to those two poor souls, Maybell and Tommy Joe. I remember being young and in love, once.$B$BThere must be something I can do to help them! Let me think...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (109, 'enUS', 'Ah, so my friend sent you here? How kind. $B$BWell the Stormwind Monarchy has abandoned our cause. Now it is up to the People\'s Militia to keep the land free from corruption. If our cause interests you, I can put your combat skills to use in the name of freedom.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (110, 'enUS', 'Yes, I have the results right here. They are... unsettling, to say the least$B$BOur initial suspicion that the bugs are being controlled by an outside force, we believe, is correct. If this is the case, then we are dealing with not only a very powerful unknown force, but also a malignant one. These bugs have been changed to be pure ravagers. I\'ve never seen anything quite like it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (111, 'enUS', 'While our families are feuding, Tommy Joe and Maybell don\'t have much of a future, but... maybe we can get them together for just a little while.$B$BHm, what can we do...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (112, 'enUS', 'You got them. Good show! Now, just one moment while I concoct the potion...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (113, 'enUS', 'Thank you for delivering the report, Junior Surveyor $n; here is your pay for your diligent service to the Gadgetzan Water Company.$B$BWhile I am usually not thrilled about handing out our valuable information carte blanche to those who would otherwise pay good coin for it, I\'ve known Pestlezugg for a number of years now. If he recommends letting everyone know about it, then so be it. Give me a moment and I\'ll make you a copy.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (114, 'enUS', 'Oh...my! I feel guilty deceiving my family, but my feelings for Tommy Joe are too strong to ignore.$B$BThank you, $n. I\'ll drink this liquor as soon as I have the chance and sneak away to my love.$B$BAnd for you... please take this.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (115, 'enUS', 'Thank you, $n. I\'ll send these to be destroyed immediately.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (116, 'enUS', 'A job well done, $n! I know well the roads you had to journey to procure this fine array of liquor. But my patrons will be happy once again! $B$BMy wife is quite a seamstress. Please accept this cloak she made in return for your services.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (117, 'enUS', 'Hops and Barley, yeast and malt $BThe taste-buds feel a virtual assault, $BNo need to plead, no need to beg $BFor Thunderbrew Lager comes in a keg!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (118, 'enUS', 'Heh, so old Verner needs some shoes, does he!?!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (119, 'enUS', 'Thank you, this really helps me out.$B$BBut what\'s this? Argus sent a note with the crate...What?? Argus wants me to PAY him? Bah!$B$BWell, $n, thank you for your help.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (120, 'enUS', 'At ease, $C.$B$BMagistrate Solomon is a noble leader. His words carry great weight with me. I shall seek the counsel of the King and make the situation clear to him. Assure the good judge that he has the Stormwind Army\'s support. Reinforcements will be sent as soon as word is given from His Majesty.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (121, 'enUS', 'Thank you for your time, $C. For your service to Lakeshire and Stormwind I reward you with these coins.$B$BNow if you\'ll excuse me, I find this correspondence somewhat of a mystery. Something is amiss in our Kingdom. I fear this is the beginning of conflict, not the end.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (122, 'enUS', 'Thanks! These will get me out of debt to Argus...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (123, 'enUS', 'Hm... I have heard of this \"Collector\" but I don\'t know whom he\'s working for. Thank you for the schedule. It will help us solve this mystery.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (124, 'enUS', 'Thank you for all your help, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (125, 'enUS', 'Excellent recovery, $n! I never thought I\'d see these tools again.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (126, 'enUS', 'Again, thank you for your help. The Stormwind army is so busy dealing with the orcs, they don\'t have the troops to protect Redridge from local threats like the gnolls.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (127, 'enUS', 'Pleasure doin\' business with you, matey!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (128, 'enUS', 'I see you have fought valiantly against the Blackrock Clan, $C. Your service to our township is great indeed.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (129, 'enUS', 'Thanks, I could really use the meal. Guarding Lakeshire from the orcs and gnolls is tough work!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (130, 'enUS', 'You need a bouquet of flowers? You haven\'t been in town very long...have you already found for yourself a sweetheart here?$B$BI know I shouldn\'t pry, but it\'s nice to see that love is in the air... especially in dangerous times like these.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (131, 'enUS', 'Oh, those are lovely! I can\'t wait to put them in some water!$B$BBut...these are Martie\'s favorite flowers. Parker didn\'t have you go to that jealous vixen for this bouquet, did he? You didn\'t tell her who they were for, did you? If you did, then it wouldn\'t surprise me if she poisoned them.$B$BOh, but it\'s not your fault. Thank you, and here\'s your meal.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (132, 'enUS', 'Bah! I should have let that scoundrel rot when I had the chance. But this information is crucial. Good work.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (133, 'enUS', 'Ah, thanks. These will do just the trick!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (134, 'enUS', 'Thank the Nec-...well, thank YOU, $n! You have more than earned your reward.$B$BAha! Happy! Happy nights ahead!!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (135, 'enUS', 'This matter might be more complex than Stoutmantle realizes.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (136, 'enUS', 'The footlocker slowly creaks open. Sand and water seem to be its only contents. But wait! A small crab scurries out with a clue to the treasure in his claws!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (138, 'enUS', 'Good work, treasure hunter!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (139, 'enUS', 'You\'re on your way to the jackpot, treasure seeker!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (140, 'enUS', 'The hinges on the old chest are rusty but they still work. You force the chest open and take the booty.$B$BCongratulations!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (141, 'enUS', 'Edwin VanCleef. . .I know the name well. To think that a man so industrious and talented could turn into such a scoundrel unnerves me. I\'ll need further proof before I believe this.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (142, 'enUS', 'This is indeed firm proof that VanCleef is in charge. Now all we need to know is where the Defias gang is hiding out.$B$BWe\'ve had a stroke of luck while you were gone. We captured a thief trying to steal Saldean\'s wagon. He has promised to lead us to the hideout in exchange for his life. I want you to defend the traitor so he can reveal the hideout. Return to me once you have uncovered the location.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (143, 'enUS', 'Good Messenger, you have served your master well.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (144, 'enUS', 'I do say, this is not good at all. I did not realize that Stoutmantle\'s situation was not unlike ours here in Lakeshire. What action is so important that it calls the Stormwind Army away from its people? Bah, I should not ponder aloud.$B$BHere, messenger. Accept these coins in exchange for your service to the Township. I might call upon you again.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (145, 'enUS', 'Thank you for making the long journey here. This is indeed important information.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (146, 'enUS', 'By the Light! The Stormwind Army retreated from Westfall. And the Guard no longer protects Darkshire? There is some treachery abound. How could this be so? $B$BOh I forgot to reward your service, $C. Here are some coins. Now excuse me. Something is amiss in Stormwind and I must get to the bottom of it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (147, 'enUS', 'You found him? Well done, $n. He won\'t be \"collecting\" from the Elwynn Mines again!$B$BAnd this ring you found is interesting... it\'s a membership ring for the old Stonemason\'s Guild in Stormwind. I haven\'t seen one of these in years, since back in the days when the Defias Brotherhood used to infest this land.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (148, 'enUS', 'Ah, Ghost Hair Thread is what you need, is it? I\'m afraid I have none in stock, but I can make some for you...if you can supply the ghost hair.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (149, 'enUS', 'What is this? A comb? It\'s lovely! And it glides through my hair as if it weren\'t the stiff, stringy horror that it is.$B$BOh, if only I had a mirror...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (150, 'enUS', 'Well done, $n. I hope you didn\'t have too much trouble with those murlocs.$B$BIt\'s strange seeing them so far from the sea. I wonder if they\'re here because they\'re running from something...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (151, 'enUS', 'Thank you so much, $n! Poor Old Blanchy will be so happy!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (152, 'enUS', 'Well done, $n. You have quite a knack for combat. Thanks to you the Coast of Westfall is a safer place.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (153, 'enUS', 'Nice work, $R. Please accept one of these items as payment for all your hard work.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (154, 'enUS', 'I can make a spool of Ghost Hair Thread with this, and have a few strands to spare. Here are some coins for those extra strands.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (155, 'enUS', 'Most excellent, $n! VanCleef is as good as ours now that we know where he is hiding.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (156, 'enUS', 'Good, you got the rot blossoms. And I\'ll tell Althea Ebonlocke of the Night Watch that you were killing skeletons.$B$BKeeping the people of Darkshire safe is a grim burden for the Night Watch, and news that others are helping stem the tide of fiends in Duskwood is welcome news indeed.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (157, 'enUS', 'Delightful! This will do splendidly...$B$BHere, good $n, take this as payment for your honorable deed.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (158, 'enUS', 'You need some Zombie Juice, do you? Hmm...that\'s some strong stuff - I don\'t usually get requests for it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (159, 'enUS', 'A thousand thanks, $n. You warm an old man\'s heart with your foolish-...I mean...with your kindness!$B$BHere you are, friend. Take this as a token of my gratitude.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (160, 'enUS', '<Ello looks at the letter...>$B$BWhat language is this? It looks ancient...I can\'t read it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (161, 'enUS', 'Ah, well Ol\' Hinderweir is wise to be worried.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (162, 'enUS', 'This report... we night elves know of the terror mentioned here. Fizzledowser was wise to send this, and you, to me.$B$BThese creatures will consume the Tanaris desert rapidly; they are nothing short of one of the greatest menaces we all will ever face. Familiarize yourself with this name, $n: silithid. The insects mentioned here are the silithid, and they are the tools of one of the greatest threats that both the Horde and Alliance face.$B$BIf only we were smart enough to see that before.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (163, 'enUS', 'No! Please don\'t kill me! It\'s just me... Jitters!! They\'re everywhere... I can\'t escape them! Monsters in Raven Hill? N-n-n-o... couldn\'t be. Only harmless Jitters.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (164, 'enUS', 'These supplies will be most useful. The aid of the Carevin family is always appreciated. Here is a little something for your trouble.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (165, 'enUS', 'Elaine Carevin? Hehe, oh yes... of course, the Carevins.$B$BNo, nothing\'s wrong but it is hard for me to go into town now. The roads are dangerous and I\'m not getting any younger. Maybe you can do an old man some favors?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (166, 'enUS', '$n, your bravery is remarkable. The People\'s Militia thanks you for your service to the people of Westfall. With VanCleef dead, this marks the beginning of the end for the Defias Brotherhood. Hopefully some day soon peace will once again grace the plains of this fair land.$B$BI salute you, Sir!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (167, 'enUS', 'My suspicions were correct. My poor brother.... Well, thank you for bringing some conclusion to this mystery, even if the conclusion was grim.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (168, 'enUS', 'Thank you for collecting these, $n. Your efforts will help bring peace to the dead in the wake of this tragedy. I will see to it that the families are notified.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (169, 'enUS', 'Fantastic work, $n! Gath\'Ilzogg led those brutes into battle, killing many innocent humans. Vindication is ours. This is the first step in reclaiming the keep for the kingdom of Stormwind!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (170, 'enUS', 'If the problems we\'ve had here are indicative of what is happening elsewhere in our lands, by Magni\'s beard, we have some troubles ahead of us! I can only hope that the king and Senate are taking steps to deal with the threat the troggs pose.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (171, 'enUS', 'What\'s this? A gift for all the orphans!$B$BMay the heavens bless you, $n. You\'ve done more for Randis than most would ever think of doing for him. Thank you - you\'re a shining star in the skies of the Alliance.$B$BRandis had so much fun with you, $n. He wanted to give you one of his favorite pets to remember him by.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (172, 'enUS', 'Zug zug! My name is Grunth, and I guess you\'re looking after me? You\'re an adventurer, like my mom and dad were. My matron says that they died with honor in battle. I\'m happy to meet you.$B$BI hope we\'ll do plenty of things together. I have some things I\'d like to do, and the matron says you\'ll be like a big brother to me during this week. I\'d like that a whole bunch.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (173, 'enUS', 'Impressive, $n. It would seem that you are capable enough to handle yourself. Perhaps a more suitable challenge could be found for one of your abilities.$B$BIt is curious though... The worgen seemed to just appear out of thin air. But then, with all the other strange creatures that have moved across our borders, I suppose it\'s only one of many concerns.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (174, 'enUS', 'This is perfect, $n. The first step is complete!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (175, 'enUS', 'Why yes, I used to own a looking glass.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (176, 'enUS', 'He did WHAT?$B$B Great, now we have a bloodthirsty beast right beneath the streets of Stormwind, with a whole army of gnolls at his command. Definitely an improvement, I feel safer already. $B$B Here\'s your reward, $n. But promise me you\'ll check in with the Stockades guards in Stormwind at some point, just to ensure that things are going smoothly.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (177, 'enUS', 'Most superb! This will work perfectly. Many thanks!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (178, 'enUS', 'You have my gratitude, $n. I sent that message to the Shadowhide Pendants weeks ago and had believed it a failure. But it seems the spell did reach at least some pendants, after a significant delay.$B$BIn time I hope to learn how to send an attack through the pendants, to immobilize any Shadowhide Gnolls who wear them.$B$BBut before that is possible, I\'m afraid that much more research is required.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (179, 'enUS', 'Ah! Wonderful. The wolf meat should do nicely. Oh, don\'t worry, $n, I wouldn\'t forget my part of the bargain. Here, one of these should fit you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (180, 'enUS', 'So the evil Lieutenant Fangore is dead? Good riddance to that foul creature. You are very brave, $C. The Township of Lakeshire thanks you for your aid.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (181, 'enUS', 'At last! The stargazing device is complete! Thank you, $n. Now I can continue my research. . .', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (182, 'enUS', 'Hopefully that did the trick. Anvilmar will be safe from trolls for the time being. Now, to find out what it is that has the trolls so agitated.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (183, 'enUS', 'Excellent! I can go back to my... leisurely... hunting now. Thank you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (184, 'enUS', 'This is useless. Look at the date on this letter. The Furlbrows have been squatting on the Jansen Steed for five years.$B$BThey never could quite get their wagon... fixed.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (185, 'enUS', 'You have finished with the young tigers... but the thrill of the hunt still holds you captive.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (186, 'enUS', 'Tiger blood coats your hands, your weapons, and your $C garb. Yet still you crave more.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (187, 'enUS', 'You have defeated the strongest tigers in Stranglethorn. You have become a great tiger hunter, and have every right to stop your hunt right here and now.$B$BSomething tells you, however, that a stronger cat still waits for you within that jungle...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (188, 'enUS', 'Most impressive, I do say! I send you out to defeat the kittens, and you return with a paw the size of a gnome! You are a true Master Tiger Hunter, $n!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (189, 'enUS', 'Oh excellent, excellent. That\'ll help a little, for the present...$B$B<He trails off, grumbling about profit margins and costs...>$B$BWhat? You\'re still here? Reward? Oh, right... of course, here, take this... it should be more than enough.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (190, 'enUS', 'You feel more experienced already. Perhaps it\'s time to move on to bigger prey.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (191, 'enUS', 'You take a moment to silently congratulate yourself on your panther hunting so far.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (192, 'enUS', 'The shadowmaw panthers proved no match for your skills as a hunter. Only one task remains.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (193, 'enUS', 'So the great Bhag\'thera is dead! Cheers to you, $n. You are a mighty $C indeed!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (194, 'enUS', 'Stranglethorn raptors were not a problem for a wily $C like you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (195, 'enUS', 'Hemet Nesingwary Jr. doubted you, but his doubts were unfounded. You are absolutely destroying these raptors.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (196, 'enUS', 'You are no longer just a raptor hunter. You are an honest-to-goodness raptor SLAYER.$B$BThough one fabled raptor still eludes you...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (197, 'enUS', 'Master $n, the raptor slayer!$B$BHas a nice ring to it, eh?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (198, 'enUS', 'These land mines will help us defend ourselves from that animal Kurzen and his men. Thank you for bringing them. If you need further work, I can probably find something for you to do. And you won\'t have to deal with those goblins, hm?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (199, 'enUS', 'This is an alarming discovery, to say the least!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (200, 'enUS', 'These are Bookie Herod\'s notes and records.$B$BAmong them are: a list of supplies within the camp, material requests from Colonel Kurzen, and various letters and correspondences.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (201, 'enUS', 'Hmmm, so my information was correct, as usual. Interesting. Well, I don\'t suppose they pose any sort of threat of interference with our operation, but perhaps they have a need for some of our excellent manufactured products. Hmmm...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (202, 'enUS', 'I dared not believe the reports, but you bring us proof that...Kurzen and his leaders are defeated. You have saved us!$B$BThese jungles are filled with menaces of all types, but none had the cruel intellect of Kurzen. We may all breathe easier now that he is gone.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (203, 'enUS', 'Well done. The loss of those Jungle Fighters will relieve some of the pressure on our camp.$B$BBut now that you\'ve had a taste of the forces we\'re up against, do you still want to throw your lot in with us??', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (204, 'enUS', 'Well done, $n. These remedies will keep more of us alive and in fighting shape. And the extracts will help us create our own remedies.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (205, 'enUS', 'Thank you. I will study these fetishes and see if they hold the secret of Kurzen\'s fall into evil.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (206, 'enUS', 'Aha! You\'ve done it! You are a great force for good, $n, and may your strength never fade!$B$BI will study this Mind\'s Eye and determine if it may be used for good. If not...then it must be destroyed.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (207, 'enUS', 'Thank you, $n. I will study these legends to see if there is some link between the trolls and the evil that has possessed Colonel Kurzen.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (208, 'enUS', 'Bloody well done! Right-o! Congratulations, $n, for succeeding where others have failed.$B$BI am honored to count you as a comrade of our great hunting party here. No one will ever doubt your expertise in hunting and tracking!$B$BCheers to you, I say! Cheers!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (209, 'enUS', 'Ah, good work, good work! Ugh, though you could have done a better job cleaning off the-- Well, no matter, no matter! My thanks to you, $n, our operation will no doubt see increased output and productivity thanks to your help! Good good, now, onto other matters...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (210, 'enUS', 'Oh I see, it\'s a Krazek\'s Crock Pot! An ingenious piece of work, but it can break a spring or throw a gear now and then. Let me see what\'s wrong with this one...$B$BAha! Its steam charger is rusted out, it has a cracked knuckle-gasket and its fobulator is missing teeth. I\'m surprised the pot could even boil a turnip in this state!$B$BCan I fix it? Of course I can fix it!$B$BBut there\'s a price. Oh yes, there is always a price...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (211, 'enUS', 'You\'ve done it! Araj... the lich is finally defeated!$B$BYou\'ve done a tremendous service to the Alliance on this day, $n. Your bravery and perseverance are a testament to what it means to be a true hero. Please - accept this as a token for all to see and know that you have delivered the Alliance a victory for the ages!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (212, 'enUS', 'Ah, this will do nicely! Many, many thanks!$B$BNow if you\'ll excuse me, I must get the haunch chopped, marinated and garnished for the banquet.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (213, 'enUS', 'Fascinating. That the crystals have been tumbled using this process can only indicate that they are planning to use them as some sort of fuel or other catalyst. I don\'t know much more than that. Samples will have to be dispatched to Head Chemist Gigglefont.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (214, 'enUS', 'Well done, $n! On behalf of The People\'s Militia I salute you for your efforts. Soon we will return this land to the people with help from brave souls like you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (215, 'enUS', 'Yes, what Thorsen told you is true. You must be discrete with this. At one time, we rebels were fiercely loyal to Colonel Kurzen. But such was Kurzen\'s corruption, our passion twisted into rage.  If it was made known that Private Thorsen has had recent dealings with Kurzen\'s men, then one night he would find himself with a slit throat.$B$BTo my knowledge, only Sergeant Yohwa, Thorsen and I were privy to Thorsen\'s mission. But there must be another. A traitor.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (216, 'enUS', 'Well done $C, well done! I will continue to send forces to thin out the furbolg, but I can at least count on a different path that will be free of Alliance entanglements. The Horde conquest of Ashenvale is now in full swing!$B$BYou\'ve earned your pay this day, hero. I salute you!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (217, 'enUS', 'Most excellent, $n! You have brought hope to Loch Modan. With Grawmug dead, we stand a better chance of seeing the Troggs eliminated from the land.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (218, 'enUS', 'I\'m glad you\'ve returned safely, $n. While it may not look like it now, you\'ve proven to be a tremendous asset in the protection of Anvilmar.$B$BHere, take this as a symbol of my gratitude.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (219, 'enUS', 'You have served the Kingdom well, $C. Thank you for assisting Corporal Keeshan with his escape from those dreaded Blackrock Orcs. You shall be rewarded for your brave acts this day.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (221, 'enUS', 'You performed well against the Dark Runners, $n. But it seems as though their numbers are inexhaustible. Every time we make progress, more spring up to refill their ranks.$B$BWhat is the evil power that draws them here? Why have they come to disturb our unhappy realm...$B$BI will put my faith in Master Carevin. No doubt he will get to the bottom of the problem.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (222, 'enUS', '$n, to be honest with you, I did not believe that you would get this far, but you are clearly a $C to be reckoned with. In fact, if you wish to formally join Master Carevin\'s struggle, I will gladly write for you a letter of recommendation.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (223, 'enUS', 'Excuse me for my cold reception, $n, but as I\'m sure you can tell, I am an extremely busy man. I see that you have impressed Calor--and I assure you, that is no small feat--and that he has given his recommendation.$B$BThere are unsavory types afoot, $n, and we can use the help of all who have proven themselves. We battle against demons, the undead, and those who would provide them aid. Be vigilant, be wary, and trust none who would not give aid to our cause.$B$BGlory under the Light.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (224, 'enUS', 'Well done, $n! You demonstrated uncanny courage on the battlefield. Our war against the Troggs will be won with efforts such as yours. You have served King Magni well.$B$BIf you fancy yourself well-suited for this kind of adventuring, I suggest you speak with Mountaineer Gravelgaw. The Captain has him assigned to a rough patrol that I am sure he can use some help with. Check with Gravelgaw just inside the tower.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (225, 'enUS', 'Morgan Ladimore?$B$BAhh, yes, of course. His was a long and sorrowful tale. I knew him, well, before he left for the war, but that was the last time I saw him. A noble and good man, he was, but he suffered a bad end.$B$BHere, I should have something here that can tell the tale better than my own recollections...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (226, 'enUS', 'Oh good. You thinned out that pack quite a bit, and without those wolves nipping out our heels, we can bind our minds to dealing with Morbent Fel.$B$BHere, take some of these Flash Bundles. They make them in town, and a lot have been donated to Sven to help with his cause.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (227, 'enUS', 'Morgan Ladimore? Never heard of...$B$BOh, you mean Mor\'Ladim! I guess you haven\'t heard the story?$B$BWell, I\'ll give you the short version. A wandering undead calling itself Mor\'Ladim has been wandering Duskwood. From what we\'ve gathered, it appears to be the undead body of Morgan Ladimore, of whom you apparently know. He\'s been causing us all sorts of problems, attacking Watchmen on patrol and killing people.$B$B<She sizes you up.>$B$BYou seem to be capable enough, maybe you could lay him to rest.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (228, 'enUS', 'You killed him? That\'s no small accomplishment, $n! On behalf of the people of Darkshire and the Night Watch, I thank you.$B$BAh... there is one small matter, however...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (229, 'enUS', 'Yes? My father...$B$B<Her eyes become downcast.>$B$BI wish... there was something I could have done for him... If only I had talked to him before he...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (230, 'enUS', '<Sven reads the book\'s pages, then slams it shut.>$B$BThat shadowy figure I saw was a witness to my family\'s death! There are questions I\'d like to ask that man...if only I knew who he was!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (231, 'enUS', '<A ghostly voice sounds on the wind...>$B$BThis is...? Sarah? Could it be she\'s still alive? The weight is removed from my shoulders...$B$B$n. Take my sword, Archeus. As my soul is put to rest, I have no more need for it. It was forged to do good, and though I have proved myself unworthy to hold it, perhaps you will carry on the Light through it.$B$BLys, my love...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (232, 'enUS', 'Ah, Zinge needs a kit, does she? It saddens me that Zinge does not choose to grace myself with her sweet presence.$B$BVery well, let me procure a kit for you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (233, 'enUS', 'Thank you, I\'ve been waiting for these letters for quite some time...$B$BUnfortunately, these letters aren\'t all for me. This one is addressed to Grelin Whitebeard. He\'s not too far away if you\'d like to deliver it to him.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (234, 'enUS', 'Ahh, excellent. It\'s been a while since I received word from Ironforge.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (235, 'enUS', 'Welcome to the new frontier, $n. Ashenvale is a land of opportunity, one where a young $C like yourself is able to find boundless chances to prove their mettle. Look around the outpost here, and be sure to travel out to the Zoram Strand, as the Horde has another outpost there as well.$B$BYour presence here tells me that you\'ve come to learn more about the hunt. Listen close, and I will gladly share with you what you need to know.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (236, 'enUS', 'Excellent! This will help fuel the flames of war for days to come.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (237, 'enUS', 'You accomplished your mission well, $R. The King himself would be proud.$B$BThe reinforcements promised to us never showed, however. Apparently the Alliance front is a sea of crimson from the blood of our fallen brethren. The news of the losses leaves a bitter taste in one\'s mouth. But we must not let their deaths go for naught. We must rally in these grim times and bring glory back to the Alliance.$B$BSpeak with Mountaineer Wallbang for reassignment. You\'re needed now more than ever.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (238, 'enUS', 'Good - it pleases me to be working with someone who takes pride in not wasting my time.$B$BAfter I make some adjustments to this kit, you\'ll be ready to head out into the field. You\'ll be using it to collect some vital data to the task at hand.$B$BIt will take just a moment for me to finish the modifications. While you are waiting, you should probably start thinking about what you\'d like to bring to the desert with you.$B$BYes, I said the desert.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (239, 'enUS', 'Marshall Dughan sent you, eh? Well you\'re not from the army, but if Dughan sent you then that\'s good enough for me!$B$BOur situation is, to say the least, a stressed one. I hope you can give us a hand.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (240, 'enUS', 'Aha! Thank you, $n. Those really hit the spot!$B$BAnd here, please let me pay you for these!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (243, 'enUS', 'Ah, our model 4711-FTZ power sources! We normally use them to power the water wells, but THANKS TO THE VILE NOMADS, we have an over abundance of power sources right now.$B$BSure, I can get you a power source for whatever nefarious science experiment you think you\'re trying to hide from me. It ain\'t gonna be free though, $R. Nothing\'s free in Gadgetzan.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (244, 'enUS', 'There are Redridge gnolls that close to Elwynn?? They may be poised to advance into our homeland. Soon the people of Lakeshire may not be the only humans under siege!$B$BHere is your pay, though you bring us dire news. And it comes at a bad time, for we are ill equipped to deal with it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (245, 'enUS', 'Ah! You\'re back. And not too much worse for wear, it\'d seem. Of course, $n, the Night Watch is grateful for your work, and I did promise you a reward, so here.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (246, 'enUS', '<Deputy Feldon listens to your report...>$B$BThere\'s a sizable force of gnolls encamped there, and from what you tell me they are tough ones. It must not have been easy for you to gather this intelligence.$B$BHere you are, $n. We are grateful for your aid.$B$BAnd if you haven\'t already done so, speak with Marshal Marris and Magistrate Solomon within Lakeshire. Our situation grows ever more desperate - those two will have need of you I\'m sure.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (247, 'enUS', '$n, you have slain all three beasts that comprise the Ashenvale Hunt. You have brought me adequate proof of their demise; there is no question of your strength and tenacity. The blood of a hunter streaks through your veins, and you should be commended!$B$BPlease, accept this bauble as a sign of congratulations on your mighty deeds done in Ashenvale for the Horde today. May your ancestors watch over you!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (248, 'enUS', 'It is a suitable vessel for the Glyph of Azora, allowing Theocritus to delve into the minds within Stonewatch Keep.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (250, 'enUS', 'The suspicious looking barrel contains a strange smelling powder.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (251, 'enUS', '<Sirra begins translating the note...>', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (252, 'enUS', '<Ello reads the letter...and his face goes white. He then looks at you...>$B$BAlthough the aid you gave Abercrombie may have been motivated from kindness, your actions have doomed our town.$B$B<Ello becomes lost in his thoughts...>', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (253, 'enUS', 'You\'ve done it! The magic within his heart will be proof against the Embalmer\'s evil, and a powerful aegis over the town of Darkshire.$B$BYou have done a great thing for our people, $n. Your name will remain in our annals forever.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (254, 'enUS', 'You dig through the semi-loose dirt over the grave...and find the remains of a coffin.$B$BWithin the coffin you can feel the vibrating scrapes of clawed fingers and the gnashing of rotted teeth...$B$BWill you open the coffin and retrieve the Embalmer\'s heart?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (255, 'enUS', 'Ah, this is excellent news! Your efforts are greatly appreciated by myself and the people of Thelsamar. Better yet, if we can solve the ogre problem, we\'ll be able to shift our focus to those Light-burning troggs.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (256, 'enUS', 'Yech! What is that thing? And why would you bring it...$B$BBloody--! Is it? Well, Light burn me, is that the head of that bloody ogre? This certainly is a catch then! Here, your reward and the thanks of myself and the people of Thelsamar.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (257, 'enUS', 'What? Success?$B$BTake it from me, Astridama, you don\'t let it get to your head. I mean, any unshaven boy with a bow could have killed such... small specimens. And I hope you don\'t enjoy killing the buzzards so much, yes? We wouldn\'t want them to become extinct. $B$BWell, err... Never let it be said that Daryl the Bold would not hold to his word.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (258, 'enUS', 'You seem a bit worse for wear. No doubt the boars gave you some trouble, hm? No worries, I will be discreet about your struggle, I can imagine how hard it must be for you without the accusations of wagging tongues.$B$BOh! You managed to kill the boars? I... well, that is... that is to say, I\'m not surprised! Any child could have--bet?$B$BWhat bet?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (261, 'enUS', 'You have proven your resolve, and the Scarlet Crusade commends you.$B$BIf you do not shy from righteous slaughter, then perhaps your path lies with us...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (262, 'enUS', 'Hmm... Someone handled this book recently, someone who is driven by revenge. But the impressions left on it, at least the impressions from the original owner, are faint.$B$BI can tell, however, that the original owner of this book is not a native of Darkshire.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (263, 'enUS', 'I knew the second I laid eyes on you, $n, that you would make Ironforge proud. You have served King Magni well. Now that you\'ve proven yourself to be such a loyal aid to the Kingdom, perhaps you should talk with the Captain to see if he\'ll entrust you with a higher detail.$B$BI salute you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (264, 'enUS', 'You place the worthless pendant on the grave, and the gemstone within the setting seems to dull noticeably.$B$BAs you stand to leave, you look down at the pendant; it lies lifelessly over the hands of the relief sculpted into the top of the coffin. Your thoughts are interrupted by a stiff, cool breeze passing over the Sepulcher. For a moment everything around you is silent.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (265, 'enUS', 'Ah, yes. I sold this book a month ago to a shady person. He didn\'t tell me his name, but he was a nervous, jittery chap, always looking over his shoulder.$B$BI hope that helps you...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (266, 'enUS', 'Oh yeah, I remember this book. Its owner rented a room for a few days, and spent his nights at the bar, writing.$B$BHe left town in a hurry, muttering about being hunted by...something. Last I saw him he was heading west out of town, looking for a new spot to hide.$B$BThe first place he\'d find to the west is Raven Hill...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (267, 'enUS', 'I am quite impressed, $n. You have done the Dwarven race a great favor by defending our lands. King Magni Bronzebeard himself would be proud!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (268, 'enUS', 'This journal explains much to me, and sets me on the path against the dark riders! I have seen these riders in Duskwood, and have seen them speak with the wizard Morbent Fel, at the Forlorn Rowe.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (269, 'enUS', '<After reading the journal and hearing your intent...>$B$BAn evil taint has gained a foothold within the already beleaguered Duskwood. I thank the Light we have a hero such as you to face it.$B$BBut you must first prepare yourself for the coming trial...for the Necromancer Morbent Fel is immensely powerful, and has cast dark enchantments about himself to protect him from physical assaults. If you are to defeat him, you will need a weapon that is proof against these protections.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (270, 'enUS', 'Lightforge Iron is a precious metal, but it sounds like your quest is a dire one. You\'ve come a long way, Brother. With luck, you might just find what you seek!$B$BAnd if you do get the iron, I know who can fashion it into a weapon against this Necromancer you speak of in Duskwood.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (271, 'enUS', 'Er... what\'s this? A bear\'s head, by the looks at it.$B$B<He runs a finger along his scar unconsciously.>$B$BWell, that\'s quite an interesting kill you have yourself there. Couldn\'t be that bear from back then...$B$B<He trails off then starts to visibly shake.>$B$BOh, take that away take it away! Get that grisly thing out of here!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (272, 'enUS', 'You\'ve completed the Trial of the Sea Lion, $n - congratulations.$B$BEach task in acquiring the pendant shows that both agility and endurance are necessary to act in harmony with what you desire to do underwater. Neither can exist without the other, and both cannot be done without your willingness to embrace the aspect of the sea lion.$B$BRemember these lessons well, and count on them once you have gained your aquatic form.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (273, 'enUS', 'Hmph, it\'ll take more than a Dark Iron ambush to stop the deliveries!$B$BBut Saean... I would never have suspected him to be one of their sympathizers. We\'ve been working with him for almost a year now... maybe I missed the signs.$B$BWell, I\'ll consider that later.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (274, 'enUS', 'A disarming colloid? For Seaforium? Oh heavens!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (275, 'enUS', 'This land is soothed by your efforts, and for this I will grant you a boon.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (276, 'enUS', 'By reducing the Gnolls\' number, you have tended to the health of the Wetlands. Although my faith in your people has been taxed in the past, $R, it may yet be restored.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (277, 'enUS', '... thank you. I will smash these flints and cast them into the sea, so that they may never again be used to burn living wood.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (278, 'enUS', 'Just in time, $n!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (279, 'enUS', 'Ah, well done $n.$B$BAccept this as payment for fulfillment of your contract with the Merchant Marines.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (280, 'enUS', 'The keg fizzles slightly as the Disarming Mixture settles in.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (281, 'enUS', 'This crate is covered with clawed scratches, and the marks of water damage. Prying it open reveals soggy and rotted straw, as if something of value was stored in the crate. You search through the straw...$B$B...and find an armload of red-colored bottles.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (282, 'enUS', 'Hm, well, if Whitebeard is sending you to Dun Morogh on important business, I can\'t very well stop you, can I?$B$BAt least let me provide you with some advice and directions.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (283, 'enUS', 'Thank you so much, $n!! You have saved the Stonewrought Dam! Without your help, the Dark Iron terrorists would have surely seen to this mighty landmark\'s demise. You have made the Kingdom and the Alliance proud, brave $C.$B$BThe Dam is once again safe. . . for the time being at least. . . .', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (284, 'enUS', 'This barrel is largely untouched. Prying it open...$B$B...you are assaulted by the pungent odor of cheese. It\'s a little wet, but still tasty!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (285, 'enUS', 'This barrel is halfway buried amidst the mud and grass ...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (286, 'enUS', 'The captain of the Blind Princess will be relieved to hear this precious cargo has been recovered. You have well earned this payment for services rendered.$B$BIt is good to see dependable folk like you, especially in trying times such as these.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (287, 'enUS', 'Ah, $n! I didn\'t think you\'d have much trouble finding the place, my directions are excellent, you know...$B$BLet me just finish up my report then.$B$B$B$BThere! Well, haha, now this certainly is funny... I don\'t suppose, $n, that you would mind doing one last favor for me?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (288, 'enUS', 'Ah! That hits the spot, and loosens the tongue. Thanks, mate!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (289, 'enUS', 'My thanks to you, $n. And the gratitude of my cursed brethren goes to you as well.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (290, 'enUS', 'The key is rusted but it fits the lock neatly.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (291, 'enUS', '<He quickly peruses the report.>$B$BAuthority of the KING!? Has Magni taken complete leave of his senses! And that damnable Whitebeard... Taken matters into his own hands, has he? The Senate will not be pleased to hear about this, not one bit.$B$BWhat, are you still here? Go, before I call the guards!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (292, 'enUS', 'An Eye of Paleth? Amazing! Hmm...there\'s something wrong with this one. You found it on one of those shipwrecks? Well that explains it! But...I didn\'t know they had such an Eye on the Third Fleet...$B$BIt\'s no wonder Fitzsimmons was so spooked. A Cursed Eye of Paleth is no cause for mirth.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (293, 'enUS', 'An Eye of Paleth. But this one has seen much sorrow...it must have been witness to a terrible, terrible tragedy.$B$BLet me cleanse it, for the Eyes were created to shine bright, and their powers can be quite dangerous if tainted like this one.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (294, 'enUS', 'Killing is a dirty business, but you did your job well, $n. If I weren\'t obligated to stay with the Prospector and Merrin, I\'d be down there shedding blood right along side you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (295, 'enUS', 'You did a bang-up job of clearing out those Raptors, $n!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (296, 'enUS', 'Thank you, $n. Your efforts here will not be forgotten. Many innocent Dwarves died at this site, simply doing their jobs. The vengeance you delivered to those evil creatures should make them think twice before attacking King Magni\'s subjects ever again.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (297, 'enUS', 'You got them! Nicely done, $n!$B$BAnd you don\'t look too worse for wear, even with those berserk Troggs about. I\'m going to keep my eye on you...you show some promise.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (298, 'enUS', 'Bah! Ironband\'s having trouble with the Troggs. I wonder if he wants that blastpowder to aid his digging, or to aid his efforts against the Trogg beasts...$B$BWell, either way it\'s powder well spent!$B$BBut what\'s strange is, I sent him plenty of blastpowder weeks ago. I wonder where it went...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (299, 'enUS', 'Ah the Goaz Stone is complete! Fine work, $n. Fine work indeed. Now I can continue with the delicate translation of this Titan script. I can make out new words already! Hmm. . .it says here, \"Old Gods. . .Chained Beneath the Land.\" This is the beginning of something epic.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (301, 'enUS', 'Curse that Ironband! I sent him a load of blastpowder weeks ago, but he\'s made no mention of it in this report.$B$BSo...where did the blastpowder go?!?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (302, 'enUS', 'What? Stormpike wants you to personally see the shipment to Ironband?$B$BI... suppose that\'s fine, the only problem is, I\'ve already sent Huldar with the porters, Miran and Saean. You just missed them, they left just a short while ago.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (303, 'enUS', 'You upheld your orders well, $C. You have proven yourself to be a valiant soldier and loyal servant to King Magni and for that I salute you! With the pressure you applied to the Dark Iron encampment, we now stand a chance of driving them off our lands for good. . .if only the damned reserve would show. . . .', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (304, 'enUS', 'Balgaras the Foul is finally dead. While his death will not bring back the many innocent dwarves who died needlessly by his hand, it will help to restore peace of mind to those who survived. With Balgaras dead, the remaining Dark Iron scum should be easy enough to manage. Now, we must wipe them out before a new leader is sent to these parts.$B$BYou have done well, $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (305, 'enUS', 'Oh, thank the heavens you are here!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (306, 'enUS', 'Such horrible news! At least 3 of them survived. But when will the killing end? I long for the day when King Magni\'s subjects can live without fear.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (307, 'enUS', 'Did you see a lot of Kobolds in there? I can\'t wait for the order to clear them out of the mine. The thought of their grimy hands clawing around our mine makes my stomach rumble!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (308, 'enUS', 'For me? You\'re a true hero, $n!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (309, 'enUS', 'It is troubling news that you bring. To think that the Dark Irons have sympathizers able to procure this sort of material for their dastardly plans.$B$BThat\'s a matter for someone else to consider, some other time. Thank you for protecting Huldar and the cart!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (310, 'enUS', 'You quickly place Marleth\'s barrel of Barleybrew Scalder among the Thunderbrew barrels.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (311, 'enUS', 'Not bad, $n! Did you have a hard time getting past their guard?$B$BNo matter - many thanks, and if you\'re ever in need of stout with a kick, don\'t forget the Barleybrews!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (312, 'enUS', 'What a kind soul you are, $n! Thanks to your bravery I won\'t find myself starving when the cold season flurries blow in.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (313, 'enUS', 'Thank you for your help, $n. Our forces are deployed thinly these days and three mountaineers could be the difference between defending Kharanos and being overrun.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (314, 'enUS', 'Very well done! Veron will be so happy when he hears the good news. To kill Vagash is no easy task. I imagine one day you\'ll be fighting alongside King Magni\'s men on the Alliance Front.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (315, 'enUS', 'Great! I can\'t wait to use these in some of my recipes. There\'s one in particular I think it\'ll work great in...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (317, 'enUS', 'By Magni\'s beard, you sure did a lot of hunting!! Thank you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (318, 'enUS', 'Bellowfiz needs some of my Evershine? Hm...all right.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (319, 'enUS', 'Thank you, $n. It\'ll be nice to work our crafts without hearing so many growls and snorts outside the village.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (320, 'enUS', 'Yes, this will do just fine. I think I\'ll open a bottle right now and have a taste...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (321, 'enUS', 'This chest is waterlogged, and its siding broken. Many claw marks mar both the inside and outside of the chest...as if Murlocs have searched through it and stolen its treasure.$B$BBut tucked deep within the chest is a single Lightforge Ingot. Unfortunately, many more will be needed if enough metal to fashion a weapon is to be gained.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (322, 'enUS', 'That is quite a tale you have. And lightforge iron! Not for years have I worked with such a metal. It will be an honor to craft with it again. And you must return to me later with the story of your battle against the Necromancer.$B$BBut we get ahead of ourselves. First, your weapon against Morbent Fel...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (323, 'enUS', 'Your skill in combat, $n, can not be doubted. Your resolve may yet see us through to the end of Morbent Fel!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (324, 'enUS', 'Ah, you found them! Now let\'s get this lightforge iron hammered into something useful, eh?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (325, 'enUS', 'Ah, this will be proof against the fiend Morbent Fel! Well done!$B$BYou are ready. Morbent Fel will fall.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (328, 'enUS', 'Opening the strongbox, it\'s clear that this is where Herod keeps his more valuable coins and items.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (329, 'enUS', 'Thank you, $n. This note is the clue we need to root out the traitor!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (330, 'enUS', 'Oh, yes I can check that for you. Let\'s see...last week it was...$B$B...why, it was Private Merle scheduled with Thorsen. They had the same patrol times all of last week.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (331, 'enUS', 'It pains me to find out it was Merle who betrayed us. He has always been a brave soldier, and I had thought his heart was true. But it is a great relief to have found him out - with a spy like him among us...we would not last long against Colonel Kurzen.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (332, 'enUS', 'Ah, so you\'ve seen Renato? Well here you are, sir. A bottle of our special pinot noir. You\'ll not find its equal in all of Azeroth!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (333, 'enUS', 'It says here that Harlan\'s business is booming. That\'s good news, but I wonder why people have need of all that armor. I\'ve heard nothing of open war...is there something the nobles aren\'t telling us?$B$BWell, thank you for bringing the request. Here\'s your payment, and I\'ll see to it that Harlan gets his supplies.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (334, 'enUS', 'Oh, blast! I thought forgetting my kit would free me from work. Now I guess I\'ll have to help the Larsons with their sewing...$B$BOh, well. Fun will come later I guess.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (335, 'enUS', 'Most excellent! These are fine samples indeed!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (336, 'enUS', 'Ah, thank you for bringing me this fine looking stout, servant. Here\'s a copper for you. Now, out of sight with you and let me enjoy my drink!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (337, 'enUS', 'Oh my! This book hasn\'t rested on these shelves since before the rebuilding of Stormwind, years ago. It will have to be restored of course, it\'s in bad shape, but I would be honored to place it within our rare books collection.$B$BThank you, sir. Please accept this as payment for returning the book!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (338, 'enUS', 'You\'ve certainly saved me a heap of trouble, $n! I can\'t thank you enough. It shouldn\'t be long now until we see Master Nesingwary\'s book in libraries throughout the world!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (339, 'enUS', 'At last! Chapter I is complete!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (340, 'enUS', 'Chapter II is finally complete! Well done!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (341, 'enUS', 'Phew! Good work, $n. Chapter III is now back together in one piece!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (342, 'enUS', 'Superb! We now have Chapter IV ready to go!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (343, 'enUS', 'Metallurgy? Ah yes, we have many books on the subject, and are happy to aid Brother Kristoff with his speech! Now...which particular book was it the good brother needed?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (344, 'enUS', 'Mr. Sheaf is wondering where his book is? Well this is a tad embarrassing. I have the book, but I\'m still making my copy and I\'m not quite ready to give the original up!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (345, 'enUS', 'You need what?? Maybe you haven\'t noticed, but we\'re busy fortifying Lakeshire\'s defenses and repairing damage from recent orc attacks. If you need Rethban Iron Ore, you\'ll have to get it yourself!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (346, 'enUS', 'That\'s the one! Thank you, $n. May the Light shine brightly upon both your body and spirit!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (347, 'enUS', 'Oh, splendid! I can use this ore to make my dye, and then can finish copying The Stresses of Iron.$B$BThank you, $n!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (348, 'enUS', '...feel strength returning....$B$BWhere might be? Booty Bay? Oh my! I had the strangest dream...$B$B...the weakness has not completely left me....', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (349, 'enUS', 'Witch Doctor Unbagwa like Gorilla Fangs! For you I talk to spirits. Make Mokk the Savage come.$B$BYou protect Witch Doctor Unbagwa from other Gorilla-beast though or no Mokk for you!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (350, 'enUS', 'You\'re kidding? Shaw wants my help again? Of all the nerve...$B$BThe Defias Brotherhood, you say? Well, you\'re certainly the bearer of wonderful news, aren\'t you? Next you\'re going to tell me that Deathwing is still alive and attacking the city.$B$BAll right, tell me everything from the beginning. And don\'t leave out something you think might not be important. I need to know it all.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (351, 'enUS', 'You place the egg... distress beacon... inside the chicken... homing robot.$B$BHums and whirs are heard from inside the robot as it struggles to stand up. After the robot comes to life, Oglethorpe\'s voice is heard once more, but now from inside the robot:$B$B\"OOX-17/TN is working at least, but it needs major repairs! It is much too heavy for you to carry it... but I think I have an idea. Are you up for watching over it some more?\"', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (353, 'enUS', 'Aha! So Grimand finally finished my axe! I can\'t wait to try it against some troggs and kobolds!$B$BMany thanks, $n. It was a long distance to travel for this delivery.  Here are some coins for your efforts.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (354, 'enUS', 'Revenge has a sweet taste, don\'t you think? When you destroyed the Agamands, did you catch any hint of free will within them? I hope so. I hope they knew fear before they were smashed into oblivion.$B$BIt is a foolish hope, I know. But it is a hope I foster nonetheless.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (355, 'enUS', 'Coleman speaks well of you, $n, telling of your successful forays into the Agamand Mills.$B$BThe Forsaken have need of your talents once again...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (356, 'enUS', 'Very good. Your efforts do much to keep the Scourge at bay. A commendation will be sent to my superior, Executor Zygand.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (357, 'enUS', 'Very good, $n. I will study this book and determine the identity of that lich. You have served your Queen well.$B$B$B$BAstonishing! The book you retrieved belongs to no one other than Gunther Arcanus!$B$BGunther was a skilled Necromancer in life; he and I were friends and comrades-in-arms, before the Plague. And judging from his book he has grown even more powerful in death.$B$BHis skills would be a great boon to us.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (358, 'enUS', 'Expertly done, $n. The Scourge are mistaken if they think they can use those corpses against us, and the fluid you gathered from the Rot Hide slaves will be studied by our Apothecaries. It may have secrets we can use against them.$B$BAs I said, well done. But our struggle continues, and the conflict will surely offer you more chances to prove your worth to the Forsaken.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (359, 'enUS', 'Magistrate Sevren wants a report? Very well: the defenders at The Bulwark are holding the majority of the Scourge at bay, but some do slip through on occasion.$B$BWe have seen sporadic Scourge activity east of this station, with a concentration around the Balnir Farmstead.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (360, 'enUS', 'It is troubling to hear of the Scourge slipping past the Bulwark. The Lich King and his minions are ceaseless in their assaults.$B$BBut we will not give in. He will not take us again!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (361, 'enUS', 'Oh...my poor Thurman! I begged him to leave Agamand Mills with me and my father, but his family loyalty was too strong! And I knew it! I just knew it that Brand meant him harm!! Oh, curse this Plague, and the Scourge!$B$B<Yvette\'s face then smoothes and grows cold.>$B$BBut regrets are for the weak. As a Forsaken, I have new goals and love is not one of them. I would thank you for delivering this letter, for I had wondered what happened to my past love.$B$BBut that life is over. Forever.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (362, 'enUS', 'Thank you. Devlin\'s bones will rest nicely on my mantle.$B$BIf my cold heart can find warmth, it is in knowing that the Agamands are destroyed. They failed me and my family when the Plague came - now I swear I will smash their remains under my heel!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (363, 'enUS', 'Another of the walking dead, hm? Must have been quite a shock, waking up in the crypt with only the cold and Mordo to greet you...$B$BI see the confusion on your face. Let me try to explain our... situation... to you.$B$BWe have been freed from the control of the Lich King by our new leader, Lady Sylvanas. The Dark Lady guides us in our war against the hated Scourge and the holdouts of humanity who dog our every step.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (364, 'enUS', 'It is unfortunate that the Scourge cannot be brought into the fold, their large numbers would be useful in the battles ahead.$B$BBut they will not join us, so we have no choice but to destroy them.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (365, 'enUS', 'You have performed your bidding well, young $C. You are proving yourself to be quite an asset to The Dark Lady\'s army.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (366, 'enUS', 'My book?!? So you were the thief who stole it!$B$BBut...what is this new enchantment upon it...Bethor? He is in Lordaeron?$B$BIf he still exists, then perhaps he too escaped from the Lich King\'s grasp...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (367, 'enUS', 'You have done well, $n and I thank you for your efforts.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (368, 'enUS', 'The scales are perfect, $n. Exactly what I needed for this concoction.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (369, 'enUS', 'Ah, this venom will do perfectly, $n. Everything else has been added to my concoction and boiled down. Finally, I am ready to try this new deadly agent!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (370, 'enUS', 'Captain Perrine\'s death will undoubtedly please The Dark Lady. You have performed your duty well, $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (371, 'enUS', 'Captain Vachon\'s death will slow down the Scarlet Crusade\'s advancement in Tirisfal quite a bit. But other threats loom.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (372, 'enUS', 'You have done extremely well, $C. With ruthless fighters like you conquering in the name of The Forsaken our race is one step closer to defeating Arthas once and for all. I have a good feeling about you, comrade.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (373, 'enUS', 'Edwin VanCleef, you say? I would as much expect a letter from my dead grandmother... So you\'ve killed him then? Pardon me in saying, but I\'m somewhat surprised. He was a peerless fighter in his younger years. Let\'s see what he finds the need to write to me about after so many years.$B$B<Baros peruses the letter.>$B$BEdwin... I see the years haven\'t changed you a bit, an idealist as ever, and a romantic. He doesn\'t care who he hurts, $n. Revenge has consumed him. But then, I\'m not sure I can blame him.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (374, 'enUS', 'Varimathras would be proud. You serve your leaders well, $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (375, 'enUS', 'I appreciate your efforts, $n. May Sylvanas recognize your bravery one day. . . .', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (376, 'enUS', 'Thank you, $n. This armor should help you out.$B$BHopefully, it should do you more good than the last one to wear it...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (377, 'enUS', 'So Dextren Ward finally paid for his crimes against humanity? Good riddance to the scum I say. And cheers to you, my friend! Not only have you given the families of the dead the peace of mind they deserve, you sent a poignant message to those corrupt bureaucrats in the House of Nobles. Stormwind must rise to the needs of the people of Duskwood or we will break free from their tyranny.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (378, 'enUS', 'So Kam Deepfury finally got to feel what it is like to be on the receiving end of Death? Good. Serves the cowardly Dark Iron scum right. You have done well, $C. The victims of the Thandol Span attack were but mere victims in a world torn with war and unrest. Their families will have the peace of knowing Deepfury got the punishment he deserved. Longbraid\'s brother\'s death has been avenged.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (379, 'enUS', 'Excellent, the power source is yours. Now, it isn\'t completely designed for use in a science kit like that, so you\'ll only have a limited amount of time before all the data you collect will go kablooey!$B$BWhen you collect everything you need for whatever it is you are doing, bring it to me and I\'ll shut it down properly. You\'ll have at most two hours to get what you need to get done, done.$B$BIf you don\'t get it done in time, I can always sell you another power source to try again. You heard me - sell!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (380, 'enUS', 'Hmm, well, it\'s a start. It\'ll take a few weeks or months to fully clean out the infestation. After that, we\'ll have to get down there with some torches to burn away the webbing.$B$BYou\'ve done your duty well, $n, I\'m sure I can find something else for you to do.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (381, 'enUS', 'If only they listened to reason, eh, $n? Perhaps we could sit them down for reasonable discourse... ha!$B$BLight-blinded fools.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (382, 'enUS', 'Hmm... Let\'s see what you\'ve got...$B$B<He begins to rifle through the documents you recovered.>$B$BSome news of their exploits against us... Worthless. Scouting reports... New orders, \"Continue to build up a camp near the ruined city...\" Nothing we didn\'t know already... Ah, what\'s this? A map of the dispositions of some of their field commanders and agents! We can use this.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (383, 'enUS', 'Yes, this is very good. You\'ve done well in obtaining this information, $n. Hmm... names of the officers in the Tirisfal Glades...$B$BImagine the blow we could deal to the morale of those bloody zealots if we were to kill their leaders in one swift strike.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (384, 'enUS', 'The Malt is brewed, the Boars are dead$BAnd before all is done and anything said$BWe will have to fight for first dibs$BOn these savory Beer Basted Boar Ribs!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (385, 'enUS', 'Well now, these are excellent specimens you collected, $n. Good money for high quality materials, that\'s for sure.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (386, 'enUS', 'Targorr the Dread has finally met his fate. I for one am glad to hear the beast now knows what it is like to be on the receiving end of Death\'s ruthless grip. You have done well, $n. Sometimes the truest justice can only be found outside the courtroom and beyond the clouded vision of politics.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (387, 'enUS', 'Your efforts in The Stockade were valiant, $n. It\'s obvious this problem is bigger than the both of us. But you have performed well and for that I am thankful.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (388, 'enUS', 'So these bandanas -- filthy tokens of corruption -- are what my Mac had to die for? Such a waste. Such a tragic sacrifice. $B$BBut alas, I cannot return to the past. Just know, $n, that you have brought my family justice through your deeds.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (389, 'enUS', 'What in the Light do you want? Can\'t you see we have a crisis here?$B$BBazil Thredd?! Why would you want to speak with that fool? How am I supposed to know you\'re not one of his cronies, come down to help with his bloody riot? If all the blasted cells weren\'t open, I might throw you in one for a while!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (391, 'enUS', 'Without Thredd to lead them, hopefully the riots will be more controllable. We shall see.$B$BI must say, after a half hour, I hardly expected you to come out, but it seems I misjudged you.$B$BI\'d guess, then, that you didn\'t get much useful information out of him? But I know a thing or two that might be of interest to you about Thredd\'s activities.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (392, 'enUS', 'Hmm. Maelik? No, the name doesn\'t sound familiar and neither does this description. But it could be anybody! A former Stonemason, one of the Defias Brotherhood... Though if he is as well connected as Thelwater suggests, perhaps someone else might recognize him. Let me think.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (393, 'enUS', 'What? What\'s this? Yes, I think I do know who this man is, but his name is not Maelik. Assassins wear many names and many masks, discarding them as they lose their usefulness. Another alias known to be used by this man is Marzon; though even that is surely not his true name.$B$BRegardless, he is an assassin and I know a good deal about him.$B$BMarzon is currently under the employ of Lord Gregor Lescovar, a lord of shady reputation, even given the corruption of the nobility of Stormwind.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (394, 'enUS', 'He is dead, then? Your justice was swift, $n. I pray to the Light that I never become your quarry!$B$B$B$BI mean no criticism, $n. How could I, being the master of all assassins? But in truth, you have done the people of Stormwind a great service, and for that, I thank you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (395, 'enUS', 'It\'s good to see you, $n. I have heard that Lord Gregor Lescovar was found dead not long ago.$B$BIt would seem that you were successful in your mission. You have my gratitude that you have saved me, and many others, from VanCleef\'s revenge.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (396, 'enUS', 'You are to be commended for the service you have done for Stormwind, $n. If VanCleef had been successful in his machinations, he would no doubt have done considerable harm to the kingdom--something we must obviously avoid.$B$BTake this, $C, as a token of the my appreciation.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (398, 'enUS', 'Maggot Eye\'s dastardly acts have finally been avenged. Maybe your acts of valor will send a clear message to those who wish to bring harm to our people. On behalf of the town of Brill I thank you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (399, 'enUS', 'Oh, thank you, $n! It\'s of no practical use to me, but the sentimental value... Needless to say, thank you for bringing this to me... at some cost to your own well-being, not to mention the time it took to go all the way down to Westfall. You have my gratitude, and have this as a token of my thanks.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (400, 'enUS', 'You have my tools? Well done, lad, well done! I just broke my last fang-ratchet an hour ago and I need one of those to finish repairs on Pilot Stonegear\'s siege engine. You did a good thing by lugging those tools here, $n.$B$BHere, take these coins for your help.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (401, 'enUS', 'There we are. It\'s translated...$B$B<The air grows heavy, as if a thick, black curtain is drawn over the land...>', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (404, 'enUS', 'Well done. I regret not being there to see you smash those $R into rotting pulp!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (405, 'enUS', 'Ah, you are the agent sent by Magistrate Sevren. Then yes, your business with me is crucial.$B$BI trust Sevren has mustered someone suitable for my task...but we shall see.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (407, 'enUS', 'By the Light! Finally some food! Sweet, sweet pumpkin. . . .', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (408, 'enUS', 'Your actions struck a telling blow against the Scourge. And your victory will not go unnoticed - not by the Forsaken, nor by our enemies.$B$BContinue the struggle as we all do, $n, and one day we will drive the Lich King himself from Azeroth!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (409, 'enUS', 'You have defeated Lillith Nefara, proving you are an enemy of the Scourge. Incredible.$B$BI had believed I was the only Undead with free will, but you have shown me there are others who have broken the Lich King\'s domination.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (410, 'enUS', 'You place the Candle of Beckoning upon the table and light it...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (411, 'enUS', 'You convinced him to join us! You have done a great deed for the Forsaken, $n. The Dark Lady will be informed.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (412, 'enUS', 'These gyromechanic gears are perfect! These two tank pilots are well on their way to being back in the fight!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (413, 'enUS', '<Mountaineer Barleybrew tastes the Shimmer Stout...>$B$BOh. Whoa! This stuff really puts a shine in your eyes. I feel like I can see in the dark!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (414, 'enUS', 'Now, let me have a taste...$B$BMagni\'s Beard! This stuff gets your head turning! And it lightens the limbs, too.$B$BI feel like I can fly!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (415, 'enUS', '$n! You know that Shimmerweed you brought me? Well I used it in a few recipes I\'ve been working on, and one of them turned out great!$B$BHere, try it out and tell me what you think...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (416, 'enUS', 'Ugh! When I asked for those ears I didn\'t realize they\'d smell so bad!$B$BHere\'s your reward, $n. Well done.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (417, 'enUS', 'This is grim news, for Hildelve was a good friend. And I wish I could have been there with you when you found the beast, Mangeclaw!$B$BThank you, $n. Your deed will be remembered by the pilots of the Ironforge Siege Brigade.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (418, 'enUS', 'Trust me, it seems a lot harder than it looks, but...$B$BOh, I\'ve never known someone to take much interest in the making of sausages, but I\'ll give you the recipe just in case. Here you go, mind you eat them fresh!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (419, 'enUS', 'Here is the corpse of a dwarf -- mauled, frozen, and picked clean by mountain scavengers. Clutched in its hand is a book containing the roughly scribbled notes of Mori Hildelve. And around its frame, still in superior condition, is a Brigadier\'s Vest.$B$BThis grisly scene must have been where the Siege Engine pilot met his end.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (420, 'enUS', 'What\'s this? Ah, my brother\'s report. Hmm...$B$BI told him to be sparing with the use of the King\'s name, but he didn\'t listen. No harm, I suppose, though it\'s sure to ruffle some feathers in the Senate. Not that those weren\'t some feathers that needed ruffling! Hah!$B$BNonetheless, I guess that means that I should send my report on to Ironforge as well, but I must admit I\'m a bit behind. Bloody cold.$B$BI should have some work for you later, if you\'re interested.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (421, 'enUS', 'You have served me well, $C. It is obvious to me that you will be a worthy aide to me as I prepare to rid Silverpine Forest of Arugal\'s curse.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (422, 'enUS', 'This is precisely the spell I was in search of, $C. If your dedication to The Dark Lady\'s cause remains infallible you will find you have quite a future amongst The Forsaken.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (423, 'enUS', 'I hope I can acquire enough energy from such a limited sample. Perhaps I should have had you get more shackles.$B$BNonetheless, you showed great skill in collecting these, $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (424, 'enUS', 'Most excellent. The mine will provide superb resources for our forces as we spread throughout Silverpine and into Azeroth.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (425, 'enUS', 'Ah, thank you. You have my gratitude, $n.$B$BAnd I will include you in the report I make to my superiors.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (426, 'enUS', 'Your efforts in the war are felt among us, $n. I will not thank you for doing what is necessary, but the Deathguards of Tirisfal will remember your name.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (427, 'enUS', 'Most excellent, $C. Your skill in the art of combat is undeniable.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (428, 'enUS', 'You found us! And not a moment too soon...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (429, 'enUS', '<Renferrel takes the hearts.>$B$BIt is troubling to hear our Deathstalkers have failed their mission.$B$BLet us hope that these failures are rare.$B$BI\'ll give you the potion in a moment, but here is its recipe - if those Deathstalkers find themselves in need of future aid, then perhaps you can give it yourself.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (430, 'enUS', '<Quinn takes the potion and drinks it.>$B$BYes, I can feel it working. I will soon be able to travel.$B$BYou have done me a great favor, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (431, 'enUS', 'You grab one of the candles and place it in your pack.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (432, 'enUS', 'Well, that makes me feel a little better...$B$BBut I won\'t be happy until they\'re all dead! All of them!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (433, 'enUS', 'What a relief! I\'m glad to see that you were able to save so many miners, $n. I will make sure that their families are notified immediately.$B$BThe Senate will know of your role in winning back the mines.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (434, 'enUS', 'So it is done? Good. Thank you greatly for your discretion, $n. You\'ve proven worthy on more than one count. I\'ll be sure to keep my eyes on you and your progress within the Alliance.$B$BBe sure to keep your nose clean.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (435, 'enUS', 'Thank you for the aid, $n. I had feared Erland was overcome by the dangers of Silverpine. With your help, I see that he has not.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (436, 'enUS', 'Jern wants to know what the slowdown is, eh?$B$BIronband is putting together his latest progress report with details, but I can tell you this:$B$BIt\'s the Troggs!!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (437, 'enUS', 'I heard of your successful raid against the rot hides, $n, and this essence is proof of Nightlash\'s death.$B$BThe Scourge have lost ground this day. Your value to the Forsaken grows.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (438, 'enUS', 'Heaped within this boat is a pile of wrapped corpses. And looking east across Lordamere Lake, an opposing dock on Fenris Isle can be seen.$B$BThe fate of the corpses must lie on Fenris Isle.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (439, 'enUS', 'You say that this ring was worn by a woman, recently killed?$B$BThe only nearby source of such a body would be the mass graves in Tirisfal. The Rot Hide Gnolls must be taking those corpses to Fenris Isle!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (440, 'enUS', 'A Deliah did live in Brill not long ago. She was buried in the mass grave but if what you say is true, then the Rot Hide Gnolls must have stolen her body and taken it to Silverpine.$B$BThe Rot Hides, like all the Scourge, must be destroyed. I trust you are taking steps to make that happen.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (441, 'enUS', 'Yes, this ring was my wife\'s. Her fate was the fate of all who fail to endure these harsh times. I feel nothing for her death, nor for the story you tell of her body\'s abduction.$B$BSuch feelings have long since grown cold within me. But desire does still burn in my dead heart, $n.$B$BDesire for revenge.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (442, 'enUS', 'The death of Thule Ravenclaw was no small event, $n. Renferrel tells me that the energies released when you destroyed Thule were felt even as far as the Sepulcher.$B$BYour actions ring through the ranks of the Forsaken. Your value to us grows.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (443, 'enUS', 'Very good. This ichor will help us understand the nature of the rot hides, and may show us their weakness...$B$B...if they have one.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (444, 'enUS', 'This substance is laced with powerful spells, some of which I have not seen for a long, long time...$B$BOne moment -- I must invoke a spell of divination...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (445, 'enUS', 'Ah, how good of Apothecary Johaan to send his research. With so many new developments here in Silverpine, I nearly forgot about the findings coming out of Lordaeron and Tirisfal Glades. Which reminds me, I need to get those samples off to the Necropolis soon.$B$BBut you must excuse my rambling. Extend your stay in Silverpine, $n. We could use an able bodied $C like you around here.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (446, 'enUS', 'This scroll is laced with powerful magic. I\'ve never used such forces in a potion ... it will be a powerful mixture, and should serve you well should you attack Thule in his stronghold on Fenris Isle.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (447, 'enUS', 'How good of Apothecary Renferrel to send you to me, $n. These samples will indeed prove useful to the cause. I will begin studying their toxic and contaminable properties at once.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (448, 'enUS', 'If what Bethor says is true then Thule Ravenclaw is a force that must be dealt with, and dealt with soon.$B$BTo aid in this task, you may take something from our arsenal here...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (449, 'enUS', 'I hadn\'t accounted for the level of Scourge presence we\'ve seen in Silverpine Forest, nor for the troubles our Deathstalkers have had in their scouting efforts.$B$BYour aid to our Deathstalkers was invaluable, $n. And you will be commended.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (450, 'enUS', 'Apothecary Berard\'s journal! Why you are quite brave, $n. Now let\'s see what Berard was up to before he went mad. . .', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (451, 'enUS', 'Such a blow to the Royal Apothecary Society to lose Berard. But through your hard work and Apothecary Renferrel\'s diligent research Berard\'s studies will still be useful to The Dark Lady\'s plight.$B$BWe are on the cusp of something great here, $n. If you were not so skilled in the ways of combat I would try to recruit you for membership in the Society.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (452, 'enUS', 'You fought like a true disciple of the Dark Lady, $n. With the council dead, perhaps we can gain control of this town once and for all and drive Arugal from Shadowfang Keep. I salute you!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (453, 'enUS', '<Jitters sighs...>$B$BYes, I\'m the author of that book. You finally found me, eh?$B$BWell if you read that journal...then you only know the half of it!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (454, 'enUS', 'Hello, $C.$B$BYou\'ll be escorting me to the excavation site? That\'s good to hear... After Saean and his cronies, who knows what else the Dark Irons might have up their sleeves?$B$BAnyways, let me just make some final checks. Talk to me again when you\'re ready to go.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (455, 'enUS', 'Your report is full of bad news!$B$BI see the Dragonmaws are making a strong move into Algaz. Normally the Dragonmaws just hide up in the hills. What made them surge into Algaz is still a mystery...$B$BThank you, $n. And we\'ll probably want your help dealing with these orcs.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (456, 'enUS', 'You performed your duties well, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (457, 'enUS', 'You have proven your dedication to nature well, $n. A young $C like yourself has a promising future.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (458, 'enUS', 'I see you found me, young $R. Melithar is a wise druid to have sent you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (459, 'enUS', 'Your service to the creatures of Shadowglen is worthy of reward, $n.$B$BYou confirmed my fears, however. If the grells have become tainted by the Fel Moss, one can only imagine what has become of the Gnarlpine tribe of furbolgs who once lived here.$B$BShould you find yourself in Dolanaar, able $C, seek out the knowledgeable druid, Athridas Bearmantle. He shares our concern for the well being of the forest.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (460, 'enUS', 'Thank you, $n. I had feared the Gnolls destroyed my body. It is good to know they did not.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (461, 'enUS', 'There we are, it\'s open. Leave me here. I spent the best days of my life in this keep, and the thought of resting within its walls gives me comfort.$B$BBe sure to close the alcove tight - I don\'t want those Rot Hides to find me.$B$BOh, and take this. You\'ve earned it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (463, 'enUS', 'The roots and ferns speak well of you, small one. Although I have many names, I answer to only a few. But you may call me Rethiel.$B$BAnd you must harken...for these lands weep from festered wounds, and I would charge you to heal them.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (464, 'enUS', 'Well fought, $n. The Dragonmaws are not the same threat they were when they had dragons, but they\'re still fierce opponents.$B$BThe Dragonmaws are led by Nek\'rosh, son of Nekros, who was the former head of the Dragonmaws years ago. Nekros used powerful magic to tame the Red Dragons, but when that magic failed him...the dragons were not happy.$B$BNekros was killed, and his son has vowed revenge against the dragons who slew his father.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (465, 'enUS', 'You place some of the tinder on the catapult then strike a spark onto it.$B$BFlame erupts and quickly spreads!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (466, 'enUS', 'You got the ore! Well done, $n! I\'ll get someone to work with this right away. Let\'s hope he doesn\'t blow himself up!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (467, 'enUS', 'Hello $n. You\'re here about the task I have, eh? It\'s good to see a young $R like yourself whose blood burns at the prospect of adventure!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (468, 'enUS', 'Hello, $C! You\'re here for the scouting mission, are you? Very good!$B$BI hope your gear is in good condition, because this \"scouting\" mission has changed into something more messy...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (469, 'enUS', 'Hmm, this is from Einar, right? Hasn\'t delivered his skins to me in person for almost a week now. No doubt it\'s some differences with the wife!$B$BMaybe I\'ll have my wife go speak with Sida sometime...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (470, 'enUS', 'Oh, thank you, $n! I was afraid that it was lost for good!$B$B$B$BHm, I don\'t have much use for this anymore, but wash it off and I\'m sure you could make use of it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (471, 'enUS', 'Hmm... Don\'t suppose you\'d be interested in becoming my apprentice, would you?$B$BHah! No, I\'m just kidding. You\'re much too old, for one thing. Besides - from the looks of you, it would seem that you have other ambitions in life. Nonetheless, these are some fine skins you\'ve procured, and I\'ll be able to put them to good use.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (472, 'enUS', 'What brave $C sends word from Menethil? Ah $n, they call you.$B$BYou bring hope to a hopeless situation. \'Tis good news indeed that young Harlo survived. While fleeing he received a grievous wound. We had but little time to send him on his way on the back of the only surviving steed with the regiment.$B$BBut now we face a perilous situation. Dun Modr has fallen and we are but a shadow of the great regiment we once were.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (473, 'enUS', '$n. Valstag briefed me on your past success with the Dragonmaw Orcs at Dun Algaz. You\'re a $R of worth.$B$BAnd I have a task for you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (474, 'enUS', 'Stoutly done, $n. You have dealt a telling blow to the Dragonmaw orc clan. They may never recover. And the destruction of those catapults has made Menethil Harbor safe.$B$BYou are a $R of merit, and the kingdom of Ironforge rewards those who serve it well.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (475, 'enUS', 'Thank the forest spirits you are here! I knew Athridas would sense trouble and send help.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (476, 'enUS', 'By the stars! This is quite disturbing indeed!$B$BWe\'ve had troubles in the past with the corruption of the furbolgs, but such troubles should be long past since the cleansing of Teldrassil. I fear for all our people if the blessings of the aspects themselves cannot redeem our great tree.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (477, 'enUS', '<Prying open the lid of the crate, you find an assortment of carefully-packed objects. On the top of the crate lies a bundle of maps, yellowing with age, that appear to depict Silverpine Forest and other parts of western Lordaeron.$B$BUnderneath the maps is a mysterious-looking magical pendant.>', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (478, 'enUS', 'Maps and this... pendant, hm? Interesting.$B$BIt is obvious that the Dalaran wizards are interested in Ambermill for some purpose, but still, their aim is hidden to me.$B$BPerhaps Dalar will know something of their use.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (479, 'enUS', 'Dalar has found that the weaving is quite far along, despite the setback.$B$BA wizard of great power is guiding the magic energy. He must be stopped quickly.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (480, 'enUS', 'The magic of the wizards dissipates, and the ley energies fade into dormancy. Your efforts have been fruitful, $n, and we have dealt a major blow to our enemies, and to the overbearing wizards of Dalaran.$B$BWe must be vigilant, however, knowing that source of energy lies so close at hand... Plans must be made for the future.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (481, 'enUS', 'Interesting, more machinations of the Kirin Tor, it would seem. This is a powerful artifact. It was used frequently by powerful wizards of the Kirin Tor to channel magical energies.$B$BThat they are delivering so many to Ambermill indicates that they must be undergoing a project of some magnitude.$B$BI will see if I can pierce their shields and ascertain their intent.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (482, 'enUS', 'Ill news you bring, $n. If Dalar is correct--and I\'ve no reason to believe he isn\'t--we must move quickly.$B$BI must confer with him on our plan of attack. But in the meanwhile we will have to delay their progress.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (483, 'enUS', 'You have succeeded, young $C! Well done. And just in time I might add.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (484, 'enUS', 'Nice work you\'ve done here, $n. The quality of these skins is superb. I\'ll have to get them into the curing process as soon as possible. Oh, but before I get to that, there\'s some other work I need done that I think you might be able to take care of.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (485, 'enUS', 'You place the egg... distress beacon... inside the chicken... homing robot.$B$BHums and whirs are heard from inside the robot as it struggles to stand up. After the robot comes to life, Oglethorpe\'s voice is heard once more, but now from inside the robot:$B$B\"OOX-09/HL is working at least, but it needs major repairs! It is much too heavy for you to carry it... but I think I have an idea. Are you up for watching over it some more?\"', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (486, 'enUS', '$n you have proven yourself a most worthy and able $C. A $R who follows the path of honor as sure as you do is certain to find great glory in this world.$B$BUrsal\'s death brings safety to our once slumbering druids, but the source of his corruption yet remains within our forests. With time, I trust that you will find this evil and cleanse it for the sake of all of our people. You are destined for great things, young $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (487, 'enUS', 'You have served the good people of Dolanaar and Darnassus well, brave $C. As a member of the Sentinel force of Teldrassil, I salute your efforts.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (488, 'enUS', 'Ha ha! Well, done indeed.$B$BWho would have ever guessed that I, Zenn Foulhoof, would have a $R to do my bidding? Certainly not me! But so it goes... this beloved world of ours is full of surprises.$B$BThree cheers for the naive and gullible!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (489, 'enUS', 'Ah, what a sweet $R! I knew you would come in handy!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (491, 'enUS', '...what is this? Thule had our Woven Wand? I thought it was lost when our friendship sundered and he sided with the Lich King!$B$BHe must not have invoked its power, for if he had I would have sensed it. And it is good that he did not use it...$B$BThis is a marvelous find. You have my gratitude, $n, and will be rewarded.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (492, 'enUS', 'Ah, a drink at last. I\'m sure it\'s no Rhapsody Malt but I\'ll take anything to whet the old whistle at this point.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (493, 'enUS', 'Ah, most excellent. Apothecary Renferrel has my utmost respect. I am anxious to study his work.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (494, 'enUS', 'The raven\'s cry from the west doth beckon?$B$BSuperb news! Hillsbrad is ripe for attack. We can carry out our plan of destruction now. Varimathras will be pleased.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (495, 'enUS', 'You were sent by Shalindra? Very well then, you\'ll find I don\'t waste much time with idle chatter.$B$BMug\'Thol is an irritant, and we will deal with him quickly, thoroughly, and without incident.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (496, 'enUS', 'These specimens are perfect. Well, $n, you have managed to bring me the slightest hint of joy on yet another miserable day. Your efforts are appreciated.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (498, 'enUS', 'So the humans thought to turn our warriors into slaves, did they? We\'ll show them!$B$BIt would seem that they have taken Gol\'dir, our leader, to another location. We must find him and rescue him. But we currently have no clue where he is...$B$BWhen we have a lead, I will be sure to tell you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (499, 'enUS', 'Croak!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (500, 'enUS', 'Very good, $n. You must have hacked through a whole mess of ogres to get all these knucklebones.$B$BThat should keep them out of our hair. At least, for a while.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (501, 'enUS', 'Superb collecting, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (502, 'enUS', 'You pour some of the elixir into your hand and hold it before Stanley\'s mouth.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (503, 'enUS', 'Thank you, $n. I led the expedition from Durotar under the Warchief\'s orders, but as you can see, our plans went somewhat awry. They have held me captive here in this town, forcing me to work, hauling burdens, digging...$B$BWhat I have suffered is unimportant, however. There is important information you must deliver to whomever is leading our group now.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (504, 'enUS', 'Very good. Here is your reward.$B$BAnd when you were hunting those Warmongers, how did Alterac city look? It\'s in bad shape I would guess. Ogres are not known for their cleanliness.$B$BThankfully, your efforts will help keep them away from Southshore.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (505, 'enUS', 'Thank you, $n. Although they may still be lurking about, we hope that some of the thieves and footpads you slew were responsible for the last magistrate\'s death. But even if they weren\'t, the Syndicate is a taint on this land and a stain on the $R race. $B$BTheir deaths are welcome to Southshore.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (506, 'enUS', 'The nerve of those overbearing humans! We\'ll show them that the orcs shall never submit to their will!$B$BThis will now be our mission, in addition to recovering the trinket the Warchief desires. It would seem that by accomplishing one, we will also accomplish the other.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (507, 'enUS', 'No, no please don\'t hurt me! I never meant your people any harm, he\'s had me against my will! Please... spare me!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (508, 'enUS', 'It is strange that the Warchief would expend so much over such a small thing. I do not claim to understand him, but I will follow him, whatever his commands. You did well, $n, no doubt the Warchief will be glad to hear of your success.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (509, 'enUS', 'Ah, the horrible reek of Mudsnout Blossoms. At least those filthy Gnolls are good for something.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (510, 'enUS', '<Maleb reads the documents.>$B$BThese are very troubling, $n. From the information in these documents, and from the Syndicate\'s actions in the past, we must assume that band of cutthroats has waged war on Southshore.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (511, 'enUS', 'What is this? Hold a moment...let me take a closer look.$B$B...$B$BHm, I have never before seen this form of encryption, but I must admit that my knowledge in that area is by no means complete.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (512, 'enUS', 'Stripping the Syndicate of its nobility will remove its ability to act decisively, and reduce it to nothing more than a gang of thugs.$B$BYou serve your Alliance well, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (513, 'enUS', 'Apothecary Lydon sent you? Ah, why didn\'t you say so!$B$BDespite his gloomy disposition, Apothecary Lydon is one of our most dedicated and skilled members.$B$BThe good chap sends Mudsnout Composite! I know what our friend in Hillsbrad is up to now. And I like the way he thinks. The Dark Lady would be most impressed with the progress he is making toward the new plague.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (514, 'enUS', 'Ah, encryption! A specialty of mine. Let me see here...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (515, 'enUS', 'The excitement of this experiment has me feeling giddy! You have been such a great help, $n. Together we will spread death and disease to this land!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (516, 'enUS', 'If our scouts and agents continue to procure valuable information such as this, we shall have the Lich King\'s Scourge removed from our lands in no time.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (517, 'enUS', 'Finally! Now we are ready for action!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (518, 'enUS', 'The snow blooms red with the blood of the ogres, but we cannot cease our efforts until the spirit and numbers of the Crushridge have been crushed and ground into the dirt and snow.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (519, 'enUS', '...and I thought ogres smelled foul enough to begin with.$B$BWhen you bring me Mug\'Thol\'s head, I will have the four of them mounted on pikes above Alterac. A fitting monument to those who go against the Dark Lady\'s wishes.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (520, 'enUS', 'You have done your duty well, $n. Mug\'Thol\'s head shall adorn the highest peak of the ruins of Alterac, and the Deathguard will sweep the remnants of the Crushridge into the ground.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (521, 'enUS', 'So this is the relic the ogre used to duck the will of the Dark Lady...$B$BStrange that something so simple could wield so great a power. And to fall into as clumsy hands as those. Well done, $n, I will deliver the crown to Lady Sylvanas, and see that she hears of your accomplishment.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (522, 'enUS', '<Maleb inspects the contract.>$B$BThat explains the latest attack on Southshore. The Syndicate must be after me, just as they were the last magistrate!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (523, 'enUS', 'Thank you, $n. My head rests a little easier, knowing that villain lost his.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (524, 'enUS', 'You rest the tainted keg on the dusty rug.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (525, 'enUS', '<Maleb reads the letter.>$B$BThis is translated from a letter you found in a Syndicate encampment? This was not written by a thief, or even a noble of Alterac.$B$BIt appears The Syndicate has ties to another power, and I shudder to think what that might be.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (527, 'enUS', 'At ease, $C. I see you have returned from battle. My sources tell me you performed well on the Hillsbrad front.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (528, 'enUS', 'The grip of death is tightening around the town of Hillsbrad. The battle goes well.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (529, 'enUS', 'Mission accomplished, $C. And carried out quite skillfully, I might add.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (530, 'enUS', 'Ah, good. These hands killed my wife, Deliah. Revenge for her murder was my only care, and you were the instrument of that revenge.$B$BHere, take the ring you returned earlier. Deliah has been dead to me for so long, her ring is a bauble I no longer care to keep. Instead, I will keep these hands as a remembrance of my friend\'s betrayal.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (531, 'enUS', 'Thanks for helping me out with my little plan, $n. I\'ve been itching for months to get back at that snooty, stuck-up boy, and I\'d say we did a pretty good job! Here, have this, you\'ve earned it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (532, 'enUS', 'News of your glories in Hillsbrad traveled fast. You have done well, $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (533, 'enUS', 'Finally, we know where Gol\'dir is being held. The camp in question is not too far from here, and hopefully they will not be as well prepared as they were when they captured us the first time.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (535, 'enUS', 'Given my impending death, I think I can safely say that this is the sweetest tasting thing ever... Here, take this, won\'t do me any good when I\'m dead.$B$BWell, get on with it!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (536, 'enUS', 'I hope this will be enough to convince Redpath that I\'ve--ha!--been carrying out his orders and ridding Southshore of the terrible and horrible menace of the dreaded murlocs! For there could be no greater evil than the ravages of overgrown fish!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (537, 'enUS', 'Well done, $n. The Argus Wake does not sound like a group we want running the Syndicate.$B$BAnd although you may have destroyed their leader here, Nagaz was merely the servant of another. The Argus Wake might run deep through the continent of Azeroth.$B$BOr even the world.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (538, 'enUS', 'Ah yes, I remember Milton. A bookworm, no doubt. Bless him.$B$BAnd he\'s right. The Ruins of Alterac still hold stores of knowledge in danger of being lost!$B$BOgres now dwell in Alterac, and they don\'t care much for higher learning.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (539, 'enUS', 'The Dark Lady will be pleased when she learns that she has a new mine from which she can arm her soldiers.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (540, 'enUS', 'Oh, you got them! Splendid! I must begin studying the Arm of Gri\'lek immediately!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (541, 'enUS', 'Your actions on the battlefield are worthy of the highest praise.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (542, 'enUS', 'My oh my! Look at these! I can\'t believe they survived the ruination of Alterac. And all those ogres too!$B$BThank you, $n. Thank you so much! You have helped preserve human knowledge, and you add to the grandeur of the Royal Library of Stormwind.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (543, 'enUS', 'Ah, this is splendid. This will have to be cleaned of course, but you really outdid yourself, $n. I will have someone prepare the tiara and place it within our collection posthaste.$B$BAs I\'m sure you will agree, our gratitude is as grand as our collection.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (544, 'enUS', 'You have done a great service for the Forsaken, $n. These artifacts have been sorely missed.$B$BAnd it is good to hear that Kegan and the other betrayers have found justice.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (545, 'enUS', 'Very good. Those ruins should now be safe enough to search, if one is careful.$B$BThank you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (546, 'enUS', 'Precious skulls! So beautiful! I owe you as promised, $n!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (547, 'enUS', 'You found it!$B$BAh, I feel so complete with my precious sword back in my hand. Many thanks to you!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (549, 'enUS', 'Excellent, $C. At this rate, the Syndicate pest will be rid from the land in no time.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (550, 'enUS', 'So you are the infamous $C who struck such a decisive blow against the Alliance forces in the Hillsbrad Foothills? Word of your glorious exploits traveled quickly to the Undercity.$B$BYou have quite a future in the Horde\'s ranks if you continue to show such leadership and expertise in battle.$B$BWhile our primary threat is the Lich King\'s Scourge army descending from the north, the humans continue to impede our battle plans. You, however, have shown that the humans can and will fall.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (551, 'enUS', 'More encrypted notes?$B$BI wish I had the talents to decrypt all the strange bits of writing that trickle down from Alterac. But I\'m afraid I lack the knowledge.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (552, 'enUS', 'You found it! Most excellent, $n!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (553, 'enUS', 'A great rumbling emanates from the ground.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (554, 'enUS', 'A wise decision. I don\'t believe there is a scholar other than myself with the skills to translate this scroll. And if its contents prove interesting, fear not. I will send for you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (555, 'enUS', 'Everyone will be so pleased: Chef Jessen\'s Soothing Turtle Bisque is served once again!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (556, 'enUS', 'These will do nicely. Although it looks like their magic is faded, it would be best if the Magus\' initial experiments were on inert specimens such as these.$B$BThank you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (557, 'enUS', 'You found them. I trust the elementals from which you removed these bracers were amenable?$B$BHm...death has not only stopped my heart, but robbed me of my comedic timing, it seems.$B$BWell thank you, $n. You serve the Magus, and hence the Dark Lady, well.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (559, 'enUS', 'Oh for the Love of the Light... they\'re dripping on the ground.$B$BCan\'t stand touching those things, myself. Fish are all well and good, but when they grow legs and start walking around... Haven\'t eaten a fish in weeks, let me tell you, and in Southshore, that means you aren\'t eating much.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (560, 'enUS', 'Farren\'s errand boy, are you?$B$BIf you assisted Farren in procuring these heads, then thank you. Southshore needs hard workers and effort to maintain its protection.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (561, 'enUS', 'The naga, eh? Redpath must have just received the same information as me... Ha! He\'ll not receive a promotion from my toil. Let me explain.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (562, 'enUS', 'With the naga put down in Southshore, I\'ll be a shoe-in to be reassigned. I\'ll get to see Stormwind again... get away from this Light-forsaken port.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (563, 'enUS', 'What\'s this? Ah... so my attentive pupil has applied himself to the problem of the naga in Southshore. Thank you for bringing this to me, I will certainly take it into account when I review personnel assignments in the next month.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (564, 'enUS', 'Ain\'t that great news! Should-a gone and found me a regular ol\' war hero from day one to take care of them beasts. You\'d-a made your pappy proud, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (565, 'enUS', 'Now it is time for the great Bartolo to work his magic! I require no wizard\'s staff, no mage\'s rod. For I, the great Bartolo, work magic with a mere needle and thread!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (566, 'enUS', 'Productive work you do, $n. With Baron Vardus dead, the number of Syndicate leaders grows smaller. Their resistance and irritation will be short-lived.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (567, 'enUS', 'You are quite the bounty hunter, $C. Well done.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (568, 'enUS', 'Your duty was performed with honor, $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (569, 'enUS', 'Well done. You have served the Warchief with honor.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (570, 'enUS', 'Your knowledge of the jungle is strong, $n. These specimens will be perfect. I will thank the spirits for sacrificing such fine beasts for the cause of the Horde.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (571, 'enUS', 'A most ideal sinew, $n. Your efforts will pay off soon.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (572, 'enUS', 'Most excellent. I will prepare these at once.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (573, 'enUS', 'With this Holy Spring Water the ceremonial choker is ready for enchantment.$B$BWear this with pride, $n, in the name of the Warchief and in defense of the mighty Horde.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (574, 'enUS', '$n. Sergeant Yohwa trusted your abilities, and I trust her. The defeat you brought to Kurzen\'s forces is almost too much to hope for. I had feared we rebels were alone, and alone we could never stand up against the Colonel\'s brilliance.$B$BPerhaps hope of our survival yet remains.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (575, 'enUS', 'These skins should do. Their quality isn\'t too important, given that they\'ll make up the inside layer of the boots, but it\'s still important that they\'re comfortable.$B$BIf they\'re a bit too rigid, I\'ll use some tiger blood to soften it up. Tricks o\' the trade, you know?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (576, 'enUS', 'Shiver me timbers and timber me shivers! Ye found me beautiful eye! Let\'s see if she still fits.$B$B$n ho! Blimey, I can see!!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (577, 'enUS', 'My boots... they\'re like sausages, $n. Look great, taste great. I tell you they\'re filled with nothing but the finest ingredients and meats, and you eat it, and you love it.$B$BIt\'s safer--and more enjoyable--if you just trust me, and don\'t ask too carefully what\'s really inside.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (578, 'enUS', 'So the stories were true! Incredible!$B$BHowever, we will need to figure out a way to defeat the curse of the Stone of the Tides, if we are to retrieve it. The curse is magical business, we\'ll need one of those wizards to help us out.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (580, 'enUS', 'Shiver me timbers! Ye found the Cap\'n\'s grog!$B$BI owe ye big time, friend.$B$BSeems like a storm is a brewin\' outside. Maybe I\'ll just keep warm with one o\' these here bottles while we wait for it to pass....', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (581, 'enUS', 'These are good tusks, $n. You prove that you have the strength to hunt the Bloodscalps.$B$BAnd that is good, because you are not done hunting them.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (582, 'enUS', 'Thank you, $n. I will inspect these heads to ensure poor Yenniku was spared the headhunter\'s axe.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (583, 'enUS', 'Jolly nice to make your acquaintance, $n. Haven\'t seen a $C around these parts for some time.$B$BI\'ve one purpose in being here: to hunt a great and elusive beast. Seeing that you\'re a hero of sorts I am willing to let you take part in the hunt, but you must first prove yourself.$B$BI shall teach the ways of raptor killing to you. Ajeck will train you with tiger hunting. And Sir Erlgadin is a master of panther tracking.$B$BProve your skill and I\'ll let you in on the big hunt.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (584, 'enUS', 'You place the heads of Gan\'zulah and Nezzliok within the cauldron...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (585, 'enUS', 'Yes. These will serve me well. When my spirit is freed, I will enter the Nether with three fine servants!$B$BYou are a credit to your $R clan, and you have appeased me.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (586, 'enUS', 'I do not want to believe it, but I cannot deny what you have done. And my rage eases, now that Ana\'thek is defeated. $B$BYour might will drive you to great deeds, $n. But one day...I will see you in the Nether and we will again match strength.$B$BI will be waiting.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (587, 'enUS', 'Good show, $n! And just before I need to ship out too! Can\'t thank you enough, old friend.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (588, 'enUS', 'Zanzil the Outcast??$B$BIf Yenniku is held by that troll, a vile and powerful witchdoctor, then I fear that only a faint hope remains. Faint. And tiny as the smallest insect.$B$BBut still, it is hope.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (589, 'enUS', 'You have done well, $n. These shards are well formed. Already I hear them sing to me.$B$BAnd while you were gone, I spoke with the spirits of the jungle. I know where the Mind\'s Eye rests...$B$B...And who guards it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (590, 'enUS', 'Incredible! What skill!$B$BThought you\'d be easy prey, $n. Got me good, you did.$B$BHere\'s the coin I promised... not as much as I said it\'d be, but that\'s on a count o\' me lying to ya in the first place.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (591, 'enUS', 'You retrieved the eye! Your might is the stuff of legends, $n!$B$BAnd in getting the Eye, you must have defeated Mai\'Zoth and sent him to the Nether. That is why the spirits would not mention him before.$B$BIt is good that he is gone from the world of flesh and bone, but now that he is in the realm of spirits...I fear he will begin a new reign.$B$BBut that is a worry for another time. Now, I must perform a ritual to bend the Mind\'s Eye to our needs...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (592, 'enUS', 'You\'ve done it! You freed Yenniku from the will of Zanzil, and completed the mission I had thought was so desperate.$B$BOur tribe will remember your name in songs, $n, and we will etch your tale in stone so that it will live forever.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (593, 'enUS', 'You place the Soul Gem upon his forehead. He shudders as his soul is sucked in, leaving his body, a mindless husk, behind.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (594, 'enUS', 'Have you come to free me?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (595, 'enUS', 'On top of the barrel, you discover a map with some hastily written text on it along with some coins.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (596, 'enUS', 'Ah, very good. I will boil these, and leech them, and the spirits of their owners will weaken as I render their necklaces to paste!$B$BHere, $n. This will repay my debt to you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (597, 'enUS', 'Sure, I remember you, $C. Back from scouting I see....', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (598, 'enUS', 'Your strength is great to have gained these necklaces, $n. The Skullsplitters fall to you like young saplings stomped to earth by a Basilisk!$B$BYour deeds are a boon to us.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (599, 'enUS', 'Bloodsail Buccaneers attacking Booty Bay? They might as well throw themselves off the Overlook Cliffs. Nonetheless, by bringing me this information you have done my fleet of swashbucklers a great service. The Blackwater Raiders thank you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (600, 'enUS', 'Great! Wonderful! I can get a bundle for these!!$B$BThat Venture Company sure picked the wrong jungle to mine in, didn\'t they? We\'d all be in better shape if they moved out of here.$B$BThank you, $n. You\'ve done a good deed this day.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (601, 'enUS', 'Hm, I haven\'t the eye to examine these bracers to see if any of them will be of any use, but I\'m sure at least one of them will be able to help. Take them to Dalaran and have a mage look at them.$B$BAfter all, I can\'t very well leave Booty Bay just to chat with those tiresome old mages.$B$BNot that I\'m worried anyone would try to elbow their way into being the new baron. No one crosses Revilgaz and lives to tell of it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (602, 'enUS', 'The Stone of the Tides? Most interesting. It might surprise you to know that I wrote a tome--several years ago--pertaining to the legends of the Gurubashi Empire. I do know quite a bit about the Stone.$B$BAt least one of these bracers appears to have some magical residue I can analyze.$B$BLet\'s see what I can see...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (603, 'enUS', 'My dagger? What are you, some sort of comedian?$B$BWhat? Do you just want me to repeat the story of how I\'ve gone from the best knife fighter in Booty Bay to the laughing stock of piratedom!$B$BI lost a knife fight to one of the Bloodsail Buccaneers--bunch of right fiends they are!--and he took my blade as spoils.$B$BIt\'s not as though I can get by as a knife fighter with insults and a sharp tongue alone...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (604, 'enUS', 'Perfect, $n. Now that we know their plan, it\'s just a matter of time.$B$BAnd to think the fools have dropped anchor just off the cape....', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (605, 'enUS', 'Aha! These will do nicely, $n. Yes, quite nicely!$B$BHere is your cut, and...it was a pleasure doing business with you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (606, 'enUS', 'Ack! Where\'d you get these? No, don\'t tell me. I don\'t want to know!$B$BWho sent you? Sea Wolf?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (607, 'enUS', 'Heh heh, well done! And when you showed Shaky those giblets, how hard did he shake?$B$BHah! He\'s so easy to read, when I play cards with him I can\'t help but win!$B$BThanks for your help, $n. Now it\'s your turn to get paid...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (608, 'enUS', 'Ah, what a great day for the Blackwater Raiders! Three cheers to you, $n!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (609, 'enUS', 'Great! I can get a good price for these, especially for what\'s inside Maury\'s foot! Thanks much, $n!$B$BI hope those three learned their lesson: don\'t bet something you can\'t afford to lose.$B$BAnd maybe it\'s too late for them, but...they should be careful of who they gamble with.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (610, 'enUS', 'You beat Duncan? I guess you aren\'t as wet behind the ears as you looked at first glance.$B$BI suppose that also means I shouldn\'t challenge you to a fight any time soon!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (611, 'enUS', 'Amazing! The Stone of Tides! It shall have a place of prominence in my collection of ancient troll artifacts. Did you see how it almost seems as though a tide is trapped within its surface? Magnificent!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (613, 'enUS', 'You found it! Thanks a million, $n. You\'ve been a big help to me!$B$BHere you go - you earned this!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (614, 'enUS', 'You found it! Oh happy day, this is! Thank you, $n. Getting back my chest cools some of the fire in me.$B$BBut my revenge isn\'t complete...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (616, 'enUS', 'Interesting. Some time ago, I seem to remember reading a book once that suggested...$B$BAh, of course, a troll legend it was. If what Krazek reports is true, perhaps I will soon be able to add the Stone of the Tides to my collection of ancient artifacts and relics.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (617, 'enUS', 'Well done, $n. 10 stalks of akiris reed wasn\'t too much a challenge after all, huh? No offense to the naga, but hey, everybody\'s gotta make a living.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (619, 'enUS', 'You pile food and drink upon the raft...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (621, 'enUS', 'Ah, very good! Here is your pay.$B$BAnd here is a little something extra... for your discretion.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (622, 'enUS', 'It\'s fixed! This is great, now I can get dinner started! Thanks so much, $n.$B$BI hope you were okay in Booty Bay. That place can get pretty rough, and... I heard rumors that the goblins will sometimes drag drunk sailors to their slave boats and take them to who-knows-where... to do who-knows-what...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (623, 'enUS', 'What?! I got a lot better things to do then talk to the likes of you, $C. Make it worth my while or get outta my face.$B$BHuh? Bloads sent ya, huh? Well, whaddaya got for me?$B$B$B$BNot bad, not bad at all. Another bundle will make my client quite happy.$B$BI suppose yer next question\'s gonna be about money, huh, $C? Of course it is!$B$BHere, take this coin and some advice: get off my docks.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (624, 'enUS', 'The soggy parchment contains the next clue....', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (625, 'enUS', 'You find a musty scroll.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (626, 'enUS', 'You open the chest to discover....', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (627, 'enUS', 'Ah, splendid! That was a big favor you did for me.$B$BHere is your payment...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (628, 'enUS', 'Great! Thanks to your help I\'ll have this order filled and sent off to Director Riddlevox on the next boat to Undermine. Ahead of schedule, even!$B$BFor all your hard work, here, I made some extras, so take a pair. It\'s on Drizzlik.$B$BAnd remember, there\'s no boot so fine as one of Drizzlik\'s Excelsior line!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (629, 'enUS', 'This is good. When I return to Orgrimmar I will place this shard in a sacred place. It will remind us of the might and honor of Gri\'lek.$B$BThank you, $n. Your deeds mark a growing friendship with the Darkspear tribe.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (630, 'enUS', '$n, you have saved me from a great dishonor. If it was within my power, I would offer you a place among my tribe. But for now, all I can only offer you is this token for the courage and nobility you have shown.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (631, 'enUS', 'You search the tiny, dwarven corpse. Engraved into the helmet you can clearly read: Ebenezer Rustlocke.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (632, 'enUS', 'Oh my, the news is grave....', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (633, 'enUS', 'The remaining bridge is saved! You have done a great service to King Magni and the people of Khaz Modan.$B$BNow if we can only hold out long enough for reinforcements to show...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (634, 'enUS', 'My condolences go out to Longbraid and his brave soldiers. The news of the Thandol Span tragedy and the fall of Dun Modr hangs heavy in my heart.$B$BBut Stromgarde has fallen! Hence our presence here at Refuge Pointe. We are faced with retaking our own great city.$B$BHonor and duty require that we do not leave the Highlands until Stromgarde raises the mighty banner of the King once again. I will send word to Longbraid that he is on his own...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (635, 'enUS', 'This crystal pulses, as if a living force is trapped within it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (637, 'enUS', 'You say you saw my Sully pinned beneath the icy waters? Say it isn\'t so!$B$BLet me read what he wrote....', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (638, 'enUS', 'How you doing, $C? The task set before us by Vol\'jin will be a difficult one, and any assistance you might provide will be appreciated and necessary.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (639, 'enUS', 'Excellent, mon. That\'s a good start to collecting the sigils we\'ll need to remove Trol\'kalar from Trollbane\'s tomb.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (640, 'enUS', 'Ah, you need me to reconstruct this sigil? It shouldn\'t be too difficult... all the pieces seem to be here and the magical structure will be easy to reform.$B$BYou might want to close your eyes.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (641, 'enUS', 'We\'re halfway to being able to remove Trol\'kalar from Trollbane\'s tomb. We are not exactly sure where the third sigil is, as its holders seem to move around Arathi quite a bit, but I have faith that you\'ll be able to find it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (642, 'enUS', 'Ah! You activated these shards. Already I feel more myself!$B$BBut alas, I am still far from free...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (643, 'enUS', 'Losing their cavalry leader will prove a large blow to the Stromgarde defenders. Better yet, it means that we are only one sigil from being able to have Trol\'kalar for ourselves.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (644, 'enUS', 'Quite a day this is, $C. The blood of Trollbane will no longer haunt my people, and the sigils needed to take Trol\'kalar are in our possession. Soon, very soon, we will strike against the hated trolls of Zul\'Gurub.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (645, 'enUS', 'Matching the lines on the face of Trollbane\'s tomb with the glowing lines of the Sigil of Ignaeus, a flash of light consumes the Sigil, and the legendary sword Trol\'kalar is released from the stone hands\' grip.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (646, 'enUS', 'For generations, the forces of Strom and Arathor ravaged the trolls. Now their greatest weapon is in our hands. Our inching progress in the steamy forests of Zul\'Gurub will be no more.$B$BYour assistance has been great, $n, and the Darkspear trolls are indebted to you.$B$BI will be sure that the sword is delivered to Nimboya at Grom\'gol.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (647, 'enUS', 'Ah, nothing like Foggy MacKreel\'s Moonshine to warm you up on a brisk day! And here I was thinking the little bugger was going to be overdue on his bills again. Thank you, friend! Many thanks indeed!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (648, 'enUS', 'Oh, YOU\'RE the great savior of OOX-17/TN! I cannot even begin to express my thanks for your assistance! My homing robot series needs more developing, but your recovery has saved me countless hours of rebuilding time, as well as many gold pieces in rebinding costs!$B$BPlease, take your choice of any of these items I have. Hopefully, you will make good use of one of them. Thank you again for your generous assistance, my robots and I are in your debt!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (649, 'enUS', 'Oh yeah, about the ripple dad wants! He wants to sell the ripple to a tavern here in Orgrimmar, but we\'ll get more money if it is in its original packaging. I know someone who can do that, and he\'s in the Hinterlands where the ripple is. But he\'s, um, a high elf.$B$BWell, he\'s a good high elf, as he\'s not a part of the Alliance. Well, he\'s not so much a good high elf as he is a drunken high elf. He loves, and I mean LOVES the hooch.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (650, 'enUS', 'Ah, so you know Malton? He is an old drinking companion of mine. You see, I have found something that transcends the boundaries of artificial treaties and alliances - alcohol. Were we all to slake our thirsts on its blissful qualities, we all would be in a much better place spiritually.$B$BAlas, something tells me that your visit here is not to learn of alcohol\'s redeeming values, now is it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (651, 'enUS', '<Myzrael speaks through the stone.>$B$BYou have done it! You opened the Stones of Binding! I can feel the shackles about me loosen, and freedom draws ever closer.$B$BYou are a noble $C, $n. My rescuer!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (652, 'enUS', 'You place the rod in the keystone. And a shudder is felt beneath you...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (653, 'enUS', 'The Lady told me of your coming, $C. You are welcome in my abode for you, like I, serve a higher cause.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (654, 'enUS', 'All right then, it will take me just a second to remove the power source. After that, you can do with it whatever you like now without having to worry about losing the data inside.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (655, 'enUS', 'Ah, Gor\'mul you speak of. Yes, from what he says, it seems he has never recovered from the death of Doomhammer.$B$BGor\'mul was a captive here when Thrall and Orgrim fought to free the camp, but as the earth shook and our brethren escaped, he was left behind. He wandered alone and lost for years without direction or hope, until we returned to establish ourselves here in Hammerfall.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (656, 'enUS', 'As the eldritch shackles are placed around the Shards, an unhuman wail is heard from far below. The scream is followed by a roar.$B$BThe Shards of Myzrael no longer invoke feelings of warmth and security as they once did. Now they pulse with an open menace... as if the thing beneath them holds ill will toward the surface.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (657, 'enUS', 'If it brings Phin peace, I\'ll destroy every single one of the Forsaken personally. He\'s done so much for us since the plague. We never thought we\'d find friends or family again.$B$BWhen you\'re prepared, come let me know. We\'ll get started right away.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (658, 'enUS', 'Excellent! $B$BLet\'s see what this is about...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (659, 'enUS', 'Try and stay as quiet as possible, traveler.$B$BOrcs don\'t take too well to being spied on, and there is more to this farm than meets the eye.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (660, 'enUS', 'You\'ve done a great service for us, $n. Thank you.$B$BI hope Kin wasn\'t too much of a handful down there. She\'s very friendly, but can be just as deadly when you\'re her enemy.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (661, 'enUS', 'So, the two troublemakers are all right after all? Haha, I\'m glad to hear it. I thank you for bringing me news of their whereabouts and status--it pleases me greatly that they are still alive; the two of them are irreplaceable as friends and traveling companions.$B$BBut... it\'s more important I thank you properly for your help. Here. Take this along with my group\'s thanks.$B$BWith any luck, we\'ll figure out what the Forsaken are up to, and put an end to it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (662, 'enUS', 'Knowing this information is secure will bring some ease to the command in Booty Bay. Well done, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (663, 'enUS', 'Ahoy, matey.$B$B\'Twas a horrible mess. Seahorn will have my ship if he finds out what went down.$B$BOne of the Professor\'s contraptions indicated the lost treasure was near this cove. I brought the Harbinger in to dock and signaled for the Spirit of Silverpine and Maiden\'s Folly to follow suit.$B$BThen the sea rumbled. The tides lowered and the two ships crashed into the reef.$B$BIt\'s not just a reef. Something\'s down there....$B$BHelp my crew out and you\'ll be rewarded handsomely.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (664, 'enUS', 'Nice work out there, $n.$B$BThose scum deserved what they got. While nothin\' will ever bring me ship or me crew back, the death of those naga gives me some solace.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (665, 'enUS', 'The goggles work perfectly now! Well done, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (666, 'enUS', 'Most superb, $n! The Captain will be very pleased! And just wait until word gets back to Fleetmaster Seahorn!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (667, 'enUS', 'We survived the attack!$B$BWe could not have done it without you, $n.$B$BWe\'ll be heading out as soon as the tide rises. If you weren\'t such a promising hero I\'d offer you a place on my crew. But I can tell you have bigger fish to fry.$B$BEven if you are a puny $R....', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (668, 'enUS', 'So the little gnomes proved their worth after all? I knew we kept those ankle biters around for a reason!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (669, 'enUS', 'Ah, leave it to good old Shakes O\'Breen! I knew that he was the right Captain for the mission!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (670, 'enUS', 'Thank you for making such a long and perilous journey. You have the gratitude of the Blackwater Raiders.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (671, 'enUS', 'It will take some time for me to ascertain the true intentions of the Syndicate, but their obvious association with dark, demonic magic troubles me. I will tell you when I know more.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (672, 'enUS', 'Excellent. The spirits of the raptors that gave these eyes will lend strength to the bearer of this amulet.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (673, 'enUS', 'My restless nights will turn to peaceful slumber when I have destroyed this orb. Thank you, $n. It is a chilling reminder of the terrible power the demons held over the orcs before Hellscream released them from their curse.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (674, 'enUS', 'What is this? Polished bone. What use have I for polished bone? Will a trinket restore the Horde to its glory?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (675, 'enUS', 'It did not work, I see. Nonetheless, I feel that showing him the power of the shamans will eventually return Gor\'mul to his once proud self. The amulet\'s power was not great enough to show him our newfound power.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (676, 'enUS', 'So, you think you\'re strong enough to help Hammerfall? So be it.$B$BTallow either sent you to that ogre mound in the hopes that you would kill yourself or he thinks you\'re strong enough to aid us. Either way, you\'re here now and I am in need of more soldiers in our fight against the constant attacks.$B$BIf you plan on staying in Hammerfall then get some rest, and come speak to me again when you\'re ready. There\'s no shortage of fighting to be had.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (677, 'enUS', 'The signs of war are upon your garments, and sweat drips from your brow. Good, very good indeed.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (678, 'enUS', 'You are as strong as a kodo, and your lust for battle does your people proud.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (679, 'enUS', 'You do your people proud, $n. The threat has been greatly lessened, and I am sure we will begin to thrive here in the Highlands.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (680, 'enUS', 'You have done me a great service, $n. Know that my husband\'s life and the safety of Hammerfall has been protected for another day because of your deeds. Please take this as a reward for your courage.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (681, 'enUS', 'Very good, $n. With the deaths of those Syndicate members we should have an easier time receiving supplies from Southshore.$B$BPerhaps our cause yet has hope.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (682, 'enUS', 'Thank you, $n. We will inform the families of these fallen soldiers that they were killed in battle.$B$BIt will be grim news to bear, but it is better than the uncertainty of a missing loved one.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (683, 'enUS', 'It will take but a moment for me to peruse this letter...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (684, 'enUS', 'You took down Marez Cowl? Well done, $n. Well done indeed!$B$BMarez menaced our Stromgarde defenders, and it is rumored she was an emissary between the Syndicate and some other, darker power. We are glad to see that she\'ll no longer ply her powers against us.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (685, 'enUS', 'Falconcrest and Otto? Wonderful!$B$BHere is your reward, $n. I am happy to pay it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (686, 'enUS', 'So King Magni Bronzebeard himself sent ye?$B$BMy, my! This must be important!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (687, 'enUS', 'Hello. Hello, hello hello hello hello HELLO!$B$BI\'m sorry - wait, no I\'m not! Don\'t be rude!$B$BOh no...that was crazy talk.$B$BBut...sometimes being crazy is my only solace.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (688, 'enUS', 'Welcome, $C. The Lady has informed me of your coming, and if we are to bring her back to the surface then we have much to discuss...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (689, 'enUS', 'Perfect! Better than I could have imagined!$B$BAnd I have quite an imagination you know...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (690, 'enUS', 'So, Malin sent ye? I don\'t care if Muradin rose up from the dead and whisked ye here on a dragon. I\'m busy! Talk to Kryten if you need somethin\'.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (691, 'enUS', 'Incredible! I can see why Malin asked you to come join us--very well done indeed!$B$BAt this rate, Skuerto and I will be headed back to Stormwind in no time at all.$B$BI believe my dwarven companion is back from scouting Boulderfist Hall; go speak to him when you\'re ready.$B$BIt looked as though he might have had a bit of a scuffle with the ogres. Don\'t mention that to him, though; he\'s very sensitive about criticisms to his skills at scouting.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (692, 'enUS', 'Ah, you found them! Good work, $n! These might be the only chance we have against the evil of Myzrael!$B$BJust let me find my scrap-ratchet and see if I can re-attach these fragments...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (693, 'enUS', 'Brilliant, $n. One item down an\' three more to go, based on my list. The other three items are all in Stromgarde.$B$BTake a rest an\' get your strength back--the next phase of my plan\'ll require a little more time an\' effort. Come back to me after you\'ve gotten a warm meal an\' some rest.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (694, 'enUS', 'You amaze me, $n. Well done again.$B$BGive me a moment to cast the proper spell to enchant the gem, an\' you\'ll be ready.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (695, 'enUS', 'Good, good.$B$BIf there are no other delays, how about we get started?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (696, 'enUS', 'Malin couldn\'t\'ve asked a worthier person to come here an\' aid us, $n. You\'ve done a grand job!$B$BHe\'ll be pleased to see what an overwhelming success our mission\'s been.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (697, 'enUS', '<Archmage Malin opens the letter, and his eyes grow wide.>$B$BBy the Light, you\'ve done it. How wonderful. Trelane will be so pleased. I\'m sure Kryten and Skuerto are just as excited as I am to have them on their way back to Stormwind.$B$BThank you, $n. Please, take this to help you in your journeys.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (698, 'enUS', 'Ah! Wonderful. Fresh meat for the fire tonight. Thank you, $n.$B$BYou\'ve helped make the coming nights more tolerable. The fire burns bright, and our stomachs will be full.$B$BIt\'s a pleasant surprise to have you bring us a shipment; Dar\'s not the most attractive orc anyone\'s laid eyes upon, but at least his attitude reeks far less than his smell.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (699, 'enUS', 'Glad to see you\'re alive and well, $n.$B$BThese claws should make do nicely. The more weapons we have at the ready for attacks against us, the better off we\'ll be.$B$BI\'ll get started on your weapon as soon as possible. It will only take a short time. I\'ve become quite adept and quick at their production.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (700, 'enUS', 'The granite seems but a small token compared to a great dwarf\'s life. But no dwarf shall last on this planet for eternity.$B$BLet us just hope that the memorial stands as a reminder to those who will trod the great Azeroth of the future. Lest we not forget it is for them that brave soldiers such as Sully Balloo so valiantly gave their lives.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (701, 'enUS', 'Yes, these will work perfectly. I will transfer their power and the spirit of the raptors into this orb. This orb will imbue Gor\'mul with the fiery strength and intelligence of the raptor.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (702, 'enUS', 'At least it is something interesting you bring to me this time. A strange power it emits...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (703, 'enUS', 'Ah, I\'ll get started on those wings right away. I can almost taste them already!$B$BAnd here\'s the recipe in case you want to make them yourself!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (704, 'enUS', 'Well done! With luck and diligence, we will find whatever link exists between the troggs and these ancient artifacts. $B$BYou have done a great deed for the Prospectors, $n. Your efforts will not be forgotten. And neither will Agmond.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (705, 'enUS', 'These will work great! I just need to smash them into powder...then mix them with a little of this and a little of that.$B$BWatch out, scavengers! Prepare to be flashed!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (706, 'enUS', 'Ah yes, this will do excellently. This will enable your weapons to strike with searing heat!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (707, 'enUS', 'So Stormpike sent you, did he? Good.$B$BI have a big task for you...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (709, 'enUS', 'Oh, fortune is with us! Thank you! We may yet be saved!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (710, 'enUS', 'These shall do nicely, $n. Thank you. Hmm, a bit more sand than I\'d anticipated. Have to adjust the coefficient of friction. No, wait; it should still be negligible with the frost oil.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (711, 'enUS', '$n, simply amazing! I would have thought it\'d take half a dozen tauren to bring all these to me.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (712, 'enUS', '$n, have you ever considered becoming a research assistant? No? I\'m not sure what I\'ll do without your abilities here. Intelligence, curiosity, and strength--all combined into one package.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (713, 'enUS', 'Stupendous! This should be the perfect coolant for my new bracers. If the enchantments don\'t last a century, then I\'ll give all my belongings to Servo.$B$BAll the mages of the Assembly will be in such awe after I finish this, $n. You just wait and see.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (714, 'enUS', 'HA!$B$B<Lotwil grabs the gyrochronatom from you.>$B$BPerfect... just about done. No, really. I AM almost done now. This project is my top priority... at the moment. Let me pop this into place, and you can see for yourself what you\'ve helped me create. It\'ll just take me a few more minutes... maybe... possibly longer. Well, maybe a lot longer. We\'ll see...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (715, 'enUS', 'YES!$B$BI mean, thank you, $n. These will be helpful while traveling through the Badlands.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (716, 'enUS', 'Here you go, $n. Try these on.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (717, 'enUS', 'No longer will the Kirin Tor need to worry over the prisoners of Lethlor. I will return to report success to my master. Krasus will no doubt be pleased.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (718, 'enUS', 'Thanks so much, $n. While you were gone, I... er... swallowed a mouthful of sand. It...$B$BWell, enough of that, we\'ll go out and get some food tonight. Eat like kings!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (719, 'enUS', 'Ye\'re me savior, $C. I\'m glad someone could teach those Shadowforge a lesson or two. Thanks for yer help.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (720, 'enUS', 'Hammertoe\'s alive?! This is amazin\', $n!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (721, 'enUS', 'Yes? Ah\'m sorry... <cough> Feel so weak. Can ye come closer, $C?$B$BRyedol sent ye? Muradin be praised, that boy\'s not as slow in the head as I once thought.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (722, 'enUS', 'Ha! <cough cough>$B$BAh knew you\'d find me amulet, $n.$B$B Well done indeed, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (723, 'enUS', 'Hammertoe\'s amulet? I think my spirits were higher when I thought he was dead. Knowin\' fer sure only makes the pain worse. This amulet was his life\'s work. I can\'t let his death be in vain.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (724, 'enUS', 'Oh! Well, this is a different matter entirely. You probably don\'t realize the gravity of this matter yet, do you?$B$BI care deeply about Hammertoe\'s demise, but if he sent you here with his amulet, then he understood what\'s at stake.$B$BWe will have to take time to mourn his death later.$B$BLet me show you one of the reasons this amulet is so valuable.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (725, 'enUS', 'Ah, word from Historian Karnik. Excellent.$B$BI always have time for his business.$B$B<Advisor Belgrum opens the note and begins to read.>', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (726, 'enUS', 'You\'re treating this matter with an urgency I\'d expect from the king himself, $n.$B$BAll right, then. I know what\'ll bring the Shadowforge down.$B$BTheir presence in Uldaman can be slowed if we strike them at their base of operations: Angor Fortress.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (727, 'enUS', 'Oh. Greetings, brother.$B$BAnd praise to the dwellers beneath.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (728, 'enUS', 'Be discrete with your signs, $C. There are those who do not believe the righteousness of our pledge.$B$BAnd there are those who would see us fail if they knew of us.$B$BWe must always be careful. And watchful. And patient.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (729, 'enUS', 'Hollee, is that you? I need some help chiseling this stone. Hand me my stone brush, I think I see something.$B$BHey, you\'re not Hollee! Don\'t suppose you\'ve seen my stone brush?$B$BNever mind that now! I promised the League I\'d send the mysterious fossil I found. Where did Hollee go?$B$BSo much work to be done! Now where is that mysterious fossil... and my brush... and Hollee...$B$BAnd who are you... \'tis no matter, so long as you\'re here to help.$B$BLet me know when you\'re ready to start looking.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (730, 'enUS', 'I\'m so glad Chief Archeologist Greywhisker sent for us....', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (731, 'enUS', 'Thank the stars Prospector Remtravel is safe! I told you he was absent minded. I can\'t believe he insisted on staying there with all of those horrible creatures lurking about.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (732, 'enUS', 'Good tidings you bring bearing the Sign, $n. The Sign of the Earth was created by the Dark Iron dwarves of Angor Fortress to bind two of Deathwing\'s ruthless lieutenants, the twins Blacklash and Hematus.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (733, 'enUS', 'It\'s a start, $n, and it\'ll have to do until we hear word from Ironforge or the Explorers\' League.$B$BNow, if only I could find some salt somewhere for my quenching barrel...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (734, 'enUS', 'Cog #5, huh? Yeah, this isn\'t going to be good.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (735, 'enUS', 'You are resourceful, $C. If it truly is your charge to begin a new chapter of our order, then I believe you will run it well.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (736, 'enUS', 'I am impressed! You possess great skill, or great fortune, to have gained these relics. Either way, you are an asset to us.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (737, 'enUS', 'You got it! Oh, hope glimmers!$B$BYour efforts may yet save us, $n. A monument they should make of you! A shrine they should build! May your name be praised until your grandchildren have grandchildren!$B$BUnless...THEY are freed and rise to the surface.  If that happens then you can forget about children.$B$BIf THEY are freed, then we\'re all worm food.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (738, 'enUS', 'This skeleton must be the unfortunate remains of Agmond. His body is broken and battered, and his bones picked clean.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (739, 'enUS', 'This is sobering news, $n. But Agmond had a strong arm and a fierce hammer-swing, and his skull was as thick as they come! Although he was killed by those cursed troggs I\'m sure he took a lot of them down with him.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (741, 'enUS', 'Oh my! Leave it to Prospector Remtravel to discover such a find!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (742, 'enUS', 'Welcome to the new frontier, $n. Ashenvale is a land of opportunity, one where a young $C like yourself is able to find boundless chances to prove their mettle. Look around the outpost here, and be sure to travel out to the Zoram Strand, as the Horde has another outpost there as well.$B$BYour presence here tells me that you\'ve come to learn more about the hunt. Listen close, and I will gladly share with you what you need to know.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (743, 'enUS', 'You have done well, $n. You seem well prepared to travel onward. Let the wind always be at your back.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (744, 'enUS', 'Thank you for your help, $n.$B$BMy part in my brother\'s ceremony is almost complete. Giving him his headdress and witnessing the ceremony are all my duty requires of me.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (745, 'enUS', '$n, it is good you\'ve taken my task seriously. Respect of the land and its creatures is important. Death and life are a circle... a necessity. One cannot exist without the other. Take time to ponder this and do well to never forget it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (746, 'enUS', 'Thank you, $n. This will calm the beasts of Mulgore. And, spirits willing, it will teach the dwarves that hollowing the land is not the path to knowledge.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (747, 'enUS', 'The $R of Narache thank you, $n. You show much promise.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (748, 'enUS', 'You have done well, $n. I will take these and bind their power into a totem of cleansing.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (749, 'enUS', 'All sides of the crate are stamped with the sign of the Venture Co. Mining Division. Notes printed carefully on the side of the carton indicate that the contents are due for processing at one of their central plants.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (750, 'enUS', 'The $R of Narache thank you for these provisions, $n. With your skill in the ways of the hunt you will surely be revered in Thunder Bluff someday.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (751, 'enUS', 'It does indeed appear that the Venture Co. is attempting to rob us of our natural resources. It is good to know this is true, and now it seems, we must do something about it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (752, 'enUS', 'You traveled all this way just to help an old woman? My my, aren\'t you a keeper?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (753, 'enUS', 'You carried this pitcher back at the Greatmother\'s bidding, I see.$B$BYour willingness to help others and provide for the $R of Camp Narache leads me to believe you would make the tribe proud in Thunder Bluff someday.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (754, 'enUS', 'News of your deed reached me moments ago -- already the beasts near the well drink from it eagerly.$B$BYou have done a great thing for the land, $n. And you have done a great thing for the $R people.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (755, 'enUS', 'Chief Hawkwind sent you? Embarking on the Rites of the Earthmother is no small task...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (756, 'enUS', 'Thank you, $n. The purity with which these beasts hunt is vital in the creation of the next cleansing totem.$B$BThe goblins will never learn the difference between contending with the land, and living in harmony with it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (757, 'enUS', 'You have passed the first test of the Rites of the Earthmother. The tribe will be proud.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (758, 'enUS', 'Ancestors of the Thunderhorn clan spoke to me in a dream, praising you for your actions near their well.$B$BFor their gratitude, they wish to give you this...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (759, 'enUS', 'Very good, $n. I can feel the sacrifice of the land in this offering, and my spirit swells with sadness, and pride.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (760, 'enUS', 'You\'ve done it. You have cleaned the wells, and healed our land. Bloodhoof village is grateful, $n.$B$BMay the wind whisper your deeds for a hundred seasons, and may the waters of Mulgore remain clean forever.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (761, 'enUS', 'I know that gathering these quills was not an easy task, $n. In doing this, you prove that you are a $C of merit. It is good to have you at Bloodhoof Village.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (762, 'enUS', '$n, your dedication to Ironforge and to the dwarven people cannot be refuted. I thank you for your help in this matter.$B$BI shall send word to Historian Karnik informing him that you have succeeded, and I shall also have aid sent to the Badlands as soon as possible to help recover the excavation site. Thank you again.$B$BBefore you go, there is one more thing I would ask of you in the name of King Bronzebeard.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (763, 'enUS', 'Word from my good friend, Chief Hawkwind! Ah, I can see by his carvings you are of a special breed.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (764, 'enUS', 'I don\'t think there could be too much misinterpreting or ignoring of that signal, hm? The Venture Co. will know not to take the $R lightly, or to be so arrogant as to believe we would allow them to steal the natural resources around us without protest.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (765, 'enUS', 'My anger is somewhat lessened at the news that the villain Fizsprocket is dead. I will go through his personal effects and see if there is any additional information about what the Venture Co. has planned for the future. Thank you for your efforts, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (766, 'enUS', 'Ah, thank you, $n. Using the items you brought me, I will be able to create the remedy I need to stop the spread of the infection and, eventually, cure it completely. I owe you my life in thanks.$B$BBut I\'ll never forget that screeching call when Mazzranache came upon me, the blaze of color as its beak descended...$B$BBe wary on your travels.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (767, 'enUS', 'I sensed you would be coming to me, $C. I can always recognize one who is trying to pass the Rites of the Earthmother.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (768, 'enUS', 'Ah, these are very nice pieces. They\'ll make fine leather goods.$B$BHere, $n. Take this in trade...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (769, 'enUS', 'Ah, good. Thank you, $n. My heart swells with pride to see youths interested in the craft of leatherworking.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (770, 'enUS', 'I can barely believe my old eyes! You defeated the great wolf Ghost Howl? I look upon you with new respect, young $C. You are a hunter of extreme skills!$B$BLet me offer you something. My days of hunting are over, but I would be honored if you used one of my weapons in your hunts.$B$BMay it strike true, and bring you renown.$B$BAnd let us hope that Ghost Howl\'s spirit has finally found peace.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (771, 'enUS', 'I see you have learned to gather from our sacred land, $n. It will take but a moment for me to brew the Water of the Seers.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (772, 'enUS', 'Be not afraid, $n. You have successfully passed the Rite of Vision.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (773, 'enUS', 'You have shown great diligence in your yearning to pass the Rites of the Earthmother, $n.$B$BWe, the Ancestral Spirits, represent the mighty $R who gave their lives bravely to found and protect our great city of Thunder Bluff. I hereby pass the duty of that protection over to you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (775, 'enUS', 'Hail, young $C. I see you found your way to my doorstep.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (776, 'enUS', 'Well done! A fine kill that was.$B$BI will send a gathering party to collect the carcass so we can retrieve the hide and meat.$B$BYou have done a great deed for your people, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (777, 'enUS', 'Ah, perfect. Stand back while I prepare to make more history!$B$B<Lotwil continues to work feverishly.>', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (778, 'enUS', 'Thank you, $n. That was quite an ordeal, wasn\'t it?$B$BI\'m not sure what could have gone wrong. It was a good thing my magics were here to help send the creature back to its home plane.$B$BPerhaps a little token of appreciation for all your hard work...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (779, 'enUS', 'As you place the Runestones into the matching recesses, a violent rumbling shakes the earth as the Seal is broken.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (780, 'enUS', 'Most excellent. These ingredients will make a fine stew and the loss of those boars should be a fair lesson to those vile Bristlebacks.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (782, 'enUS', 'With Tho\'grun dead, his corpse picked clean by the desert winds and scavenging buzzards, the ogres will be put into disarray. Though a small victory, this will lead to a much larger one$B$BThe Sign of the Earth is now in our possession, and we can open the black drakes\' prison in Lethlor. We shall free them from endless imprisonment, albeit to their doom!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (783, 'enUS', 'Ah, good. Another volunteer. We\'re getting a lot of you these days.$B$BI hope it\'s enough.$B$BThe $R lands are threatened from without, and so many of our forces have been marshaled abroad. This, in turn, leaves room for corrupt and lawless groups to thrive within our borders.$B$BIt is a many-fronted battle we wage, $n. Gird yourself for a long campaign.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (784, 'enUS', 'Word of your bravery travels fast, $C. Tales of your victory at Tiragarde Keep will be heralded in Orgrimmar.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (786, 'enUS', 'The Horde would surely prevail if the Kolkar centaurs were to attack. But by preventing such an attack, we have spared our mighty warriors unnecessary bloodshed.$B$BAnd as sure as there is sand in the Tanaris desert, we know that there will be blood spilled before these trying times are through.$B$BYou have served your people well, $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (788, 'enUS', 'Hmmm, not bad, $n. But don\'t let it get to your head...you\'ll fight tougher than boars in your career.$B$BNonetheless, you\'ve proven yourself well, and your next trial will be against a considerably more dangerous opponent, so you\'ll need some extra protection.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (789, 'enUS', 'There is an important lesson that you must take away from fighting scorpids. The smallest or largest of opponents can still send you to your doom. In fierce combat, any number of things can prove your downfall.$B$BI have no more to teach you, $n. You have done well, and I will watch your progress with interest.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (790, 'enUS', 'My blow was not enough to kill him, but looking at the damage I inflicted gives me some small measure of pride. That small measure will be all I have to sustain myself if I die, and in that light, the short list of my life\'s accomplishments fills me with anger.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (791, 'enUS', 'Most excellent, $n. Any good $C will surely find a use for this bag on the battlefield.$B$BI salute your vigor and willingness to die in the name of the Horde!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (792, 'enUS', 'You have done well, $n.$B$BAlthough the Vile Familiars were merely pets of the darker powers within the Burning Blade, your success against them foretells greater deeds ahead.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (793, 'enUS', 'The black dragonflight continues to ally with the renegade Blackrock orcs, and both must be brought into line, or removed. With Blacklash and Hematus dead, we can turn our attentions westwards, towards Blackrock Spire.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (794, 'enUS', 'You retrieved it! Well done!$B$BYour efforts within the Burning Blade Coven are key in rooting out this cult in the Valley of Trials. But I fear they have further aims in our land.$B$BWe have not seen the end of them.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (795, 'enUS', 'As you place the Runestones into the matching recesses, a violent rumbling shakes the earth as the Seal is broken.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (804, 'enUS', 'From your description of the beast, I believe you must be speaking of Sarkoth! It is no wonder that Hana\'zua was overtaken by it. Aid will be dispatched to him immediately, trouble yourself no more with Hana\'zua\'s plight.$B$BHowever, I must say, I am most impressed to hear that you brought Sarkoth to death. It is a feat to be proud of, $n. And that you would fight for a stranger\'s honor, while other tasks occupied your time, your own honor is heightened.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (805, 'enUS', 'Hm... your report comes at a bad time. The Burning Blade is not seen here in Sen\'jin, but their evil has taken seed off the coast, on the Echo Isles.$B$BThe orcs are friends of the Darkspear Trolls. Honorable friends. We want to help the orcs, but... we need help too.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (806, 'enUS', 'Aha! You got him!$B$BYou do your clan proud, $n. And because of you, Durotar is free of one more agent of evil.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (808, 'enUS', 'Thank you, $n. You have saved Minshina. You have saved my brother\'s spirit from slavery!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (809, 'enUS', 'Neeru wants the Demon Seed destroyed, does he? Strange...$B$BVery well. If he wishes the seed gone, then I will tell you how to remove it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (812, 'enUS', '$n, you\'ve saved my life. Thank you.$B$BPlease take this; I hope it can at least help you in your travels, if not bring you some coin. From what you say, I won\'t be usin\' it anytime soon. Kor\'ghan is gonna march me through more of his rituals until I prove myself. More killin\' boars, more gatherin\' scorpid tails...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (813, 'enUS', 'Here is the antidote, $n. Take it to Rhinag as soon as you can, and be cautious.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (815, 'enUS', 'You might be weak, but at least you\'re not clumsy, $n.$B$BHere, take this and get outta my way. I\'ve got cooking to do.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (816, 'enUS', 'Oh, my son... my beautiful son.$B$BUncertain of my son\'s fate, I have tormented myself since he left. Now that I know what\'s happened, perhaps I can finally begin to mourn.$B$BThank you, $n. Please take this. I was going to give it to Kron as a gift when he returned successful. Now that I know he is dead, I cannot bear to look at it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (817, 'enUS', 'I am pleased, $n. Because of your help, our people will be protected, and not go cold as the seasons change.$B$BThank you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (818, 'enUS', 'Well done, young one... well done indeed.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (819, 'enUS', 'Well, I\'ll be...$B$BThis was one of Chen Stormstout\'s kegs. He traveled with Rexxar a long time ago. I haven\'t seen or heard about him in a kodo\'s age. I appreciate you bringing this to me, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (821, 'enUS', 'Haha... I never thought I\'d make more of this stuff ever again.$B$BYou\'ve stirred in me a great sense of nostalgia, $n. Kind of reminds me of the last time I blacked out from too much trogg ale. Thank you. The memories warm my stomach.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (822, 'enUS', 'Haha! I knew you couldn\'t resist some of this brew. Come back again anytime you want some more, $n. Just bring me more of the ingredients I had you collect.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (823, 'enUS', 'Yes? You have something to report? Well then, let\'s have it!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (824, 'enUS', 'This globe... it is most disturbing. Many of the more powerful elementals receive their power and energy through such devices. To find one like this nearly intact is one thing - to find it so tainted is another.$B$BI will see that this globe is studied properly by the Earthen Ring. Perhaps we can bring an end to this corruption of the elements. Please - accept this as a fair exchange for bringing this globe to the attention of the Earthen Ring.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (825, 'enUS', 'Your recovery mission was a success, $C. I will see to it that these tools get to Orgrimmar with the next caravan.$B$BNicely done.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (827, 'enUS', 'Good. Hiding within these collars is the secret behind the Burning Blade. And I will uncover that secret...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (828, 'enUS', 'Welcome, $n. Word reached me of your coming, and of your exploits in Durotar.$B$BYou are a $C of growing skill and renown.$B$BStay on the pure path, and your future will be great indeed.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (829, 'enUS', 'Ah, and where did you get this? Good Margoz in Razor Hill sent you to me, did he?$B$BWell, let me take a closer look...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (830, 'enUS', 'This does not bode well at all. You were wise to bring this information to my attention.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (831, 'enUS', 'Countless times I urged the Warchief not to trust the humans, but personal pride is not what is at stake here.$B$BYou have served the Horde honorably, young $C.$B$BNow excuse me, I must counsel Thrall on these matters at once....', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (832, 'enUS', '<Examines the Eye of Burning Shadow.>$B$BIntriguing. The voice you heard mentioned my name? I am known for hunting down enemies of our Warchief, but...it is strange that I was singled out. Stranger still that the Burning Blade cultists from whom you recovered this pendant have a name so close to my own.$B$BI must study this pendant. I must study and, and must ponder the meaning of its message.$B$BThank you for bringing this to my attention, $n. You have done your people a great service.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (833, 'enUS', 'Well done. You have sent those foul Bristlebacks a clear message. They will think twice before attempting to meddle in this holy spot again.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (834, 'enUS', 'Hey, now isn\'t that just the thing! You\'ve got our supplies back for us. That\'s great. I\'ll be able to get back to work just as soon as I can find someone to haul these sacks for me.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (835, 'enUS', 'I think I can breathe easy now, knowing that the harpies won\'t be bothering our supply caravans anymore. And not one moment too soon, either. I hear I\'m being sent some fun little toys with the next shipment!$B$BYou\'re pretty good at what you do, $n, you ought to track down my boss in Ratchet. I\'ll bet he could find some work for you.$B$BOh, and don\'t worry, I wouldn\'t forget to pay you for the service you provided me.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (836, 'enUS', 'Oh, YOU\'RE the great savior of OOX-09/HL! I cannot even begin to express my thanks for your assistance! My homing robot series needs more development, but your recovery has saved me countless hours of extra rebuilding time, as well as many gold pieces in manufacture costs!$B$BPlease, take your choice of any of these items I have. Thank you again for your generous assistance $n, my robots and I are in your debt!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (837, 'enUS', 'I will pass along word that the quilboars have been removed from their lairs, and that their structures may be set to fire. The removal of the Razormane from Durotar brings us closer to fully securing the borders of our new homeland.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (838, 'enUS', 'Yes, $C, the High Executor mentioned you might be the one to trust in the matter of the key. I\'ve dedicated a fair portion of my recent studies to the Scholomance, and I believe I know a means by which you can acquire one that will let you past the front door at will.$B$BThis task will not be easy, but such tasks seldom are. You most assuredly look like the type to know this as fact.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (840, 'enUS', 'Oh, so Takrin sent you?$B$B<Kargal warily eyes your recruitment letter and shoves it unceremoniously into his vest>$B$BI\'ll sign this when you\'ve earned it. I\'ve got reports from down south that this whole continent is being torn apart. Meanwhile, we\'ve got supply caravans backed up to the river and outposts under siege from here to Stonetalon. You want to prove your worth to the horde? Start by proving it here.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (841, 'enUS', 'Very well, here is another power source. I highly recommend you using it properly this time, unless you are eager to spend ten more water pouches for another water source.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (842, 'enUS', 'Alright, $n. You want to earn your keep with the Horde? Well there\'s plenty to do here, so listen close and do what you\'re told.$B$B I see that look in your eyes, do not think I will tolerate any insolence. Thrall himself has declared the Hordes females to be on equal footing with you men. Disrespect me in the slightest, and you will know true pain.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (843, 'enUS', 'Khazgorm met the fate he earned. I feel no remorse for the fool. He was an infidel on these lands.$B$BMay his death serve as a warning to all those who threaten to disrupt our way of life.$B$BNow let me take a moment to study the dwarf\'s journal....', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (844, 'enUS', 'Very good, $n. That\'s one less thing to worry about on the road.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (845, 'enUS', 'Not bad, $n. Those zhevra hold a lot of strength in their legs. I\'ve seen them knock even a tauren on its haunches!$B$BIt looks like you have some strength in you too.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (846, 'enUS', 'I see you are quite industrious, $C.$B$BNo doubt we shall soon have a fitting surprise for those dwarves.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (847, 'enUS', 'When the enchantment wears off, I will speak with Gor\'mul. My thanks to you for your help in returning Gor\'mul to the land of the living. I have no doubt he thanks you greatly as well.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (848, 'enUS', 'Ah, yes. These are good specimens. Potent.$B$BI am Forsaken, and we honor our contracts. Here is your reward, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (849, 'enUS', 'You have done well, $n! May you be revered in Thunder Bluff as a defender of sacred tauren lands and a champion of the lost tribe.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (850, 'enUS', 'Well done, $n. Kodobane is just one strand of a tangled thread - there are other Kolkar leaders to kill elsewhere in the Barrens. Seek out my companions at the other oases if you wish to press the assault.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (851, 'enUS', 'You did it! That\'ll really upset the balance of power down here. Verog left no clear successor, so I expect this crew of brutes will fall on each other like hungry dogs. I\'ll stay here and keep an eye on what happens next.$B$BVerog answered directly to the Kolkar Chief - killing him is a good start, but there\'s more work to be done.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (852, 'enUS', 'I commend you, $n. These centaur are undisciplined and cannot focus their rage as the orcs can, but they are fierce nonetheless. In defeating them, your valor is proven.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (853, 'enUS', 'Ah, the spores from the Barrens that Helbrim was gathering. He garnered your aid in delivering them? Good.$B$BAnd the emulsion is still potent, so you must have wasted no time in the delivery. Well done, $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (854, 'enUS', 'It is good to see that more of our $R allies have come to assist us from as far away as Thunder Bluff. Again, I welcome you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (855, 'enUS', 'Nicely done. I\'m sure you\'ve struck fear into the surviving centaurs, $n. With their greatest warriors slain, they will either stop their advances or lash out at us in a foolhardy strike.$B$BI hope for the latter.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (857, 'enUS', 'At last, the power is mine again! Mine I say!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (858, 'enUS', 'Yep, this looks like the right key. Let me just make sure I know how to work this thing...$B$BHmm... rotation control... This regulates speed and stability... fine arm movement control. Just look at this! I can\'t believe the Venture Company could design something this much better than ours. I\'ve got to get this back to the outpost!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (860, 'enUS', 'You want to hunt in the Barrens?$B$BYour eyes are eager, and young. They\'re not yet squinted from tracking your prey across this land\'s dry plains.$B$BBut worry not. We\'ll toughen your body and your mind. And we\'ll teach you that there is more to hunting than finding and killing your quarry.$B$BPrepare yourself. Your journey begins. Now.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (861, 'enUS', 'Well done, young one.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (862, 'enUS', 'Normally I don\'t like to eat these rodents when they\'re so.... so... ripe. Like to let them rot for a few weeks to really bring out the flavor. But you look so hungry.$B$BLet me fix you some Dig Rat Stew. Ah, there we go.$B$BHere you go!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (863, 'enUS', 'He wrecked it!?$B$B<Sputtervalve mutters angrily to himself before continuing>.$B$BHopefully we\'ll be able to get something of value from the wrecked shredder prototype... my business depends on it! I\'ll start tearing that sucker apart and we\'ll see what makes it tick.$B$BOh, let me give you something for your trouble, too.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (864, 'enUS', 'I will analyze this data immediately. Perhaps Keever\'s insanity will prove to be my genius after all.$B$BAs for you... you are useful to me, and I reward quite well those who remain useful. Take this item, this coin, and most importantly my thanks along with them.$B$BCheck back with me from time to time - I wager that our working together on this project is not at an end.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (865, 'enUS', 'Great, you got it! Let\'s grind this up and mix it with some wine, and...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (866, 'enUS', 'Yes yes, these are very nice! I will grow specimens from these roots and study them. I hope to find new properties from them. Hidden, valuable properties...!$B$BAnd don\'t worry -- if I do then you\'ll be the first to know. Because I\'ll need someone to gather more!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (867, 'enUS', 'Oh, these are nice. Real nice... Good job, $n. I look forward to seeing more of your work in the future.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (868, 'enUS', 'You please me, $n. The eggs and the tool. Well done.$B$BAnd for your reward...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (869, 'enUS', 'Before you lies an entire community of raptors, ducking in and out of hovels, snarling at one another, and working together to construct primitive huts. Collections of shiny things surround their nests. Mystery solved!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (870, 'enUS', 'You found a fissure in the earth, with power emanating from it? Strange. Something is causing that oasis to bubble over suddenly with life.$B$BCome, $C - there is something else I\'d like you to investigate with me.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (871, 'enUS', 'You have done well, $n. Those insolent quilboars will finally learn that the might of the Horde is not to be ignored.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (872, 'enUS', 'Well done, $n. Without Kreenig to orchestrate the raids, my grunts should be able to keep Far Watch secure. Your people should be proud to count you among them.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (873, 'enUS', 'You now are one with Isha Awak, $n. His spirit is with you. He lurks in your eyes...$B$BEyes that, one day, will learn to see.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (874, 'enUS', 'My brother sent you, did he? Then you must have proven yourself to him. If you have earned the respect of Jorn, then you have my respect as well.$B$BAre you ready to tread farther down the hunter\'s path?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (875, 'enUS', 'Excellent work my friend. I think you\'ll go far within the Horde.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (876, 'enUS', 'HA! Well done, $n! Very well done... I wasn\'t sure you were up to the task, but you\'ve proved yourself quite the cut-throat. Thank you again for helping us suppress the Harpy epidemic. Here is your reward, use it well.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (877, 'enUS', 'Your findings are amazing! Jerrik was wise to send you there. We now know that whatever rests under these oases can create life from nothing! No doubt this is causing the havoc to the south, but my primary concern right now is preserving the balance up here.$B$BWe must study this more...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (878, 'enUS', 'Yes, yes... filled with joy because finally <snort> the Bristleback know my tribe\'s pain, $R. That is how I should feel, but that is not the case.$B$BYou aid your people and they honor you. Mangletooth aids his people, and what do they do? They turn their backs on him. They forget him and leave him to rot in cage!$B$BI aid them even from locked behind bars and they send no one to save me!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (879, 'enUS', 'It is done then. Better it ends this way, says Mangletooth. <snort> They should have <snort> made sure I was dead when they turned their backs on me instead of leaving <snort> me here in this forsaken cage.$B$BThere is but one task left for you to finish, $R.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (880, 'enUS', 'Thank you, $n. Studying the beasts of an area can tell much about the area itself. We shall see what tale these shells tell, and possibly unravel the mystery of what\'s happening in the barrens.$B$BPlease accept my gratitude for your aid... and perhaps you can use these coins. I find that I do not need them.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (881, 'enUS', 'You\'ve beaten Echeyakee, and though his days of hunting are over... his spirit is with you. He will show you the strength found in subtlety, and the honor in mercy.$B$BI underestimated you, $C. The Horde is fortunate to have you in its ranks.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (882, 'enUS', 'This fang is merely a token, but what it represents is profound.$B$BIshamuhale\'s strength is in you, $n. May you use it with temperance. That is your burden.$B$BThat is your honor.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (883, 'enUS', 'Lakota\'mani is called \"Earthshaker\" in our language, and his steps are like gathered thunder. He is with you now, and his courage mingles with yours. Together there is no conflict you cannot face.$B$BWalk tall, $n. Walk tall, Earthshaker.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (884, 'enUS', 'The thunder lizard Owatanka is called \"Bluebolt\" by the tauren, for within him resides a speed that belies his size. It is said that lightning struck Bluebolt long ago and, fed from the sky\'s rage, the great beast\'s energy is without limit.$B$BMay his energy flow through you, $n. For you now keep pace with Owatanka.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (885, 'enUS', 'The thunderhawk is a rare beast of the Barrens, and its death can portend great fortune, or great doom.$B$BBut Washte Pawne, called \"Spirit Biter\" in Taurahe, can bring neither... or both.$B$BThe pain you felt from hunting Spirit Biter is the pang all feel who make great gains at great costs. The sting you felt is the pain of regret.$B$BBut I can see you have conquered this. You have learned that regret turns to wisdom, and that through pain one gains strength.$B$BYou have learned much, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (886, 'enUS', 'My brethren in Thunder Bluff were wise to send you, young $C. For the mystery of the Barrens is one that I alone cannot unravel.$B$BWith your aid, let us hope we can find answers to our questions.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (887, 'enUS', 'Superb! Gazlowe will reward me handsomely for this. And I\'ll reward you. Less handsomely, of course. A goblin\'s got to make a living, you know?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (888, 'enUS', 'It\'s a relief to get all this stuff back, so thanks for your help, $n. You know, I could use a $R like you to help out with my operation here. If you\'re ever looking for work, don\'t forget about how well Gazlowe pays for service!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (889, 'enUS', 'Good. Go with Agamaggan\'s spirit as your guide, $R. Return to me if you desire more of Agamaggan\'s magics.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (890, 'enUS', 'Ah, Gazlowe sent you, did he? Just a moment, let me take a look here and check my own logs and make sure I didn\'t have Gazlowe\'s shipment sent to the wrong place.$B$BBet he\'s waiting for those boots from Drizzlek...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (891, 'enUS', 'I see you\'ve made the righteous Rear Admiral suffer as I have. Perhaps this blow will weaken the resolve of Northwatch. His supply of fresh recruits seems inexhaustible, but the loss of his top Lieutenants will weaken him for some time.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (892, 'enUS', 'No dice, eh? I guess it must have been the Freebooters then... Dizzywig\'s on the level, I\'m sure he wouldn\'t cheat me like that. After all, he is on the payroll.$B$BLooks like I\'ve got some goods to get back, $n. What do you say?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (893, 'enUS', 'Excellent, excellent specimens indeed.$B$BThe quilboar are further along than I had expected.$B$BHere, $C, take this and get out of my way. I\'d better take a closer look at these weapons. We may have underestimated our enemy, and that\'s the kind of mistake we can\'t afford to make.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (894, 'enUS', 'A variety of buttons, levers, and blinking lights are somewhat erratically arrayed on the face of the control console. A small gauge indicates that the apparatus is currently operating within optimal levels, and indicates that control valves one through three are currently open. There is a small keyhole set into the lower right side of the control panel.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (895, 'enUS', 'Ahhhh! That sort of business. You know, these pirates are always looking out for their own. No sooner is Baron Longshore in a stockade than this Garvey fellow comes looking for vengeance... You don\'t suppose anyone will come looking to avenge Garvey, do you? Maybe I should put you on retainer.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (896, 'enUS', 'Look at the size of that thing! We\'re going to be rich! Let\'s see, according to the rates I\'ve seen on gems going into Undermine, I should be able to figure out your cut - fifty percent, don\'t worry!$B$BNow let\'s see... I think this should do about right. Pleasure doing business with you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (897, 'enUS', 'I can tell nothing of the nature of this beast, for its spirit has fled. But as you guessed, it does not belong in the Barrens. I fear it may herald a grim future for our people...$B$BBut for now, prepare yourself, and let us hope that time unravels the mystery of this creature.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (898, 'enUS', 'And to think, we thought Firebough was long dead!$B$BYour heroics have earned you a place of honor with the Thalo\'dan Privateers, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (899, 'enUS', '$C, you take my task seriously, and for that I thank you.$B$BAlthough my desire for revenge remains, I can at least smile seeing that the quilboars have felt pain themselves.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (900, 'enUS', 'The lights indicating the three control valves dim as they are shut off. Green lights slowly fade into yellow, and the switch controlling the apparatus is now moveable, with the valves closed, it can be turned off.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (901, 'enUS', 'Turning the key in the lock, the control console unlocks. A red light at the top of the console turns off and all power drains from the terminal.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (902, 'enUS', 'This is... er... interesting... yes. A device this complex is best analyzed by a team of skilled engineers in a clean room, painstakingly reverse engineering each component in a controlled environment.$B$BBut who has that kind of time?$B$BLet\'s just fire this thing up and see what it does...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (903, 'enUS', 'Ah, I see you\'ve been busy. I hope you learned a lot from this prey, $n. You\'ll need the strength and cunning of beasts like these to serve my beloved Horde.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (905, 'enUS', 'So you\'ve been to the nests and placed the feathers of your prey within them. That took a fair portion of courage, no doubt about it.$B$BDo you feel a little tougher, $n? Because you look tougher now then when you first came here. I guess the Barrens is doing its job.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (906, 'enUS', 'You found the Razormane leader and killed him yourself, $n? Amazing.$B$BI am without words, but that does not mean I am without thanks. The people of the Crossroads would do well to respect your strength.$B$BI must admit, I thought the leader of these raids on our supply lines was another quilboar. Thank you again, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (907, 'enUS', 'It is good to see you return with proof of victory. And it is good to know you have done so with your bones unbroken.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (913, 'enUS', 'You, like the thunderhawk, should be proud. You have bested every foe we set you before, and have done so with strength, courage and honor.$B$BBut your path, $n, continues. In fact, you will find that true hunters forever strive, forever walk their path with the same pride you have shown me.$B$BNow, it is time to move on.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (914, 'enUS', 'You have done it, $n. You killed the leaders of the Druids of the Fang. My dreams are now free of their wicked faces, and you have helped save the Barrens from a cursed future.$B$BI thank you, $n. I thank you for myself, for the druids of Thunder Bluff, and for the land.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (916, 'enUS', 'Thank you, $n. When I return to Darnassus I will compare the venom within these sacs with the venom of other spiders. It is my belief that it will have properties linked to the recent growth of our new world tree.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (917, 'enUS', 'Ah, very good. I will have this egg and the venom transported to Darnassus, then return there when my studies are done here. I expect to find out a great deal from these specimens, $n. You have been a great help to me.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (918, 'enUS', 'You got them. This is good! I\'m hoping that careful study might lead me to understand where the timberling corruption stems from.$B$BI will plant these seeds in special soil I have prepared. I believe the seeds will sprout into timberlings who are much more docile.$B$BPerhaps later you can see the results!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (919, 'enUS', 'That\'s a lot! I\'m afraid they\'re spreading at a dangerous rate. I hope I can solve the riddle of what is tainting them.$B$BThank you for your help, $n. The land is a cleaner place from your efforts.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (920, 'enUS', 'Ah, $n. I was hoping you\'d be prompt in answering my summons. I have an important task that I would like you to perform.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (921, 'enUS', 'So you have heard the first part of the aftermath of the Battle of Mount Hyjal. There is much more to be told and the task you have begun here will continue through the rest of your journey through Teldrassil and into Darnassus.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (922, 'enUS', 'Ah, a timberling seed? I wanted to try growing one of these to help Denalan with his studies.$B$BBut I\'m afraid I\'ve discovered that a corruption has grown in many of the timberlings, and seeds from such creatures carry their parent\'s taint. They are beyond my talents to repair.$B$BDenalan is very skilled with things that grow. He may find a cure for future timberlings. He may be their only hope.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (923, 'enUS', 'Well done! These tumors are the symptom of the timberling\'s disease. They are filled with a poison that we must cleanse from our new land.$B$BI will dispose of these tumors. Thank you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (924, 'enUS', 'Your task is complete. You have destroyed the Demon Seed as Neeru bid. But this is not the end...$B$BThe destruction of the Demon Seed sent waves of power through the ether, waves of staggering force. I felt them, and I am sure other beings did as well. Others who will wonder why their toy was broken...$B$BBut regardless of future troubles, I have something for you. Neeru sent it as payment. And he bid me thank you.$B$BSo, thank you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (926, 'enUS', 'These stones are infused with magical energy, but purposefully flawed. They are very unstable and once removed from the table they will consume themselves quickly.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (927, 'enUS', '...What is this? A timberling heart?? It\'s covered with a foul moss!$B$BThank you for bringing this to me, $n. I will examine the heart and, if fortune shines, determine the nature of the moss about it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (928, 'enUS', 'Ah, I see. You were sent by Tenaron. Well then, it would seem we have much to talk about, much to do, and little time to do it in.$B$BI think we\'d best get started.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (929, 'enUS', 'It is only natural that our race, once immortal and revered, would struggle with regarding ourselves in the humbled manner that we likely should. We must remember to embrace our connection with nature, and with Elune, and push aside our mortal shortcomings.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (930, 'enUS', 'You found this on Teldrassil? Intriguing... this fruit is exotic. Perhaps its seeds were brought here from far off. Perhaps even as far as Azeroth! And there\'s something about this fruit... it seems to have reacted very strangely with the soil of Teldrassil.$B$BThank you, $n. Now if you\'ll excuse me, I must study this further...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (931, 'enUS', 'Where did you get this? I haven\'t seen a plant like this since a sojourn I made to the Swamp of Sorrows... decades ago! It\'s amazing that a specimen made its way to Teldrassil. And it\'s grown to such a size!$B$BThank you, $n. Forgive my shortness of words, but there is a test I would like to perform on this frond...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (932, 'enUS', 'With Lord Melenas laid to rest I can finally move on to other matters. Thank you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (933, 'enUS', 'Indeed, I have spoken to Corithras on my way here, and he has gifted me with the waters you have been gathering. They may well prove useful sooner than we\'d imagined.$B$BStrangely, the growing corruption we\'ve felt is actually a good sign. Teldrassil has been burdened with this taint for a long time, and thanks to the blessing of the dragon aspects, only now is it coming to the surface in a form that we might strike against.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (935, 'enUS', 'Thank you for returning to me so swiftly. I have but one last task to ask of you. Shall we?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (936, 'enUS', 'Greetings, $C; I am glad you are interested in assisting our research. There is much to do, and I would be doing you a disservice if I informed you that the work this entails is not dangerous. Hopefully this will not scare you off... but from the looks of you, I take it that danger is something that you have dealt with before.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (937, 'enUS', 'I am impressed by what you have accomplished here in so short a time, $n. Would that I could ask you to remain here to assist me with my duties... but I know in my heart that greater tasks lie ahead for you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (938, 'enUS', 'Oh, thank you, $n! I feared I would never see Mist again, knowing only her death in my heart, never to be united with my faithful companion. I owe you more than you can know, and you have my eternal gratitude.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (939, 'enUS', 'I sense the suffering of ancient ones, $n...$B$BHolding these items is almost unbearable...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (940, 'enUS', 'I wondered why the Oracle Tree had not communicated with me for so long... It seems that some problems are lessened while others grow to greater concerns.$B$BI fear my work on Teldrassil shall never be completed, and our immortality never restored.$B$BNonetheless, you have done the Oracle Tree\'s task well, and should be rewarded for your diligence.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (941, 'enUS', 'You place the heart within the planter, and it quickly digs itself in!$B$BA few seconds later it wriggles back out, cleansed. It pulses slightly... beckoning for you to take it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (942, 'enUS', 'Oh my! You say this came from the distant lands of Kalimdor?$B$BAmazing! Simply amazing!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (943, 'enUS', 'Most excellent, $n!$B$BNow to see if I can use the stone to decipher the powers of the fossils.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (944, 'enUS', 'The Twilight\'s Hammer is at the Master\'s Glaive?$B$BUnfortunate.$B$BThe old lord impaled by the glaive is long dead, but that does not mean slivers of his power do not still remain.$B$BThe Twilight\'s Hammer must be seeking this power.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (945, 'enUS', 'My sister was where?? Therylune doesn\'t mind getting herself dirty, but still! That\'s a long way to wander, and the Master\'s Glaive is an unwholesome place...$B$BWell thank you, $n. It was very nice of you to let me know she\'s all right.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (947, 'enUS', 'Many thanks, $n. These mushrooms are fine specimens!$B$BAnd when you were at Cliffspring Falls, did you find anything to confirm the warnings of the Ancients?$B$BThe Ancients are wise, but I had hoped that, this time, they were wrong.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (948, 'enUS', '$n. You are here.$B$BGood.$B$BWe have matters...to discuss. You and I.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (949, 'enUS', 'This book is hand printed in an ancient language, and illustrated by a master artist.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (950, 'enUS', 'A chaotic, primal magic surrounds this parchment of scribbles. I sense the work of the old ones in it.$B$BLet us hope it reveals the purpose of the Twilight\'s Hammer in Darkshore...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (951, 'enUS', 'Thank you. These relics are from a time when Mathystra shone. That once great bastion of the elves has faded, but shards of its magic remain. Let us hope we can unlock the secrets of that place before our enemies...$B$BBe vigilant, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (952, 'enUS', 'Ah. Thank you, $n. It is strange, though. The Arch Druid always seems in such a hurry. The forest knows that all shall come to pass in the appointed time. Shan\'do Stormrage understood that.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (953, 'enUS', 'We have few records from the time around the War of the Ancients, especially near the destruction of the Well of Eternity. Given the upheaval and cataclysmic events that were taking place, it is no big surprise.$B$BThank you, $n. With your help, my work here is finished, and I will be able to deliver a full report to the Circle.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (954, 'enUS', 'Ahh... To what could I possibly owe the exceedingly special honor of a $R such as yourself as company. Truly it says much of my current companions--with no offense to my hosts, the noble grells and satyrs--that your presence could be considered an improvement.$B$BBut please, do not let my uncivil tongue drive you from the place. It has indeed been many years, decades even, since I had civil accompaniment.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (955, 'enUS', 'Indeed... The grells of Bashal\'Aran do not possess that which I am searching... however they have come into contact with it recently. Recently... I would reckon that by your time, not mine. Recent for me stretches into the veil of the past, almost another Age...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (956, 'enUS', 'It... it is hard for me to even believe that what I hold now is that which has held me for so long. Let us waste no time, $n. When the seal is destroyed, I might walk the forests of the earth freely again.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (957, 'enUS', 'I am freed, $n! I can now see with my own eyes the changes that have come to our world... Only bits and pieces have I known. To think that when I last walked freely, the Well still stood and the Highborne held court with Azshara, our beloved queen.$B$BI sense that my jailor, my former master, Athrikus, still lives... Already my feelings of hopelessness will give way into thoughts of vengeance.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (958, 'enUS', 'Though it is surely a figment of my imagination, I feel as though I can almost feel the taint of foul Highborne magic upon them. I will have these destroyed, so that their long dormant evil might never be released again.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (959, 'enUS', 'Superb! And just as I was about to lose my job too! I will see to it that Gazlowe gets this gift immediately.$B$BThank you, $n. Nicely done, I say!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (962, 'enUS', 'Ah, splendid specimens. You have done well, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (963, 'enUS', 'Thank you, $n. Perhaps it would have been better... if I had done it myself. But even after these thousands of years, I could not bear to raise my hand against my beloved.$B$BPlease, I would be alone with my grief...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (964, 'enUS', 'This\'ll work, $n! My turn now - I will imbue these with a suitable temper that will allow them to survive the intense forging process they will eventually go through.$B$BThe next step will be to get an appropriate mold for the key. I know a goblin blacksmith in Tanaris that has in the past made molds for similarly macabre items. He works for whom goblins usually work for - the highest bidder.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (965, 'enUS', 'Elissa sent you? Then it is good tidings you bring. I have troubling news to report to her, and had no means with which to deliver it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (966, 'enUS', 'Many thanks, $n. This should shed some light on this warlocks\' gathering...$B$BHmm... the Cult of the Dark Strand... I have never heard of this group before. Having no knowledge of their history, it is all but impossible to say what their plans must be.$B$BThere is no more time to delay. Warning must be delivered to Delgren immediately.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (967, 'enUS', 'If you don\'t mind my saying, you seem a bit well-equipped for a messenger, hm? I suppose Balthule wanted to be certain that his letter reached my hands. Let\'s see what he has to say...$B$BThis is disturbing news. When undead or demonic forces are found, I help the Sentinels destroy them.$B$BAt first I was unaccustomed to the manners of the night elves, but I have grown to respect them greatly as allies.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (968, 'enUS', 'Ah, a copy of The Powers Below. An interesting text, this is. And some might find it useful...$B$BBut this one\'s in sad shape. And look here! There are notes in the margin of almost every page!$B$BHm...I know this copy. I sold it to Bolgar last year. Are you...a friend of his? Yes, I thought you were. You have that same fire behind your eyes!$B$BWell, by definition, a friend of Bolgar\'s is a friend of mine. And we take care of our own, yes?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (969, 'enUS', 'Ahh, yes... I will make da charm for ya now. Just you wait!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (970, 'enUS', 'Hmm, some dark magic enchants this soulstone. Let\'s see what\'s trapped inside, shall we?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (971, 'enUS', 'You found the manuscript! What a joyous day this is! Copies will be made and distributed, and the ancient knowledge of Lorgalis will finally be known to my brothers and sisters!$B$BYour service to us is more than you know, $n. But I hope that this gift shows you the gratitude we extend to our allies.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (973, 'enUS', 'It was a grand thing you did today, $n. You should be quite proud of yourself for having faced down a warlock of such power.$B$BNow, let\'s see what we can learn from Ilkrud\'s writings.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (974, 'enUS', 'Yes, I definitely see the correlation here between the temperature of the volcano and the hot springs. I\'ll have to double-check my equations for accuracy, but it seems like the distance and the rate that the heat is transferred... Hmm...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (975, 'enUS', 'Here it is, $n. Remember, ya have ta carry it with you. Why don\'t ya head back to da wilds, an\' see what ya be findin\' there?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (976, 'enUS', 'You have done well today, $C. The items we sent to aid Auberdine will be of immediate use in dealing with the dark threats that infest the forest.$B$BTake these with my heartfelt thanks.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (977, 'enUS', 'I can\'t wait to show my friends! Now, just wait a moment while I attach these...$B$BPerfect!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (978, 'enUS', 'Now I can compare the feathers... Let\'s see...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (979, 'enUS', 'Hi! Erelas sent you? Great, I was just preparing to travel up to the caves now, and I wouldn\'t mind company.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (980, 'enUS', 'Hi there, $n. Yes, what Krakle told you is true...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (981, 'enUS', 'I knew you would be up to the task, $n.$B$BI sense great deeds before you, and that you will right many of the wrongs that plague our lands. Bear my blessings, $n, as well as this gift.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (982, 'enUS', 'You\'ve done us here in Auberdine a great service, lad. We\'ll make sure that their effects are properly taken care of.$B$BAs for you, please have this. It is the least I can do for someone with the bravery to set things right.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (983, 'enUS', 'As you place the Crawler Legs inside the machine, you hear gears begin to grind. Judging from the banging sounds coming from the Buzzbox, you believe the Crawler Legs are being put in their proper place. Soon the machine begins to hum, and you hear a tiny voice calling out from within.$B$B\"Hello? Uhh... Hello! You fixed it! This is Wizbang by the way! Thanks a lot... Hey, you wouldn\'t want to fix another one, would ya?\"', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (984, 'enUS', 'This is terrible news indeed, $n.$B$BWith the furbolgs this close to Auberdine, we will have to prepare ourselves for the inevitable.$B$BThank you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (985, 'enUS', 'Wash the blood from your garments, $n, and do not mourn what you had to do. Instead, give thanks. You have lessened the threat to our people here in Auberdine, even if Darkshore is still in jeopardy from the effects of fel moss.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (986, 'enUS', 'These will do nicely, $n. I shall set to work on the cloak immediately.$B$BGive me a few moments, then return to me.$B$BOh, one more thing: after you\'ve used the enchantment on the cloak you\'ll be limited in your interactions with others. If you can help it, do no more than speak to others. Much more than that could dispel the illusion.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (990, 'enUS', 'Ah, a $C from Darkshore. Selarin has done well to send you here so quickly, $n. I wish your trip here wasn\'t under such dire stress. Perhaps with your help we can improve matters.$B$BI would start my visit by speaking to the other citizens of Astranaar. Some could surely use your aid.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (991, 'enUS', 'Teronis\' body lies broken atop the island. For some unknown reason, the murlocs have left it alone.$B$BThe deep slashes across his corpse obviously came from the weapons and claws of the murlocs.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (992, 'enUS', 'What\'s this? You were ambushed by some sort of bad mojo creepy-crawly? Oh, this does not bode well for the Gadgetzan Water Company, no indeed...$B$BI guess I should have told you about the reports coming in on some crazy bug-creatures that appear to be sucking up all the water in the desert. At first, I thought it was just a crock. We have enough trouble with the nomads as is; I thought it was just more of their shenanigans. Well, now we know at least!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (993, 'enUS', 'Wonderful! Terenthis found someone to help me.$B$BAnd look, a moonstalker cloak...<cough> Oh... that hurts. Thank you, $n.$B$BThe furbolgs wounded me before I managed to evade them. Give me a moment, and I should be fine to talk some more.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (994, 'enUS', '$n! I\'m pleased to see you\'ve returned successfully. Your assistance to Volcor has given me faith that we can overcome the challenges facing us here in Darkshore and beyond.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (995, 'enUS', '$n! I\'m pleased to see you\'ve returned successfully. Your assistance to Volcor has given me faith that we can overcome the challenges facing us here in Darkshore and beyond.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (996, 'enUS', 'You apply two Cenarion plant salves to the windblossom. It immediately begins to shed its corrupted form, blossoming into a vibrant and healthy plant.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (997, 'enUS', 'Ah, it\'s here! I have waited for this rare earth for quite some time. I hope it\'s still fresh...$B$BThank you for bringing it to me, $n. You are a $R who is generous with his time.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (998, 'enUS', 'You apply two Cenarion plant salves to the windblossom. It immediately begins to shed its corrupted form, blossoming into a vibrant and healthy plant.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1000, 'enUS', 'Greetings, $C.$B$BI am pleased to see the likes of you taking an interest in the well being of nature; tales of your mighty deeds precede you, and you are welcomed here. Seeing you before me instills the feeling of confidence that the Cenarion Circle will get the proper aid it needs.$B$BOur connection with the Cenarion Circle in Moonglade transcends racial and political divides. Remember this as you work on behalf of the Circle, and you will do well.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1001, 'enUS', 'The Buzzbox hums to life soon after you place the Thresher Eyes inside. There is a moment of static before you hear a familiar tiny voice call out.$B$B\"Crawler wine is the best...\" *hic*$B$B\"What? Oh, you fixed it already? Wow, you\'re a fasht one. Sho, would ya like to fix another?\"$B$BYou believe you hear a liquid being poured into a cup, followed by loud gulping sounds.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1002, 'enUS', 'The Buzzbox suddenly leaps into action, and you can hear the distinct sounds of teeth being ground to dust coming from within.$B$B\"Sho, ya made it! Thatsh exshellent newsh... Sho... Why doncha move along? Oh! Unless you want another job!\"', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1003, 'enUS', '\"Sho yash gotsh the scalps in? That\'sh sho great... Wha? Well, yesh I shuppose ya have realized that I can speaksh to ya without the materials in the machine. But I was telling the truth when I shaid they were broken!$B$BSee, in addition to being communimacation toolsh, the Buzzboxes also brew and distill fine liquor. Shorry for any deceptshion, but the nightelves do not apprecimerate my liquor trade in their townsh. I put shomething special in thish box to show my gratitude for yer effortsh. Enjoy!\"', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1004, 'enUS', 'Greetings, $C.$B$BI am pleased to see the likes of you taking an interest in the well being of nature; tales of your mighty deeds precede you, and you are welcomed here. Seeing you before me instills the feeling of confidence that the Cenarion Circle will get the proper aid it needs.$B$BOur connection with the Cenarion Circle in Moonglade transcends racial and political divides. Remember this as you work on behalf of the Circle, and you will do well.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1007, 'enUS', 'You found it! Thank you, $n!$B$BThe old city of Zoram holds many secrets, and this statuette may be the key to many of them.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1008, 'enUS', 'Very well done, $n. Your actions in the Zoram Strand against the naga are commendable.$B$BI know your feat was not easily accomplished, for the strength and guile of the naga is well known to the night elves. We know this, for we share a history with them.$B$BIt is a history I care not to repeat.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1009, 'enUS', 'You found the ring! Thank you, $n. Please take this as payment, and know that your deeds today will remain forever in my memory.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1010, 'enUS', 'Ah, you have the hair! Now I will mix a cure for the child, and pray my concoction works.$B$BAnd... Forsaken are at Bathran\'s Haunt? Very troubling to hear, that is. Very troubling indeed...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1011, 'enUS', 'Very good, $n. Now... by testing the contents of this bottle, the goals of the Forsaken will be revealed!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1012, 'enUS', 'I felt the anguish of the Dor\'danil Barrows lessen, and knew you had completed your task.$B$BIt was grim work you did, $n, for the spirits you destroyed were merely victims of the Forsaken\'s evil. But with their destruction, let us hope the other maddened spirits find peace.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1013, 'enUS', 'Very good. This book will add nicely to my collections of the workings of Ur. His knowledge was great, but his conscience held him from true power. And so when the Scourge came and his strength was tested, it failed.$B$BWe of the Forsaken cannot afford such weakness, if we are to survive...$B$B...If we are to conquer.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1014, 'enUS', 'Silverpine Forest is finally free from the vice of that wretch Arugal. You have done the Dark Lady a great service, $n. Your tenacity shall be rewarded.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1015, 'enUS', 'What makes you think I wish to be pestered by the likes of you, $C? Wait - let me stop the important duties I attend to on a daily basis so I can help you locate a doodad somewhere. By all means, the welfare of Darnassus - nay, all of Teldrassil - should come second to the acquisition of a bauble for your collection.$B$BThe fools in Moonglade waste my time with their call for assistance, and now I must endure this?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1016, 'enUS', 'Well done, $n, this should give us the information we need to have the culprit discovered. How would you feel about taking out the mage who fills our pools with corrupt elementals? The corruption of Ashenvale and beyond makes my stomach turn on its own--to have a wizard actively attempting to destroy it further sickens me to no end. We shall make him pay.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1017, 'enUS', 'Now I can see that repugnant smile wiped from his face forever. You have done very well, $n. Take this, the Light of Elune, drink of it when all seems lost and death is near. It may save you, Goddess willing.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1018, 'enUS', 'Greetings, $C.$B$BI am pleased to see the likes of you taking an interest in the well being of nature; tales of your mighty deeds precede you, and you are welcomed here. Seeing you before me instills the feeling of confidence that the Cenarion Circle will get the proper aid it needs.$B$BOur connection with the Cenarion Circle in Moonglade transcends racial and political divides. Remember this as you work on behalf of the Circle, and you will do well.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1019, 'enUS', 'What makes you think I wish to be pestered by the likes of you, $C? Wait - let me stop the important duties I attend to on a daily basis so I can help you locate a doodad somewhere. By all means, the welfare of Darnassus - nay, all of Teldrassil - should come second to the acquisition of a bauble for your collection.$B$BThe fools in Moonglade waste my time with their call for assistance, and now I must endure this?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1020, 'enUS', 'This is from Orendil?$B$BHis skill with herbs and healing is great. This cure gives me hope, when before I had little...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1021, 'enUS', 'Judging by the gaping wound in her side, Anilia is on the verge of death.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1022, 'enUS', 'The Tome of Mel\'Thandris works in strange ways. The events it records, the events it shows... No one truly understands why it does what it does.$B$BNonetheless, it is clear that what Priestess Starsong received--this Scythe of Elune--bears further investigation.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1023, 'enUS', 'This is terrible, $n. Teronis is dead?!$B$BThe time for mourning will come, but for now we must focus our efforts. I shall send some of the Sentinels to recover Teronis\' body as soon as possible.$B$BYou have Teronis\' journal and the gem he was seeking. Do you think you\'re capable of completing his task? I would rest much better knowing his death was not in vain.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1024, 'enUS', 'Oh, Raene sent you? It must be serious indeed. What can I help you with?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1025, 'enUS', 'You have done well, $n. Thank you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1026, 'enUS', 'I\'m pleased to see you\'re back, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1027, 'enUS', 'You found the final piece? Wonderful for you... and for Raene.$B$BI guess we\'ll have to see about repairing it, won\'t we?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1028, 'enUS', 'After the glow dies, you can see the re-enchanted rod within the shrine\'s basin.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1029, 'enUS', 'You\'ve recreated the rod Teronis had set out to find? That\'s wonderful to hear.$B$BTeronis would be proud to see his quest is continuing even after his tragic passing.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1030, 'enUS', 'Yes, yes... you are one of my kind, but still whole. Still clean.$B$BI am unsure how you have escaped the atrocities the night elves have visited upon our people, but I am glad to know that I am no longer alone.$B$BCorruption now runs through the veins of our kin, young one, corruption brought down upon us by the prideful night elves. They are the ones who did this to our people... They are the ones I blame for being cast out of my tribe.$B$BAt night I can still see the campfires burning in the darkness.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1031, 'enUS', 'Anilia died? No... that can\'t be! It was just yesterday that we were running and playing together in the woods.$B$BI guess those days are over...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1032, 'enUS', 'Thank you, kind $R. My sister\'s spirit is gladdened by your efforts.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1033, 'enUS', 'Oh, you found it! I will place the tear in a stew, and pray that it will cure her.$B$BThank you! Thank you, $n!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1034, 'enUS', 'You gathered the dust! I will create a poultice from these -- it should bring down Relara\'s fever.$B$BThank you again, $n. Without your help, my child would surely perish.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1035, 'enUS', 'Oh, $n, you have saved my daughter! I know the power of the moonstone will give back my daughter her strength!$B$BI can never repay you fully for the aid you have given me, but... please, take this. Other than Relara, it is my most dear possession.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1036, 'enUS', 'Avast ye, scallywag. I hears that ye be the type for a bit o\' the skullduggery. Well, if yer no friend of Booty Bay, then ye might have a special place with the Bloodsail Buccaneers.$B$BArrrrr...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1037, 'enUS', 'Velinde Starsong? That the priestess disappeared some time ago is almost certainly common knowledge. As to further details about the events that surrounded her departure... it would take good reason for me to divulge such information.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1038, 'enUS', 'It seems that Velinde\'s disappearance is more troubling than it originally appeared. Even still, I cannot believe that she has come to a bad end. Perhaps it would be beneficial for us to make contact with her.$B$B$n, since you have shown a vested interest in this matter, I would like to enlist your aid.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1039, 'enUS', '$R, eh? Must admit, we don\'t get much of your type out here. Looking for someone, eh? Just give me a moment, I\'m sure I have it written in one of my ledgers.$B$BLot of people travel through here, though, so just give me a moment.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1040, 'enUS', 'Yes? Now, it doesn\'t look to me like you\'re the sort with goods that need shipping, so either you\'re looking for a job with my caravan--and there\'s no jobs to be had now--or you\'re probably talking to the wrong goblin.$B$BSo which one is it?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1041, 'enUS', 'With all the strange happenings of the last few years, I keep as good records of strangers as I can. Never know when it might come in handy... though it\'s usually not pleasant business when it does. Not to suggest anything about this Velinde you\'re looking for.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1042, 'enUS', 'Dark times these are. Ill news is whispered from every swaying branch, and evil can be found under every rock.$B$BAnd in such times an unknown stranger enters my home and asks of worgen... I\'ll ask no forgiveness for my suspicions, only answers.$B$BAnd answers, I will have.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1043, 'enUS', 'It is an interesting story you tell, $n. Though the Carevin family\'s influence is not large enough that we might find the root of these troubles and finish them once and for all, we will do what we can to contain the worgens\' numbers in Duskwood.$B$BAlready I have instructed Calor to make this his first priority.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1044, 'enUS', 'So this is the fate of Velinde, is it? The high priestess will be disappointed to hear of this, they were good friends before her disappearance. Still, the Scythe of Elune was surely granted to her as a boon by our goddess, and it is unsettling to me that it has fallen out of her servants\' hands.$B$BAnother matter for another time, however. I owe you my thanks in no small amount for what you have accomplished.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1045, 'enUS', 'You have done well, my friend.$B$BThe forest is still tainted, but at least we know we have done a small part to return it to its former glory. If you wish to return to me again, I would enjoy the company of my own kind.$B$BBe well. Perhaps our tribes will meet again under less violent times.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1046, 'enUS', 'So, this is the skull of the fiercest furbolg threat in Ashenvale?$B$BI am more than pleased with your abilities, $n. The Sentinels cannot thank you enough. I hope we can rely on you again in the future to help our people.$B$BI shall put this skull in a place of reverence for others to see.$B$BTeronis would be proud. Thank you again.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1047, 'enUS', 'What makes you think I wish to be pestered by the likes of you, $C? Wait - let me stop the important duties I attend to on a daily basis so I can help you locate a doodad somewhere. By all means, the welfare of Darnassus - nay, all of Teldrassil - should come second to the acquisition of a bauble for your collection.$B$BThe fools in Moonglade waste my time with their call for assistance, and now I must endure this?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1049, 'enUS', 'The Compendium of the Fallen! I was beginning to wonder if the very book existed!$B$BYou have served the tauren of Thunder Bluff well, $n. Your dedication shall not be forgotten.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1050, 'enUS', 'You have rescued the sacred text!$B$BYour journey was long and acquiring the book was undoubtedly no easy task. But the dwarves of Ironforge stand to benefit from your success.$B$BThank you, $n, on behalf of the Explorers\' League.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1051, 'enUS', 'Vorrel led such a troubled existence. And what a good mate he was...$B$BDamn those filthy humans!$B$BVorrel is gone, never to enjoy undeath again. At least I can take solace in the revenge you took in his name. For that I thank you.$B$BNow let me be to mourn....', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1052, 'enUS', 'Brother Anton still spreads the word of the Scarlet Crusade? Oh my....', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1053, 'enUS', 'While disciples of the Light never revel in the loss of life, we must accept that on occasion, such sacrifices must happen for the greater good of the Kingdom and the planet.$B$BThrough your deeds in the Monastery you have spared many innocent lives, $n. I salute your tenacity.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1054, 'enUS', 'Well done, $n.$B$BWe are under constant threat from the furbolg. Hopefully this will keep them at bay for a bit longer.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1055, 'enUS', 'You\'ve done it, $n! Well done!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1056, 'enUS', 'Finally, you arrive. I see Faldreas has listened to the forest spirits....', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1057, 'enUS', 'Now that the Bloodfuries have suffered such a great loss, perhaps the true work of restoring the land can begin....', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1058, 'enUS', 'Oh how happy these little woodland nymphs shall be! We\'ll make them feel right at home with this special concoction!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1059, 'enUS', 'Keeper Albagorm was wise to send you with such urgency.$B$BLet us see if we cannot call forth aid from the great forest to travel to the Charred Vale before it is too late.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1060, 'enUS', 'Ahh, a letter from Darsok. I have not heard from him in many years.$B$BSo the last of the Bloodfeathers are dead, eh? That is good news, I can remember when my friend Rokhan went with that crazy mok\'nathal to kill her older sister. Ah, to be young again, like you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1061, 'enUS', 'Ah, a $C. Very good. We will need your skills to deal with these plunderers!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1062, 'enUS', 'Bravely done, $n.$B$BWe may ultimately need the wisdom of tauren elders to calm the spirits of Stonetalon, but...$B$BRidding the Venture Company of its personnel is a good first move against them.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1063, 'enUS', 'I bid you greetings. You are here seeking counsel?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1064, 'enUS', 'Magatha sent word of your coming, $n. Although my heart beats no longer, I still feel sorrow for the spirits of Stonetalon.$B$BThe Forsaken are eager to offer what help we can.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1065, 'enUS', 'A note from Zamah? Her needs must be great to send word from so far. Let us see what she says...$B$BAh, splendid! I know exactly what to do!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1066, 'enUS', 'Very good! I trust the Syndicate gave you little trouble?$B$BAs I mentioned, the blood of innocents is not an easy thing to obtain. We are lucky the Syndicate shadow mages did the difficult work for us.$B$BNow hold a moment while I create my concoction...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1067, 'enUS', 'A toxin from Apothecary Lydon? Very good. If I know Lydon, then this toxin will be quite effective against the Venture Company...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1068, 'enUS', 'The destruction of the machines XT:4 and XT:9 have soothed many of the spirits of Stonetalon, $n. With their loss, let us hope the Venture Company will remove itself from these mountains.$B$BFor if they do not, then I fear the atrocities this place has suffered will pale to what lies ahead.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1069, 'enUS', 'You got them! Thank you!$B$BI\'ll have these sent to Dizzywig and get them shipped to Booty Bay. I can already taste the profits!$B$BHere is your cut, $n. I couldn\'t have made this deal without you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1070, 'enUS', 'Ah, you\'ve come from Ashenvale at the behest of Tara--how I miss her, my homeland and friends. She must be doing well with the Sentinels to be sequestering adventurers for other tasks. I\'m proud of her.$B$BIt is a pleasure to meet you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1071, 'enUS', 'Thanks so much for your help, $n. You\'re actually back just in time.$B$BI think I\'ve got a perfect plan... explosives. No, that\'s not the plan, but it\'s part of the plan.$B$BSee, I\'ve been using high explosives--don\'t worry, they\'re not as dangerous as everyone thinks--to scare the goblins back a bit. I can herd \'em pretty good with a few key detonations along the mountains\' edges, but I\'ve been low on supplies now for a few days...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1072, 'enUS', 'Ahhh.... Gaxim sent you, huh? How do I know you\'re not lying to me?! Hmm? Hmm...?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1073, 'enUS', 'This\'ll do nicely.$B$BIf I didn\'t know better, stranger, I\'d say you knew somethin\' about alchemy. Good, good... the world\'s not all about swords and sorcery, you know?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1074, 'enUS', 'Your task has been done flawlessly, $n.$B$BNG-5 is the unequivocal component in the recipe to ignite the wagons of dynamite.$B$BWhat? Yes, dynamite... wagons of it. It\'s the perfect distraction, I tell you... just wait and see.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1075, 'enUS', 'Now, this spell you need; one that will allow you detonate explosives from a distance... hmmm.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1076, 'enUS', 'Allow me just a few moments and I shall have your scroll ready for you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1077, 'enUS', 'This is perfect, $n! Well done.$B$BThis spell, mixed with the NG-5, shall make the perfect remote explosive.$B$BWe\'ll be able to create the perfect distraction for you so you can sneak in and steal the Venture Co.\'s letters and engineering plans.$B$BThat will surely slow down their deforestation acts.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1078, 'enUS', 'This is perfect, $n. Thank you greatly.$B$BPerhaps one day in the future I\'ll be able to help you as you have helped me.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1079, 'enUS', 'Truly amazing! This is a huge blow to the Venture Co.$B$BWe\'ll be able to find weaknesses in their machines and also incorporate their designs into our own creations. This is a wonderful day, indeed.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1080, 'enUS', 'Incredible, $n! Kaela and the Sentinels will be so happy. With your skill and my creations, we\'re unstoppable.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1081, 'enUS', 'Hello, $n. I have indeed heard your name before, and although you are still full of youth, I am pleased to see the path you have set for yourself.$B$BYou would do me a great honor if you would accept this as a gift of appreciation.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1082, 'enUS', 'Thank you, $n. You have done a service to the Sentinels.$B$BI\'m glad to see Kaela... and even Gaxim, are doing well.$B$BYou\'ve earned our appreciation.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1083, 'enUS', 'The remains of our enemy... your strength serves us well, $n. Thank you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1084, 'enUS', 'Thank you, $n.$B$BThis should be more than sufficient for Gaxim to figure out how the treants were corrupted. I shall give this to him right away.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1085, 'enUS', 'Treacherous... treacherous and dangerous, that\'s what this land is.$B$BBut I\'m here to show the night elves that it\'s a little safer with the help of good ole engineering and a dash of magic.$B$BSee, swearing off magic completely won\'t help the night elves--they need it. The world needs it. And with engineering and alchemy to boot, you can\'t go wrong. The possibilities are endless.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1086, 'enUS', 'You found the port? Well done, $n! I am sure the goblins were quite surprised from your delivery! Let us hope that toxin finds its way to their headquarters.$B$BYou have served your people well, and I advise you to ignore any guilt you might harbor for poisoning the Venture Company. They are deserving of far worse.$B$BAnd if there is one mote of wisdom the Forsaken might teach its allies, it is this:$B$BDo not shy from dark paths... if those paths lead to victory.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1087, 'enUS', 'Already I can feel it... yes... power so sweet... the rushing flood. The kaldorei are foolish to spurn the gifts of this world, to reject the coursing of magic as it flows singing through your veins.$B$BWhat a pale mockery life is without its burning embrace!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1088, 'enUS', 'I knew Ordanus once, long ago. He had the arrogance of his kind, holding himself above even the Highborne.$B$BHis head does not rise so high now, does it, $n?$B$BI promised you a reward, and you shall have it. Attend to me.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1089, 'enUS', 'As the three keys are slid into their matching openings, in the middle of the glowing light a number of items appear.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1090, 'enUS', 'Thanks, $n. It was looking a little dicey there for a second and we\'ve probably drawn far too much attention to ourselves.$B$BThe important part is that we have the ore sample, though, and we should get it to Ziz as soon as possible.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1091, 'enUS', 'This is interesting indeed.$B$BYou and Gaxim have done very well, $n. Thank you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1092, 'enUS', 'Ahhh, I knew that I did the right thing by telling Piznik to stay behind. I\'m sure the engineers in Undermine will be most interested to look at this...$B$BOf course I\'ll want to keep a little piece for myself, but what they don\'t know won\'t hurt them, yes?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1093, 'enUS', 'Hey, is that what I think it is? Great!$B$BNow, let\'s see. Ugh... how am I supposed to understand this stuff... can\'t figure out the way gnomes think. Lemme tell you, I\'m surprised they could build the wheel, let alone those flying machines they like so much.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1094, 'enUS', 'Fizziks, eh? Can\'t say I trust him, but this information seems good enough. What I wouldn\'t give to have the Venture Company out of my hair.$B$BThey do come up with some good machines though... I can\'t deny that we\'ve benefited from using some of the improvements they\'ve devised.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1095, 'enUS', 'Eliminate Gerenzo? Now there is a prospect that I have no objections to.$B$BGnomes are bad enough as it is, but what I learned about Gerenzo only makes it worse. Apparently, he was one of the head engineers in Gnomeregan, where he was working on their secret project--we could never infiltrate Dun Morogh well enough to find out specific details. It was a failure. Our reports say that whatever it was literally exploded in their faces.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1096, 'enUS', 'Look at that thing... it\'d take a gnome to come up with something like that. I can\'t understand why you\'d cut off an arm, and replace it with that, when you could just keep both arms and add a third!$B$BGnomes, I tell you...$B$BAt least Gerenzo\'s out of commission. Hopefully that\'ll be enough proof, and Sputtervalve will assign me to one of the Tinkers\' Union workshops in Gadgetzan or maybe even Undermine.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1097, 'enUS', 'You\'re here to help with my delivery? Very good!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1098, 'enUS', 'Vincent\'s body lies broken, and still.$B$BAlthough Deathstalker Adamant will send his report to Hadrec... whatever additional details Vincent gained must remain with him.$B$BBut clutched in his hand is a small trinket...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1100, 'enUS', 'You say you found this journal on a deceased dwarf? Give me but a moment to peruse the pages...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1101, 'enUS', 'The crone has been laid to rest. This is fantastic news, $n.$B$BIn these times of peril, it is refreshing to see one as brave as yourself.$B$BWith Razorflank\'s minions taken care of, our studies in the area can continue. Perhaps now we can gain further knowledge of exactly what happened to corrupt the resting place of Agamaggan.$B$BHowever, I fear the answer to that question lies in treachery as well...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1102, 'enUS', 'I see the nasty tribe of Razorfen Kraul now knows the pain of my people. Serves the foul beasts right.$B$BTo drive one from a holy land is a sin worthy of the most severe revenge.$B$BThank you, $n, for aiding me in my plight.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1104, 'enUS', 'All right! This venom is going to work great! Thanks, $n!$B$BNow I just need to boil it down, apply skipper-otomer, add a nega-catalyst, and... Drat! Where are my notes!?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1105, 'enUS', 'You got them! Thanks, $n!$B$BWow, these shells are harder than I thought! Heh, when I work with them I\'ll probably bend a whole box full of tools!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1106, 'enUS', 'Fizzle sent you? Well that changes things! Of course I\'ll help you. Fizzle and his brother are two of the few people who don\'t want me dead!$B$BSo Fizzle wants to make pistons, eh? Pistons strong enough to take whatever stresses he has planned, and if I know Fizzle, then this new car of his is a volcano on wheels!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1107, 'enUS', 'Thank you, $n! After I grind these down and fuse them with a few other ingredients, the composite will be ready. And after that, the car will be safe from its own speed.$B$BSafe, as long as it doesn\'t hit anything...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1108, 'enUS', 'Ah yes, these will do fine. Now let\'s put them in some heat and see how they fare...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1109, 'enUS', 'Splendid! This is just the start we needed, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1110, 'enUS', 'Whoa, look at all that! There\'s some good stuff in that heap of junk. I\'m sure I can pawn it off for a hefty sum.$B$BAnd... is that an ear I see squashed between those two sheets of metal?? Ouch! Heh, I sure wouldn\'t want to be that pilot!$B$BThanks, $n. Here\'s your pay. And here\'s some advice, free of charge...$B$BIf you look hard enough, you can always find profit in someone else\'s tragedy.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1111, 'enUS', 'Oh yes. Parts for Kravel. I\'m glad you\'re here. These things have been sitting out here for days, and I was afraid they\'d rust!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1112, 'enUS', 'Great, you got them! Now we need to get them to the gnomes. But first...$B$BLet\'s take out one small item...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1113, 'enUS', 'My what an abundance of gore you have brought me, $n! Your efforts have been most excellent!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1114, 'enUS', 'There they are! My hydro ratchets and repeater bolts! These will do just fine. Thank you!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1115, 'enUS', 'You want to know about Nazz Steamboil? He\'s a racer pilot out in the Shimmering Flats, isn\'t he?$B$BYeah, I might be able to find something out about him... but it won\'t be easy. Or fast.$B$BOr cheap.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1116, 'enUS', 'Well, look at that. You found the dream dust!$B$BAnd how did you like the swamp, $n? It\'s not easy to get a hot meal there, is it?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1117, 'enUS', 'Well well! This is interesting... very interesting. Heh, perhaps I should bow to the goblin pilot the next time I see him.$B$BThis is worth plenty, $n. I\'m sure Nazz will pay a small fortune to keep his secret... a secret.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1118, 'enUS', 'I see. Kravel has a big scheme planned. He has a sinister mind -- I like that about him.$B$BSo you\'ll need something to addle wits, eh? Something powerful...$B$BI wonder if the mixture Zanzil feeds his zombies could be used...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1119, 'enUS', 'Wonderful! Fizzlebub came through! I\'ll remember that next time I see him.$B$BAnd thank you, $n. You\'re doing a great job, and we both stand to make a lot of money from this...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1120, 'enUS', 'Stout? How considerate! Thank you!$B$BWe shouldn\'t be drinking with the race coming up, but... maybe my boys could do with just a little. Besides, it can\'t be that strong, can it?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1121, 'enUS', 'What do we have here? Booze, eh?$B$BWell, I don\'t mind if we have just a little...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1122, 'enUS', 'It says here that the fool\'s stout was a success. That\'s good news!$B$BIf you ever want more of the stuff, then let me know and I\'ll have you gather more of Zanzil\'s Mixture.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1123, 'enUS', 'I am Rabine Saturna, and on behalf of Keeper Remulos and all of the denizens of this sacred glade I bid you welcome.$B$BI appreciate your interest and eagerness to assist the Cenarion Circle. The tauren have shown an incredible propensity to the tenets of Cenarius, and by their proxy the Horde is welcomed within the safety of this glade. The preservation of Azeroth is a common goal we both share.$B$BYou are among friends here, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1124, 'enUS', 'Rabine sent you, did he? Well, consider yourself welcomed here... as much as this desolate and forsaken land can welcome someone.$B$BI wasn\'t alive during the time of the great tribulations that took place here, but there are parts of this desert that might as well be eerie pictures of that forgotten past. With your help, the Cenarion Circle might be able to bring finality to a threat that was supposed to have died generations ago.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1125, 'enUS', 'I had thought that maybe the huge swirling hive near the ruins of the village had played a part in its demise. It would seem that your work has proven this to be correct; the proximity of the hive poses a threat to any future work we may do here.$B$BI thank you for your benevolence in freeing the souls of the fallen; such work is never easy, I know. When you\'re ready, however, I\'d like to ask for your assistance once more...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1126, 'enUS', 'What the... what is it?$B$BIt has the shape of a... box, maybe? It\'s encrusted over something fierce. Wait, there\'s a portion here that\'s exposed... is that a book underneath all this gunk?$B$BI think you\'ve stumbled across quite the find here, $n... well, at least I hope it is. Perhaps it is a journal or a log from ancient times, preserved in this shell.$B$BI think I know a way to find out what this is.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1130, 'enUS', 'So, $n, you have come. Now, are you prepared to journey through distant lands?$B$BIf so, young $C, then listen on...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1131, 'enUS', 'In defeating the creature Steelsnap, you have cleansed the Thousand Needles of a horrible taint.$B$BAnd that, $n, is your lesson this day.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1133, 'enUS', 'You are here to offer your services to the Alliance, $n? We welcome the aid, for although its beauty remains... Ashenvale Forest is not the peaceful place it once was.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1134, 'enUS', 'Thank you, $n. It saddens me to order the slaughter of creatures who may not willingly aid our enemies, yet it is my duty to do so.$B$BYour heart might weep for the pridewings you slew, my friend, but may your heart then swell in knowing that your allies are safe from the pridewings\' venom.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1136, 'enUS', 'Both strength and cunning were needed to find and defeat Frostmaw, $n. You have both.$B$BYour path has taken you to the valleys of Thousand Needles and to the high mountains of Alterac. It will one day lead you through all the Earthmother\'s lands, for such is the way of the hunter.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1137, 'enUS', '$n, you\'re back! You found Martek? You spoke with him? What did he say? Can he help??$B$BTell me, tell me, tell me!!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1138, 'enUS', 'Wow, these fine crab chunks are what I need. Thanks $n! I found this while I was fishing a while ago, and I have no use for it. Maybe you can do something with it. It sure don\'t make any good crab dishes.$B$BI told you all about crabs, right?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1139, 'enUS', '<Advisor Belgrum shakes his head in disbelief as you hand him the stone shard.>$B$B$n, this is amazing. I shall have this brought to the king\'s mages as soon as possible. They will want to study it to find out if the legends are true. But that is a discussion for another time: you have aided our people more than I would have ever expected--thank you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1140, 'enUS', 'Perhaps the satyrs have been allowed to persist in the forest for too long. I will speak with Elissa about it when next I travel to Auberdine.$B$BNonetheless, with the soulgems destroyed, Athrikus\' power will be greatly diminished. We must seize on this opportunity to eliminate him and his cult.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1141, 'enUS', 'Them\'s some real nice groupers, $n! Thanks for helping me out!$B$BYou\'re at least as good of a fisherman as my cousin, Graun Blump. She also has a beard and she smells kinda funny, but that don\'t do nothing to stop her fishing. I reckon you have what it takes to use a Blump Family Fishing Pole.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1142, 'enUS', 'No!$B$B\'Tis not fair. Oh, cruel and intolerable world!$B$BI must now carry this burden of grief and not let Heralath\'s work die with him.$B$BYou were brave to journey from such a dangerous place, $n. But please leave me now.$B$BIn solitude I shall mourn.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1143, 'enUS', 'With the death of Athrikus, I hope that the demonic forces in this area will be driven back. I have no doubt that they will regroup and return, but then we shall be even more prepared to meet their threat.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1144, 'enUS', 'Thanks again, friend! I told you I\'d make it worth your while.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1145, 'enUS', 'Ah, a note from Korran. He has been a loyal servant for some time now. I was wondering how his duties were going in the Crossroads.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1146, 'enUS', 'Don\'t mind the thundering echoes off the mountains\' walls. The goblins and gnomes are out among the Flats racing one another in atrocities created by what they call science. There are bigger threats here in Thousand Needles, one of which killed more than half my war party.$B$BBut we remain here until we hear word from Belgrom in Orgrimmar. Until then, we hold our position here, and stay vigilant.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1147, 'enUS', 'Ha! That ichor you\'re covered in actually makes you prettier, $n! It certainly does make you reek at the same time though. Don\'t stand too close to the kodo; you\'ll either start a stampede or cause them to go into heat.$B$BI think my companions would agree that your smell alone is proof you\'ve done your part to help us against those insect creatures. Thank you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1148, 'enUS', 'Ahh, you\'ve seen Moktar recently? He always be lookin\' out for me. I\'m sorry to hear he spends his time watching over his little war band instead of seeing battle, but I\'m sure he be takin\' great joy in killing members of the Explorers\' League.$B$BYou be smart to bring me these specimens, $n. Along with the eggs we be findin\' in the Field of Giants, these should give me a good start in figuring out what these insects are, and what they be wantin\'.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1149, 'enUS', 'It is not until faced with unforeseen adversity that we begin to see how strong our spirits are. You took the leap of faith even though you had no guarantees that you would survive. Your mind is open, and your spirit is prepared for what is to come.$B$BIf you are willing, then the next of your tests is ready.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1150, 'enUS', 'Grenka was powerful, even by $R standards. You should be proud of your accomplishment this day.$B$BDuring your test of faith you displayed strength of spirit, and now you have shown endurance in battle by taking on the Roguefeather harpies, but the time has come for a new test.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1151, 'enUS', 'The Test of Strength is done. You have shown strength and bravery, and you are now prepared for the final test before these rites are completed.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1152, 'enUS', 'The days and nights grow colder throughout the world as goblins rape the land, and the night elves point fingers at the younger races. The Forsaken and orcs are not the only ones to blame for the hardships found throughout Kalimdor and Lordaeron. Many of the intelligent races have contributed to the world\'s pain. Dorn knows this, and he has sent you to me because you have shown the attributes necessary to realize it also.$B$BNow you stand before Braug.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1153, 'enUS', 'A good sample, $n. A good sample indeed.$B$BThose travelers can no longer lord over me the ore they found, now that you have been successful.$B$BI shall get to work on it immediately. If it proves strong enough, I will send a weapon crafted from the material back to the warchief to show him our success.$B$BI shall not forget your help, $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1154, 'enUS', 'This is the book I named. There is hope you will answer my question correctly then... unless you\'ve forgotten to study its contents.$B$BWhen you are prepared, speak to me again and I shall ask a question of you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1159, 'enUS', 'Braug sent you? Impossible.$B$B<Parqual looks you up and down.>$B$BBraved Dor\'Danil, took a leap of faith, fought and overpowered a powerful elemental? Bah! You\'re probably a farce; some adventurer that overheard talk in a tavern. And now you think you have what it takes to pass my test. So be it... $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1160, 'enUS', 'This is the book I sent you to search for.$B$B<Parqual slowly caresses his hand over the top of the book as he stares down at it.>$B$BSo now you wish a question to prove your mind is as powerful as your body and spirit. If you have studied the book long enough, then I shall ask you your question and you can be done with these tests!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1164, 'enUS', 'You stand before me as a figure of vengeance, and I bow down before you in thanks.$B$BWith that cow\'s death, and knowing that their seed will no longer spread over the face of Azeroth, I can put my energy into something more lucrative. I hope you looted their farmstead after you finished them off.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1166, 'enUS', 'You good $R. Me miss things very much. Me like you now.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1167, 'enUS', 'Ah, it\'s good to see you again, $n! The strangest things have been happening around here, I\'m glad you showed up!$B$BIt was very strange, $n. Everything seemed normal--well, as normal as things get around here--and then suddenly there was a blinding flash of light from the upper room of the tower. Then came the screams. The cries split the air, and lightning struck the ground from time to time. What\'s going on?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1168, 'enUS', 'You kill many of the black dragon army. You brave $R.$B$BMake Tharg happy.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1169, 'enUS', '\'Twas a perilous affair, no doubt, collecting all of these hearts and tongues. I shall delve into the depths of the arcane to trace the origin of the black menace which has descended upon our former home.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1170, 'enUS', 'Why you tell me \'bout dragons? Me no like dragons. Me like it here. No dragons here.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1171, 'enUS', 'That lazy coward!$B$BMok\'Morokk displayed strength when Rexxar was our leader. But soon after Rexxar\'s departure Mok\'Morokk fell prey to temptations of power. Rather than lead, he demanded. He does not serve his people but rather expects us to serve him.$B$BSomething must be done.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1172, 'enUS', 'Splendid! It will be sometime before Onyxia can produce more eggs.$B$BOn behalf of my people, I thank you, $n.$B$BI have asked much of you so far. Perhaps you can do the Stonemaul Clan one last service.$B$BMok\'Morokk is not fit to lead us. Tharg would make an excellent leader but is hindered by his injuries and age. Drive Mok\'Morokk from this place by force. Only then can we appoint a new leader to return us to our proper home in the south.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1173, 'enUS', 'That cowardly fat mass Mok\'Morokk fled like a scared child. You have done a great service, $n.$B$BNow my people can appoint a new leader, one who will help us reclaim our old home once and for all.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1175, 'enUS', 'I hope you didn\'t kill any of the basilisks on the tracks themselves! It takes a long time for the carcasses to decay in the sun, and I\'d rather not have to take a shovel out to clean up.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1176, 'enUS', 'Great! I can get the crew working on this right away. Thanks, $n!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1177, 'enUS', 'Me like fishy head!$B$BMe no eat you now.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1178, 'enUS', 'Pozzik sent you, huh? You know, Pozzik was one of the most respected tinkerers in Undermine before he retired to start working on the racers.$B$BThe first thing he did when he joined the team was develop the two engine model racers. Could be that the businesses will suffer without him working with the Tinkers\' Union, but who cares, right? There\'s nothing like the races!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1179, 'enUS', 'Ah! Longbeard sent you with a load of helmets! Hooray!$B$BNow maybe we can talk a pilot into driving our car...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1180, 'enUS', 'Shipments to Ratchet? Let me just check the logbooks...$B$BNo, the most recent shipment I have on record of going out to Ratchet were boots from Drizzlik. Hasn\'t been much else, other than building supplies and tools. Definitely no race parts.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1181, 'enUS', 'Oh, sure of course I know what you\'re talking about. Nope, can\'t help you though. Some of my men were supposed to go track down the blueprints for the fuel regulator, but they came back empty-handed.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1182, 'enUS', 'Now, let me just get a copy of these blueprints for my own notes and you can bring these straight to Pozzik. Wouldn\'t want him to think that one of his sponsors was abandoning him.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1183, 'enUS', 'I was hoping that it would be already assembled, but this does present the unique opportunity to improve on the design from the ground up. Thanks, $n. I\'ll be able to sketch up some improved schematics, build the thing, and have it installed real soon.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1184, 'enUS', 'I\'m appreciative of your efforts, $C. The warchief will enjoy hearing how tenacious you have been in your duties.$B$BOh, of course I will mention your name to him. He speaks to me often and values my opinions greatly.$B$BGo now, I have much to do. And if you speak to Korran soon, and he has news of these creatures, then return to me--I would think they are a threat that must be taken seriously.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1185, 'enUS', 'Hey, it\'s you again! Well, I mean you - as in the you that is $n! Yes - that you!$B$BHey, let me start over!$B$BThat object you found in Silithus - it was in fact a book. It was a journal written in a very ancient dialect of what we now call the Darnassian tongue. It\'ll take a while, but I\'m going to go over it very thoroughly. I thought you should know that what you found was extremely valuable for the Circle.$B$BThe den mother\'s all excited and everything!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1186, 'enUS', 'You\'re the $R that\'s been helping Pozzik out, right? I was like you once, running around the pit area, helping with whatever work the mechanics would let me do.$B$BNow look at me! The goblin racing team\'s pilot extraordinaire!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1187, 'enUS', 'Just take a look at this beauty! Fifteen pounds of solid seaforium power! Once I get this strapped on to the side of my racer, I\'ll be around the track so fast I\'ll beat the gnomes twice!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1188, 'enUS', 'Only Razzeric would take a look at this and say that I\'d somehow put some sort of safety lock on it. This booster is almost completely seaforium explosives. I already modified it so that more of its payload would be delivered upon use, but Razzeric is never content unless he goes to eleven!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1189, 'enUS', 'Now that\'s more like it! I wish I could see the look on the gnomes\' eyes when they see me set this baby off.$B$BToo bad I\'ll be too busy pulling off a new course record!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1190, 'enUS', 'Scribbled in a hasty, hurried hand, a scroll of notes and designs for the refinement of some raw material.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1191, 'enUS', 'Let\'s go!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1194, 'enUS', 'Indurium? Interesting... If it is capable of resisting heat to the degree that you say, it would explain the performance increase the gnomes have displayed of late.$B$BThis sample you\'ve brought to me will be invaluable, also. I will be able to perform some tests on this, but I\'ll need more for this to actually be of any use to us.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1195, 'enUS', 'Very good. This should fill Rau\'s request just fine. Could you deliver this to him? You might enjoy spending some time out in the Needles.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1196, 'enUS', 'Hm, yes, this phial of water will serve our purposes perfectly. Because of the shared ancestry of the dryads and the centaurs, the water of the moonwells will enable us to breach the protection of the eternal flame surrounding the centaur relic.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1197, 'enUS', 'I sense a strange energy emanating from the Hoof... I have no doubt that we will discover surprising new information about the centaur from its study.$B$BMy thanks, $n. Now I must begin to decipher the mysteries you have brought to me.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1198, 'enUS', 'Dawnwatcher Shaedlass was good to send you. There is indeed a wicked plot underway in this corrupted place.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1199, 'enUS', 'You are no doubt of brave and noble blood, $n. The Argent Dawn commends you for your efforts against evil.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1200, 'enUS', 'Kelris has eluded us for quite some time.$B$BIt seems as though whenever evil made its presence known in these parts, Kelris had played a role. For some time we considered him dead or missing.$B$BBut this makes perfect sense. By ending his reign you have spared the lives of many innocent people.$B$BBy the light! To sacrifice someone to a servant of an Old God for one\'s personal gain is beyond reproachful!$B$BYou have done a great deed, $n. I salute you on behalf of the Argent Dawn.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1201, 'enUS', 'Good work. Their infiltrators are skilled, but they\'re not good enough to escape the watchful eye of the Horde!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1202, 'enUS', 'I commend you, $n. This was not an easy mission. But the documents you gained are valued greatly by our superiors.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1203, 'enUS', 'That\'s a good blade. It has a good swing. It\'s a good swing blade. Thank you. Thank you, thank you, thank you!!! Now I want to try it.... $B$BLater. I\'ll try it later. But like I was saying... thanks.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1204, 'enUS', 'These will do fine! Thank you, $n!$B$BNow I\'ll try different cooking techniques on these tongues to find the best way to bring out their flavor.$B$BEventually I\'ll have a dish worthy of serving at the Blue Recluse in Stormwind!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1205, 'enUS', 'Very good, $n.$B$BI am sure the hunt was not easy, but it is good to know the beast\'s pain has finally ended.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1206, 'enUS', 'You got the eyes! You got the eyes! Tasty, tasty spider eyes!$B$BSome eyes... when you stare at them they stare back. Eat those last. They keep you company... out in the swamp.$B$BHello, little spider eyes. Hello! You taste like chicken. Gooey, salty chicken!$B$BMmmh.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1218, 'enUS', 'Ah, that\'s the spice! Now here\'s some swampy good stew.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1219, 'enUS', 'Hm... I can\'t read orcish very well, but this looks like a report... written by an orc spy!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1220, 'enUS', 'A report from an orc spy! How did you find this? No matter -- let\'s see what it says...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1221, 'enUS', 'Great, you got them! I can sell these to my contacts for a bundle!$B$BThanks for the delivery, $n! You are a great partner.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1222, 'enUS', 'Mr. Ignatz was just here with the bogbean leaves, and he told me you helped him out of some real trouble.$B$BThank you, $n. I will ask my uncle to treat you to a feast the next time you visit the Blue Recluse in Stormwind.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1238, 'enUS', 'Good work, $n. Our agent, Marg Nighteye, was stationed outside Theramore. He was charged to spy on the city. $B$BThis is his latest report, and it may be his last -- from your testimony, I fear that Marg met his end in the swamp...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1239, 'enUS', 'This is the head of Marg Nighteye! As I feared, he is dead.$B$BLook at the wound on his neck! Could a weapon have made that ragged cut, or did some beast slay our agent?$B$BThis is a mystery we must solve, $n. We must discover if Marg\'s death was at the hands of the Alliance!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1240, 'enUS', 'Ah, I now see. The spirit of this orc is bound in our world, boiling with rage from a task yet undone.$B$BWell, let us have words with this angry orc...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1241, 'enUS', 'Hmm... <Jorgen looks up at you through squinted eyes.>$B$BAll right. I\'ll help ya, but not because I want to, but because I\'m obligated.$B$BAnd let\'s get one thing straight: you\'re knowin\' far more than you should to begin with. Let\'s keep this our little secret, and don\'t go blabbin\' at any taverns about anything you find out. Understand?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1242, 'enUS', 'I\'m sorry, a letter for me? Well, I\'ll be... not often you get a delivery that\'s not a crate or barrel when all you do is work. Let\'s see what this is about.$B$B<Elling looks up at you while he opens the letter.>$B$BYou look a little tense, you okay, $n?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1243, 'enUS', 'Oh, Trias sent you, huh? That\'s a little different.$B$BFeel free to fight whatever monstrosities come at you from the darkness. I\'m sure you\'ll be able to handle it pretty easily if you\'re the type to be hanging around with the likes of him.$B$BSo, what does my friend need of me on such a gloomy day as this?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1244, 'enUS', 'You\'re back safe and sound. Couldn\'t ask for much more than that, could you?$B$BNow, let\'s see what we have here. This looks pretty official as far as any Defias documents I\'ve seen. But it could be a dupe just to throw you off track of whatever it is you\'re looking for.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1245, 'enUS', 'Yeah... this is an interesting read. Jorgen and DeLavey were smart to bring me in on this.$B$BSeems the Defias have been trying pretty hard to complete this plan of theirs. Hmm... what\'s this? \"Fist\"? That\'s a familiar name.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1246, 'enUS', 'What?!$B$BYou come into me alley and ask questions about me personal business? Business which you obviously have nothing to do with.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1247, 'enUS', 'Good to see you\'re still alive, $n. So, it went well with Dashel then, huh?$B$BI\'ve been reading up more on this plan of theirs, and although it doesn\'t state the name of the diplomat they were after, it does talk about this plan B they enacted after failing on their first attempt to capture him.$B$BThis Slim that Dashel mentioned doesn\'t have any other name written here, but if you say he\'s in Menethil, then that\'s a start.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1248, 'enUS', 'Welcome, welcome, welcome, $n. Good of you to join us. Perhaps an ale today? Or some mutton?$B$BName your pleasure and I\'ll see to it that you\'re taken care of. My name is Mikhail, and I look forward to serving you.$B$BHmm? What\'s that? Trias? Keep your voice down, my friend. This isn\'t something for the whole tavern to hear about.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1249, 'enUS', 'Caught him, huh? Good for you. I\'m surprised that was Slim.$B$BI\'ll be honest, I never thought I\'d miss the excitement of some of the more devious underground activities. I guess old habits die hard. Don\'t mention that to Elling--he\'d probably try and get me to come back to Stormwind if he heard I missed the action.$B$BI\'m glad I could at least be of a little service to you while you were in Menethil. If you need anything else, just ask.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1250, 'enUS', 'THE KING?! I mean... the king? The real king? Are you joking? You didn\'t mention you were looking for the king\'s kidnappers. I didn\'t even hear he was missing--that would explain his son being crowned some.$B$BI\'ll send word to Elling right away. He\'ll want to know you\'re on the trail.$B$BI would never have guessed Jahn would sell out Stormwind for some lousy coin. Even a rogue\'s got to have honor when it comes to some things.$B$BThe key now is to find this Hendel. And I think I can help with that.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1251, 'enUS', 'I grant that humans have strange decorating tastes, but would they use a plain iron shield to adorn their fireplace? Perhaps this shield was placed there by the ones who burned the inn.$B$BA warning? A threat?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1252, 'enUS', 'It\'s never good news when a guard\'s badge is brought to me.$B$BSometimes it\'s a man or woman who\'s fallen in battle, and I\'m left to bring the news to the grieving family. Other times we never find out what happened to them. And other times it\'s even worse than that.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1253, 'enUS', 'Hm, I\'m no expert with the construction of arms and armor. Their use, yes, but not their making.$B$BThis shield seems as ordinary as any, excepting the damage from the fire that engulfed the inn.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1258, 'enUS', 'These are wonderful specimens, $n! The shells are in perfect condition, and the meat is pink and tender. The blood of a chef is in your veins, I\'m sure of it!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1259, 'enUS', 'Hello, $n. You\'d like information about a specific member of the Theramore guard? Reethe, was it?$B$BJust give me a moment to find the right book.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1260, 'enUS', 'Oh, my uncle sent you, did he?$B$BWell I\'ve been researching recipes and I have some leads, but I need help.$B$BHelp from someone willing to get his feet wet.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1261, 'enUS', 'This pendant was worn by a soldier of Stormwind??$B$BThank you, $n. You have revealed the secret of Marg\'s death. But this pendant is a new mystery to solve, one that may have dire effects on the Alliance, and the Horde.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1262, 'enUS', 'This pendant was crafted with great skill and precision, $n. No orc would covet such a jewel...$B$BBut many humans would.$B$BI will have this pendant studied. In time let us hope that its mysteries will be revealed to us.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1264, 'enUS', 'A pleasure to meet you, $n. I am Commander Samaul. I\'m in charge of Miss Proudmoore\'s forces here in Theramore.$B$BAlthough I\'m quite busy, if you have any questions, please feel free to speak up. I might have to keep it short, but there\'s no reason for me to be rude if you need my help.$B$BIf you plan on traveling out of the city, I\'d suggest you be careful. Besides ogres, orcs and murlocs, the southern part of the swamp is filled with black dragons.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1265, 'enUS', 'The man you seek, the one known as Hendel, is not here. Other sources have brought to the Lady Proudmoore information I believe you already know. She has asked me to find you, and give you aid in your venture. If you would allow it, I will tell you where to find Private Hendel so we can bring him to justice.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1266, 'enUS', 'Hello, citizen. This is a dangerous place to be wandering about. I wouldn\'t hang around too long if you value your life.$B$BIf you keep your questions short, I\'ll see if I can help you. Just keep your eyes open for any of those filthy ogres. Rumor has it there\'s a Horde base somewhere in the swamps. The infiltrators are doing what they can to find it, but the process has been slow going and we\'ve had little progress.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1267, 'enUS', 'Let me be first to thank you for all you\'ve done, $n. You have been an incredible blessing to my people and the people of Stormwind. I am sorry I couldn\'t make your acquaintance under better circumstances.$B$BUnderstand also that I cannot overtly thank you for your efforts, but I assure you again, it is appreciated.$B$BWhen you visit Theramore in the future, seek me out and I shall try to keep you appraised of the situation.$B$BThank you again, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1268, 'enUS', 'Hoofprints... It could have been the quilboar, I suppose. Or maybe even the centaur. Their presence in the Barrens is not insignificant, and I would not think it a stretch to commit such an act.$B$BDid I tell you? It wasn\'t just that the inn was burned down... The owner\'s woman and pup were killed. Bad business, that.$B$BNonetheless, I will send Kagoro to find where the tracks lead.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1269, 'enUS', 'I know this human, Paval Reethe. Pathetic, even for his kind. He deserted his fellow soldiers and is now hiding out in the woods.$B$BI sent Ogron out to track him down. Some of the others suspect he\'s been stealing from our supplies. I think I saw Ogron heading off to the southeast.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1270, 'enUS', 'Stinky was here with the herbs I ask of him, and he told me you helped him out of a bind. Good work! A friend of Stinky\'s is a friend of mine!$B$BHe asked me to give you this if I ever saw you, right before he walked back south toward the marsh.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1271, 'enUS', 'My nephew said you aided him with his research in Dustwallow, and that you helped his associate out of a tight spot. Well, we Sterns stick together and we don\'t ignore our friends!$B$BSo wash your hands, $n, because we\'re about to throw a party in your honor!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1273, 'enUS', 'Hmph. It is good that Reethe will no longer be lurking around the swamp, but it is a shame that humans are built so frail that he could not recover from a little arrow wound.$B$BIt seems that this thread of the mystery has led nowhere, and we must look elsewhere.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1274, 'enUS', 'Hello, $n. I am Bishop DeLavey.$B$BNo doubt if you\'re asking me about my business here in the keep then young Thomas has succeeded in requisitioning you to help our kingdom. He has always been adept at following my requests.$B$BI\'m afraid I don\'t have time to elaborate much, so allow me to be blunt... and please, try to keep this from becoming common knowledge.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1275, 'enUS', 'You have traveled far and undoubtedly faced great peril.$B$BYour actions have been selfless. \'Tis for the good of all Azeroth that you help me with my research. I thank you, $n, for all that you have done.$B$BNow if you\'ll excuse me I have much work to do....', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1276, 'enUS', 'Yes, you are certainly correct. This is a shield that I made.$B$BI\'m afraid there\'s not much more I can tell you though, $n. I craft so many different shields and sets of armor, I can\'t keep track of them all.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1282, 'enUS', 'James Hyal\'s his real name. He opened an inn at the edge of the Barrens. Wanted to make a nice place for travelers to stop over...$B$BMight be he was thinking too big, but the inn did do well for a while. Then we lost control of the roads in the marsh, and the ogres took over.$B$BJames was too stubborn to give up his dream for safety within our walls, and the Shady Rest Inn was burnt to the ground. We\'re investigating, but haven\'t had much luck. If you want to help out, see what you can find in the rubble.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1284, 'enUS', 'Hoofprints, eh? It is true that there is an extraordinary amount of centaur activity in the lands near the Dustwallow Marsh. In brief excursions into the Barrens, we found their camps and holdings dotting the area.$B$BI\'ll send out Falgran Hastil, one of our trackers, to see if he can find where the tracks lead.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1285, 'enUS', 'Missing? That would be Tesoran\'s kind way of saying traitor.$B$BNo doubt he\'s one of those fools so blindly loyal to Admiral Daelin that he turned traitor. Isn\'t that a delicious irony? The deserters accuse us of betraying the Alliance.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1286, 'enUS', 'Did Vimes send you? Couldn\'t do his dirty work himself, could he...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1287, 'enUS', 'I will send Lieutenant Caldwell to deal with the deserters at Lost Point. In addition, I will instruct him to find Reethe. The marsh is not so big and trackless that he should be unable to find him.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1302, 'enUS', 'James Hyal, James Hyal... Why does that name sound so familiar?$B$BOf course! The Shady Rest Inn incident. If you have any information about what happened there, I would urge you to speak with Captain Vimes about it. He\'s handling the investigation.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1319, 'enUS', 'Hm... interesting piece of iron you have here. Definitely not made by any of the blacksmiths I know over on this side of the sea, and it\'s certainly not my work.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1320, 'enUS', 'That\'s not much information to work on, but we\'ll have to keep it in mind. Hopefully some of the other pieces of this mystery will fall into place, and paint a more helpful picture of what happened.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1321, 'enUS', 'This is good shield. Very strong, heavy, good. But Do\'gol don\'t need it. I make shields, not buy them, $n.$B$BYou should know that.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1322, 'enUS', 'I don\'t like spiders that are so big I can\'t sit on them and squish them.$B$BThanks for getting them, $n. Now I rip them open and pour them onto the shield...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1323, 'enUS', 'Mosarn? Sounds familiar... I can\'t put my finger on it. Maybe it\'ll come to me later.$B$BIt probably isn\'t important. A shield can come from anywhere and not mean anything...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1324, 'enUS', 'I assure you, we\'re all quite upset about current events, but allow us to take him back to Theramore for some questioning, and we\'ll see about finding the King.$B$BI\'m sure my lady Proudmore would like to thank you for your help. Speak to her when you have a moment.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1338, 'enUS', 'Ah, a request from one of the Stormpikes. That\'s a proud clan, full of quality dwarves. Rich ones, too.$B$BThank you, $n. I\'ll get to work on Mountaineer Stormpike\'s shield by the end of the day.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1339, 'enUS', 'That\'s right. I have a task that I can\'t do while I\'m guarding. In fact, it\'ll take you far from Loch Modan.$B$BYour perfect chance for adventure!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1358, 'enUS', 'Ah, very good. If initial reports are true, then the toxicity of these hearts could lead to further advances in our knowledge of poisons.$B$BThis is a welcome delivery, $n. Your reward is well earned.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1359, 'enUS', 'Ah yes, the heart samples of which Renferrel spoke. I am eager to experiment with these... it is always of interest to the Royal Apothecary Society when new forms of toxins are discovered.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1360, 'enUS', 'Ah, I feel complete again - thank you kindly.$B$BSome dwarves may treasure the luster of metal, and some may treasure the glint of gems... but a Stoutarm without a treasured stein in hand is hardly a Stoutarm at all!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1361, 'enUS', 'Yes, I am recruiting. This mission leads into Desolace, against the centaur there. I will be brief so that you may quickly begin.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1362, 'enUS', 'You are here on orders from Regthar? Then you are here to deal with the centaur. Good.$B$BI hope you\'re rested. I don\'t know how our campaign against the centaur will end, but I know the struggle will be long.$B$BHave a seat, and listen.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1363, 'enUS', 'It wasn\'t my fault. Really it wasn\'t.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1364, 'enUS', 'Ah, most splendid!$B$BFor some time I had feared my request for Khadgar\'s Essays on Dimensional Convergence had been overlooked by Master Mazen of the Academy of Arcane Arts and Sciences.$B$BHe must be bogged down with requests considering the many strange happenings going on.$B$BNow, if you\'ll excuse me, I need to begin my research at once. To finally glimpse the insight of one as great as Khadgar excites me to no end.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1365, 'enUS', 'Well done. This swift act against the Kolkar is an effective show of strength, and strength is what the centaur understand best.$B$BThe death of the Kolkar leader will not only disrupt that clan, but give pause to the others.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1366, 'enUS', 'By now, the centaur must know your name. I\'m sure they know it, and respect it, and fear it. And just as they know you, they know the banner under which you ride.$B$BThe banner of the Horde.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1367, 'enUS', 'The Gelkis cry when they hear your name, $n. They fear you and they hate you. They hate you like they hate the Magram!$B$BMaybe you are strong like the Magram. We will see...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1368, 'enUS', 'The earth speaks to me. And it says you have killed many Magram. This is good.$B$BIf you are a friend of the Gelkis, then listen. I have more tasks for you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1369, 'enUS', 'You got the tears! Now the Gelkis will know that Theradras will not help them. She will not cry when we crush them! The Magram will rule!$B$BYou are smart to help us, $n. We are the strongest.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1370, 'enUS', 'Good. I can hear the loud Magram bellies grumble from their village, far away!$B$BI will give this meat to the Gelkis. And we will fight for many days without rest.$B$BWhat you did is good for the Gelkis, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1371, 'enUS', 'Yes, this is what I had! I will make it move again. It makes me laugh!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1372, 'enUS', 'Zraedus was just telling me about the nosey little Infiltrator before you arrived.$B$BQuite a problem, indeed. The last thing we need is a busybody sniffing around Beggar\'s Haunt.$B$BI am sure the orcs of Stonard would be quite anxious to hear what our friend has to say. Seeing that they are our allies, ahem, we must help them to get any information they can from the human.$B$BWork with me, $n, and we will deliver a truth serum to Stonard.$B$BFor the Horde!$B$B...did I get that right?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1373, 'enUS', 'Yes, this is the shard of which the earth whispered. I can feel strong magic in it! I will use this magic against the enemies of the Gelkis.$B$BYou have done one more task for us, $n. Maybe soon, we will call you friend.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1374, 'enUS', 'Khan Jehn was strong, but stupid. He thought his own strength was great. He did not see that earth magic is stronger. The Magram centaur will now fall without their leader, and the Gelkis will one day rule!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1375, 'enUS', 'Khan Shaka is dead! I will place his head on a pike, and keep it with me so Shaka can see the strength of the Magram!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1380, 'enUS', 'He is dead! Khan Hratha is dead! And now the Maraudine will fall! When the Gelkis rule this land, $n, we will remember you. And the spirits of the earth will sing your name for all time.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1381, 'enUS', 'Khan Hratha is dead! Now the Magram are the strongest of the centaur! We will crush the others, and rule Desolace!$B$BWe will remember, you, $n, and you will be spared our hooves when all others are trampled.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1382, 'enUS', 'Your name is whispered among the Gelkis. You have much Magram blood on your hands. This is good.$B$BMaybe it is also good if we speak. Speak of what you can do for the Gelkis, and what we can do for you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1383, 'enUS', 'Splendid, $n! And just in time too. From what Zraedus tells me, the Infiltrator has recovered from his wounds and is ready to be questioned by the orcs.$B$BGive me but a moment to prepare the pois-- I mean prepare the truth serum.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1384, 'enUS', 'You do a good thing for the Gelkis. The Kolkar are weak and will die. Their magic is pathetic and will not save them. You showed them this, and their fear will make them even more feeble!$B$BAnd in our lands, the feeble soon die.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1385, 'enUS', 'Many Gelkis fall to you. I like that. Maybe you can do more things I like. Maybe you and the Magram can be friends.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1386, 'enUS', 'The Kolkar fear you now! I hope you laughed when you killed them. It is better to laugh when you kill, so the enemy can see he is weak.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1387, 'enUS', 'Ah, the ears. Well done.$B$BYour service to the Alliance is unquestioned, $n. The blood you spilled in Desolace will, no doubt, save countless lives.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1388, 'enUS', 'I knew old Faustin would come up with something good! You were a tremendous help, no doubt.$B$BNow let\'s get down to business, shall we?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1389, 'enUS', 'Thank you, $n. We, are in your debt.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1391, 'enUS', 'A drink for me? How kind. It\'s been days since they\'ve allowed me to drink. Let me drink this down and then I\'ll tell you what I\'ve learned....', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1392, 'enUS', 'You have gained the weapon of the dreaded Noboru? Is he then dead?$B$BThis is a joyous day for the Harborage. Noboru and his gang were a terror to us, wandering the swamps and killing any Draenei who crossed his path. He was a direct threat to our survival, and we did not have the strength to face him.$B$BWe are fortunate to have a friend in you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1393, 'enUS', 'The keyhole of this strongbox is filled with black resin. It is impossible to open.$B$BBut Galen told you of a hidden latch on the box\'s bottom... $B$B...After a quick search the latch is found. The latch is flipped, and the strongbox opens...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1394, 'enUS', 'Ah, $n. It has been a long time since you have last stepped into my home. It seems like many moons have passed since you took your first leap that set you upon your path.$B$BAnd now you return to me stronger than you once were... more enlightened. This pleases me. And you should be justly rewarded for seeing my tests to the end.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1395, 'enUS', 'Ah, the powerstones! The mages will be happy to see these!$B$BMany thanks, $n. I know your trip from Darkshire was not an easy one.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1396, 'enUS', 'The sounds of animals are now faint, more distant. Thank you, $n. Now let\'s hope they don\'t return!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1398, 'enUS', 'This wood is light, but tough. Thank you, $n. It will work nicely.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1418, 'enUS', 'Helgrum wants a report, eh? I sent an outrunner to Stonard not three days past. Did he fail his mission? If Stonard does not have my report, then it appears he did fail.$B$BLet\'s hope the next runner I send has more skill, or more luck.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1419, 'enUS', 'That\'s a nice collection of jawbones, $n. Now those coyotes won\'t be tearing through our supplies for scraps.$B$BWell done. The coyotes of the Badlands aren\'t easy prey. Your deed shows me that you might just survive here.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1420, 'enUS', 'Thank you, $n. From Neeka\'s report, I see the Alliance is not without its own problems. And we may find that some enemies are shared between us.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1421, 'enUS', 'Ah, you found them! With these, the wizards in Nethergarde may continue their work. I do not know what it is that they do, but it is linked to the Dark Portal in the Blasted Lands, and is vital to the security of the Alliance.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1422, 'enUS', 'Be silent a moment, $C. Even over the dull roar of the sea you can still hear those disgusting gurgles from the fish men.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1423, 'enUS', 'Ah, thank you $n! So many supplies are lost from Darkshire to here. It\'s good to see that some are recovered.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1424, 'enUS', 'Your diligence in collecting the Atal\'ai artifacts is worthy of praise, $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1425, 'enUS', 'What? A package from Biggs\' caravan? We thought it, and he, were lost.$B$BThank you, $n. With luck, these supplies will carry the much needed reagent, Azoran Crystals. Our stock is running low...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1426, 'enUS', 'The sea seems vast and relentless, but you have stained her with the blood of her own spawn. The fish men know pain. Their stupidity will force them to refortify their ranks from the devastation you\'ve caused, and that is good.$B$BLet their gurgling screams overshadow the sea\'s roar long into the night. I shall smile every time I hear one of them silenced.$B$BReturn to me if you wish to slay more of the fish men. I will gladly reward you again.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1427, 'enUS', 'Ah, $n, you\'ve returned. Good. I\'ve finished crafting more weapons for our fight against the murlocs. For bringing us the fresh sawtooth meat, and for bringing a small smile to Katar\'s face, I offer one of them to you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1428, 'enUS', 'Well done, $C. I call for more of their blood. Let all the fish men perish.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1429, 'enUS', 'You bring me the summoning artifacts of the Atal\'ai?$B$BI see Jammal\'an the Prophet is leading the disciples of the Chamber of Blood towards their chosen fate. Has Hakkar returned to the Temple I wonder? Has their fate been met?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1430, 'enUS', 'Ah, this should be perfect. I can\'t wait to taste these after they are prepared perfectly over an open fire. You should take a sample of it with you on your travels. It truly is delicious.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1431, 'enUS', 'Putanni, noble $C. I am Keldran, student of magic... and other mystical arts.$B$BThe business Craven sent you here to speak to me about requires a touch more subtlety than you\'ve previously needed, but that does not mean you can not still aid our cause.$B$BPlease, make yourself comfortable, and I shall tell you why the Warchief has need of my aid... and yours.$B$BIt starts with appearances, $n. How the Horde sees itself. How our allies see us. And most importantly, how our enemies see us.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1432, 'enUS', 'Greetings, $C. You bring news from Orgrimmar; that is good.$B$BThe demon infestation overwhelms this area even more than the smell of centaur dung. The horse-men claim this land as their own, but clearly there are influences out of their control that will crush them if not kept in check.$B$BIt does not please me that we aid the centaur indirectly, but it is for the good of the Horde, so I do my duty for the Warchief, even if it means helping the warlocks within the Horde.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1433, 'enUS', 'Ah, yes. Aid from those loyal to the Warchief is always appreciated.$B$BMy name is Maurin. I am here at the behest of Keldran and the Warchief cub Thrall. It is my desire to aid the youngling leader against the evils that threaten his rule.$B$BCome, come closer, $C. I shall explain to you my plan for removing the festering boil that is the Burning Blade.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1434, 'enUS', 'You have done well, $C. You have shown honor and dedication.$B$BWhen our campaign in Desolace is done, the Warchief will know that you aided us in our actions here, and that you helped make his will manifest.$B$BI thank you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1435, 'enUS', 'Ah, you\'ve returned successful. That is good. Give me the gems and I shall put them someplace safe....$B$BMy tasks for you are complete, $C. Speak to Takata when you are rested. He may have other tasks for you, or perhaps needs you to return to Orgrimmar to inform Keldran of our progress here in Desolace.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1436, 'enUS', 'You have returned from Desolace more than successful, $n. Thank you.$B$BThank you for your efforts, your time, and your understanding to my plea. Thrall understands and desires peace between the Horde and Alliance, and by taking measures to insure our appearance is acceptable, we will not provoke any further threats of unnecessary violence.$B$BI am indebted to you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1437, 'enUS', 'As you rummage around the remains of the wagon, it becomes obvious that it was attacked by both spells and weapons. The battle seemed one-sided and quick; there\'s no blood to be found, and no remains of any bodies.$B$BWhoever attacked the wagon was efficient and swift.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1438, 'enUS', 'Thank Elune someone has finally come to help. Please, $C, I need your help. My brother Tyranis and I were captured and brought here by the Burning Blade. I think they mean to sacrifice me to their demon masters!$B$B I knew coming to Desolace was a mistake, but Tyranis has recently begun craving knowledge of all things arcane--even against the wishes of our people. He decided to make this trek even after hearing tales of this land\'s demon infestation.$B$BI fear the worst for him.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1439, 'enUS', 'This is terrible! But I suppose it was necessary. If it wasn\'t by your hand, it would have surely been by that of my people\'s. I suppose I should thank you--please forgive me if it doesn\'t seem sincere. I mean no offense.$B$BWith the knowledge of my brother\'s fate in hand, it seems the time for my escape has come. If you are still willing, I will accept your aid in escaping this horrible place.$B$BWhen you are ready, we can make our way to safety.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1440, 'enUS', 'Thank you, $n. Dalinda made it safely back here to Nijel\'s Point, and in much better condition than I could have asked for. The fact that she arrived here under her own power was a good sign. I hope you\'re well?$B$BIt pains me to know that both Tyranis and Dalinda didn\'t return, but the embarrassment of Tyranis\' decisions is hers to endure. It is something she will have to deal with when she returns to Darnassus.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1444, 'enUS', 'It has been quite some time since we spoke, $C. I hope your journey was successful.$B$BWhat knowledge did you gain from the exiled Atal\'ai in the Hinterlands?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1445, 'enUS', 'Brave $C, you have proven yourself to be a great champion of the Horde.$B$BNow this collection of fetishes of Hakkar must be dealt with at once!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1446, 'enUS', 'Jammal\'an\'s reckless trust of false visions led my people to their eternal doom.$B$BI thank you, $C, for avenging my exile. I mourn for my people. I have no home to return to.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1447, 'enUS', 'So, you came to me for a reason. What is it you need?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1448, 'enUS', 'The Temple of Atal\'Hakkar has been sunk beneath the Pool of Tears? And surrounded by the green dragonflight?$B$BAn amazing discovery, $C. But obviously something has gone terribly wrong.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1449, 'enUS', 'What do you want, laddy? Can\'t you see I\'ve got problems to take care of here? Who has time to stand around yapping with some $R chatterbox?$B$BThere are trolls coming out of the woodwork! Make it snappy.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1450, 'enUS', 'Ah, yes. Of course I remember sending one of my gryphon riders to lend a hand to Brohann Caskbelly.$B$BAlways had a soft spot in my heart for the Explorers\' League. My father helped with the initial excavation team that discovered Uldaman as a matter of fact.$B$BI sent who I thought was my best gryphon rider at the time with Brohann. He was a chap by the name of Rhapsody Shindigger.$B$BAnd what a gryphon jockey he was! Well until...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1451, 'enUS', 'Hiccup!$B$BI\'m a-lookin\' right at you but you keep bobbing up and down. Makin\' me dizzy. Hoho!$B$BHiccup!$B$BWhoa! Did ya feel that? Ground is a-shakin\', my friend. I need a drink.$B$BHiccup!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1452, 'enUS', 'I see ya had no trouble at all getting me what I need! Musta been quite a stint you had over there in far-off Kalimdor.$B$BIt won\'t take but a moment for me to extract what I need from these gizzards and livers. Hiccup!$B$BThen I can distill me a fierce ol\' drink and finally tell ya what ya wanna know.$B$BHiccup!$B$BGetting shivers just thinking about that ol\' swamp.$B$BHiccup!$B$BLemme whip up a drink real fast.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1453, 'enUS', 'Glad to see the Reclaimers aren\'t in short supply of work, or adventurers willing to help out.$B$BI\'m Kreldig, $n. You\'ve made a smart move helpin\' out the Reclaimers. Everyone needs something these days: they\'ve lost a family heirloom, a magic item, a loved one. You never can tell. But with the Reclaimers around, there\'s no reason, for the right price, everyone can\'t find what they\'ve lost.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1454, 'enUS', 'You notice the chest does not seem to have any water damage.$B$BThe second thing you notice is that the top has been forced open, and whatever magical traps were set to safeguard it have long been triggered--this much is obvious from the burn marks around its edges.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1455, 'enUS', 'Welcome back to Nijel\'s Point, $n. So, tell me what you found.$B$BWhat!? Nothing? An empty chest ain\'t gonna get us paid, I\'m afraid.$B$BTell me more about these marks you saw... these slashes.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1456, 'enUS', 'This is great, $n! You found Karnitol\'s belongings!$B$BThose blasted naga, always causing trouble. I think I\'d rather spend my life at war with the Horde than have to go against their kind.$B$BYou\'ve done a great service to the Reclaimers. Please, accept this with our thanks!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1457, 'enUS', 'Ah, the Karnitol business in Desolace. Excellent!$B$BSo, the chest was destroyed, but the items are all here, huh? Well, I\'ll let him be the judge of that. One good part about this business I got going on here is that if they\'re not satisfied, I can tell \'em to shove it! They\'re relyin\' on us to find their stuff... not make sure it\'s all pretty and in good shape when they get it back.$B$BGood work, $n. Let\'s see about gettin\' you paid.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1458, 'enUS', 'Good good, $n. Not sure if these are of any good quality, but if Leftwitch wanted a specific type, he should have specified before sending the Reclaimers out here to begin with.$B$BWhat say you to getting on with the next item on his list?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1459, 'enUS', 'Your help here\'s a boon, $n. You\'re cutting my time spent here in Desolace by half at least. I\'ll be back in Ironforge warming my feet in no time with your help.$B$BWhen we get this list taken care of, and I\'m back home, look me up, I\'ll buy you an ale for sure.$B$BBut until then, let\'s get the rest of these reagents gathered.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1465, 'enUS', 'Infuriating! This entire fiasco!$B$BThe two of them were warned about how dangerous coming here would be. Now they\'re both missing... or worse.$B$BYou said you saw tracks heading to a fort nearby? That may be our best bet.$B$BFrom what the locals have said, that fortress is infested with members of the Burning Blade--orcs who have dedicated themselves to demon causes. They may not represent the majority of the Horde\'s beliefs, but they are evil and will slay you on sight if you\'re caught spying.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1466, 'enUS', 'Honestly? I wouldn\'t have thought it. You\'re amazing, $n. Taking on demons for a mage\'s experiments, and coming back in one piece to boot. Great job!$B$BI\'m personally gonna tell Roetten about you when I get back to Ironforge.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1467, 'enUS', 'Ah, the package for Leftwitch from Desolace. Excellent. I \'preciate the work, and your timeliness, $C. Glad to have you aboard for at least this small bit of work.$B$BI got your payment right here. Let it be known that Reclaimers Inc. always pays well for a hard week\'s work.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1468, 'enUS', 'Um... hi. My name is Randis, and I guess you\'re looking after me? You\'re an adventurer, like my mom and dad were. I\'m happy to meet you.$B$BI hope we\'ll have a lot of fun together. I have some things I\'d like to do, and the matron says you\'ll be like a big brother to me during this week. I\'d like that a whole bunch, yes sir.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1469, 'enUS', 'Rhapsody Shindigger! That\'s right... how could I forget such a name?$B$BSo he knew about the sinking of the temple all along? Well, that certainly would have saved you some leg work.$B$BBut this Atal\'ai artifact! Why, it\'s quite a find!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1475, 'enUS', 'The Atal\'ai Tablets! Thank you, $n!$B$BOnce I deliver these to the Explorers\' League our names shall go down in history together. You have helped an old dwarf fulfill his final obligation in life. For that I am eternally grateful.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1480, 'enUS', 'Hmm, these runes are indeed a language, $n, an old language. They seem remotely similar to the Eredun language, but the differences are drastic. If I am not mistaken, this is a contract of some kind.... Here, yes, this symbol is the demon\'s signature: Lord Azrethoc.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1481, 'enUS', 'The shadowstalkers were unable to slay you, $C. Impressive... most impressive.$B$BThis scalp will do nicely for my spell. While you were away, I was able to gather some of the other reagents needed for the spell.$B$BI am almost ready. I only need a focus for the spell--something to project an image of the demon lord and his whereabouts.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1482, 'enUS', 'A precious oracle crystal... flawless... beautiful. Indeed.$B$BWe shall begin immediately. Finding this demon lord should be our primary concern, and its slaying demands our utmost attention.$B$BTake a step back, $C, and let us see what this demon is, and where he may be found.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1483, 'enUS', 'I thought those Venture Company higher-ups were slave drivers, but then I met Sputtervalve, the cruelest of them all!$B$BHa! Though it\'s better than being out of a job, I suppose... Or worse, working with that Gerenzo. If you\'re here to help, I\'ve got something for you to do.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1484, 'enUS', 'I witnessed what just occurred with Maurin and the demon lord. That creature, even as an apparition, seems to have more power than my warlock companion. I wonder if he\'s up to the task of defeating the monster? It would serve the Warchief better if I go myself, but with my responsibilities here, I would disgrace my clan if I turned my back on my duties here to battle this evil for my own honor.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1486, 'enUS', 'Your efforts shall not go unnoticed in gathering these hides, $n.$B$BThank you for your dedication.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1487, 'enUS', 'As a Disciple of Naralex, I commend your bravery, $n.$B$BYour aid in ridding the caverns is the first step in our long plight to see the Barrens restored.$B$BThank you and may you prosper.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1488, 'enUS', 'A mighty victory for us all, $n. Let us rejoice in your success. I trust now that our future here is not as bleak as I once thought.$B$BThe demons will be slain, the Burning Blade will fail in their efforts, and we may one day have a place we can safely call home again.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1489, 'enUS', 'I bid you greetings, $n. You are welcome here, and I suggest you gather all your strength. For the task we now set before you ... is a dire one.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1490, 'enUS', 'Yes, it is true. I know of the druids in the Wailing Caverns. I have seen their twisted, scaled faces. I know their crimes, and I know their plans.$B$BYou must stop them, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1491, 'enUS', 'Great, you got it! I can\'t wait to try it out. This is going to make me a fortune, I just know it!$B$BThank you, $n. Take this, and after I\'m rich I\'ll send you more, I promise!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1492, 'enUS', 'A small enough crate. Yes, I should be able to find room for this aboard the ship. It sails with the next tide, I trust this is soon enough? Excellent. Let me just enter it into my ledger.$B$BAll taken care of! Good day to you, $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1514, 'enUS', 'You apply two Cenarion plant salves to the windblossom. It immediately begins to shed its corrupted form, blossoming into a vibrant and healthy plant.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1523, 'enUS', 'Hmmm, Xanis sent you to me, did he?$B$BWell then, Xanis could always be trusted, so that must mean you\'re ready to take part of the next sapta--the sapta of fire.$B$BI see it in your eyes already, the burning, the desire. You\'ve tasted power, and now you\'re ready for more of it. Patience, $n. Remember what earth taught you. Power and destruction will come easily, but you must still know when to stay your hand.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1524, 'enUS', 'Ah, my mistake, $n. I apologize. I did not realize you possessed the Torch. I am pleased to make your acquaintance.$B$BMany adventurers find their way here while looking for lost treasure or the promise of mystical creatures to slay, only to be turned away from a seemingly empty shrine. But we know the truth, do we not, $n?$B$BThis shrine holds a greater secret from the mundane members of the Horde. To us, it is sacred.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1532, 'enUS', 'For the time being, I shall give you what you need to focus your spells and to call upon the spirits of air. Take this totem, and when you are ready, train with me some more.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1559, 'enUS', 'So you\'re an engineer, eh? Well, for your help in getting me those pearls, here\'s the recipe for flash bombs.$B$BGood luck, and try not to blind yourself!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1560, 'enUS', 'So he couldn\'t find his way back on his own? Figures.$B$BWell, I guess you deserve something for your troubles....', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1578, 'enUS', 'Ah, apologies! I didn\'t realize you were here to help resupply our troops. The stuff you made will be a big help!$B$BHere are plans for some armor. I hope you find them useful.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1579, 'enUS', 'I can\'t believe you found them! You must be the luckiest $R in Kalimdor!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1580, 'enUS', 'I can\'t believe it! You found them! You are the best fisherman I\'ve ever seen!$B$BThank you, $n. And here, take these. Although you\'re so good and you probably don\'t need them, they will help you with your fishing.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1581, 'enUS', 'These are nicely mixed. You take great care in your trade, $n, and that is important for an alchemist.$B$BHere are your herbs, freshly gathered. May you use them well.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1582, 'enUS', 'Yes, these are good pieces. You have a skilled hand and an eye for detail. Your future as a leatherworker is promising.$B$BHere, $n. Let me show you the pattern to make the Moonglow Vest.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1618, 'enUS', 'Thank you, $n. This will help me out tremendously. With the Blackrock orcs attacking from Stonewatch Keep and the Redridge gnolls lurking in the foothills to the north, I\'m always fixing armor and making new weapons for our troops.$B$BThe supplies you brought will give me a moment\'s peace. I will have enough time to teach you something...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1639, 'enUS', 'Hello, fancy boy! What\'s a prissy thing like you doing in the Pig and Whistle? Be careful, or you might break a nail...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1640, 'enUS', 'You\'re a lot tougher than you look!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1649, 'enUS', 'Ah, you wish to petition me for a test of valor. Splendid.$B$BThere are many tasks throughout the city and surrounding lands that hold much challenge, and they could use a woman with your skills.$B$BThis test should not be taken lightly, $n. This, like many things along our path, could take our lives. The Church always wishes to bolster its ranks, but it understands the sacrifices needed to ensure the paladins serving it are worthy.$B$BAre you prepared?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1655, 'enUS', 'Yup yup, this is it. Ha! If I\'d known you were really gonna head out there and fight some o\' them ogres, I woulda given you a list of things to get instead of just this one thing. But, you\'re not here doin\' business for me, are ya?.$B$BA deal\'s a deal; Jordan wanted a shipment of me alloy, that\'s what he\'s gonna get.$B$BTell \'em hello fer me, and be safe, $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1656, 'enUS', 'Ah, the furs I was expecting. Thank you.$B$BThese came faster than I had expected. They will make good blankets for anyone who wishes to stay here.$B$BPlease feel free to stay as long as you like. My inn is always open and all of our people are welcome here. And don\'t underestimate a good night\'s rest, it can make a great difference while you\'re out traveling.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1658, 'enUS', 'Well done, $n. The Wickerman Festival will continue to be trouble so long as the abominable Forsaken are allowed to haunt the remnants of Lordaeron. It is NOT a fitting tribute to that once proud kingdom, let me tell you...$B$BHere\'s some coin for any repairs you might need, and in the spirit of the season I\'ve also included some treats you might like. Happy Hallow\'s End - let\'s keep it as safe as possible here in Southshore.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1665, 'enUS', 'There it is! He sure didn\'t want to part with it, but you were persuasive. Well done.$B$BNow for your lesson...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1666, 'enUS', 'Who\'s that? Burlguard, is that you? You\'ll have to step a little closer. My eyes aren\'t what they used to be. Walking up like that, I thought you were a gnoll!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1667, 'enUS', 'Great, you found it! I feared it was gone forever, used by the Defias to impersonate a Stormwind official. Well that won\'t happen now, thanks to you.$B$BHere are some weapons I used when I was a Marshal. I kept them in great shape--take your pick!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1681, 'enUS', 'You found it! Well done!$B$BI hope you killed a host of Dark Iron dwarves while searching. I fear that Ironband\'s compound won\'t be the last place we see them.$B$BBut thank you for the ore, $n. I\'m eager to work it and find its properties.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1682, 'enUS', 'Here are some weapons, hot off the anvil and made with the new umbral ore you brought me!$B$BPlease, $n, take your pick!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1685, 'enUS', 'About time you showed up. Though, perhaps I should have sent someone more capable than Remen to find you.$B$BNo matter.$B$BYou\'ve gone too long without training, and a $C without training isn\'t like to keep his head on his shoulders for long.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1690, 'enUS', 'Now that\'s what I call efficiency! You\'ve helped thin out the nomad\'s numbers enough now that we can start to think about how we can win back those water wells.$B$BThe Gadgetzan Water Company thanks you for your help, but we\'re far from done in dealing with those foul nomads. Listen close - here\'s what we need next...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1691, 'enUS', 'Top notch work, $n... top notch! You\'ve thinned down the nomad population to a point where we can actually take the fight to them!$B$BIf you haven\'t already, talk to Spigot Operator Luglunket here; he gives a bounty on any of the Wastewander water pouches you may have run across while bringing those nomads to justice!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1707, 'enUS', 'Excellent - the more water pouches we are able to reclaim, the further away from disaster we\'ll all be! Keep \'em coming, and I will be sure to reward you for every five water pouches you bring me.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1789, 'enUS', 'If you fail to help Narm after you\'ve used the Symbol of Life, $n, then return to me and I shall give you another.$B$BIt is important to both my husband and I that you return successful. Please, make haste.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1790, 'enUS', 'Make haste, $n, more than one life hangs in the balance if you fail.$B$BIf for some reason you fail to help Gazin and Henze, then return to me and I will give you another Symbol of Life for your travels.$B$BDon\'t mistake my aid as simple charity, you must prove yourself worthy like any other $C and continual failure may reflect upon you later.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1819, 'enUS', 'Well done, $n. Ulag was no minor agent of the Scourge. You have shown the Deathguards, and hence the Dark Lady, that you are a $C of skill and resolve, and are worthy of further instruction.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1820, 'enUS', 'Yes, it is true. I have need for your skills in combat. My means are small, but my task aids both me and the Forsaken\'s cause. Do it, and your reward will be twofold.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1821, 'enUS', 'Ah! You retrieved them! Now the last semblance of honor and memory is stripped from the cursed Agamand lands. The family truly is no more!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1822, 'enUS', 'I will keep three of the weapons you returned to me, as mementos of my pain and loss. But please, $n, choose your favorite of the heirlooms and keep it for yourself.$B$BMay it serve you greater than it served its cursed, former owners.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1878, 'enUS', 'Excellent - the more water pouches we are able to reclaim, the further away from disaster we\'ll all be! Keep \'em coming, and I will be sure to reward you for every five water pouches you bring me.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1918, 'enUS', 'You find a nasty ball of water and decide to bring it to me? Who am I, the Earthen Ring or something?$B$BActually, that does give me an idea... perhaps you do need the Earthen Ring for this. I mean, if it helps clear up the situation at the lake for good, then I\'m all for it.$B$BYou know who the Earthen Ring are, $n?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1938, 'enUS', 'I feared that Morganth had hidden the book elsewhere, or that he had found a way to open it and read its contents.$B$BIt is good to see that he did not, and that the seals we placed on Ur\'s Treatise on Shadow Magic still hold.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (1941, 'enUS', 'Here is your robe. It\'s one of my best made yet! Wear it with pride, and when people ask... tell them you got it at the Larson Clothiers!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2038, 'enUS', 'You\'ve done the movement a great service, $n. The troggs and leper gnomes infesting Gnomeregan will soon feel the brunt of a gnomish air assault!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2039, 'enUS', 'So Gnoarn doesn\'t trust my reconnaissance abilities? I wonder why...$B$BNevermind that, I need your assistance! Nay! The movement needs your assistance!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2040, 'enUS', 'Well done, $n. Thanks to you, Gnomeregan is one step closer to its day of liberation!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2041, 'enUS', 'Do you bring word from central command?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2078, 'enUS', 'Yarrr... Ye be provin\' yerself to be more than a yella sea dog, landlubber.$B$BThe Cap\'n may promote ye to poop deck swabby if ye be keepin\' this up...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2098, 'enUS', 'Avast ye scurvy sea dog! Ye did it! Soon me first mate be back in workin\' order and them beasties won\'t know what hit em!$B$BHrm, have ye seen me first mate?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2118, 'enUS', 'Our work has only just begun, $n. Are you prepared for your next task?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2138, 'enUS', 'You have done Auberdine a great service, $n, but your task is not yet complete.$B$BReturn to me once you have rested.$B$B...and $n, prepare your mind and soul for more bloodshed.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2139, 'enUS', 'You have fought for Auberdine with bravery and honor, $n. In our war torn lands, death and despair are common. Many will not rise to meet the challenge of life in our new home, choosing instead to hide in their homes or escape to the more fortified lands of the Alliance.$B$BThank you, $n. Perhaps someday soon we shall fight along side as comrades versus another foe.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2158, 'enUS', 'Rest and relaxation for the tired and cold -- that\'s our motto! Please, take a seat by the fire and rest your weary bones.$B$BWould you like to try a sampling of some of our fine food and drink?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2159, 'enUS', 'Ah yes, the delivery of herbs from Shadowglen. It is a shame Porthannius could not bring it himself, for we have much to discuss, he and I. But I am glad to get the herbs nonetheless, and I am glad you came.$B$BWhile you are here, please, rest yourself. Heroes must keep their strength and spirits high, and must find rest and solace whenever they may. For to neglect one\'s peace of body and mind is a sure path to failure.$B$BSo... rest.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2160, 'enUS', 'Ah, at last, the supplies from Hands! I was beginning to worry, we\'ve had scarce word from Anvilmar ever since the troggs overran the pass.$B$BThanks for bringing this to me, $n. And please, make yourself comfortable. You must be tired after your journey.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2161, 'enUS', 'Ah, this is the food Ukor brought to the Valley of Trials. Did they not need it? Well, I guess they like to starve you heroes in training. Builds spirit, they say!$B$BThank you for returning the food. I\'ll stick it back on the shelves... but here, let me offer you some refreshment first!$B$BAnd don\'t forget to rest here in the inn. You may be a brave $C ready to take on the world, but if your energy is sapped you won\'t be doing yourself, or the Horde, much good.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2178, 'enUS', 'Fantastic! Try it and tell me what you think, $n!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2198, 'enUS', 'Yes... I am the jeweler who made this necklace. I also am not in the business of handing out information as though it were free. Information is never free, friend. If I am going to help you out, you need to help ME out first.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2199, 'enUS', 'You\'re all right in my book; I don\'t care what everyone else has been saying... I kid, I kid! Seriously, thank you my friend.$B$BNow, the necklace you have is one of my finest creations, but it is missing three gems that help bind magic power to it. I crafted it for a paladin some time ago. He was on a self-appointed quest to clear out the \"evil taint\" present at an excavation site in the Badlands called Uldaman.$B$BWell... that paladin died in Uldaman, and that was just the beginning of my woes.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2200, 'enUS', 'Reading the journal reveals that after much effort, the paladin had identified the location of all three missing gems:$B$BThe Shadowforge dwarves hid the ruby in their barricaded area as they battled the Troggs for control of Uldaman.$B$BThe troggs stashed the topaz in an urn located near some unusual inhabitants of Uldaman - Alliance dwarves!$B$BFinally, the trogg elder himself took the sapphire as his own.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2201, 'enUS', 'Yep, those indeed are the three gems. Good job! Unfortunately, your work isn\'t quite over just yet.$B$BNo, I\'m not trying to pull a fast one here, silly $C. This is about making magic jewelry - Talvash del Kissel quality-level jewelry! You need a strong power source to give it some oomph. Well, that necklace has no more oomph to give.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2202, 'enUS', 'Well done $n, you\'ve proven your worth once again! The caps will be an interesting study, you betcha.$B$BAnd now, as promised, enjoy a taste of one of my more potent restorative brews from my personal stock...$B$BIf you are a skilled alchemist, then perhaps we might have some additional business to conduct. Regardless, I thank you for your assistance!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2204, 'enUS', 'Well I\'ll be - I sure am glad to see you and this power source in one piece. It will take me a moment to magically fuse the power source with the necklace now that the gems have been reunited with it. Once it is done, however, the necklace will be yours!$B$BYou\'re excited, aren\'t you? All tingly with anticipation? Now where did I put that spell focus at...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2206, 'enUS', 'I am astonished... Astonished that Keryn finally found a recruit that could possibly have the makings of a proper $C. Take this blade, $n. A day will come when you will be called upon again to serve the SI:7. Hone your skills, keep your eyes open and that blade sharp.$B$BYou won\'t see us coming but you\'ll feel it when we get there...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2240, 'enUS', 'You have seen the Chamber of Khaz\'mul? Amazing! Baelog was sure it existed, but I must admit that I had my doubts.$B$BThank you, $n. With luck your finding will incite our leaders to renew their efforts to retake Uldaman. And if it is ever again under Ironforge control, then the secrets of the Chamber of Khaz\'mul will be ours!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2258, 'enUS', 'Ah, well done! These should last me for a little while, at least. Here is your pay as promised.$B$BIf you\'re interested, I have some more reagents I could use. These, however, are only found in the depths of the Uldaman excavation. They will not be easy to get, but I will make it worth your while if you are interested in helping me again.$B$BPerhaps something more than some shiny coins, yes?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2278, 'enUS', 'Following the instructions of the stone watcher, you once again access the Discs of Norgannon now that you have learned about the history of the Earthen, the troggs, and the dwarves. A faint, brief hum is heard from within the discs. The stone watcher then speaks:$B$B\"The discs have now been synthesized. Access the Discs of Norgannon once more to receive your personal copy.\"', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2279, 'enUS', 'What do you have there... oh my. You found this where?$B$BNo, really?$B$BYou spoke to whom about what?$B$BNO, REALLY?!$B$BMy fine $C, you may have just made the archaeological find of the year! By the Gods who am I kidding, the find of the century! If these discs are what I think them to be, then this could be the very key we have been looking for! This may well prove outright once and for all the legitimacy of the Dwarves\' enchanted creation!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2280, 'enUS', 'These discs will change everything. Their secrets are ours now.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2281, 'enUS', 'Ah, so your fingers are a little fat, eh? Don\'t worry, $n, I\'ve turned more useless thieves than you into accomplished rogues.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2283, 'enUS', 'Mmmm salvage! I can smell the necklace from here. Now let\'s look at that beauty...$B$BHey, what do you think you\'re trying to do here? This necklace is missing the gems! You think we wouldn\'t notice? Even Malton could see that the gems are gone.$B$BWell, maybe you didn\'t steal them... but this necklace is worthless without those gems! Your job isn\'t done here yet, not by a long shot!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2284, 'enUS', 'You seem to have found the remains of the paladin that Dran had mentioned from before. Searching the remains uncovers what would appear to be his journal. The book is in poor condition, as dried blood has stained most of the text in the book. What you can read, however, is gibberish; it is written in the native tongue of the humans, to the best of your knowledge.$B$BYou will need the book translated if you are to glean any knowledge from it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2318, 'enUS', 'Mighty $C, I know what you are doing out here in the Badlands. I know you seek my gift of tongues. You\'re after the parts to that necklace you found in Uldaman.$B$BSurprised? Don\'t be - it\'s my job to know such things. Instead, let\'s discuss what you\'re going to do for a translated journal. Trust me - I\'m the only one you\'ll find that can translate it.$B$BBetter still, let me make you a better offer than Dran Droffers is, and it\'ll let you still complete your deal with them. Interested now, yes?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2338, 'enUS', 'You know a good deal when you see one - I knew I could count on you.$B$BIt will be just a moment while I cast a spell to quickly translate the human tongue into a form you will understand. I am a troll of my word; ask anyone in the Horde about Jarkal Mossmeld. Now, let\'s get this started...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2339, 'enUS', 'Very well done, $C. Now the fruits of our redirection will pay themselves out handsomely. I\'m finished with the necklace itself, and I am confident I can duplicate the construction process consistently.$B$BBut first, I will use a specially prepared incantation to quickly analyze those gems before you take them and the necklace back to Orgrimmar. This will take just a brief moment to do.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2340, 'enUS', 'Yep, it\'s all here. Say, mind if we keep the bag too? No? Thanks, appreciate it! Har!$B$BThis necklace will be our greatest haul yet! Don\'t feel too bad though; I\'m sure the necklace wouldn\'t fit you anyway. Har again!$B$BA deal is a deal, so here\'s your fee for the salvage. Our business is done here for now; check back later to see if we have more work. Until then, beat it!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2341, 'enUS', 'The power source you gave me exceeded every expectation I had for it. I was able to make both you and me a necklace, and I have enough left over to make at least three more. They will fetch quite the fee, with Droffers and Son Salvage none the wiser.$B$BHere you go, my friend. I give you my thanks on a splendid job... and something quite special for your trouble.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2342, 'enUS', 'At last - a broom worthy of a Garrett. You\'re bloody well worth every silver I spent on you, I say!$B$BDo you hear me, foul bats? I now wield the Garrett family weapon of choice! Tremble, bats, tremble!!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2358, 'enUS', 'To deny Ravenholdt the artifact, would be to deny one\'s own existence. Well done, $C. You may yet walk among the assassins of Ravenholdt.$B$BAs a young $C adventuring in Darkshore, I discovered a tea that replenishes energy in combat. It has served me well throughout the years and now I pass on a taste of the thistle tea to you. Drink of it when you tire, $n, and fight with vigor once more!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2361, 'enUS', 'I gladly give you this necklace, mighty $C. You have done me an invaluable service, and for that I thank you. Wear it with pride, and make sure you mention loudly and repeatedly who made it for you!$B$BAlso, if you could toss in the fact that it really isn\'t cursed, that would be great.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2383, 'enUS', 'Ah, that\'s my parchment. I heard you\'d be coming to the Valley, $n. I\'m glad to see you made it--let\'s hope you survive the trials ahead.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2398, 'enUS', 'It\'s about time you showed up! I was in danger of losing a bet with Olaf!$B$BHe wagered that his beard would grow to his knees before he saw another living $R. Well here you are, and as you can see Olaf\'s beard has a few more inches to go.$B$BThanks, $n. I\'ll split the winnings with you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2399, 'enUS', 'The fronds Denalan planted within his garden have sprouted and grown. They tremble, eager to be picked....', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2418, 'enUS', 'That\'s great, you found some! Thanks a lot, $n. You\'re the most dependable $R I\'ve met!$B$BI can\'t wait to try these on some devices I\'ve been tinkering with. I bet that, under the right circumstances, devices powered with these stones will keep going, and going, and going...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2438, 'enUS', 'My emerald dreamcatcher is of great importance to me. It is a gift only given to few. Thank you for returning it, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2439, 'enUS', 'As yes, a voucher from the Explorers\' League... and quite the valuable one, might I add.$B$BThe Explorers\' League lends me the authority to offer you a choice in your compensation directly from their vault. You are to receive this item, and you may choose between five superior healing potions or five greater mana potions as your reward.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2440, 'enUS', 'Well $n, you must have pleased Sage Truthseeker greatly for him to give you this item. You are also to be granted a gift from his cache of potions; you may choose between 5 superior healing potions or 5 greater mana potions as your reward.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2459, 'enUS', 'Now I can repair my dreamcatcher. Thank you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2498, 'enUS', 'Ah, so Rellian sent you to aid me. I am glad you arrived here so soon.$B$BI have discovered something quite disturbing!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2499, 'enUS', 'I knew you were more than capable of ridding the lake of that horrible beast.$B$BIt is a distressing situation; Oakenscowl was once a grand leader amongst his kind... but corruption does not discriminate between the lowly and the noble.$B$BThe size of this tumor is quite disturbing, but I must study it to learn more about the disease that upsets the timberling population.$B$BThank you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2500, 'enUS', 'Ah, well done $n. These should last me for a little while at least. Here is your pay, as promised.$B$BIf you\'re interested, I have some more reagents I could use. These, however, are only found in the depths of the Uldaman excavation. They will not be easy to get, but I will make it worth your while if you are interested in helping me again.$B$BPerhaps something more than some shiny coins, yes?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2501, 'enUS', 'From one alchemist to another, I salute you. Here, take this recipe and commit it to your knowledge. May it bring you the success and rewards that it has brought me over the years!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2518, 'enUS', 'The forest mourns for Lady Sathrah, but it was something that had to be done. I hope now that she can rest in peace.$B$BThank you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2519, 'enUS', 'So, Sister Aquinne sent you?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2520, 'enUS', 'The loss of Lady Sathrah is distressing, but in this way only may she be reborn with a renewed spirit.$B$BMay Elune willingly accept the sacrifice that you have offered her.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2521, 'enUS', 'Do my eyes deceive me? Impossible! Where did you... How...$B$BPerhaps now a rift can be opened, returning us to our homeland.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2522, 'enUS', 'Simply amazing. I had doubted that you could find another sphere, yet here it is, pulsating in my hands.$B$BWell done, $n!$B$BShould you find anymore--and I believe this to be an impossibility--return them to me and I shall reward you again.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2523, 'enUS', 'You apply some Cenarion plant salve to the songflower. It immediately begins to shed its corrupted form, blossoming into a vibrant and healthy plant.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2541, 'enUS', 'Thank you, $n.$B$BWhat an odd trinket this is... I can sense the vile aura emanating from it; this is a very powerful enchantment.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2561, 'enUS', 'I am finally free of the control of the Gnarlpine. Thank you, $n.$B$BMy spirit may now rest peacefully forever in the Emerald Dream.$B$BPerhaps one day we may meet again, young $C. But, for now, please accept this reward as a symbol of my gratitude.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2581, 'enUS', 'How do you feel, $n? You radiate with the aura of the snickerfang--your strength, unmatched by mortals!$B$BUnfortunately, with the components you brought to me, I could only make enough of the assay for one tablet. I will require more components, should you require another tablet.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2582, 'enUS', 'There you go! Remember, use it sparingly. If you require another, come see me after you finish the one you currently have in your possession.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2583, 'enUS', 'The spirit of the boar illuminates your being, $n! I wonder, do you believe yourself to be as invulnerable as these beasts?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2584, 'enUS', 'I must warn you, $n, do not attempt to mix the formulas I have created. The chemical properties of the formula you most recently ingested will always take precedence over former enchantments; thus, rendering the other formulation null.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2585, 'enUS', 'Fascinating. This has been the most difficult of all assays to analyze.$B$BTake a pinch of this residual salt and place it under your tongue. You should experience a slight feeling of euphoria followed by a state of unprecedented agility and quickness.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2586, 'enUS', 'Fine work, $n. Here is one sample of the assay for you and the remaining nine will be used in my research.$B$BAnytime you require more of the ground scorpok assay, return to me and we shall work out a deal.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2601, 'enUS', 'This is wondrous, $n! My offering to you is a taste of the inner workings of the redstone basilisk.$B$BShould the compound be to your liking, more can always be made... for a price.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2602, 'enUS', 'Yes, yes, give me a moment and more of the compound will be ready for your consumption, $n.$B$BTake it... and $n, do not attempt to consume these experimental concoctions in rapid succession. The results could be disastrous.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2603, 'enUS', 'Most excellent! I shall get to work immediately. Perhaps a cure for many of the ailments of the world is to be found!$B$BAh yes, your reward - of course, should you require more of the gum, return to me and another task you shall be given.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2604, 'enUS', 'Perhaps one day I shall be able to isolate the active enzymes of this assay and produce a much more efficient resultant. Unfortunately, you will have to settle for what I am currently able to produce. Return to me should you require more gizzard gum.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2605, 'enUS', 'Oh, wonderful! Give it here -- let me drink it!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2606, 'enUS', 'Marin\'s a good guy, don\'t get me wrong -- just a little... eccentric, I\'d say.$B$BAnd of course he needs my help, yet again.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2621, 'enUS', 'Thrall\'s Grasp... The story is still fresh in my mind.$B$B<Commander Ruag shakes his head.>$B$BTerrible thing that happened to that regiment. The horde lost some of its best soldiers when that battalion was destroyed.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2622, 'enUS', 'Swamp tal... talk.. talker...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2623, 'enUS', 'It is worse than I imagined. Nineteen of my men could have been spared from the agony which they faced. If only I had not acted so irrationally.$B$BYou must help them, $n! I am powerless to act.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2641, 'enUS', 'That\'s the stuff, $n!$B$BIt looks so good, I might just... Haha, gotcha!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2661, 'enUS', 'Violet Powder, eh? Let\'s give it a try -- it sure couldn\'t make it any worse, that\'s for sure!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2662, 'enUS', 'Drink this! It\'s absolutely wonderful!!!$B$BI knew I had it in me... Oh thanks again, $n for your help, but please, stand a little to the side, will you? You\'re blocking my business! I\'m going to make millions on the fabulous Noggenfogger Elixir, millions!$B$BWhat\'s that? Sprinkle says I owe her a favor? Oh, that Sprinkle, such a kidder!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2681, 'enUS', 'You have done it, $n! The poor, tortured souls can finally rest.$B$BOur work is not yet over, $n. We have much to discuss and much more to accomplish.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2701, 'enUS', 'You are temporarily blinded by the sparkle and gleam.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2702, 'enUS', 'Indeed, you have honored us all. Our thanks alone are not sufficient.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2721, 'enUS', 'Trebor? Trebor sent you?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2741, 'enUS', 'You place the hippogryph egg into the machine. A series of beeps follows along with some strange mechanical sounds.$B$BIt appears that the Eggometer has weighed, measured, and packaged your egg. Now that\'s speedy service!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2742, 'enUS', 'This troll tablet must be the \"secret\" that Rin\'ji was talking about.$B$BCovered with strange markings, you can not decipher what the tablet says.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2743, 'enUS', 'The demon lord displays a cunning almost as calculated as his brutality, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2744, 'enUS', 'I will help you. Although, I do not think you fully understand what you are up against.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2745, 'enUS', 'What the...?! Don\'t sneak up on me like that! And keep your voice down! What in the name of the Shadow are you doing sneakin\' up on me for? Can\'t you see I\'m busy, you daft fool?$B$BWhat do you mean, what am I doing? I\'m waiting for the next Dark Portal to open, what the heck do you think I\'m doing? Speak your peace and be off with you... \'fore the guards notice the two of us hiding in the bushes together. I have a reputation to maintain.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2746, 'enUS', 'These will do nicely, $n. Great job.$B$BI have the rest of the gear I\'ll need in my pack... thank the Light I just got a tailor friend of mine to make me a silk bag. I was running out of room carrying all my gear around.$B$BBefore Spybot heads in, let\'s get the details set so you know what you have to do.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2748, 'enUS', 'Very good, $n. I will be sending this egg to Darnassus where I hope it will soon hatch!$B$BPlease accept this as a symbol of my gratitude.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2749, 'enUS', 'So, you have found an ordinary egg, $n? Please give it to me... And we can only hope for the best.$B$BI don\'t have much, but please accept this reward. If you ever find yourself in Feralas again, know that I am always here to take the eggs you find there.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2751, 'enUS', 'The bloodline will fade but perhaps the legend of Omosh will continue!$B$BYou have proven that you are a blacksmith, $n. For the blacksmith the reward is the satisfaction of seeing their weapons and armors coated in the blood of their enemies!$B$BMay the breastplates you create for the generations ahead be drenched in the blood of your enemies!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2752, 'enUS', 'You do Orokk proud, $n. Read, learn of iron shoulders!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2753, 'enUS', 'You remind Orokk of his grandfather, Chief Gorokk of Omosh! Read, learn of the iron boot.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2754, 'enUS', 'Astounding! You have learned the way of the Omosh, $n!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2755, 'enUS', '<Orokk pants.>$B$BIt has been a very long time since Orokk has done the Omosh dance of joy. Learn now, $n!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2756, 'enUS', 'It seems you are but another complacent lemming of the Warchief, $n. How disappointing.$B$BHow I long for a glorious death upon the battlefield. Instead, I am stuck here, by order of Thrall, to teach infants how to craft! Watch and learn, child!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2760, 'enUS', 'Ah, many years it has been since one has been worthy of the Mithril Order\'s initiation. We will start now!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2762, 'enUS', 'She\'s a cruel mistress, is she not, $n? I\'m speaking of truesilver, naturally. Aye, Galvan spent over a decade in the mountains of Alterac learning this lesson. Take this and learn from it all that you can!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2764, 'enUS', 'Galvan the Ancient sent you??$B$B<Trenton bows.>$B$BIt is a great honor to meet another of the master\'s pupils!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2766, 'enUS', 'You place the egg... distress beacon... inside the chicken... homing robot.$B$BHums and whirs are heard from inside the robot as it struggles to stand up. After the robot comes to life, Oglethorpe\'s voice is heard once more, but now from inside the robot:$B$B\"OOX-22/FE is working at least, but it needs major repairs! It is much too heavy for you to carry it... but I think I have an idea. Are you up for watching over it some more?\"', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2767, 'enUS', 'Oh, YOU\'RE the great savior of OOX-22/FE! I cannot even begin to express my thanks for your assistance! My homing robot series needs more development, but your recovery has saved me countless hours of extra rebuilding time, as well as many gold pieces in manufacture costs!$B$BPlease, take your choice of any of these items I have. Thank you again for your generous assistance $n, my robots and I are in your debt!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2768, 'enUS', 'You found it! Well done, $n! Did you have to fight Bly for it? I hope you knocked him and his band down good and hard!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2769, 'enUS', 'Ah, you want to help me? Great!$B$BThere\'s a part I want for an experiment I\'m working on, and I need a brave $C like you to help me get it!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2770, 'enUS', 'Wow, you got the scale! Thanks, $n. I can\'t wait to get to work on this thing!$B$BSo you saw Gahz\'rilla? Was he as big as they say??', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2781, 'enUS', 'Finally - justice is served! You have struck a mighty blow against those vile nomads! With Scorpidsting\'s demise, this might be just the thing to finally push those squatters off of Gadgetzan\'s water wells!$B$BBy the authority of the Gadgetzan Water Company, I gladly award you with this bounty. You\'ve done a great service for us all.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2782, 'enUS', 'This tablet... Quite unusual... Where did you find this, $n?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2783, 'enUS', 'The Alliance wishes to assist?$B$BI thought to never see this day again. A day in which the Alliance fights side by side with the Horde against a common enemy.$B$BIt shall be... we will unite.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2784, 'enUS', 'As a spirit, I am powerless, $n. Assist me. Allow me to regain my honor.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2801, 'enUS', 'The time to act is now, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2821, 'enUS', 'Did you stop and examine the texture of these hides? Properly treated, these hides could very well be stronger than iron. Not many people know about these hides, and that in combination with the vast treating solutions I know will allow me to potentially make items with a true mark of quality... thanks to you, of course!$B$BAnd now, please choose from what I am able to offer you for your service!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2822, 'enUS', 'Ah, excellent! These hides, when given a special treatment, are as resilient as iron I tell you! It will take many tries in order to get the right mix down for the tanning, but I am confident they will bring my mark of quality to the next level.$B$BAs for you, $n... your assistance has proven invaluable to me. Please take your choice from these items I have crafted.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2841, 'enUS', 'I knew you could do it, $n! Sovik on the other hand, he had his doubts.$B$B$B$BNow that we have the blueprints in our possession, it is only a matter of time before we figure out all of the gnomish innovations that went into the creation of the rig and make them goblin innovations for our new shredder models.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2842, 'enUS', 'She\'s a beauty, isn\'t she? Pinnacle of goblin transport technology -- only exploded a dozen or so times.$B$BDon\'t worry, $n, I\'ve got her all fixed up. She won\'t blow up on you - and if she does, it\'ll be quick and painless.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2843, 'enUS', 'All done! When you\'re ready, just step on the pad.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2844, 'enUS', 'Oh, I am so glad you found me! I must admit, I am a bit lost... But isn\'t this a beautiful place?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2845, 'enUS', 'What? You say dis bell will work when Shay runs off? Well, how \'bout that!$B$BSince you did so much fer me, here ya go, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2846, 'enUS', 'Wonderful! You found it! And what\'s just as important--Velratha no longer has it! Thank you, $n. I am forever in your debt!$B$BAnd if I sounded a little... mean before, pay it no mind. You\'ll find me a much nicer person to those who haven\'t stolen from me.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2854, 'enUS', 'Well $n, I\'m glad to have you on board as my apprentice. You\'ve made a wise decision as a leatherworker; those who make use of leather armor will be clamoring to you once you learn how to master the art of Wild Leather.$B$BLet\'s get to work - where do you want to start?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2861, 'enUS', 'That\'s right. I do have a bone to pick with a troll. A few bones. All her bones!$B$BWant to help me?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2862, 'enUS', 'This is proof of a good start, $n. Your skills as a soldier are to be commended.$B$BYou are not the only one I ask that task of. Many before you have fought them, and in times past, our efforts have kept them at bay. Recently though, their attempts to push northward into our area of influence have increased dramatically. Their attacks have also become more savage... almost as if they were desperate.$B$BI have a plan though, $n, that might settle things once and for all against them...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2863, 'enUS', 'You\'ve done as I have asked, and for that I salute you. Take this small reward as compensation for your efforts, $n.$B$BWith their numbers thinned and their leaders slain, the gnolls will think twice about storming the camp. This doesn\'t conclude the issue at hand, however...$B$BThere must be some reason why the gnolls have been whipped into such a frenzied state. Before they have a chance to regroup though, I want to find out what is causing it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2864, 'enUS', 'Hello!$B$BOh, you spoke to my cousin Krazek? How is he? I hope Booty Bay is treating him well...$B$BScarab shells, you say? Well... let me tell you a story...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2865, 'enUS', 'Oh, great! You got them!$B$BThanks, $n. You\'re a real lifesaver!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2866, 'enUS', 'Examining the gazebo reveals some interesting and unusual things in contrast to the surrounding ruins.$B$BFirst, the gazebo is clearly a more recent construction than the other objects in the ruins. Second, the gazebo has a small series of glyphs engraved into the sides of it; the glyphs repeat in their pattern, but it otherwise is unknown in origin and meaning. Third, the stone of the gazebo itself is warm to the touch.$B$BThis certainly quantifies as \"standing out\" in the search of the ruins!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2867, 'enUS', 'Welcome back, $n. Your report on this gazebo is most... disturbing. It would seem that even if the naga there were not planning on an attack, their continued presence bodes ill for our push into Feralas. At any rate, here is compensation for your investigation.$B$BIt would seem that Feathermoon Stronghold is in dire need of additional assistance concerning the Hatecrest naga. I\'d like to offer you the task, if you are so inclined. There is much to do.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2869, 'enUS', 'Yes, yes, these scales are more than satisfactory. Actually, I think I will send these down to Pratt for him to take a look at. Maybe he can craft something useful out of them.$B$BAs for you, I have another mission I want to send you on. But first, here is your bounty for the scales. Your continued service to the General and Feathermoon Stronghold at large is a blessing in the face of adversity.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2870, 'enUS', 'Well played, $n! Lord Shalzaru\'s demise will throw the naga into disarray for some time, if not permanently. The threat they pose is now under more of our control than it has ever been, and we have you to thank for it.$B$BHere is more bounty money from the stronghold treasury - you\'ve earned every copper. You and yours are welcome in Feathermoon Stronghold anytime, my friend.$B$BOne more thing before you depart, $n... if you could...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2871, 'enUS', 'Interesting... this came from the Ruins of Solarsal, you say? Well, Latro\'s right that I need to look at this. At first glance, it seems non-indigenous to the ruins. Then again, this isn\'t of naga design... not something as alien as this.$B$BIt is going to take some time to research this item. Once my research on it is done, maybe I will make use of your talents, should you be around.$B$BHere, take one of these extra items we have; I hope you\'ll find some use for it!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2872, 'enUS', 'Ah, so you\'ve been speaking with MacKinley, have you?$B$BIt\'s not what he thinks! I\'m not afraid to give him some Steamwheedle Bilge Rum...$B$BI just don\'t have any rum to give him!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2873, 'enUS', 'Great, thanks for the shipment! Now MacKinley and I can finally settle our wager.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2874, 'enUS', 'Hm, let\'s try this...$B$BI can\'t believe it! This stuff is... terrible! That\'s great!$B$BWell I guess Stoley can keep the money he owes me. He\'s true to his word about Steamwheedle bilge rum. That stuff is as thick as gravel and burns like lava!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2875, 'enUS', 'Ah, Andre\'s head. So I guess he\'ll make his trial after all!$B$BMuch appreciated, $n. Now if you\'ll just sign here, I can issue you payment for services rendered.$B$BDon\'t worry about the small print. It\'s harmless, and... I don\'t think there\'s a magnifying glass small enough to read it anyway.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2876, 'enUS', 'Oh my, this looks to be a list of ship departures and arrivals, with accurate dates and times and everything! If the captain of a pirate ship got his hands on this, he could have himself a field day!$B$BYou say you got this from a pirate? Well that\'s disturbing news...$B$BThank you, $n. I\'m glad you took it from him, and I\'m glad that pirate\'s eating dirt right now!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2877, 'enUS', 'Well look at you, helping out the Wildhammers! You\'re a $C of action, and action is what we respect around here.$B$BYour little clean-up mission is a lot more important than you might think it is. With folks thinning out the oozes and sludges, we\'ll be able to extend our sphere of influence in no time. Here - have a little something in addition to just making new friends here in Aerie Peak!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2878, 'enUS', 'You apply some Cenarion plant salve to the songflower. It immediately begins to shed its corrupted form, blossoming into a vibrant and healthy plant.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2879, 'enUS', 'Holding the energized stave is almost impossible. Its power rumbles through your body.$B$BYou reach out to the cold surface of the Equinex Monolith...$B$BA small door opens, allowing you to reach in and take the artifact inside.$B$BBeams of light fill the area around the Equinex Monolith.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2880, 'enUS', 'Now that\'s what I am talking about! See - these necklaces prove that you\'re willing to get your hands dirty. We Wildhammers value that in those we associate with.$B$BYou keep this up, and you\'ll be in our good graces yet.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2882, 'enUS', 'Captain Cuergo\'s hidden treasure awaits!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2902, 'enUS', 'Examining the map reveals it to be the battle plans that Hadoken Swiftstrider wants. Numerous arrows that perhaps represent troop movements are drawn on the map, though most seem to be headed toward the south rather than to the north where Camp Mojache lies.$B$BAnother section of the map has a picture of what you think is a large insect of some kind, with various tick marks under it. From the looks of the gnolls, it perhaps represents their losses against the unknown bugs...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2903, 'enUS', 'Ah, this is exactly what we needed. While I am not so skilled at ciphering what most of the scrawling on this map is, I\'ve seen enough battle plans in my day to understand what is happening with the gnolls. It looks like they are currently fighting a two front war. One is with us... but it seems that their main energy has been against a foe to their south.$B$BThere\'s more at hand here than meets the eye. Perhaps it is time to figure out who this other foe they face really is.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2904, 'enUS', 'I suppose you should be rewarded for bringing Kernobee back to us in one piece, $n. If you go back to Gnomeregan, these might give you the edge you\'ll need in defeating that good-for-nothing backstabber, Mekgineer Thermaplugg.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2922, 'enUS', 'Oh, this is wonderful! Thank you, $n! I\'ll get to work immediately!!$B$BPoor, poor Techbot... Don\'t feel bad. We\'ll get you fixed.$B$BNow, where did I put that plug for my hydro-jack swivel spanner...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2923, 'enUS', 'Yes it\'s true. Techbot has gone rogue! Please, can you help me?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2924, 'enUS', 'You got them! Now I can begin my new experiments!$B$BI can\'t thank you enough, $n! Your bravery has advanced gnomish research by a leap and a bound!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2925, 'enUS', 'I do! I do need help with a mission into Gnomeregan. And you\'re just the $C to do it!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2926, 'enUS', 'Excellent work! The time has come to test the new formula. If this works, not only should that captured leper gnome be cured, but I should have a full head of hair! Stand back!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2927, 'enUS', 'I thought the loss of that outstanding head of hair would have been the end of him for sure, but he survived!$B$BI was standing right next to him when he was hit by the radiation bolt. Those delightful brown locks of hair hit the floor in clumps! Clusters! The horror of it all was only compounded when Gnoarn completely freaked out! Oh yes, freaked out I say! Gnoarn touched his bald head in disbelief, the look in his eyes screamed revenge, $n! Then he turned his anger towards the troggs!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2928, 'enUS', 'This will do nicely, $n. One gyrodrillmatic excavationator fixed; 398 left to go.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2929, 'enUS', 'I like to think that the last thing that went through his head as he collapsed to the ground was your foot, $R. With Thermaplugg dead, our plans to retake Gnomeregan are one step closer to becoming a reality.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2930, 'enUS', 'You did it! You found the prismatic punch card and recovered the lost data! Vitally, critically, immeasurably important data that we could not live another moment without!$B$BYour service to the gnomes will be long remembered, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2931, 'enUS', 'I\'m organizing data extraction missions into Gnomeregan. If you\'re here for that, then listen up!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2932, 'enUS', 'Word reached me of your deed. A grim deed, but one of honor. Let us hope the Witherbarks heed your message.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2933, 'enUS', 'Ah, and what is this? It appears to be venom... from some arachnoid creature I deem. And where did you get it?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2934, 'enUS', 'Oh, splendid! The venom is quite fresh and I\'m sure very deadly. Thank you!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2935, 'enUS', 'Ah, so you want fresh venom from the spider of the witherbarks, yes? Well, sit down... you\'re not going to like what I have to tell you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2936, 'enUS', 'Ah, Elortha no Shadra... speak this name only in whispers, for its utterance carries much weight in the realms of the spirit.$B$BI will put it to parchment and create for you a spell of summoning...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2937, 'enUS', 'Sweet, you got it!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2938, 'enUS', 'Ah, very good. Lydon mentioned the poison in this parcel is the most potent he has come in contact with, and is busy working on new applications with it.$B$BWith luck, we will find many.$B$BThank you, $n. Your service to the Royal Apothecary Society is undeniable.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2939, 'enUS', 'Oh, so Troyas Moonbreeze sent you?$B$B<mutters>...always bothering me with his wild notions that he found something important...$B$BLook, $n, I\'ll be blunt. Troyas always thinks he has found something important in those ruins. The truth is, anything that was truly valuable was probably hoarded away long ago by the ogres or the harpies that now reside there.$B$BYou\'re welcome to take a look around, but I don\'t know if you\'ll find any books here that will help you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2940, 'enUS', 'So, you\'d like to borrow Feralas: A History?$B$BLet me think...$B$BI will allow you to borrow this book, $n, but only if you do something for me first.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2941, 'enUS', 'Are you another one of Daryn\'s interns? She\'s quite a knowledgeable historian, $n. You should be grateful to be studying under her leadership.$B$BNow, let me get the Super Snapper FX. You\'re gonna love it!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2942, 'enUS', 'I have been reading since you left, $n. This stone you have found is called the Morrow Stone. While I am not entirely sure what its purpose is yet, you can be sure that it is something of great significance to be hidden away inside the Equinex Monolith the way it was.$B$BI will continue my studies, and I hope I will be able to count on you in the future, $n. Thank you for all your help.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2943, 'enUS', 'You have brought a book along. Excellent!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2944, 'enUS', 'Well! Curgle\'s invention lived up to its expectations.$B$BDon\'t get too excited, but do know that I am pleased with your work, $n.$B$BNow, where did I leave that book...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2945, 'enUS', 'You place the ring inside the Sparklematic 5200 and deposit a silver coin into the slot.$B$BPainful grinding noises emanate from the machine as it begins to jerk around violently. One can only guess what is happening with the ring inside the machine...$B$BThe machine suddenly stops, trumpeting its cessation with a simple \"Ding!\" The item receptacle opens up to reveal a small box that is neatly wrapped with a red bow. It would appear that the Sparklematic 5200 also gift wraps!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2946, 'enUS', 'As you touch the pedestal, the miniature discs from Uldaman hum and glow in your backpack. A familiar feeling tingle washes over your hands, as the image of another Stone Watcher springs to life in front of the gates of Uldum.$B$B\"Salutations. I am a guardian of entry. The discs that have been used for access are missing the correct entry discs, the Plates of Uldum. Please incorporate these plates with the disc structure and repeat your access sequencing. Thank you.\"', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2947, 'enUS', 'A ring, you say? Let\'s see... yep, I made that ring by golly.$B$BBefore Gnomeregan went up, I had a small shop there. Perhaps it\'s one I left behind when I ran for the hills; it\'s a simple design, and I made several like it. Maybe it\'s from someone who didn\'t make it out.$B$BDark Iron dwarves had this? Well, that puts a new layer of conspiracy on the nine different layers already floating about. Maybe the authorities would like to know; it\'s wasted on the likes of me.$B$BNow, back to the ring...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2948, 'enUS', 'Ah, exactly what I needed! Someday, when I find myself out of this hole that I am unjustly in, I will make for you, for EVERYONE the finest trinkets in all of Azeroth! Until then, thanks for being a sport about the reagents. I\'d normally charge ten times what I have charged you for a ring of this quality...$B$BSpeaking of rings, here\'s your new one!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2949, 'enUS', 'A ring from Gnomeregan? Let\'s see... not mine, but interesting.$B$BThe Gnomeregan thing - couldn\'t have happened to a nicer bunch of numbskulls. For years they\'re a joke, and now almost overnight they\'re making better rigs than us?$B$BDark Iron dwarves had the ring? That\'s even more interesting! You know, maybe the gnomes stole their crazy golem plans and modified them. Those Dark Irons, they\'d definitely want some major payback. It\'s certainly worth investigating.$B$BYou know, about that ring...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2950, 'enUS', 'Here\'s your ring, better than before. Thanks again for the information about those Dark Iron dwarves. This seems like a reasonable lead as to how those gnomes suddenly were struck with goblin-like inspiration.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2951, 'enUS', 'You insert the grime-encrusted item and three silver coins into the Sparklematic 5200. The machine surges to life in an almost frenzied attempt to purge the grime from the unknown item. Loud, violent churning sounds beat from the heart of the machine. The metal frame of the beast creaks and groans as it lurches from side to side...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2952, 'enUS', 'As quickly as it started, the Sparklematic 5200 comes to an abrupt halt. A simple \"ding\" is heard from the machine, and the mouth of the machine spirals upward. Inside the Sparklematic 5200 is a small box wrapped in a little bow. The machine would appear to wrap an object up after eliminating its grime-encrusted status. Even if the machine didn\'t work, at least the item is now wrapped in a pretty box.$B$BWell, there is one way to find out if it cleaned the item or not...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2953, 'enUS', 'The Sparklematic 5200 comes to life, lurching to and fro. Inside, the helpless grime that has encrusted the object placed within it is being pulverized by the machine\'s gnomish cleaning agent.$B$BThe Sparklematic 5200 comes to a halt, heralded by a familiar \"ding!\" The item receptacle of the machine spirals open, revealing a neatly wrapped box.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2954, 'enUS', 'The stone watcher\'s voice resonates with power and authority:$B$B\"Your discs will be returned to you once you access the pedestal. When you have obtained the Plates of Uldum and incorporated them into your existing disc set, you may then enter the complex.\"', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2962, 'enUS', '<Ozzie examines you.> Impressive, you still have all of your limbs. Now it\'s time to see what this stuff can do!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2963, 'enUS', 'I am glad you\'re maintaining your interest in exploration, $n! With your natural talent and moxie, we could crack open secrets that we never thought were possible!$B$BThe Discs of Norgannon in Uldaman were a repository of vast information; you were only permitted to access the top layers, as it were, of the information stored there. The Creators were rumored to have more repositories located throughout Azeroth. This now brings us to Uldum...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2964, 'enUS', 'We\'re all in agreement then - the best plan of attack is putting this on hold until we can narrow down the areas where these plates might lie hidden. You\'ve brought us this far $n, and I hope you will be the one to head back in to the field to unlock Uldum for us all!$B$BAllow me to give you this honorarium in the meantime; this should cover any expenses you\'ve encountered in assisting us, and a little something extra. Be sure to check back with us soon... Uldum beckons!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2965, 'enUS', 'There is a place deep in the Tanaris desert called the Valley of the Watchers. The discs that you recovered... their translated glyphs make mention of a place called Uldum. Most think this place to be a myth.$B$BI know this to be false; Uldum exists. I\'ve been there. Well... outside, at any rate.$B$BThe crux is this, $n: we think there is a connection between the discs and Uldum, and we want you to go there.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2966, 'enUS', 'As you touch the pedestal, the miniature discs from Uldaman hum and glow in your backpack. A familiar feeling tingle washes over your hands, as the image of another Stone Watcher springs to life in front of the gates of Uldum.$B$B\"Salutations. I am a guardian of entry. The discs that have been used for access are missing the correct entry discs, the Plates of Uldum. Please incorporate these plates with the disc structure and repeat your access sequencing. Thank you.\"', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2967, 'enUS', 'You were missing some discs? Interestingly enough, it seems that the discs of Norgannon are only parts of the complete set - like keys on a key ring, if you will.$B$BIf we can find the Plates of Uldum, then we could actually access the Uldum complex itself. I don\'t know whether to be excited by that prospect, or be terrified.$B$BWe may need to risk everything to ensure that those secrets the earth keeps which would destroy us all remain just that - secrets.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2968, 'enUS', 'We\'re all in agreement then - the best course of action is to wait until we can narrow down the areas where these plates might lie hidden. You\'ve brought us this far $n, and I hope you will be the one to head back in to the field to confront the secrets of Uldum.$B$BAllow me to give you this honorarium in the meantime; this should cover any expenses you\'ve encountered in assisting us, and a little something extra. Be sure to check back with us soon... Uldum beckons us all, young one.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2969, 'enUS', 'Thank Elune, $n! You are a great blessing to us and to the darters you saved. Jer\'kai and I would never have been able to help the dragons ourselves--your intervention in the Grimtotem\'s affairs will be remembered.$B$BSpeak to Jer\'kai now... I have to prepare for our retribution. Just because the faerie dragons are free, it doesn\'t mean they will remain so. Jer\'kai and I will now go on the offensive.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2970, 'enUS', 'Kindal was right about you, $n--you do have a knack for this. You are a more seasoned adventurer than the both of us, and for that we thank you.$B$BThe Grimtotem clan is much more hostile than most tauren... I wonder if their own kind frowns upon their actions. I wouldn\'t be surprised. As you are probably aware, many races on Azeroth have ties to less kindly counterparts.$B$BBut that is a discussion for another day.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2972, 'enUS', 'That\'s an amazing story, $n. It pleases us that you\'ve not only helped dragonkind, but also one of our own priestess. Jer\'kai was right to send you to me. I will make sure she has her ring returned to her--I have scouts heading the direction of Feralas soon. This will be one more thing for them to investigate while on their mission.$B$BThank you again, and please, make yourself at home while you\'re here.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2973, 'enUS', 'Wonderful, $C! These will do nicely. There are enough here to make a cloak for Korrin, and possibly even more! Thank you.$B$BI hope the trip wasn\'t too dangerous? The area of Feralas is not known for being forgiving, but you\'ve obviously done well enough.$B$BThank you again, $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2974, 'enUS', 'These are good horns, $C. Well done. I\'ll take these from you and be sure to tell Belgrom as soon as we return to Orgrimmar. You\'ve helped me twice now, and for that, you have my mark. Perhaps in the future I can help you! Good luck to you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2975, 'enUS', 'Excellent, $n. In time, the land will heal. We must not give up.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2976, 'enUS', 'The Grimtotem!? This is interesting. You say you came across them while hunting in Feralas? Truly?$B$BMy peers will want to hear your news, $C. Tell me more and I\'ll count out your coin while you do so.$B$BAnd before you go, know that I give you thanks for the information you\'ve passed on to me this day.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2977, 'enUS', 'You were missing some discs? Interestingly enough, it seems that the discs of Norgannon are only parts of the complete set - like keys on a key ring, if you will.$B$BIf we can find the Plates of Uldum, then we could actually access the Uldum complex itself! Imagine actually exploring the place where the Creators worked without having to bring a huge shovel!$B$BNot that there\'s anything wrong with shovels... or digging...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2978, 'enUS', 'This scroll... You found this in the Gordunni Outpost?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2979, 'enUS', 'This orb will provide us with more information about the Gordunni... which we will then use in planning their downfall, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2980, 'enUS', 'You have proven yourself to be a trustworthy and dependable ally, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2981, 'enUS', 'So, Belgrom sent you. You are just in time, $R.$B$BLet me explain the situation, and then you can decide if you are up to the challenge... But if Belgrom sent you, I am sure your courage is unquestionable.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2982, 'enUS', 'I am very grateful you were able to do this for me.$B$BNow, please, don\'t mention a word to Troyas about this...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2984, 'enUS', 'Hmmm, Narm sent you to me, did he?$B$BWell then, Narm could always be trusted, so that must mean you\'re ready to take part of the next sapta--the sapta of fire.$B$BI see it in your eyes already, the burning, the desire. You\'ve tasted power, and now you\'re ready for more of it. Patience, $n. Remember what earth taught you. Power and destruction will come easily, but you must still know when to stay your hand.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2986, 'enUS', 'The wind approaches you quickly from the horizon; the earth is steady beneath your feet; and your spirit is already warmed by fire--I would say it is time for you to learn about the purity of water.$B$BYour path has led you this far, and your spirit will give you the strength to carry on even further. But the first step is yours to take.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2987, 'enUS', 'Perfect! I will take these along as I continue my travels, $n. I\'m sure I can find someone that will pay a pretty penny for this cobalt.$B$BHere, take this for your trouble.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2988, 'enUS', 'Sharpbeak wasn\'t at the villages? Hm... this is grim news. That gryphon is destined to lead the next generation of gryphons. If he is lost then the strength and spirit of our fleet will suffer immensely.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2989, 'enUS', 'Sharpbeak wasn\'t there? That is bad news, but it is good news too...$B$BIt is good that he was not at the Altar of Zul for the Vilebranch trolls are known for performing sacrifices.$B$BI thank you for all of your aid, but I\'m afraid I have an even greater task ahead.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2990, 'enUS', 'Ah, yes. This feather belongs to a fine gryphon. One destined for great things... if he can survive into adulthood undamaged.$B$BYou wish to find him? Then listen close, brave $C...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2991, 'enUS', 'Ah, the medallion of Nekrum. It is rumored to have given the troll an unnatural long life, but at a cost. I hope to discover a way to reap the medallion\'s power while avoiding its... unpleasant side effects.$B$BYou have done me a service, $n. I shall now uphold my end of our bargain.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2992, 'enUS', 'The ritual was a success. I have seen Sharpbeak and can tell you how to find him.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2993, 'enUS', 'You\'ve spoken with Grimshade? What did he say?$B$B...What? The Vilebranch trolls have Sharpbeak? That\'s unbearable! We must organize a force to get him back!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2994, 'enUS', 'Swiftwind and Keenclaw returned with their child moments ago, and are fussing over him like new parents. Seeing them together again is enough to give even this old dwarf mist in the eyes...$B$BWe cannot repay you enough for what you have done, $n. The gryphons of Aerie peak, and their riders, will sing your name for a long, long time.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (2995, 'enUS', 'Perfect, $n. While the Highvale work to recover their lost information, we will be able to seal off the lines even further...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3002, 'enUS', 'Fascinating, $n...$B$BThis orb is very powerful, but also very dangerous.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3022, 'enUS', 'Thank you for your help, $n. The hippogryph population simply wouldn\'t flourish if it weren\'t for us. Ah, but I am sure you understand all too well, having traveled to Feralas yourself, and seen the devastation of the Gordunni there.$B$BWell, as you can see, I am hard at work here, so you\'d best be on your way.$B$BThanks again, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3042, 'enUS', 'Ah, this is very nice temper indeed! And so much of it! I\'ll be at work for days before I use it all!$B$BThank you, $n. Please, take this as payment.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3062, 'enUS', 'Now I know that Edana is truly dead. This heart; I will keep it in memory of what did here for me, $n. Thank you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3063, 'enUS', 'Thank you, $n. You have put an old tauren\'s mind at peace.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3065, 'enUS', 'You\'ll find this place fitting as you continue to train in the $C ways. If you have any questions, feel free to ask anyone around, and when you feel you\'re ready, come back to me for training. I\'ve mastered as much as any $C here in the Valley and can pass that knowledge on to you... for a price.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3083, 'enUS', 'Until such time comes, you\'ll be under my tutelage. I will show you the ropes as some folks say. Whenever you feel prepared, come to me and I will see about training you further. Until then, practice your skills and keep them honed to a razor\'s edge. You\'ll never know when you\'ll need even the most insignificant ability.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3084, 'enUS', 'As you probably know, the elements give us our power. Fire, earth, water and air--these are our tools.$B$BOur strength comes from the spirits of our ancestors. We are the leaders of our people. Like Thrall is to the Horde, so shall we be to the villages and towns we visit. They will look to us for wisdom and protection. To be better prepared, you must train often. When you feel you\'ve gained more power, come to me and I shall teach you what I can.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3085, 'enUS', 'As ya test yeself, ya\'ll find ya have better an\' better control o\'er da spirits--ya\'ll be callin\' on dem fer greata an\' greata tings... dat\'s where ol\' Ken\'jai come in. You come talk to ole Ken\'jai when ya feel yer ready. If da spirits agree, den we see \'bout teachin\' ya more powerful abilities... but only if da spirits agree.$B$BGo now, talk to da rest of da Valley\'s people. Speak to me again later.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3086, 'enUS', 'You be among da most powerful beings on da planet, $n... there no arguin\' dat. And everyone know it... know it good, too!$B$BBut ya must be weary! \'cause it not orc, or elf, or dwarf dat you should be scared of... it be da tings you can\'na see. Magic be powerful. Make you do tings you may not want to do. Make ya tink you more powerful then you really be. Be patient. Be smart.$B$BWhen ya tink you be ready for trainin\', come back to Mai\'ah. She teach you what you ready to learn, no problem.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3087, 'enUS', 'As a $C, my skill rivals the arcane powers of the mages, the martial prowess of the warriors, or even the blessed acts of our beloved shaman. To be a $C requires focus and dedication. Let us continue.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3088, 'enUS', 'Now that you know where I\'m holding up, I should let you know: the Shattered Hand\'s asked me to look out for you. They want me to train you and keep you informed of anything they might need. Even if you\'re not interested in helping them yet--which you\'d be a fool not to be--I\'ll do what I can to train you... for a small fee.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3089, 'enUS', 'You will be the conduit in which our ancestors communicate. You will have the power of the elements at your beck and call. Sundering your enemies will be child\'s play; healing your allies as easy as breathing.$B$BBut not yet. First, you train.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3090, 'enUS', 'And now you\'ve joined our ranks also. Don\'t misunderstand, $n. Just because you have access to power that many will be jealous of, it does not mean you will be accepted easily. Hellscream allows our kind into Orgrimmar because we are still his fellow kin--he cannot turn his back on us, or we would become as pathetic as the humans who enslaved him. My point is: be careful. You can be powerful, but if you are foolish, then you are as good as dead.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3091, 'enUS', 'Each of us plays a role in our tribe. Each of us must also go through all the same rites. Do not ever let your pride tell you otherwise.$B$BI remain here to train new warriors that seek the glory of battle and who follow the ways of our oldest ancestors. When you feel ready, come back to me and I will do what I can to train you further. Go with the tribe\'s blessing, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3092, 'enUS', 'And it is my job to make sure you are prepared by teaching you the basics of our profession--how to tame your pet, how to fire your rifle, and many other skills that will become necessary.$B$BHunters are a strong part of our tradition and history, $n. You would do well to remember how important you are to the tribe. You will be respected and revered if you serve our people well.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3093, 'enUS', 'We will meet many times in the days to come. Each time you will hopefully leave my company a little more powerful... a little more prepared. Like the Eternal Flame, your spirit will burn bright and hot.$B$BGo now, $n. Go and test yourself. I will be here for you when you need.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3094, 'enUS', 'Though we share a common bond, do not expect much welcome from the night elves. Their pride still limits their sight, but that is none of our concern. The teachings of the Earthmother are all you must concern yourself with, and I will teach you what I know as you grow in wisdom.$B$BReturn to me often, $n. You will find that the Earthmother grants incredible abilities to those who are most dedicated.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3095, 'enUS', 'I will be here to train you as you grow in experience... find me as often as you like. I charge a minimal fee, but you\'ll find everything I can teach you to be worthwhile.$B$BGo now and test your skill. Many more things will become known to you as you gain experience, some of which I am unable to discuss with you here. Just know that Sylvanas will need warriors with your type of strength in the future. You will be the backbone of our salvation. Do not forget it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3096, 'enUS', 'I spend most of my time here in the inn keeping an eye out for new faces and just keeping tabs on up-and-coming prospects. Keep your nose clean and I\'ll do what I can to teach you a few things. Just come back to me whenever you want, and we\'ll see if you\'re ready for a new skill or two.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3097, 'enUS', 'There is only one thing you must know: we have survived through will alone. It is faith in ourselves that separates us from others, and with our powers, we will cause great change in all of Azeroth. The weak will come to lean on you. The lepers will call you Lord. And the ignorant will look to you for guidance. It is my duty to make sure you have the necessary tools so when the time comes, you are prepared. As you grow in experience, seek me out. I will teach you greater powers if you are ready.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3098, 'enUS', 'You must return to me often if you are to become powerful enough to destroy those who would oppose us. The rules of magic still apply to you, $n. You will still find corruption follows you like hungry wolves in the night. You will find its caress is not unlike an addiction, an old lover still wanting to be by your side. But these things can be staved off. These things will only control you if you allow them to.$B$BGo now, return to me when you\'ve grown more powerful.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3099, 'enUS', 'I mentioned a slave still being a slave, no matter the master. Do you remember? We both know that it is that type of control--that type of power--that now drives us. We seek to have creatures serve us. We know we are more powerful and deserve more respect than others give us ... and so now we look to take it.$B$BI will be your ally in this struggle--the struggle for our own freedom. Freedom to seek our own allegiances. When you feel ready, return to me and I will teach you all I know.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3100, 'enUS', 'Get yourself squared away, learn the layout of the land, and come back to me whenever you need training. I\'ll be here night or day.$B$BThe Knights of the Silver Hand have done well in making this place fairly safe, but as you meet the other citizens, I think you\'ll find they all have problems they could use some help with--help a $C can give them. Good luck.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3101, 'enUS', 'In the meantime, you should know one or two other things. You are a symbol to many here in this land--act accordingly. The Holy Light shines within you, and it will be obvious to both your allies and your enemies.$B$BAlso, as you gain in wisdom and power, you will need to train to learn new abilities. That\'s where I come in. When you feel you\'ve gained some experience here in Northshire, come back to me and I will teach you what I feel you are ready to learn. Good luck... $C!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3102, 'enUS', 'You\'re gonna find a number of outfits that covet our skills, $n. Adventurers, SI:7... heck, even the disorganized rabble wouldn\'t mind a spy or two inside Stormwind. But you remember this: You\'re your own man. Don\'t let nobody bully you into doing something you don\'t wanna do! Besides, we hold all the cards... at least, we do before the game\'s done. Know what I mean?$B$BAnyway, just wanted to introduce myself and let you know that I\'m here if you need any training. Come by anytime.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3103, 'enUS', 'As you grow in experience, return to me and I will do what I can to impart my knowledge upon you. Until then, go with compassion in your heart, and let wisdom be your guide. Remember, the world only becomes a better place if you make it so.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3104, 'enUS', 'Haha, I knew my note would not dissuade you from your path. So, you\'re prepared then, are you? Prepared to accept your fate? Prepared to challenge the gods and any other forces that stand before you in the pursuit of knowledge and power?$B$BI will not lie, $n, you will be feared as much as respected. But also know this, I will be here for you when you need training. Simply seek me out as you grow more powerful.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3105, 'enUS', 'As you grow in power, you will be tempted--you must always remember to control yourself. I will not lie--corruption can come to any practitioner of the arcane; especially one who deals with creatures from the Twisting Nether. Be patient, and be prudent... but do not let that repress your ambition.$B$BAs you grow more powerful, return to me and I will teach you more about our ways.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3106, 'enUS', 'If you look around, you\'ll see a few interesting things: dwarves using magic in a fight instead of a rifle. Some of us have taken to worshipping the Holy Light like some of the humans, and, last but not least, troggs. You\'ll learn more about them later, but what you need to know now is that I\'m here to help you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3107, 'enUS', 'The humans have taught us a great deal about the Holy Light and how important their order is to them... and to protecting all of Azeroth. We have a great respect for how it is steeped in tradition, and how just one figure, one sacrifice, can make the difference between pain and salvation.$B$BThe two of us are gonna be working together for a bit.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3108, 'enUS', 'One thing you should always remember is that you gotta respect the world you\'re livin\' in. The beasts that choose to fight along side you; the shine of your weapon; the weight of your ammo; the bite of a bitter wind; all these things you need to know intimately.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3109, 'enUS', 'Anyway, as you start peekin\' around Coldridge you\'ll probably have need of training at some point. You just come back to me whenever you feel you need it and I\'ll teach you what I know--a trick here, a maneuver there. You know, the stuff that keeps you alive and makin\' a little extra coin.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3110, 'enUS', 'I\'m the one who can train ya as you start to find your way around Coldridge Valley. When ya feel yer ready, come back to me and we\'ll see about maybe teaching you a new spell or two to fend off the valley from them blasted troggs.$B$BYa got the Light on yer side, along with all of Ironforge, $n... no pressure on ya to become somethin\' special.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3113, 'enUS', 'I pretty much keep to myself around here so we don\'t get a bad rap for things gone missing, but if you need anything, some training or somethin\', just come back anytime.$B$BYou and me got an understandin\'. We know when it comes down to it you can only trust family... and that\'s just what we got going on here, isn\'t it? Regardless of Gnomeregan being... overrun, and whatever else is thrown our way, we got one another to back each other up. Right?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3114, 'enUS', 'Oh, yeah! Arcane Arts... of course.$B$BHere\'s what you need to know, $n: Don\'t get into too much trouble. Save some coin to buy me an ale... or any spells ya might want. And stay the heck away from Gnomeregan. Other than that, have a good day! I\'ll be here if you need me or want to train... or whatever.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3115, 'enUS', 'But none of that matters. What matters is that you\'ve seen your own Holy Light! You know where power truly comes from. You realize that having our own allies is much more... prudent. Special allies. Allies that, no matter the task, will obey till their last breath.$B$BAnd that\'s where I come in, $n. I can train you in the beginnings of those special powers. Seek me out often and I will do what I can to teach you more spells.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3116, 'enUS', 'Soon you will see others from different races in the boughs of our home--do not let it cause any prejudice within you. They are welcome. They will aid us when they can. Not all of them will be altruistic, but they should be granted some amount of trust.$B$BBut none of this matters now. Now we must focus on you, and how you can aid our people. I am here for that very purpose. I will train you in the ways of a $C as you become stronger. Return to me whenever you wish and I will do what I can to aid you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3117, 'enUS', 'As you experience what Shadowglen has to teach you, come see me--I will be able to help you understand more about your role as a $C and what you are capable of.$B$BThese are dangerous times, $n, do not take for granted the skill of your pets--there are things in this world more dangerous than you can imagine. Go with Elune and return to me again.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3118, 'enUS', 'Until then, know that our kind are needed more than ever in this tenuous time. Peace with the other races can fail at any moment, and there is much talk about members of the Horde looking to sow even greater seeds of distrust. Remember that.$B$BAs you gain in power and feel more prepared, come back to me here and I will see about getting you some training. It\'s important that you know how to handle your weapon of choice, among other things.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3119, 'enUS', 'Do not concern yourself with the world below Teldrassil for now. There will be time for that later. Go now and speak to the others in Shadowglen--they can tell you more about what events have transpired. Help them in any way you can, and anytime you feel you need more training, come back to me and I will teach you what I can.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3120, 'enUS', 'I will be your mentor and guide for now. It is my duty to teach you how to interpret not only what the spirits communicate to us, but also the complexities and secrets of the Emerald Dream.$B$BAs you learn and grow in power, you will learn more of our sleeping trance. But until then, return to me as often as you like and I will teach you what I can in the ways of our kind.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3121, 'enUS', 'I have been waiting for Uzer\'i to deliver this...$B$BLet me give you the herb mixture I prepared for his... well, experiments...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3122, 'enUS', 'This is just what I need to prepare the muisek vessel, $n. Soon we will see if my latest incantations prove to be as powerful as I had hoped.$B$BPlease, a moment. I must perform the ritual and apply the herbs.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3123, 'enUS', 'Upon a violent death, the muisek of a creature may escape and seek revenge. Shrinking the creatures, as you did, $n, prevents the muisek from escaping. Now we may use it as we please!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3124, 'enUS', 'After killing an animal, if we allowed the muisek to escape, it may take the form of an even greater creature. We would never be safe from its vengeance.$B$BThis is a dangerous job I have given you, $n. Again, you have completed it well.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3125, 'enUS', 'Thank you, $n. Now the spiritual powers of the faerie dragon will be available to us.$B$BI hope you willing to perform another task for me.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3126, 'enUS', 'Thank you, $n. Our muisek collection is almost complete.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3127, 'enUS', 'Just imagine the possibilities of what we now possess, $n. Along with the materials that you have collected, I will be able to forever trap the muisek of these creatures into whatever I wish.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3128, 'enUS', 'Excellent! These materials, combined with the muisek, should yield just the results we\'ve been wanting.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3129, 'enUS', 'As promised, you have your choice, $n. The muisek of the creatures has been forever sealed into the power of these weapons.$B$BYou may now wield their energy as your own. Use it well.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3130, 'enUS', 'Excellent, $n - I value a \"can do\" attitude in those who I work with.$B$BI\'ve been tasked by General Feathermoon to address the naga in a direct manner. My plans are two fold: one, we will weaken their forces both in number and in command, and two, we will uncover what they are up to in the ruins. With your help, we\'ll counter the naga presence without sacrificing the general security of the stronghold.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3141, 'enUS', 'So be it...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3161, 'enUS', 'Perfect; these ornaments are perfect for my studies. Thanks so much for the help, $n.$B$BOh, can I get my helmet back?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3181, 'enUS', 'What have you got there? A fake obviously!$B$BI\'ve been face to face with Margol and I know for a fact that her horn is at least eight times larger than the one you\'ve got in your hands, $n. You probably got that off of a stormsnout in the Barrens. No sir, you can\'t fool Pebblebitty!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3182, 'enUS', 'What\'s this?$B$BOh my, I don\'t believe it! Do you realize what you\'ve got here, $R? This is the horn of Margol the Rager! Margol, the bane of every archaeologist that ever set foot in the Searing Gorge.$B$BMay I keep it?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3201, 'enUS', 'I don\'t know how someone as weak as you overcame Margol, but a deal is a deal. Here is the key.$B$BIf anything happens to you after this, it will be your own fault.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3221, 'enUS', 'Ah, very good. There is a matter regarding the wolf hearts you brought to me earlier that demands our attention.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3261, 'enUS', 'Greetings, young one. You are here to walk the hunter\'s path?$B$BVery good. Let us begin.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3281, 'enUS', 'Aha! So the raptors did hide the silver at their lair. It\'s hard to believe these beasts would have the desire for silver. Perhaps the stories of their intelligence are not as far from the mark as I thought!$B$BThank you, $n. Take this as a reward for your service to us, and know that the guards of the crossroads owe their next month\'s pay to you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3301, 'enUS', 'Ah, what is this? This shell has an inner glow, as if it still pulses with life. Amazing! I must study it further.$B$BThank you, $n. With luck, this shell will hold a secret I can use to aid the poor land of Silverpine.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3321, 'enUS', 'Rise and be recognized, $n, blacksmith of the Mithril Order.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3341, 'enUS', 'The loyalty of a Lich is unswerving, $n. Let them know that such loyalty will only bring them destruction.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3361, 'enUS', 'Huzzah, you\'ve found them! Get ready, women of Gnomeregan: here comes Felix!$B$BNow, which road takes me out of here...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3362, 'enUS', 'They\'re that big? I might have to rethink my plan...$B$BBut you did hold up your end of the bargain, so here\'s what I promised.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3363, 'enUS', 'You apply some Cenarion plant salve to the songflower. It immediately begins to shed its corrupted form, blossoming into a vibrant and healthy plant.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3364, 'enUS', 'Ah good, now this will hit the spot. Allow me to take a small break while I enjoy this scalding mornbrew!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3365, 'enUS', 'Excellent, $n! You\'d be surprised how many couriers I meet who forget to do something as simple as bring back the mug. Mugs don\'t grow on trees, or so they tell me!$B$BHere\'s a little something for the effort. Thanks again for your help.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3367, 'enUS', 'This letter was dropped by Dorius when he was shot.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3368, 'enUS', '<Thorius wipes his face dry.>$B$BIn the name of the Light, he will have his wish. This I vow.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3369, 'enUS', 'Falla sent you to me? Well now, let\'s see that shard that you say she was so hesitant to talk about...$B$B<Hamuul peers into the shard>$B$BOh... I didn\'t... Well.$B$B$n, thank you for delivering this to me. It isn\'t wise to speak of things such as this, though, I advise you. These are matters that you should not concern yourself with.$B$BLet\'s just say that things in the Emerald Dream are not as we thought...$B$BPerhaps in the future, our paths may cross again.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3370, 'enUS', 'Falla sent you to me? Well now, let\'s see that shard that you say she was so hesitant to talk about...$B$B<Mathrengyl peers into the shard>$B$BOh... I didn\'t... Well.$B$B$n, thank you for delivering this to me. It isn\'t wise to speak of things such as this, though, I advise you. These are matters that the Cenarion druids alone must attend to.$B$BLet\'s just say that things in the Emerald Dream are not as we thought...$B$BPerhaps in the future, our paths may cross again.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3371, 'enUS', 'Forget about me, help the others.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3372, 'enUS', 'You place the mysterious artifact on the altar of Suntara.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3373, 'enUS', 'You place the gem inside the essence font.$B$B\"Thank you mortal, you have - no... this, this cannot be! No! The corruption of this accursed god even taints the sanctity of a Green Dragonflight essence font! No please, I feel my essence ripping from me! THE PAIN! I feel the grip of an eternal nightmare taking hold on me...\"$B$B\"Mortal, I implore you! Find one of the Green Dragonflight to stop this... help me... I am chained in darkness... forever in agony... please do not torture...\"', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3374, 'enUS', 'You have made your choice, and so it begins. Your choices have been, to date, what I would consider to be the correct ones; your actions and deeds henceforth will determine whether this is to continue.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3375, 'enUS', 'The mighty adventurer delivers. Hooray for me. By the way, I lied - I had another phial. Ungh, take it.$B$BWell, what did you expect?! \"Oh, here, have another one! I\'m just some goofball who has nothing better to do than hand out magical communication devices to adventurers!\" Rest assured though, what you have given to me will be enough make you another phial when you lose that too.$B$BPlease prove me wrong and don\'t lose the new one, okay?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3376, 'enUS', 'Justice has been served on this day by your swift actions, $n. Let this deed serve as a warning to all who would threaten our home.$B$BYou have earned this reward to help you on your sojourn, young $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3377, 'enUS', 'I have accepted my fate here in this cage, but the prayer must be found.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3378, 'enUS', 'Elune smiles upon you this eve, $n. You have returned a lost soul to her. Please accept this as a token of our gratitude.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3379, 'enUS', 'Excellent work, $n.$B$BI am sure that this is a piece of shadoweave that you have not yet seen.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3380, 'enUS', 'Yes, the Sunken Temple, I can say that I have seen it with my own eyes. Perhaps you\'d like to hear more about it?$B$BYou look like the adventurous type -- I know things that may intrigue you...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3382, 'enUS', 'You\'ve done it! Thank you! Surely this was one of the most brutal battles I\'ve ever taken part in. I can\'t thank you enough.$B$BI only wish more of my crew could have lived through this trial.$B$BI would ask you to speak to any surviving passengers from the Horizon Scout. Some of them had business in the area, and may have a request or two of you if you\'re willing. Again, thank you for all your help.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3402, 'enUS', 'Kovic slain??! This is a glorious day, $n! For years that scoundrel has been running shady undermarket trades, undercutting honest, hard working traders all over the world.$B$BYou deserve a fine reward for this accomplishment! And a fine reward you shall have!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3421, 'enUS', 'I will return you to Sanath\'s camp now.$B$BDo not mind his bad manners--he is rude to everyone save for the master. Perhaps he shall learn his lesson one day.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3441, 'enUS', 'Have you heard enough? Are you prepared to act?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3442, 'enUS', 'Well done, $n. We are almost ready.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3443, 'enUS', 'You\'re an exceptional laborer, $n. Have you ever considered a change of vocation? Perhaps a detective? Junk collector? Oh, I know, one of those silly gnomes that crawl across the beaches of Azeroth with their metal detecting contraptions, looking for lost treasure. Yes, that would be an ideal position for you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3444, 'enUS', 'Yes... This is the stone.$B$BIt\'s all coming back to me now, $n...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3445, 'enUS', 'Yes, the Sunken Temple, I can say that I have seen it with my own eyes. Perhaps you\'d like to hear more about it?$B$BYou look like the adventurous type -- I know things that may intrigue you...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3446, 'enUS', 'You push the stone circle into the opening, and you hear it lock into place.$B$BNow that the stone is in place, you may continue... but what should you do next?$B$BPerhaps you should examine this altar further...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3447, 'enUS', 'The eye of the idol glitters brightly even in the half-darkness of the temple. Its transparency reveals something shiny behind it. Sliding it to the side, you reach in and grasp what lies inside the statue.$B$BThe Atal\'ai treasure is now yours.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3448, 'enUS', 'Ah, Karnik sent you, did he? I expected to hear from him soon. I\'ve discovered such wonderful things about Azshara.. I know night elves have incredible life spans, but everyone forgets some of the more... unfortunate events in their lives sometimes. That\'s not meant as a criticism, just an observation.$B$BLong ago, there were cities created by the elves filled with knowledge about magic and other lost arts. Being a student of such things, I started my investigation. What I found was quite thrilling.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3449, 'enUS', 'Good to see you safe and sound, $n. I hope things weren\'t too tough for you.$B$BI\'ll get these to Tymor right away. He\'ll be thrilled to take a look at these things.$B$BGo ahead and toss the flare gun... probably won\'t be any good to you anymore anyway, and I already got a new one.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3450, 'enUS', 'What can I do for you, $n? Kinda busy at the moment preparing my ship for a long trip. I just came over to see some of the tank pilots--I need some wendigo manes to line my cockpit and they usually have extra.$B$BI can give you a minute if you\'re brief... one of the boys just took off to Steelgrill\'s Depot to see if the pilots down there have extra manes for me.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3451, 'enUS', 'Here\'s my standard issue flare gun, and be patient after you fire off the gun--there are only a couple charges in one of these, and it might take me a minute to get to you depending on how far out to sea I am. Just look out to sea after you fire off the flare.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3452, 'enUS', 'The symbol radiates foul magic; perfectly suited for our task.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3453, 'enUS', 'There is one final task that you must complete.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3454, 'enUS', 'You grab the torch with both hands; waves of hatred and anger pulse through your being.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3461, 'enUS', 'Wonderful! I\'m so glad to hear it.$B$BI\'m really excited to get to my tower and go over those rubbings. I\'m so envious of you... going to a place like Azshara. That\'s any student of the arcane\'s dream, I would guess.$B$BWell, we never spoke about any kind of reward since you were just working for the Explorer\'s League, but you deserve something.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3462, 'enUS', 'Are you prepared to make your strike?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3463, 'enUS', 'It is glorious! Is it not glorious master?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3481, 'enUS', 'The chest opens at your touch. How much cheese could possibly be in here? There is only one way to find out.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3483, 'enUS', 'Ah, another flare gun, huh? That I can do.$B$BAnd don\'t forget, southern tip of Azshara. It\'s a small little island that\'s been turned into a landing pad. You can\'t miss it if you\'re in the right spot.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3501, 'enUS', 'You truly disgust me, $R. A grown man drooling over a pile of junk? Pitiful! Take your reward and get out of my sight.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3502, 'enUS', 'You truly disgust me, $R. A grown man drooling over a pile of junk? Pitiful! Take your reward and get out of my sight.$B$BSounds familiar? It should, because I say it a thousand times per day.$B$BOh how I loathe this world.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3503, 'enUS', 'There was a time when I could ask whatever I wished from those who sought out my master. But now, you need only ask and I will send you to him.$B$BIt is upsetting to me, but I do not question his orders. I can only take enjoyment in making fun of the ridiculous clothes you wear or the pathetic look on your face. Even that bores me...$B$BEven this rambling bores me! Do you wish to see my master or not?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3504, 'enUS', 'We\'ve sent everything we had at her and her workers, but they are strong in battle. None have bothered to return. I\'m assuming some have died, but I\'m sure others simply ran... cowards.$B$BBut now that reinforcements have arrived, perhaps there\'s still something we can do. Perhaps we can still punish her for lying and betraying Belgrom.$B$BWhat do you say, are you in?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3505, 'enUS', 'You begin to skim some of the pages until you come to a page that\'s marked by a thin, black strip of silk cloth.$B$BAs you try to decipher the script, you see a diagram on the bottom of one of the pages. A quick look around at the scene before you and you realize that the crystals, the circle, and even some of the other components on the ground are set up in a similar pattern as the diagram.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3506, 'enUS', 'RAR!! Incredible!$B$BYou\'ve done it! Belgrom will be pleased! Finally the spirits of dozens of our warriors can be at peace. Thank you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3507, 'enUS', 'HAHA! Look at you now, pathetic woman! I spit on your remains!$B$BThank you, $n. Thank you from the bottom of my heart! This is indeed a great day!$B$BI would give you a kingdom if it were in my power! But perhaps you will settle for this.$B$B$B$BHaha! Stupid blood elf, look at you now... in a burlap sack and missing your body! You should never have betrayed Belgrom!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3508, 'enUS', 'The name of the beast must be found.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3509, 'enUS', 'A demon lord?$B$B<Lord Arkkoroc laughs.>$B$BPerhaps the one can assist you with this information; but first, you must assist the one.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3510, 'enUS', 'Finally, one worthy of the wisdom of Arkkoroc!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3511, 'enUS', 'The temper will add the necessary vitriol we require for the forging of the weapons. There is but one more step in their creation.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3512, 'enUS', 'Itharius was wise to send you to me. While he is not dead as you understand death, Eranikus will soon give us advice in his own words. For now though, I cannot directly help you... at least not yet.$B$BI will lend my skill at death-speaking to you, but first I need the help of one who - unlike me - is not limited by the frailties of old age. My most potent augury device has been taken from me against my will, and unless I have it back we will be at a loss to help Eranikus.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3513, 'enUS', 'Well now... Where did you find this?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3514, 'enUS', 'You\'re a tough one! Ridding the Den of Rynthariel is a major step, $n. They will be slow to regroup, and so we can count on less Alliance runners in the Barrens. They will think again before sending another this way.$B$BThank you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3517, 'enUS', 'Perfect, $n, and in good condition also. These should get us a good price from all the parties concerned.$B$BLet\'s not dawdle around here any longer than we have to. We have some money to make.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3518, 'enUS', 'Ah, at long last, the Tablet of Beth\'Amara. I was wondering if my partnership with Jediga had failed or if the poor orc had found her death in Azshara. I am glad she was successful.$B$BBeth\'Amara was one of the Highborne\'s most influential alchemists. The wizard alone was said to have mastered transmuting metals among other things.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3519, 'enUS', 'Oh dear; I was wondering why I hadn\'t seen Iverron yet today. And I\'ve always warned him about those spiders...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3520, 'enUS', 'You have done well, $n. Perhaps now it is time to reveal more...$B$BThere is an ancient prophecy among the trolls, where the dead god Hakkar, the Soulflayer, will one day return to this earth. Some say the coming of Hakkar will bring joy and glory to trollkind, while others believe his resurrection marks the end of all things.$B$BI do not believe this myth, but I do know the essence of Hakkar holds great power, and this power should be kept from those who would abuse it...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3521, 'enUS', 'Thank Elune that you have gathered these ingredients so quickly!$B$BIn just a moment, I will have the antidote prepared.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3522, 'enUS', 'What is this, $n?$B$BOh, I knew Dirania would be able to help me!$B$B$B$BI\'m always getting into trouble and Dirania, well, she always manages to help me out of it.$B$BI am feeling much better, but I think I will sit here for a while longer, until I feel completely better. Well, that, and I was hoping you\'d clear a path for me through those vicious Webwood spiders...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3523, 'enUS', 'Fantastic, I thought you looked to be the heroic type! We\'ve got our work cut out for us $n, I\'m not going to lie to you. If we stay focused on the task at hand, we should be able to come out on top.$B$BNow, let me bring you up to speed on what\'s been going on...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3524, 'enUS', 'I hope the murloc did not give you much trouble in collecting this for us! I will be sure to get this on the next hippogryph to Darnassus. The Temple of the Moon has given me some funds to hand out for assistance; please have some with our thanks.$B$BYour success here emboldens me to offer you a chance to aid the Temple of the Moon further, if you are interested...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3525, 'enUS', 'Belnistrasz\'s sudden departure has left behind a burning brazier. As you stare in to the flame, the image of a great-winged red dragon fills your mind. A more graveled version of Belnistrasz\'s voice speaks directly to you.$B$B\"Indeed mortals, you are the heroes I had hoped you would be. Thanks to you the butchery these beasts were perpetrating is a thing of the past. My faith in mortals such as yourself has been once more renewed. Take this as a reward befitting of a hero.\"', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3526, 'enUS', 'Well $n, you were wise to seek my tutelage! Any schlep can become an expert at the basics of engineering - the gnomes and their irradiated home of Gnomeregan are proof of that. It takes a strong mind and a steady hand (emphasis on STEADY) though to master the discipline of Goblin Engineering.$B$BKeep your ears perked $n, and you might learn something.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3527, 'enUS', 'Ah, this is good. I can fuse these ancient tablets together, and then study their hidden secrets.$B$BMy debt to you grows, $n. And it will grow further still.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3528, 'enUS', 'The essence of Hakkar has been collected! Even now the Egg stirs with life! Could the myths of Hakkar be true?$B$BThat must not be true, for it is then I fear what Hakkar may do if birthed again. I will keep this egg safe and be sure it does not fall into evil hands.$B$BYour labors with me are finished, $n. You have touched a legend, and your actions may one day change the face of our world.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3541, 'enUS', 'Ahhh, Jediga be sendin\' her package all the way from Azshara. Good. I be waitin\' on this.$B$BJediga, now that be a fine woman... deadly too. What could be better?$B$BLook at this... the tablet be in perfect condition. You make a good messenger, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3542, 'enUS', 'Here, take some coin... get yerself... <hic>... some new armor or something....$B$BThis\'ll bring a shiny gold or two to the right... <hic> people.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3561, 'enUS', 'Ah, the Tablet of Sael\'hai... how long I\'ve longed to study it.$B$BThank you, $n. You may tell Jediga that she has done well. This will definitely be worth the payment she asked for.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3562, 'enUS', 'Ah, good. She was much more reasonable than I expected... at least towards you. I half expected her to have you punished for my insolence in sending someone in my place. She must really want that tablet. Interesting.$B$BSomething to ponder another time.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3563, 'enUS', 'I can see he was more than pleased by the looks of his note.... a little too pleased.$B$BPerhaps some day I\'ll take time to make sure he knows how much I appreciate his advances also. Well, at least there\'s one reason to look forward to spending more time in Azshara.$B$BHere\'s your share of our payment.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3564, 'enUS', 'Perfect... wait a minute.$B$BHaha! What\'d you tell him when you delivered the tablet, $n? He overpaid us way too much! Not that I\'m complaining. Did you tell him the deal changed? Or was he drunk again?$B$BDoesn\'t matter... here, take an extra share also.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3565, 'enUS', 'No problems? None at all? Well, that\'s something to be said... I expected him to show some sort of reluctance or to at least betray our deal. Hmm, maybe he can be trusted after all.$B$BWell, no need to dwell on it that much. Here\'s your share. Thank you for your help.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3566, 'enUS', '<Thorius\' face turns white.>$B$BThis... this cannot be. My own brother? He set this all up?$B$B<Thorius\' face contorts in anger.>$B$BHe betrayed me, he betrayed Ironforge! It seems as if the influence of the Old Gods is still strong in these lands. I fear that if my brother - a once noble and honorable man - was so easily turned to the will of Ragnaros, no one is safe.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3567, 'enUS', 'Would you like to see the view atop one of the twin colossals? I can send you there, $R.$B$BDo consider how you will get down, though. This is a one way trip only.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3568, 'enUS', 'Wonderful. You\'re back before she died. Good, good.$B$BLet\'s get started on this then, shall we?$B$BI\'ve been studying her symptoms for some time now, and it\'s quite ugly. I\'m surprised she\'s still standing. She\'s vomited three or four times all over the place... quite a pleasant odor.$B$BGive me just a moment to put something together to test on our... friend.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3569, 'enUS', 'Chemist Cuely has been very accommodating... disturbingly so. But if he says this will help me, then I have no other choice. I don\'t have time to find any other solution. I\'ve already spoken to priests and shamans, none of which could help me.$B$BLet us hope that the Earthmother watches over my spirit.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3570, 'enUS', 'I never dreamed the elixir would take hold that quickly. Thank you, $n. I will get to work on the rest of these samples right away. I\'m sure they\'ll be usable by the Lady Sylvanas and the Forsaken soon enough.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3601, 'enUS', 'Haha! You got them good, didn\'t you?!$B$BThis is great! That\'ll teach those Blood elves to betray their partners... especially someone as loyal as a goblin merchant! Bah, Blood elves...!$B$BWell, the least I can do for you is pay you for your time... just like I said I would.$B$BHere you go... those should be useful.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3602, 'enUS', 'Your tasks in Azshara are complete, mortal. I must now forge the necessary components for the next step in your adventure.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3621, 'enUS', 'My goodness, I can only imagine what you went through to get these materials. Few venture into Azshara and return alive, let alone those that face demi-gods and giants!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3625, 'enUS', '<Galvan wipes the sweat from his brow.>$B$BA finer demon slayer has never existed, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3626, 'enUS', 'You look battle hardened, $n. Are you prepared to face Razelikh and his minions?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3627, 'enUS', 'The amulet of Razelikh the Defiler is once again complete but he senses the disruption in his chain of command. He knows you come for him...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3628, 'enUS', 'Today is a day that will be remembered for all eternity. The demonic overlord of the Blasted Lands has finally fallen!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3629, 'enUS', 'Well $n, you were wise to seek my tutelage! Any schlep can become an expert at the basics of 	engineering - the gnomes and their irradiated home of Gnomeregan are proof of that. It takes a strong mind and a steady hand (emphasis on STEADY) though to master the discipline of Goblin Engineering.$B$BKeep your ears perked $n, and you might learn something.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3630, 'enUS', 'Ah, a young engineer interested in pursuing the one, true discipline of engineering! Well forgive my melodrama, friend. While our situation remains dire, it still is always a good day to see another engineer respond to the clarion call of true engineering!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3632, 'enUS', 'Ah, a young engineer interested in pursuing the one, true discipline of engineering! Well forgive my melodrama, friend. While our situation remains dire, it still is always a good day to see another engineer respond to the clarion call of true engineering!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3633, 'enUS', 'Well $n, you were wise to seek my tutelage! Any schlep can become an expert at the basics of engineering - the gnomes and their irradiated home of Gnomeregan are proof of that. It takes a strong mind and a steady hand (emphasis on STEADY) though to master the discipline of Goblin Engineering.$B$BKeep your ears perked $n, and you might learn something.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3635, 'enUS', 'You\'ve come to learn about gnome engineering, eh? Well, you\'ve come to the right gnome. While the ruling faction of Gnomeregan may use politics to ostracize those they view as an intellectual threat, they know that knowledge is something they cannot control.$B$BThey and I both know that when I left Gnomeregan, they lost one of their greatest gnome engineers alive today. If you\'re a capable student, I\'ll teach you what I know.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3636, 'enUS', 'Thank you , $n. The Scourge will now think twice before attempting to bolster its ranks again.$B$BMay you be blessed by the Light.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3637, 'enUS', 'You\'ve come to learn about gnome engineering, eh? Well, you\'ve come to the right gnome. While the ruling faction of Gnomeregan may use politics to ostracize those they view as an intellectual threat, they know that knowledge is something they cannot control.$B$BThey and I both know that when I left Gnomeregan, they lost one of their greatest gnome engineers alive today. If you\'re a capable student, I\'ll teach you what I know.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3638, 'enUS', 'Welcome aboard, Goblin Engineer $n! You soon will have access to the most powerful engineering schemata anywhere on Azeroth.$B$BNow that you have committed yourself to the pursuit of this knowledge, it is time to examine your goblin-oriented engineering work. From there, we\'ll help you build a plan to maximize your goblin engineering talents!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3639, 'enUS', 'It appears you are indeed ready for proper training. I dub you Goblin Engineer $n!$B$BYou\'ll be able to access any goblin engineer for some diabolical creations. Have fun and try not to blow your face off.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3640, 'enUS', 'Welcome aboard, $n! You soon will have access to the most advanced engineering schemata anywhere on Azeroth.$B$BNow that you have committed yourself to the pursuit of this knowledge, it is time to examine your gnome-oriented engineering work. From there, we\'ll help you build a plan to maximize your gnome engineering talents!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3641, 'enUS', 'Today is the day you become a real engineer, $n. Don\'t worry about all those messy bombs and explosives, because it\'s time to shine!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3642, 'enUS', 'Welcome aboard, $n! You soon will have access to the most advanced engineering schemata anywhere on Azeroth.$B$BNow that you have committed yourself to the pursuit of this knowledge, it is time to examine your gnome-oriented engineering work. From there, we\'ll help you build a plan to maximize your gnome engineering talents!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3643, 'enUS', 'Today is the day you become a real engineer, $n. Don\'t worry about all those messy bombs and explosives, because it\'s time to shine!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3644, 'enUS', 'Since you\'re a practitioner of goblin engineering and are in good standing, I am able to renew a lapsed membership card for the fee of two gold pieces. Once this fee is paid, you will be reissued a new membership card. This will allow access to goblin engineering schemata from appropriate trainers.$B$BThis card will last for two weeks of activity, at the end of which your membership fees will become due again! If you want to access schemata, you\'ll need to bring your dues out of arrears.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3645, 'enUS', 'Since you\'re a practitioner of gnome engineering and are in good standing, I am able to renew a lapsed membership card for the fee of two gold pieces. Once this fee is paid, you will be reissued a new membership card. This will allow access to gnome engineering schemata from appropriate trainers.$B$BThis card will last for two weeks of activity, at the end of which your membership fees will become due again! If you want to access schemata, you\'ll need to bring your dues out of arrears.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3646, 'enUS', 'Since you\'re a practitioner of goblin engineering and are in good standing, I am able to renew a lapsed membership card for the fee of two gold pieces. Once this fee is paid, you will be reissued a new membership card. This will allow access to goblin engineering schemata from appropriate trainers.$B$BThis card will last for two weeks of activity, at the end of which your membership fees will become due again! If you want to access schemata, you\'ll need to bring your dues out of arrears.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3647, 'enUS', 'Since you\'re a practitioner of gnome engineering and are in good standing, I am able to renew a lapsed membership card for the fee of two gold pieces. Once this fee is paid, you will be reissued a new membership card. This will allow access to gnome engineering schemata from appropriate trainers.$B$BThis card will last for two weeks of activity, at the end of which your membership fees will become due again! If you want to access schemata, you\'ll need to bring your dues out of arrears.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3661, 'enUS', 'Thank you for bringing these to me, $n.$B$BWhat did you learn of the Wildkin?$B$BPerhaps these feathers will tell us more. Ferocious monsters, or simply misguided pets? One day I\'d like to know the truth...$B$BI am going to continue my research -- please come back and talk to me again after some time has passed.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3701, 'enUS', 'Oh my! This information explains quite a lot about Thaurissan.$B$BThere is much more to learn!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3702, 'enUS', 'Shall we proceed?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3721, 'enUS', 'You\'ve recovered three of my OOX homing robots! You\'ve gone above and beyond what you needed to, so I will give you something special for all your help. It is a miniature version of the very same robot that you have come to know and love, the OOX!$B$BI normally wouldn\'t let anyone have such a potentially lethal device, but you\'ve been a real help. Besides - the combat mechanisms and the homing logic on this one have been disabled.$B$BOther than that, it\'s an OOX by golly!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3741, 'enUS', 'Thank you for finding my necklace mister $C... you are very kind! My kitty thanks you too - isn\'t that right Effsee?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3761, 'enUS', 'Oh that\'s right Private $n, don\'t make any effort to help me unload this soil up on to the piles! If the Great Spirits had wanted it done they would have miracled it up there, now wouldn\'t they?!$B$BArch Druid Runetotem wants to speak with you again, but for his next task you find that a journeyman\'s understanding of Herbalism would be helpful. If you don\'t know it though, he\'ll still find a use for you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3762, 'enUS', 'Greetings, $C; I am glad you are interested in assisting our research. There is much to do, and I would be doing you a disservice if I informed you that the work this entails is not dangerous. Hopefully this will not scare you off... but from the looks of you, I take it that danger is something that you have dealt with before.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3763, 'enUS', 'Well now, $C... since you were informed that I was leading this research myself, you obviously came as quickly as you could.$B$BNow that you are here, be sure to pay attention the first time I tell you the details; I have no desire to repeat myself.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3764, 'enUS', 'Unload your precious cargo here, noble $C. It must have been quite the burden... not unlike the burden I carry being a visionary. Here is an honorarium for your work, as dictated by the Arch Druid himself.$B$BArch Druid Staghelm wants to speak with you again $n, but for his next task you will be aided if you have a journeyman\'s understanding of Herbalism. If you don\'t know it though, you will still be able to aid the Circle!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3765, 'enUS', 'Ah, so my brother, Argos, sent you? I realize you must be weary from your travels. But we have much to talk about.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3781, 'enUS', 'Yes, this voucher is good for twenty packets of seeds. If you run out, we have more that can be provided. Unfortunately though, Tharlendris seeds are quite costly to produce in significant numbers. This is why the Cenarion Circle asks that those who are assisting us help out by offsetting some of the cost.$B$BThose who return with morrowgrain find that the reward the Arch Druid offers far makes up for the cost.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3782, 'enUS', 'Yes, this voucher is good for twenty packets of seeds. If you run out of seeds, I do have more that I am able to sell to you.$B$BUnfortunately $n, Tharlendris seeds are quite costly to produce in significant numbers. This is why the Cenarion Circle asks that those who are assisting us help out by offsetting some of the cost. Those who return with morrowgrain find that the reward the Arch Druid offers far makes up for the cost.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3783, 'enUS', 'These furs are perfect, $n. We\'re off to a great start here!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3784, 'enUS', 'Greetings, $C; I am glad you are interested in assisting our research. There is much to do, and I would be doing you a disservice if I informed you that the work this entails is not dangerous. Hopefully this will not scare you off... but from the looks of you, I take it that danger is something that you have dealt with before.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3786, 'enUS', 'Well done $n, I will be sure to give these to the Arch Druid himself when he has need of them. Meanwhile, please accept this as a token of the Cenarion Circle\'s appreciation.$B$BOur need for morrowgrain, for now, is constant; if you wish to continue aiding us, then please return when you have cultivated more of it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3787, 'enUS', 'I thank you for coming, weary traveler! Allow me to introduce myself - I am Quintis Jonespyre, druid and humble servant of Malfurion and the Cenarion Circle. My colleagues (General Shandris Feathermoon and the Protectorate - mayhap you\'ve heard of us) and I wanted to talk with you about the work you are doing for Fandral Staghelm in Darnassus.$B$BBefore we get much further, I\'d like to ask that our discussion stay between just us. If you have no problem with this, then we may proceed.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3788, 'enUS', 'I thank you for coming, weary traveler! Allow me to introduce myself - I am Quintis Jonespyre, druid and humble servant of Malfurion and the Cenarion Circle. My colleagues (General Shandris Feathermoon and the Protectorate - mayhap you\'ve heard of us) and I wanted to talk with you about the work you are doing for Fandral Staghelm in Darnassus.$B$BBefore we get much further, I\'d like to ask that our discussion stay between just us. If you have no problem with this, then we may proceed.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3789, 'enUS', 'Well now, $C... since you were informed that I was leading this research myself, you obviously came as quickly as you could.$B$BNow that you are here, be sure to pay attention the first time I tell you the details; I have no desire to repeat myself.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3790, 'enUS', 'Well now, $C... since you were informed that I was leading this research myself, you obviously came as quickly as you could.$B$BNow that you are here, be sure to pay attention the first time I tell you the details; I have no desire to repeat myself.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3791, 'enUS', 'Gah, these morrowgrain make me feel... weird. What in the name of Elune is Staghelm up to with these things?$B$BAnyway, thank you again for your help. Here\'s a little something... you know, for the effort. My research on morrowgrain is going to take a while, and I no doubt am going to need more. While I\'m at it, I suppose I could take the time to see if morrowgrain have some pertinent alchemical uses. Still, I feel uncomfortable even being around them.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3792, 'enUS', 'Thanks $n; I\'ll just store these out of the way for the time being. While I don\'t command the resources that Staghelm does, I am able to give you a little something for your trouble. Anyway, who knows what will happen if Staghelm keeps getting Morrowgrain after Morrowgrain. There\'s something fishy going on, mark my words.$B$BRemember that I\'m always looking for more Morrowgrain. I\'ll take them in quantities of five at a time.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3801, 'enUS', 'Whether it is greed which brought you here or an act of selfless devotion, what you are about to undertake is for the good of us all.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3802, 'enUS', 'The hammer slides firmly into place, within the grasp of the massive Dark Iron monument. A tiny compartment opens at the base of the statue. Inside you find...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3804, 'enUS', 'Excellent - this morrowgrain will no doubt help when the time comes. We know that the night elves are conducting similar research into these herbs, though it isn\'t clear to what end they will use them for if they discover anything. The Arch Druid thinks that the need for morrowgrain will remain until we\'ve figured out the mysteries behind this herb.$B$BThis is for you, $n. The Cenarion Circle thanks you for your continued support.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3821, 'enUS', 'My poor Thal\'trak.$B$BWe were planning on moving in with my mother and father in the Hinterlands after our tours were over. Now those plans are ruined!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3822, 'enUS', '<Thal\'trak collapses.>$B$BWe were going to move to the Hinterlands. Did she tell you? This was going to be her last mission.$B$B<Thal\'trak sobs.>$B$BI don\'t want it, $n. Keep it, throw it away, I don\'t care! The ring means nothing to me now. Not without her...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3823, 'enUS', 'Outstanding, soldier! The orcs and ogres will be reeling from this blow for minutes, maybe even hours!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3824, 'enUS', 'Just look at it, soldier. Disgusting! When the remaining Firegut ogres see this fat mug stuck on a pike sitting at the top of their mountain, mass hysteria will ensue!$B$B<Oralius cackles wildly.>', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3825, 'enUS', 'Outstanding work, soldier! Simply amazing! Oh I\'ll get the medal of bravery for this, er, I mean you\'ll be rewarded handsomely for this!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3841, 'enUS', 'Ah, a sprite darter egg, and one near maturation, if I\'m not mistaken. Remarkable you\'ve found one, let alone in this good a condition. They\'re rarely seen in Feralas these days. I understand there were various poachers in the area trying to capture and kill them for many reasons. Too bad really... such beautiful creatures when they reach adulthood.$B$BHow did you come across such a find?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3842, 'enUS', 'Wonderful. These will do nicely. Give me a moment to mix together my components and we shall see how well this egg is going to do.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3843, 'enUS', 'Well, well, well... I haven\'t seen one of these in a dragon\'s age. Faerie dragon, if I\'m not mistaken... looks to be of the sprite darter variety. Excellent, excellent indeed.$B$BWell, if this little fella\'s gonna hatch and live to be strong and healthy, then we should get some food for it. Good ya got it to me in time.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3844, 'enUS', 'A water-logged pack rests at the bottom of the pond.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3845, 'enUS', 'Oh... Yes, these things do look familiar... well, kind of...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3861, 'enUS', '\"BACAW!!!\"$B$BThe chicken starts to gobble up the feed you put down.$B$BAfter a moment, the chicken looks around, startled. It uncomfortably shuffles back and forth. Is this chicken pregnant? You think you better check underneath it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3881, 'enUS', 'You got both our foodstuff AND our research equipment back! You\'ve not only saved the expedition, you\'ve also saved our lives. I and the rest of my team can\'t thank you enough; we just might make it here for a while now thanks to you.$B$BWhile we don\'t have much, I\'m certainly not going to leave you high and dry for the invaluable help you\'ve given us. Thank you, $n - you\'ve got a friend in Marshal Expeditions!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3882, 'enUS', 'Excellent, these should do the trick. And now, if you\'ll permit me...$B$BTHANKS FOR DIGGING ALL OF THESE BONES OUT OF THE GROUND FOR US AND NOT HURTING THE NATIVE BEASTS IN ANY WAY.$B$BWhether you really did or didn\'t, I don\'t want to know. Let\'s keep it that way, shall we? Here - maybe this will keep your lips shut on the whole thing. Anything to get out of here alive, I say!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3883, 'enUS', 'Yes, this will do nicely. You\'ve been a great help... I wish I could give you more than this, but being virtually stranded in the middle of a savage land does limit what I can and cannot give. Rest assured though - the research done here would not have been possible without your assistance!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3884, 'enUS', 'Well look at this - my journal! I thought it was lost, much like almost everything else we brought into Un\'Goro. I thank you kindly for returning this to me!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3901, 'enUS', 'I am finished with you, $n - you have shown yourself to be worthy of the freedom that you have been given. Many will stand against you for what you have become, but know that no matter what they may try to do against us, we are free and will not be shackled again.$B$BTake these and be on your way. You have much to accomplish, $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3902, 'enUS', 'Great work $n, I knew you weren\'t useless. Here - have one of the better items I\'ve found out of the lots that have been collected so far.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3903, 'enUS', 'Oh, Deputy Willem told you to speak with me? He\'s a brave man and always willing to help, but his duties keep him stuck at Northshire Abbey and I\'m afraid the problem I have today is beyond him.$B$BPerhaps you can help me?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3904, 'enUS', 'Oh thank you, $n! You saved my harvest! And I hope you showed a few of those Defias that they can\'t cause trouble around here.$B$BWe might be short on guards these days, but we\'re lucky to have heroes like you to protect us!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3905, 'enUS', 'Let\'s see here...$B$BOh my! Milly\'s grapes have been saved! When she told me that brigands overran her vineyards I nearly despaired, but my faith in the Light did not waver!$B$BAnd through your bravery, we now have grapes for more wine! May the Light bless you, $n, and keep you safe!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3906, 'enUS', 'This cannot be... it must not be. The destruction of Pyron has merely lifted a veil. Within the depths of Blackrock is a much more insidious source of corruption.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3907, 'enUS', '<Thunderheart clutches the Tablet of Kurniya.>$B$BRagnaros... here...$B$BThe elders were right to fear the corruption emanating from Blackrock Mountain. A general of the Old Gods! IN OUR WORLD! We must reassess our position here in Kargath. We must decide on whether we stay and fight or run for fear of a new sundering.$B$BBe wary of any further exploration in Blackrock Mountain, $n. A far greater evil than anything that exists in this world resides in those fiery depths.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3908, 'enUS', 'Well, of course I can temper this sword. While I\'m working, you can explain what took Linken so long to get this to me. He\'s not usually one to forget things...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3909, 'enUS', 'I\'ll need one vial to make sure that this is truly the elixir... Yes... This is the stuff.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3912, 'enUS', 'It has been long since any have come to talk with me...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3913, 'enUS', 'You push on the stone firmly, and it slides away, revealing a small depression underneath it.$B$BPlacing Linken\'s Sword into the depression, you realize that it fits perfectly...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3914, 'enUS', 'My sword... It\'s finished!$B$BNow, if only I could remember why I needed this...$B$BHere, let me at least pay you for your work.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3921, 'enUS', 'Oh, what\'s this? A samophlange, you say? What\'s a samophlange??$B$BAh... but look at the way it moves when you handle it. And there\'s the sound of intricate gears inside. Oh my! I want to fix it and see what it does, don\'t you??', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3922, 'enUS', 'Great, you got them! Now I can get to work...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3923, 'enUS', 'What\'s this? Why, it\'s a samophlange! Where did you get this thing? Have you been poking around the Venture Company holdings in the Barrens?$B$BYou must be very brave, or very stupid.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3924, 'enUS', 'You got the manual! I can\'t wait to read it and find out how to fix the Samophlange! I spoke with Sputtervalve in Ratchet--he wanted to thank you for your help, and wanted to reward your efforts.$B$BI want to thank you too, and if I ever get this thing fixed I\'ll let you know...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3941, 'enUS', 'Stay where you are for just a moment... I am testing out a new combination of crystals...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3942, 'enUS', 'Hello, $C. Is there something I can help you with?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3961, 'enUS', 'Did J.D. help you at all?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (3962, 'enUS', 'Thank you, $n.$B$BThe Golden Flame will now be returned back to my homeland, where it may be protected, never again to be used for evil.$B$BWithout you, I\'d still have no idea who I am! Since you did so much for me, I\'d like you to have something special...$B$BTake any one you want!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4005, 'enUS', 'Blazerunner, you say...$B$BYes, I am aware that a strong elemental resides in the charred center of the crater. We all just keep our distance; none have been able to penetrate the powerful aura that surrounds him. Many have been pushed from the rocky paths onto the ground below when trying to face him.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4021, 'enUS', 'You have done it! You have defeated the Kolkar!$B$BI will be sure Thrall receives word of your actions here, $n.$B$BStand tall. You do yourself, and the Horde, proud.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4022, 'enUS', '<Cyrus examines the molt.>$B$BYou have proven that you are not entirely useless, mortal.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4023, 'enUS', '<Cyrus examines the molt.>$B$BYou have proven that you are not entirely useless, mortal.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4024, 'enUS', '<Cyrus snatches the fiery essence from your hands.>$B$BYES!$B$BTake your prize and be gone. Leave my sight at once!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4041, 'enUS', 'So, you pulled a fast one on ol\' Miblon, eh?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4061, 'enUS', '<Theodora lays the fragments out before her.>$B$B<Theodora carefully examines the fragments with an enormous monocle.>$B$BInteresting. Etched into the structural foundation of each of these fragments are... initials.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4062, 'enUS', '<Lotwil\'s face turns white.>$B$BI... I know these markings.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4063, 'enUS', 'What is this?!? This head is not flesh. This is some sort of dark iron creation: A shadow of Argelmach - ANOTHER machine! I suspect that your destruction of Argelmach will be short lived as another shall rise to take \'its\' place soon.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4081, 'enUS', 'This first strike should put a crease in Angerforge\'s pants. Now, move along soldier. I have work to complete, battles to plan, enemies to crush!$B$BCheck the wanted signs for more work.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4082, 'enUS', 'Angerforge is undoubtedly... angry. Return to me when you receive a third tribute and we shall proceed with Operation: Death to Angerforge. I cannot go into any detail of what this entails, but rest assured, it involves somebody dying.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4083, 'enUS', 'The gems make no sound as they fall into the depths of the chalice...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4084, 'enUS', 'Very well, $n. I can see you are serious about helping your friend.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4101, 'enUS', 'This blood amber will be used to find a means of defeating the corruption present in Felwood. You\'ve proven to me that you\'re willing to do whatever it takes for the Cenarion Circle. I trust you now, friend.$B$BIt might seem odd that killing the elementals proved yourself to me, but the end of saving countless sentient beings must justify the means we take. You and I can now focus on the preservation of life through the salves I can make.$B$B$n, the time has come to cleanse Felwood.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4102, 'enUS', 'This blood amber will be used to find a means of defeating the corruption present in Felwood. You\'ve proven to me that you\'re willing to do whatever it takes for the Cenarion Circle. I trust you now, friend.$B$BIt might seem odd that killing the elementals proved yourself to me, but the end of saving countless sentient beings must justify the means we take. You and I can now focus on the preservation of life through the salves I can make.$B$B$n, the time has come to cleanse Felwood.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4103, 'enUS', 'Very well then, here are your Cenarion plant salves. Scour Felwood for corrupted plants, $n! Wherever they are, use the salve I have given you to purify them.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4104, 'enUS', 'Very well then, here\'s some Cenarion plant salve. If you find more vitriol when you mine in Felwood, remember to hold on to it for me! The more of it you find, the more salve I will be able to make.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4105, 'enUS', 'Excellent work $n - every bit of this, once we purify it, will help us take back Felwood. Here, take some doses of Cenarion plant salve and head into the forest. The reclaiming of the land from the corruption waits for no $R!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4106, 'enUS', 'This is exactly what I need. Here - take these plant salves and use them to reclaim Felwood from the corruption. We must persevere, $n!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4107, 'enUS', 'Here are some Cenarion plant salves - I only need one lesser nether essence for this, $n. Essence is by far the most potent item I can make use of to turn swords into plowshares, as it were. If you need more salve, then find me here!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4108, 'enUS', 'Very well then, here are your Cenarion plant salves. Scour Felwood for corrupted plants, $n! Wherever they are, use the salve I have given you to purify them.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4109, 'enUS', 'Very well then, here\'s some Cenarion plant salve. If you find more vitriol when you mine in Felwood, remember to hold on to it for me! The more of it you find, the more salve I will be able to make.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4110, 'enUS', 'Excellent work $n - every bit of this, once we purify it, will help us take back Felwood. Here, take some doses of Cenarion plant salve and head into the forest. The reclaiming of the land from the corruption waits for no $R!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4111, 'enUS', 'This is exactly what I need. Here - take these plant salves and use them to reclaim Felwood from the corruption. We must persevere, $n!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4112, 'enUS', 'Here are some Cenarion plant salves - I only need one lesser nether essence for this, $n. Essence is by far the most potent item I can make use of to turn swords into plowshares, as it were. If you need more salve, then find me here!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4113, 'enUS', 'You apply some Cenarion plant salve to the songflower. It immediately begins to shed its corrupted form, blossoming into a vibrant and healthy plant.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4114, 'enUS', 'You apply some Cenarion plant salve to the songflower. It immediately begins to shed its corrupted form, blossoming into a vibrant and healthy plant.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4115, 'enUS', 'You apply two Cenarion plant salves to the windblossom. It immediately begins to shed its corrupted form, blossoming into a vibrant and healthy plant.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4116, 'enUS', 'You apply some Cenarion plant salve to the songflower. It immediately begins to shed its corrupted form, blossoming into a vibrant and healthy plant.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4117, 'enUS', 'You apply three Cenarion plant salves to the whipper root. It immediately begins to shed its corrupted form, blossoming into a vibrant and healthy plant.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4118, 'enUS', 'You apply some Cenarion plant salve to the songflower. It immediately begins to shed its corrupted form, blossoming into a vibrant and healthy plant.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4119, 'enUS', 'You apply four Cenarion plant salves to the night dragon. It immediately begins to shed its corrupted form, blossoming into a vibrant and healthy plant.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4120, 'enUS', 'It sounds from your descriptions as if the creatures show a mindless ferocity...$B$BStay alert, $n. It would be in your best interest to learn more of the corruption. Always know your enemy.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4121, 'enUS', 'Your heroics will not go unrecognized, $n. You have earned this tribute. The Warlord will speak with you now in regards to the final mission.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4122, 'enUS', 'Can you not see the power we gain by joining the legion of Blackrock?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4123, 'enUS', 'You have the heart! Amazing! It is even more beautiful than I imagined!$B$BPlease, $n, take this as payment!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4124, 'enUS', 'Latro sent you to me, I presume. While I think it is a lost cause at this point, I\'ll take whatever assistance you can offer. I must admit... I\'ll be glad once I can put my mind to rest on this matter. Every day I set out into the wilds of Feralas looking for signs of Raschal, and every night I have come back with nothing to show for my work save gnoll teeth and disappointment.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4125, 'enUS', 'You have found what appears to be a wrecked row boat. Through the slightly muddy water you see gaping holes riddling the port side; the boat is nearly shredded down to its simple wooden frame. Whatever broke this boat most likely tried to snap it in two. A small crest of the Feathermoon Stronghold is painted on the starboard side.$B$BIn the mud at the base of the boat, a glint of something metallic catches your eye...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4126, 'enUS', 'You found it! Well done! And I hope you gave those Dark Irons, and Hurley Blackbreath, a good thumping!$B$BThe Thunderbrews are at your service, $n. You are a hero of heroes!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4127, 'enUS', 'Interesting... I believe that this is Raschal\'s knife. You say you found it at the bottom of the water, along with his boat? Well, just the knife\'s discovery might not be enough to connect this to Raschal\'s disappearance; we do, however, have an unconventional option available to us to try and learn more.$B$BHave you ever heard of psychometry?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4128, 'enUS', 'You\'re right I\'m mad! I\'ve been robbed! Robbed of one of our family recipes! How dare they! As if I didn\'t have enough cause to hate the Dark Iron dwarves! Now, oh... now they\'re in for it!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4129, 'enUS', 'Thank you, $n.$B$BWhile psychometry isn\'t an exact art by any stretch of the imagination, I\'ve used it in times past to give insight into situations that others have chalked up to lost causes. I think using it on the knife you\'ve uncovered will give us a glimpse into what happened to Raschal.$B$BIt will only take a moment to do, but it isn\'t what I\'d call a caster-friendly task. Bear with me on this.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4130, 'enUS', 'Thanks for passing along Quint\'s visions to me, $n. I\'ll try to remember to be a little easier on him the next time we play cards.$B$BRaschal can hold his own in a fight, and gnolls aren\'t anything that would get him worried - nay, terrified. Still, maybe you and yours can act on this new information and make the breakthrough we desperately need.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4131, 'enUS', 'Tacked up on a nearby tree are two large leather backpacks. Each bears a small insignia of the Feathermoon Stronghold on the front of the pack...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4132, 'enUS', 'Finally! The villain been brought to justice! You are a remarkable individual, $n. Wear this medallion as a symbol of your stalwart dedication to the Horde and the K.E.F.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4133, 'enUS', 'Thank you for coming, $n. This place is not without a stark charm, but it lacks the amenities of the Apothecarium.$B$BEnough with the pleasantries. To business, yes?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4134, 'enUS', 'Very good. I will send this recipe to the Apothecarium to be studied, and in time we will unlock its secrets. One day, the dwarves of the Alliance may find the virtues of this drink used against them!$B$BI find that terribly amusing. Perhaps death has an affect on one\'s sense of humor... do you think?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4135, 'enUS', 'You have discovered a large pod of an alien nature. The texture of the pod appears mucous, though hardened protrusions stick out from random parts of it. The pod itself has the height and width to easily encompass a humanoid inside of it... and from the faint humanoid shape visible through the skin of the pod, you surmise that this is what it indeed encapsulates.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4136, 'enUS', 'Aha! You found him! And... it appears that my little brother didn\'t want to come quietly. It\'s a pity. I would have liked to see the look on his face when I told him our plans to turn him in to Revilgaz.$B$BThank you, $n. You\'ve made my family very happy. And Ribbly\'s never looked as peaceful as he does now.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4141, 'enUS', 'I hope they didn\'t give you too much trouble, $n. I try to steer clear of them -- they\'re wild!$B$BI tried jumping over one... I was attacked! Plants just shouldn\'t be acting that way!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4142, 'enUS', 'Bloodpetal, eh? Hmmm... Well, you better explain the plan, and then I\'ll tell you whether or not I can help you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4143, 'enUS', 'I admit, I\'m a bit hesitant to touch that stuff, $n! But if Gregan says it\'ll work, then I\'m willing to give it a try!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4144, 'enUS', 'Here goes nothing, $n! This will get Larion\'s attention!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4145, 'enUS', 'Thanks for your help, $n, but I\'m afraid I have some bad news...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4146, 'enUS', 'So, Marvon wasn\'t around? Well, show me what you got, anyway.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4147, 'enUS', 'Marvon\'s been gone quite a long time now, $n. He went off to Tanaris to study some of the ancient troll ruins.$B$BNow, what was it that you said you needed?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4148, 'enUS', 'Looks like this is going to work perfectly, $n. Now remember, if you see any of those pests around, don\'t hesitate to use that zapper!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4161, 'enUS', 'Always remember what I have shown you today, $n. You are part of the balance that the night elves strive to keep intact, even during troublesome times.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4181, 'enUS', 'Well $n, you were wise to seek my tutelage! Any schlep can become an expert at the basics of engineering - the gnomes and their irradiated home of Gnomeregan are proof of that. It takes a strong mind and a steady hand (emphasis on STEADY) though to master the discipline of Goblin Engineering.$B$BKeep your ears perked $n, and you might learn something.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4201, 'enUS', 'Oh, you\'ve done it! I can now make my potion and use it on Rocknot. Finally, he will fall victim to my charms!$B$BThank you, $n. You are a champion of love!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4221, 'enUS', 'You apply two Cenarion plant salves to the windblossom. It immediately begins to shed its corrupted form, blossoming into a vibrant and healthy plant.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4243, 'enUS', 'As you look A-Me 01 over, you see two huge dents tarnishing what Karna obviously feels is a well-crafted piece of engineering. One dent decorates A-Me 01\'s shoulder, the other is on her head.$B$BNo power seems to be running through her components as you investigate further.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4244, 'enUS', 'You remove the panel from A-Me 01\'s back yet again and carefully remove the old mithril casing that seems to have burnt out. The new one slides into place easily. The only thing left to do, assuming the mithril casing was the problem, is to flip the switch to the \"Off\" position, and then back to the \"On\" position.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4245, 'enUS', 'Oh, you\'ve done it! Wonderful! Thank you, $n.$B$BOh, my poor, A-Me. Look at all those dents. We\'ll get her up and running perfectly with just a little work.$B$BThank you again, $n. I really appreciate it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4261, 'enUS', 'Yes, $n. Felwood has been through much hardship. Thank you for what you have done.$B$BOne day, we may be able to reclaim Felwood from the corruption; every kind deed helps.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4262, 'enUS', 'Incendius? Oh dear...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4263, 'enUS', 'Oh dear! Are you sure Incendius said \'Ragnaros?\'$B$B<Jalinda hands you something and pats you on the wrist as she fades deep into thought.>', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4265, 'enUS', 'Welcome home, $n. Raschal checked in just a little while ago and told me what happened.$B$BI... no, the entire Stronghold can\'t even begin to thank you for your great deed; Raschal would have been dead without your timely aid, and your assistance came to us without even so much as the whisper of a mercenary\'s fee. You bear the mark of a true hero. Know that you will always have a friend in Ginro Hearthkindle.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4266, 'enUS', '$n, I give you my warmest thanks for the aid you\'ve given us. Your bravery in the face of overwhelming odds has warranted a reward worth a thousand times greater. Nevertheless, I hope you accept this on behalf of the entire Stronghold.$B$BNow, if I may... I\'d like to ask for your aid one more time. In light of the discovery of the Zukk\'ash hive here in Feralas, I think you\'re the best choice for this mission.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4267, 'enUS', 'We night elves know of the terror outlined in this report. Shandris was wise to send this, and you, to me.$B$BThese creatures will consume Feralas rapidly; they are nothing short of one of the greatest menaces we all will ever face. The insects you and the courier encountered are known as the silithid. They are mere tools of one of the greatest threats that both the Horde and Alliance will ever face.$B$BI pray we will have the resolve to defeat them before they destroy us.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4281, 'enUS', 'Elune be praised, the delivery from Feathermoon Stronghold has finally arrived!$B$BYour presence here indicates to me that perhaps some ill fate may have befallen Raschal, our usual courier. I hope that everything works out. I know that recently, the Woodpaw have become highly agitated from some source to their south. Maybe he fell to the wilds there; I would be careful, friend.$B$BRegardless, thank you again. Here is payment for this parcel\'s delivery.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4283, 'enUS', 'You\'ve done it! Your reward is: RUB MY BELLY! GO ON! RUB IT!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4284, 'enUS', 'Now I will continue my experiments! Thank you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4285, 'enUS', 'I know the stories are true, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4286, 'enUS', '<Oralius starts rummaging through the mountainous pile of fanny packs.>$B$BOH WONDERFUL!$B$B<Oralius sticks his large nose into the pile and takes a whiff.>$B$BDELICIOUS! Look at all these goodies!$B$BTake one for yourself, $n. There\'s plenty to go around.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4287, 'enUS', 'Un\'Goro is such a strange place, $n. When you enter the crater, it\'s as if you are in a completely different world...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4288, 'enUS', 'There are many hidden areas of the crater along the sides. Try exploring the winding paths if you can\'t think of anywhere else to look, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4289, 'enUS', 'So, you have proven strong, strong enough to best the great apes of Un\'Goro--that is good. The challenge was small compared to Lar\'korwi, but I will not take such a great victory away from you.$B$BThese pelts will make wonderful clothing for my tribe. You have helped me and proven worthy.$B$BBefore you set off on another task, let us give praise to the Earth Mother and thank the spirits of those apes you killed--they must know their deaths will not be wasted.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4290, 'enUS', 'This will do nicely, $n. It seems Lar\'korwi eats well these days. Although, I wonder where he found a threshadon in this area. Odd.$B$BRegardless, I will hold onto this until you are ready for your confrontation with the beast. Do not worry for my safety--I am sure you\'ve angered Lar\'korwi enough that he will not kill me until after he\'s tasted your flesh.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4291, 'enUS', 'Ah, fine specimens, $n. The two pieces you\'ve found should be perfect for baiting Lar\'korwi into the open. I cannot wait to see the outcome of your fight with the monster. He is a massive thing with claws as sharp as the finest blades. You will look into his eyes and you will know death.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4292, 'enUS', 'The Earth Mother be praised! You\'ve done it! Fair play to you, $n. You have proven to be a great hunter in your own right. Your spirit is strong. Your bravery unmatched. And your strength has been fortified. I bow down to your skills.$B$BOnly the greatest of my ancestors could have accomplished something so incredible. Congratulations.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4293, 'enUS', 'Ah, excellent. These samples will do nicely. I thank you for your help.$B$BOnce we\'ve gathered all the samples of slimes, we\'ll be able to determine more. I am beginning to suspect that Lady Sylvanas\' hunch was not only correct, but more insightful than even she could have predicted. These samples are very interesting. Just seeing how they\'ve reacted with my compounds... hmm, yes, we\'ll definitely need to investigate further.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4294, 'enUS', 'Truly amazing, $n. This Un\'Goro Crater is holding many more secrets than I expected. I wonder what else we will discover as we test further.$B$BSimply amazing... look at the purity of these samples. Hmm, I wonder what it could mean. They seem to be elemental in nature, but... something more. They remind me of... of the Old Gods. We\'ve found traces of them throughout Azeroth, and now I wonder if these creatures aren\'t related to them somehow.$B$BWe shall have to test further.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4295, 'enUS', 'What\'s this? Ah! It smells like Dark Iron Ale! Drink has blurred my sight, but I can tell that you, sir, are a real friend!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4296, 'enUS', 'Ah, you found the tablet! And your transcription is superb! Are you sure you\'re a $C and not a scribe?$B$BHere you are, $n. Here is the payment I promised.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4297, 'enUS', 'These\'ll do nicely, and if I\'m not mistaken, your little egg\'s just about ready to hatch. How does it feel? You know, you about to be a father? Haha, didn\'t think ya\'d have to deal with such responsibility, now did ya?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4298, 'enUS', 'Here, take the egg... better you have it so it bonds to you and looks to you to be its dadda.$B$BCongrats, $n. You\'re a parent... kinda.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4300, 'enUS', 'Ah, these do nicely. Thank you, $n. An\' you be here long before they needed... that be good.$B$BMaybe we have a weapon fashioned for you from the claws. What do you say?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4301, 'enUS', 'Well done, $n. I am pleased. You have done a great thing here today.$B$BU\'cha was one of the greatest apes I had ever seen. The fact that you have bested his strength speaks well of your own strength... and courage.$B$BIt is only fitting that I give you this as a sign of respect. You have earned it.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4321, 'enUS', 'Alright, $n. This is fascinating... I can only hope that my diagrams are correct! I compiled everything into this manual, which you can have.$B$BI believe I can answer any questions you have about the crystals now, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4324, 'enUS', 'Yes, I\'m looking for my brother. We must find him and retrieve him, bless his heart.$B$BBut if we can\'t bring all of him... then his head will suffice.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4341, 'enUS', 'Please, let me explain.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4342, 'enUS', 'As you can see, there was nothing I could have done.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4361, 'enUS', '<King Magni slams his fist down in anger.>', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4362, 'enUS', '<Princess Bronzebeard weeps over the loss of Emperor Dagran Thaurissan.>$B$BWhy???', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4363, 'enUS', '<King Magni Bronzebeard is visibly upset.>$B$BIt seems that even in death, Thaurissan has won.$B$BYou are dismissed, $n. Before you leave, you may choose your reward from the royal vault.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4381, 'enUS', 'Arranging the crystals on the table, you carefully align the colors. As you push the last one into place, a burst of light fuses the crystals together.$B$BThe resulting combination is now yours.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4382, 'enUS', 'Arranging the crystals on the table, you carefully align the colors. As you push the last one into place, a burst of light fuses the crystals together.$B$BThe resulting combination is now yours.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4401, 'enUS', 'You apply some Cenarion plant salve to the songflower. It immediately begins to shed its corrupted form, blossoming into a vibrant and healthy plant.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4402, 'enUS', 'Wonderful, $n! As promised, here is your cactus apple surprise plus a little something extra.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4421, 'enUS', 'Ridding Felwood of Xavathras is only the first step in a long process. It will take much work to repair the damage that has been done here.$B$BI sense that there is something much larger than just the satyrs at work here, but we will only know the truth with time...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4441, 'enUS', 'Thank you, $n. Now, we must pray that we can undo the damage that has been done...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4442, 'enUS', 'With the blessed water, I was able to break the link between the flute and the ancient spirit. I do believe though, that if you were to use this flute in a sacred spot, you may be able to summon the ancient spirits.$B$BPerhaps you might try the ruins in the southern part of Irontree Woods. Many corrupted treants inhabited this area at one time, and it is possible that they may still reside there, even in spirit form.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4443, 'enUS', 'You apply three Cenarion plant salves to the whipper root. It immediately begins to shed its corrupted form, blossoming into a vibrant and healthy plant.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4447, 'enUS', 'You apply four Cenarion plant salves to the night dragon. It immediately begins to shed its corrupted form, blossoming into a vibrant and healthy plant.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4449, 'enUS', 'Oh, this is great! Nice feel to it too!$B$BWhat? Oh, yeah... geologists. Got \'em good, did ya? I can\'t thank ya enough, $n. That\'ll give me some time to finish up in here... Thanks again, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4450, 'enUS', 'Oh, you\'ve seen fit to do some of the work I requested. Excellent. And here I thought you were going to ask me to craft you something.$B$BThese are all perfect... and everything\'s here. Thank you, $C.$B$BMaybe it wouldn\'t hurt to reward you some... hmm, what would you find useful.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4451, 'enUS', 'Well, I\'ll be! How in tarnation!? You found the key! I knew them blasted Dark Irons were hiding it after they locked me in here! Thank ya so much, $n. You\'re the greatest! Now, just turn the key and let ol\' Locheed outta here!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4461, 'enUS', 'You apply three Cenarion plant salves to the whipper root. It immediately begins to shed its corrupted form, blossoming into a vibrant and healthy plant.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4462, 'enUS', 'You apply four Cenarion plant salves to the night dragon. It immediately begins to shed its corrupted form, blossoming into a vibrant and healthy plant.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4466, 'enUS', 'You apply two Cenarion plant salves to the windblossom. It immediately begins to shed its corrupted form, blossoming into a vibrant and healthy plant.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4467, 'enUS', 'You apply two Cenarion plant salves to the windblossom. It immediately begins to shed its corrupted form, blossoming into a vibrant and healthy plant.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4484, 'enUS', 'My head throbs... The energies of the vast expanses that makes up our universe... ARGGH!$B$B<Mathredis looks pained.>$B$BTake what you desire and leave!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4491, 'enUS', 'Ringo\'s not in the best shape, $n, but I am glad to have him back in one piece!$B$BThank you for finding him.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4492, 'enUS', 'Oh... I\'m so glad someone found me...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4493, 'enUS', 'Yes, I gathered that Gracina sent you to me. Your experience in fighting these beasties is priceless; no one to my knowledge has ever dealt with their kind before. As for Gracina\'s idea that Un\'Goro Crater is their heart... perhaps it is. Un\'Goro is a mysterious and dangerous jungle to the west of here. Perhaps it is also one step closer to figuring out what the silithid are all about.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4494, 'enUS', 'Yes, I gathered that Zilzibin sent you to me. Your experience in fighting these beasties is priceless; no one to my knowledge has ever dealt with their kind before. As for Zil\'s idea that Un\'Goro Crater is their heart... perhaps it is. Un\'Goro is a mysterious and dangerous jungle to the west of here. Perhaps it is also one step closer to figuring out what the silithid are all about.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4495, 'enUS', 'I\'m so glad you found me, $n. How did you know I was here?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4496, 'enUS', 'The scent gland, ah yes. The membrane appears intact - excellent. The soil, yes, this should work out perfectly. With my special additions, I should be able to grind all this up into the perfect lure.$B$BGive me a moment to make the lure, $n. Once it\'s ready, we\'ll be able to enact the next step that may hopefully stop the silithid before they overrun Gadgetzan... and the rest of Kalimdor.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4501, 'enUS', 'I am absolutely terrified of the pterrordax here! Thank you for decreasing their numbers, $n. Now I might feel safe enough to venture out of Marshal\'s Refuge.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4502, 'enUS', 'These samples will be perfect for my studies! Thank you, $n! I promise, you\'ll be the first one I will tell about my findings.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4503, 'enUS', 'Yes, these scales are perfect. I should be able to get this flyer off the ground with these scales for sure!$B$BThanks!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4504, 'enUS', 'Yes, this tar is just the stuff...$B$BHey, wait a minute, my hands... I can\'t seem to get them apart...$B$BMaybe this wasn\'t such a good idea. Oh, but wait! Super sticky tar remover! That\'s where the real money is!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4505, 'enUS', 'Very well. We must find a way to use this corruption against our enemies...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4506, 'enUS', 'Excellent!$B$BThe Forsaken will resort to any means necessary to crush the alliance! The use of the corrupted water will surely prove useful in our future efforts...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4507, 'enUS', 'Fantastic, $n! We\'ve won quite the victory here today. It will take some time for us to fully understand what we have here; we\'ll put it to good use though, don\'t you worry about that.$B$BAs for the silithid, I genuinely doubt that this was the full extent of their presence. This is much too clean of a resolution, I\'m sorry to say.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4508, 'enUS', 'While I genuinely appreciate this, I am not sure what good it will do us. The Alliance ignores threats from any source other than the Horde, and even our own leaders here in Darnassus cannot agree on what exactly this threat is. Fools like Fandral Staghelm theorize that the silithid are nothing like the threat from the ancient past I claim them to be. His ignorance is only surpassed by his arrogance.$B$BYou have seen the threat yourself, $n. We must remain committed to defeating the silithid!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4509, 'enUS', 'While I genuinely appreciate this, I am not sure what good it will do us. The Horde ignores threats from any source other than the Alliance. While Thrall himself might take action were the facts presented to him, he is beset on all sides by conflicting advice. These fools would have us chase our own imaginary tails were they to think it might catch the Alliance unawares!$B$BYou have seen the threat yourself, $n. We must remain committed to defeating the silithid!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4510, 'enUS', 'This bank voucher authorizes me to withdraw an item from Gracina Spiritmight\'s account with us and present it to you. Congratulations would seem to be in order!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4511, 'enUS', 'This bank voucher authorizes me to withdraw an item from Zilzibin Drumlore\'s account with us and present it to you. Congratulations would seem to be in order!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4512, 'enUS', 'Amazing results, $n! You got close enough to take a sample of the corrupted things, and managed to look no worse for wear! Thank you. Oh, these samples will be perfect. I can\'t wait to start testing on them.$B$BIf I\'m right, I could be one of the Explorers\' Leagues greatest scientists!$B$BThat\'ll prove to all the dwarves that it wasn\'t a mistake to allow us gnomes into Ironforge after the Gnomeregan incident.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4513, 'enUS', 'You\'ve found it? $n, you are a treasure unto yourself. Your dedication and aid has been magnificent and I could not possibly thank you enough.$B$BPlease, take this for your troubles. I have some work to do, but I\'m sure, if you come back at a later time, I\'ll have need of your services again.$B$BThank you. Thank you so much.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4521, 'enUS', 'This is excellent news, $n. With these beasts out of the way, we can begin to adventure further into Winterspring. Are you ready for more?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4542, 'enUS', 'More bad news! These centaurs have gone too far! They raided one of my supply caravans and slaughtered the whole party. This is an outrage, and it will not go unpunished!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4561, 'enUS', 'You pick up the petri dish with the slime sample in it. The only way to discern if the samples is pure enough is to open it and examine the contents yourself. Ideally you\'ll find a sample that fits Chemist Fuely\'s criteria.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4581, 'enUS', 'Ah, Shindrell\'s report! Many thanks, $n. Shindrell is a fine sentinel, brave and keen. I must read this for clues of a wickedness I fear is moving through Ashenvale.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4601, 'enUS', 'You insert the grime-encrusted item and three silver coins into the Sparklematic 5200. The machine surges to life in a frenzied attempt to purge the grime from the unknown item. Loud, violent churning sounds beat from the heart of the machine. The metal frame of the beast creaks and groans as it lurches from side to side...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4602, 'enUS', 'You insert the grime-encrusted item and three silver coins into the Sparklematic 5200. The machine surges to life in an almost frenzied attempt to purge the grime from the unknown item. Loud, violent churning sounds beat from the heart of the machine. The metal frame of the beast creaks and groans as it lurches from side to side...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4603, 'enUS', 'The Sparklematic 5200 comes to life, lurching to and fro. Inside, the helpless grime that has encrusted the object placed within it is being pulverized by the machine\'s gnomish cleaning agent.$B$BThe Sparklematic 5200 comes to a halt, heralded by a familiar \"ding!\" The item receptacle of the machine spirals open, revealing a neatly wrapped box.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4604, 'enUS', 'The Sparklematic 5200 comes to life, lurching to and fro. Inside, the helpless grime that has encrusted the object placed within it is being pulverized by the machine\'s gnomish cleaning agent.$B$BThe Sparklematic 5200 comes to a halt, heralded by a familiar \"ding!\" The item receptacle of the machine spirals open, revealing a neatly wrapped box.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4605, 'enUS', 'As quickly as it started, the Sparklematic 5200 comes to an abrupt halt. A simple \"ding\" is heard from the machine, and the mouth of the machine spirals upward. Inside the Sparklematic 5200 is a small box wrapped in a little bow. The machine would appear to wrap an object up after eliminating its grime-encrusted status. Even if the machine didn\'t work, at least the item is now wrapped in a pretty box.$B$BWell, there is one way to find out if it cleaned the item or not...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4606, 'enUS', 'As quickly as it started, the Sparklematic 5200 comes to an abrupt halt. A simple \"ding\" is heard from the machine, and the mouth of the machine spirals upward. Inside the Sparklematic 5200 is a small box wrapped in a little bow. The machine would appear to wrap an object up after eliminating its grime-encrusted status. Even if the machine didn\'t work, at least the item is now wrapped in a pretty box.$B$BWell, there is one way to find out if it cleaned the item or not...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4621, 'enUS', 'Avast ye... Admiral $n! Yer a pirate of pirates, lad!$B$BIt warms the cackles of me heart to give ye this hat. Ye will ne\'er find one like it anywheres, and all that gazes upon ye will see that yer an Admiral of the Bloodsail Navy!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4641, 'enUS', 'Another one of Kaltunk\'s recruits, hm?$B$BA sorry state of affairs we find ourselves in if this is the best the Horde can produce. No matter. By the time we think you\'re ready to leave the Valley, you\'ll be a proud warrior of the Horde.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4642, 'enUS', 'Oh, most excellent, $n. This is most excellent.$B$BSuch a tale. Who would have thought the two would interact with one another in such a way... I will have to spend more time looking at the environmental issues that cause changes in these creatures.$B$BI will continue my research for now. Perhaps we will speak again soon. Until then, I wish you well.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4661, 'enUS', 'You pick up the petri dish with the slime sample in it. The only way to discern if the sample is pure enough is to open it and examine the contents yourself. Ideally you\'ll find a sample that fits Chemist Fuely\'s criteria.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4681, 'enUS', 'You\'ve been a tremendous help here today; we now have a solid chance at unlocking the mystery of why these creatures choose to beach themselves on the coast of Darkshore. The idea that they might be fleeing the area around Teldrassil is disturbing. Should you come across others in your travels, please let me know. Our research here is but in its infancy.$B$BOn behalf of the Temple of the Moon, please accept this as thanks for the effort you have given. Thank you, $n!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4701, 'enUS', 'Do not dwell upon her suffering, $n. The beast was corrupt - a product of dark magic. You have done the Alliance a great service and you shall be rewarded.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4721, 'enUS', 'Hard work is something I have great respect for, $n. Now we can move forward with our plans for Winterspring.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4722, 'enUS', 'Another discovery for us to research - well done $n! These remains will be properly studied once they get to Darnassus. Please accept this small honorarium in exchange for the remains you have provided for our study.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4723, 'enUS', 'Another discovery - well done $n! These remains will be properly studied once they get to Darnassus. Please accept this small honorarium in exchange for the remains you have provided for our study.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4724, 'enUS', 'Rabid pups? Gizrul the Slavener? And you live to tell the tale?! Incredible! A tribute well earned!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4725, 'enUS', 'I have heard of the kinds of carriages found on the back of the turtle you found. Those I do believe are naga carriages, used both in battle and in transportation of their supplies on to land. The markings on the box you found are of the naga; this would explain their invasive presence in far-northern Darkshore.$B$BI will send this box to Darnassus along with the rest of your discoveries. This is for you - thank you again for your assistance.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4726, 'enUS', 'It worked! You got the essence! Well done, $n! Well done!$B$BThis essence will be invaluable in my research. I hope to one day learn how to capture living dragon whelps, or perhaps capture their eggs. If I could find a way to do that, then I would be the envy of dragon scholars around the world!$B$BMy employer would also be happy, and as I said before, he\'s someone you want to keep happy...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4727, 'enUS', 'It is interesting that the murlocs were not present at this site; we will keep this in mind as we study this creature\'s remains! Thank you for your effort, $n; the Temple of the Moon would like for me to give you this, as compensation for your time.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4728, 'enUS', 'Ah, I believe you discovered one that was originally being examined by the Explorer\'s League. They have a presence here in Darkshore, and they had mentioned that they were studying one of the beached creatures until they were chased out by the murlocs. You appear to have succeeded where we both have not. Please - accept this honorarium on behalf of the Temple of the Moon.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4729, 'enUS', 'You got \'em at the perfect time, $n. Any later and they\'d be untrainable! Gimmie a minute and I\'ll have the little fella ready for you to take home.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4730, 'enUS', 'The bones you have are from gentle sea mammals that are known to inhabit the waters surrounding the base of Teldrassil. They had never been known to violently beach themselves like the threshers had been known to. Why would these creatures do this, I wonder? Well, perhaps the study of their remains will bear the fruits of knowledge that we seek.$B$BAgain, thank you for your aid. Please accept this honorarium from the Temple of the Moon.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4731, 'enUS', 'I have heard of the kinds of carriages found on the back of that turtle. Those I do believe are naga carriages, used both in battle and in transportation of their supplies on to land. The markings on the box you found are of the naga; perhaps the creature you discovered was killed as it was heading further south... to Ashenvale maybe?$B$BI will be sure to send this box to Darnassus with the rest of your discoveries. This is for you - thank you again for your assistance.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4732, 'enUS', 'You\'ve found even more beached creatures, $n? Sea turtles were commonly found frolicking along the base of Nordrassil before it was destroyed. With the birth of Teldrassil, however, these creatures have been seen in ever decreasing numbers. Some, as we have discovered, end their lives here for some unknown reason.$B$BYour aid will perhaps unlock the mystery before us. Please accept this in exchange for the remains you have provided for our study.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4733, 'enUS', 'The dimensions of the thresher you mention are mind-boggling. Even the eldest of the threshers in this region grow to only a fraction of that size. While some larger ones were known to be close to Teldrassil, their numbers have declined steadily. I cannot help but wonder if these creatures beaching themselves here are somehow related to this.$B$BWhile we analyze the bones you have brought us, please accept this on behalf of the Temple of the Moon. Thank you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4734, 'enUS', 'It worked? Splendid! I knew it would!$B$BWhile you were gone I was working on a new version of the eggscilloscope. This one has a much longer range.$B$BHere, you can have one in case you go back to Blackrock Spire.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4735, 'enUS', 'Wow, you got them! The collectronic module worked! I\'m a genius!$B$BThank you, $n. My patron will be very pleased to get these eggs...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4740, 'enUS', 'Well, looks like you\'re here to more than just inquire! The citizens will be delighted to know that tonight they will sleep a little bit more soundly and safer.$B$BYou\'ve acted as the executioner of justice for the people of Auberdine today, $n. For that, I would like to offer you this as a suitable reward... for a true hero of the Auberdine people.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4741, 'enUS', 'I sense that you are enjoying your work. And I believe that we are onto something here.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4742, 'enUS', '<Vaelan\'s image blurs for a moment.>$B$B<The pieces that you had collected have disappeared.>$B$BThe Unforged Seal of Ascension is now ready. It is time for the final step.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4743, 'enUS', 'Stand steady while I cast the final invocation of protection.$B$B<Vaelan\'s eyes turn to twin orbs of flame.>$B$BThe Seal of Ascension is complete...$B$BKnow this, I have imbued the artifact with some powerful protective wards. In your darkest hour, when all appears to be lost, the might of the red flight will be at your disposal.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4761, 'enUS', 'Well met, $C. Your scouting of the furbolg situation comes at a fortuitous time for us in Auberdine. For some time we have dealt with not only forest dwellers who are now openly hostile to our presence, but also the outright corruption of the forest itself. Hopefully you might be able to lend Auberdine aid during these troubling times.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4762, 'enUS', 'It\'s no surprise how tainted this water is, but just look at how putrid it is becoming! It would seem that action is needed sooner than later, eh $n?$B$BTo our east is Felwood; it is the real source of this corruption, one that I have seen in times past. I predict this sample will confirm this. We might be able to enact a cure for it here, but in order to even try we will need assistance. When that time comes $n, I hope you will be able to give the aid we need.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4763, 'enUS', 'We have just begun this war to reclaim our forest from the forces of corruption, but a battle this day has been won! $n, the people of Auberdine owe you a debt of gratitude that will never be easily repaid. Please accept this along with our thanks. What we have learned here today may one day free our furbolg friends from the shackles of torment permanently.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4764, 'enUS', 'You found it! So the rumors were true!$B$BThank you, $n. My patron, Count Remington Ridgewell, will be quite pleased to see this addition to this collection.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4765, 'enUS', 'Ah, so you are he! You have my thanks, $n. And... if you didn\'t already know... the gratitude of a noble in the house of Ridgewell is nigh worth a king\'s ransom.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4766, 'enUS', 'You came from Stormwind? Wonderful -- I know of an object of art that Remington would kill to have in his museum, but extracting it won\'t be easy.$B$BThat\'s where you come in.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4767, 'enUS', 'Perfect! With these eggs I hope to take the next step to become a wind rider. Here is a token of my appreciation; may good hunting always shine upon you, $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4768, 'enUS', 'Excellent. The formulae etched into this tablet will consume days of research, but I am confident such research will bear sweet fruit.$B$BThank you, $n. You have been invaluable to us.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4769, 'enUS', 'It is true. I believe there is a tablet held within Blackrock Spire, a tablet that contains old alchemical recipes. I am glad you are here, for I need help in retrieving the tablet.$B$BMake yourself comfortable, and I will explain further.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4770, 'enUS', 'What!? If I knew that Pao\'ka Swiftmountain was going to snoop around Highperch, I would have chained him to a totem! His father was reluctant allowing him to come with me to Thousand Needles. We came here to study the different creatures that inhabit these beautiful lands.$B$BI appreciate the help you have shown Pao\'ka. I hope this covers any misfortunes this deed has cost you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4771, 'enUS', 'You did it! Vectus is defeated! And Dawn\'s Gambit... did it work?$B$BHm... maybe my device wasn\'t the success I had hoped, but I\'m glad you were able to handle things anyway. Well done, $n!$B$BThe Argent Dawn, and the good people of Azeroth, are in your debt.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4787, 'enUS', 'Ah, you have the egg! I bow to you, $n. Your strength and bravery will be long remembered.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4788, 'enUS', 'Unbelievable! You found them! You truly are a hero, $n!$B$BThat\'s a good thing, because I\'ve been reading the tablets you brought me before, and from what I gathered... we\'re going to need a hero to stop what\'s coming...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4801, 'enUS', 'Mau\'ari can derive da juju for $n... Use da Cache of Mau\'ari to cast dis upon yourself or your allies.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4802, 'enUS', 'Mau\'ari can derive da juju for $n... Use da Cache of Mau\'ari to cast dis upon yourself or your allies.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4804, 'enUS', 'Mau\'ari can derive da juju for $n... Use da Cache of Mau\'ari to cast dis upon yourself or your allies.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4806, 'enUS', 'Mau\'ari can derive da juju for $n... Use da Cache of Mau\'ari to cast dis upon yourself or your allies.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4807, 'enUS', 'Mau\'ari can derive da juju for $n... Use da Cache of Mau\'ari to cast dis upon yourself or your allies.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4808, 'enUS', 'That\'s no blanket! But what have we here? A letter from Tinkee? I wonder what she has to say...$B$BAh, she\'s studying in the Burning Steppes. That makes sense--that\'s a good source for dragonkin. Oh, but look! It says here that she needs some parts for a device she wants to build. I guess that\'s where I come in, eh?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4809, 'enUS', 'These are good specimens. They\'re packed with cold! Let me get some gloves on, then pack them along with the other things Tinkee needs...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4810, 'enUS', 'You brought them! Great! I\'ll get to work right away! Thanks a ton, $n!$B$BYou\'ve been so helpful to me, I might need you for even more important tasks later...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4811, 'enUS', 'So the crystal exists, $n? Fascinating... anyway, your task has been well executed; good work!$B$BWhat exactly is this crystal? This is but a single question in a sea of questions. Another question that comes to mind is why the moonkin would be drawn to this object? Is the crystal benign in nature, or does it hold a more sinister purpose?$B$BI have many more questions, but we\'re going to need to examine that crystal closely to get some answers.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4812, 'enUS', 'You have made it once more to the mysterious crystal. Removing the stopper on the tube of moonwell water, you gingerly pour the contents out over the top of the crystal. As the water cascades down its lattices, you see the opaque surface turn transparent...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4813, 'enUS', 'Well, it would seem that even though we know more about this crystal, more unanswered questions are the result!$B$BI\'ll take the information you\'ve uncovered to the council here in Auberdine. Perhaps they\'ll know a course of action to follow on this crystal. Well, that is if there is one to be taken; for now, the threat remains far enough to our east to be out of harm\'s way.$B$BAs for you, please have this. Consider it a reward for a job well done, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4821, 'enUS', 'Yes, finally... the mysterious egg has finally been delivered to me. Now, let me take a closer look at this... What..! What is this..?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4841, 'enUS', 'For my gratitude, this is for you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4842, 'enUS', 'Yes, the demons there in Darkwhisper Gorge; I believe it is their presence that is affecting the land in this way. I am going to continue observing the hot springs here and see if I learn anything more.$B$BI\'d wait until you are a bit stronger if you have plans of heading farther into Darkwhisper Gorge.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4861, 'enUS', 'A severely damaged crate stands out among the rest of the destroyed belongings at the camp...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4862, 'enUS', 'What a load! I mean, look at all those eggs! These should bring in a right few gold.$B$BAs promised, here\'s your own little critter. Don\'t get bitten!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4863, 'enUS', 'Approaching the wagon, it doesn\'t appear that there is much more here for Jaron. But, looking on the ground, you notice some crates that look undamaged, despite being half-buried in the snow.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4864, 'enUS', 'Well, I expected to hear as much, lad. I figured that the crazed beasts destroyed all of my belongings...$B$BBut you did find my tools, and most importantly, the amulet! I wonder why the wildkin so fiercely defended this item... In any case, I\'ll think twice before heading their way again!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4865, 'enUS', 'Vengeance! So, the legend of Arikara is true. Indeed, this is a problem that must be resolved immediately.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4866, 'enUS', 'This is gonna make the best ale yet!$B$BEh, what did you think I was gonna use this for? Poison or something??$B$BCrazy kids...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4867, 'enUS', 'Aha! I can already feel my powers returning to me. And my mind is no longer wracked by Urok\'s curse.$B$BThank you, $n! Humility is below me, but I must acknowledge your aid.$B$BAnd I bid you fortune in your journeys through Blackrock Spire. You are a fool to think you could make your way to the top, but your bravery is without question.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4881, 'enUS', 'They come to kill me? I will show them how a Tauren fights!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4882, 'enUS', 'An important discovery?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4883, 'enUS', 'The energy of this necklace, $n. I believe that it has only one source.$B$B$B$BThe wildkin, now misguided and confused, were once not so. They once had a purpose, and were guided to serve. The night elf goddess Elune created these creatures to guard her most sacred areas.$B$BWhile this fact may seem like it holds little value; I urge you to look deeper.$B$BYou will learn, in time. Thank you for bringing this to me, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4901, 'enUS', 'Did you meet up with Ranshalla?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4902, 'enUS', 'Do not hesitate; tell me why you have come.$B$BOh, yes? Hmm. Well.$B$BThank you for bringing this straight to me; it is always best to go right to the top... But... Yes. The druids of the Cenarion Circle have known of the Wildkin for some time. That they are pets of Elune, and were chosen to guard the most sacred of places.$B$BSo while this isn\'t news, I definitely compliment you on your effort. Here, take this -- it is something that seems suitable for such a deed.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4904, 'enUS', 'Thank you, $n. Deep in my bones I knew that she would find her way back to me. She told me of your bravery and how you helped her escape. These items belonged to her brother. I know she would want you to have them.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4906, 'enUS', 'Thank you for all you have done, $n. May Elune protect you and guide you on the path ahead.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4907, 'enUS', 'Felnok was right! I do need your help again, and this time... it\'s going to be really dangerous.$B$BAre you ready??', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4921, 'enUS', 'I understand. Thank you, $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4966, 'enUS', 'Thank you for your help, $n. I couldn\'t have held them off without you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4970, 'enUS', 'Thank you for assisting me, $n. My opinion of you grows as time passes.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4971, 'enUS', 'Time feels easier to mend now, thank you $n. While you might not understand fully the consequences of a fractured timeline, I appreciate that you understand the urgency of countering what I suspect more and more to be a Scourge-driven malfeasance.$B$BI see no harm in rewarding you for your assistance, mortal. Your greatest feats are to come, and the awarding of a bauble will certainly not alter your future destiny. Your continued aid, however, is something that I and the dragonflight could use...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4972, 'enUS', 'These are exactly what I need, $n. Thank you.$B$BAllow me to suitably reward you with this. You may not understand what you would need with such a device. You might not even know quite what it really is. Alas, I cannot tell you when you will find it most useful; just know that at some point in the future, you may find this item to have extreme utility and function when consumed. Whether or not you choose to hold on to it until then... this, my friend, is up to you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4973, 'enUS', 'Again, I thank you for your assistance. Please accept this gift. While you still may not know when to use such an item, perhaps in the future the path of time will reveal its true intentions to you. You could say that I\'ve seen as such happening to you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4981, 'enUS', 'I won\'t lie to you, $R. I needed the money! Kibler pays very, very well - much better than the K.E.F. ever could.$B$BBut don\'t doubt my loyalties! Why do you think I\'m down here? To get that crazy goblin some exotic animal eggs or something equally inane? No, only someone truly daft would come to Blackrock Spire to collect pets. Yep, I don\'t reckon too many people like that exist in this world.$B$B$B$BThe last thing we need are those kinds of people breeding.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4982, 'enUS', 'You\'ve done it! Once Lexlort has these plans, he\'ll forget all about the nasty things he wanted to do to me.$B$B$n, you\'re my hero!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4983, 'enUS', 'I\'ve got to hand it to her; she\'s managed to weasel her way out of another sticky situation! These reports will prove to be invaluable in our dealings with the Blackrock orcs.$B$BOf course, you will be rewarded for your hard work.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4984, 'enUS', 'I... I sense that the souls of those wolves you have put down now have rest. They - and I - thank you for a merciful release.$B$BThe wolves are not alone in their suffering within this damnable place, $n. Please, I have need of you once more.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4985, 'enUS', '\"I know that the grizzlies have been put down, their torment at an end. I... I have one more thing for you. I wrote it down on... on this branch, sensing that my time in this realm had come to an end.$B$BIndeed... I am at that end. Thank you again friend... the creatures of this accursed land may rest... and now shall I.\"$B$BWith that, a heavy sigh is uttered by the druid, followed by what sounds like a death rattle. His body lies motionless on the cottage floor.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4986, 'enUS', 'This... this is quite strange. This druid - this tauren druid no less - entrusted you with a great deal of valuable information on the extent of the Scourge\'s plague. I am surprised a tauren would give a $R such information, and freely at that if what you say is true. We will make good use of this, I promise you. Perhaps the tauren have not fallen as far from Cenarius as Fandral would believe...$B$BErm, anyway, please - accept this as a token of thanks from the Cenarion Circle.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (4987, 'enUS', 'This is from Mulgris, you say? We had heard he sought to minister to the creatures suffering in the Plaguelands, but this is most unexpected... and most saddening. Mulgris is... was a good friend. I will mourn his passing deeply.$B$BHis efforts have not gone in vain, thanks to your diligence. In this branch, much lore is stored about the plague the Scourge propagates upon the land. We will study it diligently. As for you, please accept this. It is the least we can do for your noble efforts.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5001, 'enUS', 'Well I\'ll be! Where were they??', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5002, 'enUS', 'This coincides with much of what we already knew. Regardless, it is interesting and rather unfortunate information.$B$BThe markings on this map will prove valuable in thinning the Blackrock menace.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5021, 'enUS', 'A package in the barn that you might have otherwise overlooked now stands out amongst the decay and ruin. Perhaps this is the one that the ghost of Janice Felstone was clamoring about...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5022, 'enUS', 'A package for an Emma Felstone, you say? Last known address was Stormwind, four years ago? Well let me see here...$B$BAccording to what we know, there was an Emma Felstone in the city about four years ago. Well, I should say possibly - this census I refer to is from five years ago, and I don\'t have a report of her vacating the city. This doesn\'t mean that she is still here though.$B$BWait a second... this package couldn\'t be for Ol\' Emma, could it?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5023, 'enUS', 'A package for a Jeremiah Felstone, with his last known address as Lordaeron... I should be able to assist you.$B$BJeremiah Felstone was in the city before the plague. Well, I should say the former Jeremiah Felstone - some of the Forsaken have changed their last names to rid themselves of their former countenance. This case is no exception; the former Jeremiah Felstone is now known as Jeremiah Payson.$B$BWait a minute... isn\'t he the cockroach vendor?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5041, 'enUS', 'Haha! Thank you, $n! This will help us greatly. I\'ll get these supplies packed up and on their way to the Crossroads immediately.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5042, 'enUS', 'If only Mangletooth were free to see you decimate those who betrayed him! <snort>$B$BI thank you for destroying more of the Bristleback by gathering these shards. Please, continue to do so, and I will continue to aid you. <snort>', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5043, 'enUS', 'May your aim be unerring, and the mark of your weapon be true, $R.$B$BAgamaggan\'s spirit is with you. Return to me if you wish. Mangletooth will bless you again if the great boar god deems me worthy.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5044, 'enUS', 'Your enemies should fear you even more, $R. $B$BTell Mangletooth tales of your cunning in battle when next we meet--be it in this life, or the next.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5045, 'enUS', 'Our greatest warriors, and our greatest <snort> shamans would envy your will, $R.$B$BDefeat more of the Bristlebacks knowing that they cannot stop you!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5046, 'enUS', 'Go to war, $R.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5047, 'enUS', 'Alive? Suit worked? And what do we have here?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5048, 'enUS', 'My name is indeed Emma Felstone - you\'re the first person in years to call me that. Who... who are you?$B$BYou say you knew... my sister?! Is she still alive? Who am I kidding, of course she isn\'t. When the plague broke out in Lordaeron, I had come to expect the worst. So many of my family died... or worse. Here I am now, crazy Ol\' Emma, all alone. What are you, a grave-robber or something? Looking for your next target?$B$BWhat... you have a package for me from my sister?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5049, 'enUS', 'I haven\'t used that name in years... but yes, at one point in my life - before this glorious unlife - that was my last name.$B$BYou claim to have known my sister... or rather, have recently spoken to my sister.$B$BWho\'s been dead.$B$BFor four years.$B$BYou know, if my bones weren\'t already brittle enough, I\'d be tempted to break my foot off on your posterior. You\'re quite the vulture, aren\'t you?$B$B What... you have a package for me from my sister?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5050, 'enUS', 'You give the half-charm back to the apparition. She looks at it, then at you expectantly.$B$B\"Honey, where did you put your half of the charm? We can\'t be together unless we have both halves. Honey... can you see me?\"', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5051, 'enUS', 'You hand the reassembled good luck charm to Janice Felstone\'s ghost. As soon as she grasps it, you observe an almost immediate change in her. Her ethereal form shimmers, and she looks upon you with clear yet sad eyes.$B$B\"You\'ve freed me... I no longer will need this charm. Please, take it and perhaps it will give you the solace it has given me.\"', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5052, 'enUS', 'Ah, you do understand. <snort> My words were not lost on your ears, $R. Mangletooth may be caged, but no cage can stop Agamaggan\'s power! $B$BThe touch of the shard, the feel of it... it has been a long time, $R.$B$BDo you know what it is like? To have your power severed from you?$B$BBut, that is a tale for another time. I promised you information. I promised you Agamaggan\'s power. My people turned their backs on Mangletooth. Now, I lose you upon them.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5054, 'enUS', 'This was no easy task, yet you have excelled. But do not be too quick to think you have impressed me, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5055, 'enUS', 'Yes. You have returned. I believe now that you are a true hunter by nature. You have faced the most ferocious of creatures and have returned, surpassing my expectations.$B$BYou are now ready for my final request.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5056, 'enUS', 'You have proven both your strength and your determination. Many more challenges await you, but I have already shown you as much as I am able.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5057, 'enUS', 'We learn from our life experiences, $n. I am sure that you have only become stronger and wiser as a result of yours. You have grown much since we first spoke.$B$BTake this in remembrance of the difficult tasks you have accomplished as a $R $C, and know that you will always have my respect and admiration.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5058, 'enUS', 'A diary with yellowing pages lies open to the last entry:$B$B\"I don\'t know what\'s going to happen now that Harold\'s turned. It took everything out of me to drag him into the only place I could find that\'d lock him up tight and hold him. I hid the key on me, and I pray it works...$B$BI hope I can get out of here, but I need money to do that. Our stuff\'s all in the cabinet I think... and now that I think about it, doesn\'t Harold keep the key on him?$B$BI\'ll rest on it, I\'m dead tired now.\"', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5059, 'enUS', 'Using the key found on the skeleton of the former Mrs. Dalson, you unlock the door to the outhouse. A bloodthirsty ghoul leaps from the outhouse, having been trapped inside for who knows how long.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5060, 'enUS', 'The key taken from Farmer Dalson\'s ghoulish remains fits the lock perfectly. The doors of the cabinet swing open, revealing several items that remain intact and appear quite valuable.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5062, 'enUS', 'Good, you have gathered the essential amount of Incendia agave. This rare herb found only in Thousand Needles will help light the dormant sacred fire of life.$B$B<Magatha begins to enchant the Incendia agave.>', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5064, 'enUS', 'Great Spirit Totem! This is dire news indeed. I must begin to plan for whatever may come.$B$B$n, as promised, here is your reward for your brave service.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5065, 'enUS', 'You found them! Great!$B$BThank you, $n. I will pour over these texts and decipher their meaning.$B$BYou, however, have another task...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5066, 'enUS', 'Welcome to the Plaguelands, hero. This is one of the most dangerous frontiers in Azeroth, with the looming threat of the Scourge to our east, and then even further still eastward from there. I am glad to hear that the call of duty and service to the Horde did not fall on deaf ears with you!$B$BIf you are ready to get your hands dirty, then there is plenty for willing and able heroes to do here on the edge of the Plaguelands.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5081, 'enUS', 'Excellent work, $n. At the very least, you have bought us more time - precious, precious time.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5082, 'enUS', 'Thanks so much, $n! Now I can get back to work here.$B$BDid you find anything of interest at the Winterfall camp?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5083, 'enUS', 'You got this from the Winterfall?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5084, 'enUS', 'Inside the cauldron bubbles a rotting, repulsive-smelling substance...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5085, 'enUS', 'You are very resourceful, $n. Do you think that the Winterfall furbolgs have been to the Deadwood camp?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5086, 'enUS', 'I have to compare this to the substance you found in the cauldron, and then I\'m sure we\'ll know more.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5087, 'enUS', '<Donova examines the contents of the crate.>$B$BI wonder if consuming this substance makes the furbolg more aggressive. They do seem much larger...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5088, 'enUS', 'This does not make sense. Why would Arikara be after Cairne Bloodhoof? Magatha could not be wrong, she is our most powerful shaman.$B$B<Motega shakes his head.>$B$BRegardless, your great deed to the Horde shall not go unnoticed - please, choose one of these as a reward for your aid.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5089, 'enUS', 'The plot thickens...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5090, 'enUS', 'Welcome to the Plaguelands, hero. This is one of the most dangerous frontiers in Azeroth, with the looming threat of the Scourge to our east, and then even further still eastward from there. I am glad to hear that the call of duty and service to the Horde did not fall on deaf ears with you!$B$BIf you are ready to get your hands dirty, then there is plenty for willing and able heroes to do here on the edge of the Plaguelands.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5091, 'enUS', 'Welcome to the Plaguelands, hero. This is one of the most dangerous frontiers in Azeroth, with the looming threat of the Scourge to our east, and then even further still eastward from there. I am glad to hear that the call of duty and service to the Horde did not fall on deaf ears with you!$B$BIf you are ready to get your hands dirty, then there is plenty for willing and able heroes to do here on the edge of the Plaguelands.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5092, 'enUS', 'Mission accomplished, $n! Well played, well played.$B$BAllow me to reward your work thus far with some coin from the Stormwind coffers. I\'d recommend spending it on materiel that will aid you in your next mission... one that will take you into the ruins of Andorhal itself.$B$BBe ready! Be vigilant!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5093, 'enUS', 'Welcome to the Plaguelands, hero. This is one of the most dangerous frontiers in Azeroth, with the looming threat of the Scourge to our east, and then even further still eastward from there. I am glad to hear that the call of duty and service to the Horde did not fall on deaf ears with you!$B$BIf you are ready to get your hands dirty, then there is plenty for willing and able heroes to do here on the edge of the Plaguelands.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5094, 'enUS', 'Welcome to the Plaguelands, hero. This is one of the most dangerous frontiers in Azeroth, with the looming threat of the Scourge to our east, and then even further still eastward from there. I am glad to hear that the call of duty and service to the Horde did not fall on deaf ears with you!$B$BIf you are ready to get your hands dirty, then there is plenty for willing and able heroes to do here on the edge of the Plaguelands.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5095, 'enUS', 'Welcome to the Plaguelands, hero. This is one of the most dangerous frontiers in Azeroth, with the looming threat of the Scourge to our east, and then even further still eastward from there. I am glad to hear that the call of duty and service to the Horde did not fall on deaf ears with you!$B$BIf you are ready to get your hands dirty, then there is plenty for willing and able heroes to do here on the edge of the Plaguelands.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5096, 'enUS', 'Finely executed, $n. Your attack on the Scarlet Crusade will buy us time. I have sent one of my best scouts out to watch over the camp and make sure that the Crusade forces that come to reinforce the position will take the bait.$B$BWith the pressure on us lessened, we should now finally be able to risk a mission into Andorhal itself, and an important one at that. I\'d very much like it if you\'d perform this one as well, based on your success to date.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5097, 'enUS', 'My priests are reporting that our beacon trackers are picking up extremely strong signals on all four towers! Dare I say it, but I believe you\'ve executed another mission with complete and total success! Well played once again, $n!$B$BWith the towers marked, our preparations for the attack are now in place. The time has come to take the fight to the Scourge!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5098, 'enUS', 'My warlocks are reporting that our beacon trackers are picking up extremely strong signals on all four towers! You\'ve executed my orders with the skill and precision of a seasoned veteran. Your service to the Horde is duly noted once more, $n!$B$BWith the towers marked, our preparations for the attack are now in place. The time has come to take the fight to the Scourge!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5102, 'enUS', 'Surely, we will strike against their dragon lords next. Well done, brave soldier of the Alliance! Well done, indeed.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5103, 'enUS', 'What the...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5121, 'enUS', 'Thank you so much, $n. I hope to get back to my studies without worry that I will be ambushed. Please accept this as a reward.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5122, 'enUS', 'You retrieved the medallion! Now I will be able to leave the chapel and perform my duty.$B$BThat duty is to kill the beast who helped corrupt me and so many of my brothers and sisters, Baron Rivendare!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5123, 'enUS', 'I wonder what all of this means... I can\'t make anything out...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5124, 'enUS', 'As promised, $n, here\'s your recipe! I\'m also going to throw in a little something extra for all of your hard work and dedication. What am I going to do with these anyway???', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5125, 'enUS', '$n, I am dying... but my soul is saved. For that, I owe you more than words can say. Please, take this... and fight on, brave $C!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5126, 'enUS', '<Lorax laughs.>', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5127, 'enUS', 'Our pact you did not break. The soul I did take. The reward I shall make.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5128, 'enUS', 'Yes, if you let me examine this for a while, I might be able to translate it for you.$B$B<Kelek flips through the pages.>$B$BWhen I am able to look over this fully, I will send word to you.$B$BThank you for sharing this with me, $n. Very little is known about the Winterfall furbolg.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5142, 'enUS', 'Hello. I\'m Pamela, what\'s your name?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5147, 'enUS', 'Hah! Well, this is excellent news! I was starting to think that he would never be brought to justice!$B$BHere you are, $n... and thanks. News of Arnak\'s foul deeds boiled my blood!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5149, 'enUS', 'You found it! You found my doll! Oh, thank you!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5150, 'enUS', 'Placing the sprouts in front of her, Dadanga seems pleased.$B$BShe nudges a small brown-wrapped package towards you, in exchange for the treat!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5151, 'enUS', 'My hypercapacitor! This will keep me busy for a while until I can figure out what it does...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5152, 'enUS', 'You saw Pamela? How is she? Is she alive?? Oh, no! Poor Pamela! She was so young, and such a happy child...$B$BShe doesn\'t know what happened to her father, does she? My brother, Joseph?$B$BNo... she couldn\'t understand what happened to Joseph. And even if she could, I don\'t have the heart to tell her...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5153, 'enUS', 'What is this? A wedding ring? Let me see...$B$BAh! This ring was once on the hand of a great man, but there is much tragedy in that man\'s past.$B$BAre you here to help him? It\'s far too late for him now... but perhaps we can help him in the past!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5154, 'enUS', 'Well done, you found it!$B$BDid you read it? Is Joseph Redpath mentioned? If he is, then perhaps we can save him!$B$BAh. The Annals of Darrowshire tell a very unsettling tale. It says that during the battle, Joseph Redpath was corrupted and joined the ranks of the Scourge! He then betrayed the defenders of Darrowshire.$B$BThat is an ill fate. If we can, we should try to change that fate, don\'t you think?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5155, 'enUS', 'Good, $n! With their numbers weakening, we can further press them.$B$BHow was your first taste of their power? Were they as strong as you thought they would be? Stronger? Or perhaps they were no challenge for you at all. Wouldn\'t that be something?$B$BWe can discuss the details of your exploits later. There are other things we must focus on now.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5156, 'enUS', 'Good that you\'re alive, $n. Bad that the water still exists. I was hoping to find at least one pure source of water here in Felwood. It looks like my search has failed. The process of healing the land could have been made much easier with its existence.$B$BI will have to concern myself with saving what forest creatures I can while destroying those that have become so corrupt that they endanger themselves as much as travelers through this once-mighty forest.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5157, 'enUS', 'This will do nicely, $n.$B$BNow all we have to do besides apply the waters to the braziers is to have it purified. That should not be difficult--it should only require a little travel.$B$BHopefully we can do this quickly.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5158, 'enUS', 'Ah, you need the water from this Moon Well cleansed. Stopping this force of malice in Felwood seems noble enough, but it is not for me to decide if the water will be purified. I will ask the spirits of water--it will be up to them to decide if the cause is just.$B$BGive me a moment and I will tell them of your plea. I hope they are as sympathetic as I am to your cause.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5159, 'enUS', 'This should be more than enough water for our purposes.$B$BI\'m glad to hear that Islen was able to help us. Her strength with the water spirits has always been a strong one. She is also wise, and rare is it that the spirits do not agree with her purpose. The fact that both of them have blessed our task gives me hope that we are on the right path.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5160, 'enUS', 'The Aspects insist that we stay out of the affairs of mortals. In this world, however, such a thing is not always possible - especially when one of our own contemptuously disregards such edicts.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5161, 'enUS', 'So it shall be...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5162, 'enUS', 'I am Jeziba, known to many as the Sculptor. What I sculpt, however, are not statues, but heroes.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5163, 'enUS', 'Hah! They\'ll think twice before making fun of my ideas again!$B$BThanks for your help, $n. I couldn\'t have had so much fun without you!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5164, 'enUS', 'Hundreds upon hundreds of pages filled with garbled text and symbols make up the brunt of this book.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5165, 'enUS', 'I was able to see you with my spells as soon as the last brazier\'s flames died. Well done, $n.$B$BI learned a few things before the Shadow Council\'s warlocks were able to recast their spells. Hopefully it will be enough.$B$BFor now, rest. I will tell you what I\'ve learned in a moment.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5168, 'enUS', 'You have the libram and the shield!$B$BTheir history is entwined with the history of Darrowshire, and somehow, I know that fate will bring them together again.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5181, 'enUS', 'You found them! Well done, $n! These relics hold a power I do not fully understand, a power you will one day harness.$B$BThey also bring dark memories to my mind, memories of death and treachery, and of the last night of Darrowshire...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5201, 'enUS', 'I am relieved to hear that for a short moment, we will not have to worry about the Winterfall intrusion.$B$BYou are beginning to show your perseverance, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5202, 'enUS', 'The key, you\'ve found it! Please, give it to me and I can finally escape!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5203, 'enUS', 'You were the one who helped Arko escape? I\'m so glad you found me. She passed by here not too long ago on her way to Darnassus. She had Lightforge in tow and wouldn\'t even take time to speak to the druids to be healed. She said it was far too urgent.$B$BBut she did tell me about your encounter with Trey\'s spirit. What a terrible fate for a Knight of the Silver Hand. She didn\'t show it, but I know it\'s tearing her up inside, the thought of losing one of her closest friends.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5204, 'enUS', 'You gather the paladin\'s remains as carefully as you can, recalling that at least his spirit has been released from any pain or suffering that might have been forced upon him.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5206, 'enUS', 'You found them! Wonderful!$B$BAlthough I don\'t fully understand how they can help us, I feel their power and I know in my bones that they are linked to my home village of Darrowshire.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5210, 'enUS', 'What is this book? The Annals of Darrowshire? That was my home before the war... my home before the battle that destroyed it! Why did you bring me this book?$B$BBut look here! At the end there are pages that tell tales of the days beyond the battle! Here is a passage about me! And one about my brother Joseph! Is this book true?$B$BWe must discover the truth!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5211, 'enUS', 'It is good to hear you found the tortured souls of my brethren and freed them. My heart will weep less, knowing at least some of the defenders of Darrowshire no longer wallow in the vile subservience of the Scourge.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5212, 'enUS', 'There is something that has changed in the chemical structure of the plague\'s active agents. More data is required.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5213, 'enUS', 'Astonishing! This will take months to dissect and analyze. The Argent Dawn thanks you for your efforts, $n.$B$BWith our blessing, take your pick between either this Seal of the Dawn or this Rune of the Dawn. You can use either of them instead of a standard Argent Dawn Commission to collect scourgestones on our behalf. Should you lose yours, speak with me and I\'ll get you a replacement.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5214, 'enUS', 'Oh sweet, sweet stogie, how I have missed you...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5215, 'enUS', 'May the power of the Light shine upon you in these dark times, $C. I am pleased to serve as your coordinator for our plan to sabotage the Scourge\'s cauldrons.$B$BOur goal will be to change the composition of the plague that the cauldrons create into something that negatively affects the Scourge. In order to affect this change, you will need to secure access to each of the four cauldrons present in the Western Plaguelands.$B$BStand ready hero, for a glorious battle against the vile Scourge awaits you!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5216, 'enUS', 'The key taken from Cauldron Lord Bilemaw is a perfect fit in unlocking the access panel. A foul stench emanates from the opened panel as an unknown mix of concentrated substances swirl inside the cauldron.$B$BThere is a small spigot that the bottle fits well enough under, allowing you to draw a sample for High Priestess MacDonnell back at Chillwind Point.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5217, 'enUS', 'Well done $n - it should take Alchemist Arbington no time at all to analyze this and devise a counter agent to introduce into the cauldron. If we are able to pump out enough of it into the air, we might be able to weaken all the Scourge present here.$B$BThis will no doubt be an ongoing ordeal; we will need to continually hit these cauldrons and introduce our reagents into their mix. Still, if we can keep it up, we will have a huge advantage over Scourge when the time comes for battle!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5219, 'enUS', 'The macabre key found on the cauldron lord easily slides into a keyhole and then disappears, allowing you permanent access to it. Crude, viscous ichors pulsate through twisted tubes within the cauldrons underpinnings.$B$BThere is a small spigot that the bottle fits well enough under, allowing you to draw a sample for High Priestess MacDonnell back at Chillwind Point.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5220, 'enUS', 'Ah, there is plenty in here for study. Well done, $n!$B$BFor now, the next step is in our hands. We\'ll need to produce a list of reagents that, when combined with what is already in the cauldron, will act as its counter.$B$BThe only way we will be able to introduce this is by placing the reagents into the cauldron directly. Alchemist Arbington will have more information on that, so be sure to speak with him at your earliest convenience.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5222, 'enUS', 'The cauldron lord\'s key unlocks the access panel to this cauldron and then disappears, much like the other keys have for the previous two cauldrons you\'ve dealt of. A putrid smelling substance churns violently beneath the platform of the cauldron.$B$BThere is a small spigot that the bottle fits well enough under, allowing you to draw a sample for High Priestess MacDonnell back at Chillwind Point.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5223, 'enUS', 'Another finely executed mission, $n! This makes three cauldrons available to us to turn against the Scourge. If Kel\'Thuzad\'s necromantic dominance over these lands wasn\'t as strong as it currently is, I\'d dare say you\'d shut them down completely!$B$BAlchemist Arbington will have a report on what this cauldron - as well as the other cauldrons subverted - will require to deliver our counter agent to the plague. With that, the time has come to bring the last cauldron of the west under our influence!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5225, 'enUS', 'Cauldron Lord Soulwrath\'s key disintegrates as it touches the lock on the access panel at the base of the cauldron\'s platform. A slight clanking noise is heard within, and the access panel swings open to reveal more viscous toxins within the cauldron\'s framework.$B$BThere is a small spigot that the bottle fits well enough under, allowing you to draw a sample for High Priestess MacDonnell back at Chillwind Point.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5226, 'enUS', 'Congratulations $n, you\'ve unlocked the last cauldron for us! Alchemist Arbington will soon know what this cauldron requires as far as reagents needed for the mix.$B$BAs for you, I am sure that Commander Valorfist will want to thank you personally for the assistance you\'ve given us. Without you, our struggle against the Scourge would have been nearly insurmountable. Thank you, hero!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5228, 'enUS', 'From the shadows, brother $R, I bid you greetings. I am to serve as your coordinator for our plan to sabotage the Scourge\'s cauldrons.$B$BOur goal will be to change the composition of the plague that the cauldrons create into something that negatively affects the Scourge. In order to affect this change, you will need to secure access to each of the four cauldrons here in the Western Plaguelands.$B$BThe time has come for you to lead the charge against the Scourge, hero.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5229, 'enUS', 'The key taken from Cauldron Lord Bilemaw is a perfect fit in unlocking the access panel, though it disappears after you use it. Still, you think you can open the panel now without a key. A foul stench emanates from the opened panel as an unknown mix of concentrated substances swirl inside the cauldron.$B$BThere is a small spigot that the bottle fits well enough under, allowing you to draw a sample for Shadow Priestess Vandis back at the Bulwark.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5230, 'enUS', 'Well done $n - it should take Apothecary Dithers no time at all to analyze this and devise a counter agent to introduce into the cauldron. If we are able to pump out enough of it into the air, we might be able to weaken all the Scourge present here.$B$BThis will no doubt be an ongoing ordeal; we will need to continually hit these cauldrons and introduce our reagents into their mix. Still, if we can keep it up, we will have a huge advantage over Scourge when the time comes for battle!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5231, 'enUS', 'The skeletal key found on the cauldron lord easily slides into a keyhole and then disappears, allowing you permanent access to it. Crude, viscous ichors pulsate through twisted tubes within the cauldrons underpinnings.$B$BThere is a small spigot that the bottle fits well enough under, allowing you to draw a sample for Shadow Priestess Vandis back at the Bulwark.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5232, 'enUS', 'Ah, there is plenty in here for study. Well done, $n!$B$BFor now, the next step is in our hands. We\'ll need to produce a list of reagents that, when combined with what is already in the cauldron, will act as its counter.$B$BThe only way we will be able to introduce this is by placing the reagents into the cauldron directly. Apothecary Dithers will have more information on that, so be sure to speak with him as soon as possible.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5233, 'enUS', 'The cauldron lord\'s key unlocks the access panel to this cauldron and then disappears, much like the other keys have for the previous ones you\'ve dealt of. A putrid smelling substance churns violently beneath the platform of the cauldron.$B$BThere is a small spigot that the bottle fits well enough under, allowing you to draw a sample for Shadow Priestess Vandis back at the Bulwark.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5234, 'enUS', 'Another finely executed mission, $n! This makes three cauldrons available to us to turn against the Scourge. If Kel\'Thuzad\'s necromantic dominance over these lands wasn\'t as strong as it currently is, I\'d dare say you\'d shut them down completely!$B$BApothecary Dithers will have a report on what this cauldron - as well as the other cauldrons subverted - will require to deliver our counter agent to the plague. With that, the time has come to bring the last cauldron of the west under our influence!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5235, 'enUS', 'Cauldron Lord Soulwrath\'s key disintegrates as it touches the lock on the access panel at the base of the cauldron\'s platform. A slight clanking noise is heard within, and the access panel swings open to reveal more viscous toxins within the cauldron\'s framework.$B$BThere is a small spigot that the bottle fits well enough under, allowing you to draw a sample for Shadow Priestess Vandis back at the Bulwark.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5236, 'enUS', 'Congratulations $n, you\'ve unlocked the last cauldron for us! Apothecary Dithers will soon know what this cauldron requires as far as reagents needed for the mix.$B$BAs for you, I am sure that High Executor Derrington will want to thank you personally for the assistance you\'ve given us. Without you, our struggle against the Scourge would have been nearly insurmountable. I thank you, mighty $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5237, 'enUS', '$n, your successful re-engineering of the cauldrons is a tremendous victory for us!$B$B$B$BLet all within my presence hear: for service to the Kingdom of Stormwind and to the Alliance above and beyond the call of duty - in the face of overwhelming odds and incredible danger, might I add - I give $n this, and I extend upon him our undying gratitude as a true hero of the Alliance. Cheers, I say!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5238, 'enUS', '$n, your successful re-engineering of the cauldrons is a tremendous victory for us!$B$B$B$BLet all within my presence hear: for service to the Forsaken and to the Horde as a whole above and beyond what was expected - in the face of overwhelming odds and incredible danger, might I add - I give $n this, and I extend upon him our undying gratitude as a true hero of the Horde. Huzzah!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5241, 'enUS', 'You\'ve seen Pamela? She\'s alive?$B$BShe\'s dead? No! Oh, Pamela! Why does your spirit still suffer in this world. Why are you perished, while fortune keeps me alive? In an instant I would switch places with you and wander dead Darrowshire, a ghost and alone!$B$BAh, but this news cannot change fate. Thank you, $n. Now my duty, my duty to revenge, burns hot as ever.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5242, 'enUS', 'Earthmother be praised, $n! Thank you! This is a great day, indeed!$B$BThe Shadow Council will be hard pressed to scale to the same levels of power they once held with this blow to their hierarchy. You have done a great thing this day!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5243, 'enUS', 'Well done, $R. You have proven that while you are tolerant of others, you will stop at nothing to destroy the minions of Kel\'Thuzad.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5244, 'enUS', 'Well, sure, I can tell you more about Kel\'Theril. Mind you now, I\'m no historian, but I can tell ya about what I saw there...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5245, 'enUS', 'The past of the high elves is something that I do not wish to discuss, $n. Please, it pains me to think of all that has happened... And that there is no way to make things right.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5246, 'enUS', 'The pages have gone untouched for many many years...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5247, 'enUS', 'Using these elements, I can fuse the fragments together... to hopefully what they once were.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5248, 'enUS', 'Pleeease, leeeave meeeeee...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5249, 'enUS', 'A friend of Ivy Leafrunner? Nice to meet you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5250, 'enUS', 'A student of Daryn Lightwind? Nice to meet you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5251, 'enUS', 'Outstanding, $n! Years of intelligence, lost! The Scarlet Crusade will surely feel this blow in the coming weeks.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5252, 'enUS', 'Is that... what I think it is?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5253, 'enUS', 'I have heard of the Crystal of Zin-Malor; the user of the crystal becomes capable of incredible arcane power, but only if truly worthy. I am impressed that you not only recovered the crystal, but you also realize your own limits, and did not attempt to use the crystal yourself.$B$BThank you for bringing it to me, $n. We will keep it safe, and may the cursed spirits that once guarded the shattered crystal finally be at peace.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5261, 'enUS', 'That\'s true. I\'m looking for someone to hunt me some sick wolves! Are you that person?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5262, 'enUS', 'What will this mean to the Crusade? Could it be possible that they have been under the control of this demon? Unwittingly serving as the pawns to a greater evil?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5263, 'enUS', 'It has been many years since I have ushered a champion into our ranks. You are the first to be granted audience with Lord Maxwell in years.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5264, 'enUS', 'You are a worthy ally, $n. You shall reap the rewards of your stalwart dedication.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5265, 'enUS', 'Whoa...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5281, 'enUS', 'Alen has finally found someone brave enough, eh?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5282, 'enUS', '<Egan wipes a tear from his eye.>$B$BDid you see my mother, $n? Did you free her?$B$BYou are a shining example of decency. For your troubles...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5305, 'enUS', 'May you bash in many a brain, $n!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5306, 'enUS', 'Knowledge is power!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5307, 'enUS', 'It should be obvious that a sword is always the best choice.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5321, 'enUS', 'Oh, I am so happy Kerlonian made it! And I\'m sure I\'ll find him sleeping somewhere around here, won\'t I?$B$BThank you for showing him the way, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5341, 'enUS', 'Excellent work, $R. Now to secure my inheritance...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5342, 'enUS', 'Wonderful! Now only I hold the power and fortune of house Barov!$B$B<Alexi throws a large bag at you.>$B$BAnd keep the change, you filthy animal.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5343, 'enUS', 'There is but one more task that I must ask of you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5344, 'enUS', 'A glorious day it is! Finally, the pockmark of house Barov eliminated and my fortune secure!$B$BTake this and leave my sight, peasant.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5361, 'enUS', 'I know not what connection you have with my brother but it would seem he has trust with you, $C.$B$BI am Nataka Longhorn and would like to welcome you to Ghost Walker Post, we have many needs of a $C! Talk with others, perhaps there is need of you here.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5381, 'enUS', 'Ah, the Demon Box. Give it to me, $C, and I shall open... and thus reveal to you the Hand of Iruxos.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5382, 'enUS', 'I can feel my spirit healing, $n. You have done my husband and I a great service, but I am certain there is more to be done.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5384, 'enUS', 'With the death of Kirtonos, a chapter in the horrible tale of Scholomance comes to a close. There is, however, more to be done. Others here are now aware of your brave acts. Take this, $n. It is a piece of our own essence. It will allow you to communicate with the other lost souls of Caer Darrow.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5385, 'enUS', 'You\'ve done it! Praise be to Elune and whichever powers you worship, $n. You have done an honorable and noble thing this day. I am certain Arko\'narin will be pleased that you not only took the time to save her, but to also slay the creature who tortured one of her best friends.$B$BI wish I could do more for you, but please, take this.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5386, 'enUS', 'You are a savior, $n! These Bloodbelly fish look almost good enough to eat raw.$B$B$B$BHere you go - choose one of these items as a payment for these fish.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5401, 'enUS', 'All who fight evil on behalf of the Argent Dawn must don our commission. Wearing such will earn your right to claim rewards our quartermasters have to offer.$B$BWhile under commission, you\'ll be able to acquire Scourgestones from slain Scourge; these are marks of insignia that they sometimes possess. Procure these insignia and bring them to me. For each set you turn in, you will receive our marker of heroism - the Argent Dawn Valor Token.$B$BAchieve your destiny, $C - join the fight today!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5402, 'enUS', 'Congratulations, $n - I am pleased to award you with an Argent Dawn valor token!$B$BThe tokens serve two purposes. One - they can be used to further your standing within the Argent Dawn; to do so may avail you options and rewards that are reserved for our honored and revered allies. Two - they can be given to our quartermasters, along with coin, as payment for some of the Dawn\'s more prized items.$B$BWhatever you decide, know that you are earning your commission. Well done!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5403, 'enUS', 'Congratulations, $n - I am pleased to award you with an Argent Dawn valor token!$B$BEach valor token handed out to worthy recipients is a sign of the tide turning against the Scourge. With more and more heroes fighting the Scourge in their own lands, the Argent Dawn will assuredly be at the forefront when the time comes to wage war on the ultimate evil of the Scourge. I hope that the valor you\'ve shown what it took to acquiring your token will carry the day then as well!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5404, 'enUS', 'Congratulations, $n - I am pleased to award you with an Argent Dawn valor token!$B$BTo acquire a valor token in such a manner indicates that you are a true hero in the cause of good. We value all effort brought to bear against the Scourge, but to take down one of their leaders is to truly deliver onto them a crushing defeat!$B$BFor the Dawn, my brother!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5405, 'enUS', 'All who fight evil on behalf of the Argent Dawn must don our commission. Wearing such will earn your right to claim rewards our quartermasters have to offer.$B$BWhile under commission, you\'ll be able to acquire Scourgestones from slain Scourge; these are marks of insignia that they sometimes possess. Procure these insignia and bring them to me. For each set you turn in, you will receive our marker of heroism - the Argent Dawn Valor Token.$B$BAchieve your destiny, $C - join the fight today!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5406, 'enUS', 'Congratulations, $n - I am pleased to award you with an Argent Dawn valor token!$B$BTo acquire a valor token in such a manner indicates that you are a true hero in the cause of good. We value all effort brought to bear against the Scourge, but to take down one of their leaders is to truly deliver onto them a crushing defeat!$B$BFor the Dawn, my brother!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5407, 'enUS', 'Congratulations, $n - I am pleased to award you with an Argent Dawn valor token!$B$BEach valor token handed out to worthy recipients is a sign of the tide turning against the Scourge. With more and more heroes fighting the Scourge in their own lands, the Argent Dawn will assuredly be at the forefront when the time comes to wage war on the ultimate evil of the Scourge. I hope that the valor you\'ve shown what it took to acquiring your token will carry the day then as well!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5408, 'enUS', 'Congratulations, $n - I am pleased to award you with an Argent Dawn valor token!$B$BThe tokens serve two purposes. One - they can be used to further your standing within the Argent Dawn; to do so may avail you options and rewards that are reserved for our honored and revered allies. Two - they can be given to our quartermasters, along with coin, as payment for some of the Dawn\'s more prized items.$B$BWhatever you decide, know that you are earning your commission. Well done!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5421, 'enUS', 'Sweet mon, nothing beats work being done, especially if it\'s someone else doing the work... eh?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5441, 'enUS', 'Good, good. Maybe they\'ll think twice before slacking next time! Thanks for the help!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5461, 'enUS', 'I had doubted the possibility of such an item existing. This certainly opens up an incredible opportunity - one we must not squander.$B$BPrepare yourself, $n, for what I am about to ask of you will take you into the depths of hell.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5462, 'enUS', '<Leonid looks over the keepsake.>$B$BYour quandary stirs up memories that I had hoped would never resurface.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5463, 'enUS', 'You place the keepsake on the tainted soil.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5464, 'enUS', 'As the soul clung to the keepsake, so shall it cling to the physical form of Ras Frostwhisper.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5465, 'enUS', 'I was certain that you would be joining me as another hapless spirit.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5466, 'enUS', 'Ten thousand souls cry out in unison, $n! You have struck a mortal blow to the Scourge and their masters.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5481, 'enUS', 'What\'s this? Gloom weed?! I\'ve no need for gloom weed! That mindless mass of flesh is out there picking daisies and convincing the Lady knows how many dupes like you into doing the same. No offense.$B$BVery well, you\'re here now and that\'s all that matters. Since I\'m not going to bother to try to explain things to that abomination how about you collect what I really need... doom weed!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5482, 'enUS', 'Ah, my doom weed. Excellent!$B$B<Junior Apothecary Holland rubs his hands together greedily.>$B$BThese will come in quite handy. You\'ve done me, uh, the Lady, a fine service today, $n. As promised, here is the reward that you deserve.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5501, 'enUS', 'Ah, goodie! My bones!$B$BI can\'t wait till the chaps at home see my face on the front cover of Goblin\'preneur. I\'ll be the richest goblin in the world... and my physical education teacher said I would never amount to anything.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5502, 'enUS', 'May the spirits bless you, $n. You\'ve done more for Grunth than most would ever think of doing for him. Thank you - you\'re the epitome of what it means to be a hero of the Horde.$B$BThe orphans will love your gift; it\'s not often that they get a new toy.$B$BGrunth had so much fun with you, $n. He wanted to give you one of his favorite pets to remember him by.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5503, 'enUS', 'All who fight evil on behalf of the Argent Dawn must don our commission. Wearing such will earn your right to claim rewards our quartermasters have to offer.$B$BWhile under commission, you\'ll be able to acquire Scourgestones from slain Scourge; these are marks of insignia that they sometimes possess. Procure these insignia and bring them to me. For each set you turn in, you will receive our marker of heroism - the Argent Dawn Valor Token.$B$BAchieve your destiny, $C - join the fight today!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5505, 'enUS', 'Good luck, mighty hero. May your victories continue to bring you fame and power as you lay waste to Scholomance!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5507, 'enUS', 'Outstanding - your tribute to the Dawn is duly noted, $n. With this accomplished, I am delighted to make our mantles available to you for purchase. So long as you maintain your current status with us, these mantles will be purchasable from any authorized quartermaster of the Argent Dawn.$B$BFor the Dawn, my brother!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5508, 'enUS', 'Congratulations, $n - I am pleased to award you with an Argent Dawn valor token!$B$BTo acquire a valor token in such a manner indicates that you are a true hero in the cause of good. We value all effort brought to bear against the Scourge, but to take down one of their leaders is to truly deliver onto them a crushing defeat!$B$BFor the Dawn, my brother!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5509, 'enUS', 'Congratulations, $n - I am pleased to award you with an Argent Dawn valor token!$B$BEach valor token handed out to worthy recipients is a sign of the tide turning against the Scourge. With more and more heroes fighting the Scourge in their own lands, the Argent Dawn will assuredly be at the forefront when the time comes to wage war on the ultimate evil of the Scourge. I hope that the valor you\'ve shown what it took to acquiring your token will carry the day then as well!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5510, 'enUS', 'Congratulations, $n - I am pleased to award you with an Argent Dawn valor token!$B$BThe tokens serve two purposes. One - they can be used to further your standing within the Argent Dawn; to do so may avail you options and rewards that are reserved for our honored and revered allies. Two - they can be given to our quartermasters, along with coin, as payment for some of the Dawn\'s more prized items.$B$BWhatever you decide, know that you are earning your commission. Well done!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5511, 'enUS', 'Good luck, mighty hero. May your victories continue to bring you fame and power as you lay waste to Scholomance!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5513, 'enUS', 'Outstanding - your tribute to the Dawn is duly noted, $n. With this accomplished, I am delighted to make our mantles available to you for purchase. So long as you maintain your current status with us, these mantles will be purchasable from any authorized quartermaster of the Argent Dawn.$B$BFor the Dawn, my brother!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5514, 'enUS', 'Dithers said you\'d deliver the goods, and deliver you have! A deal is a deal; let me pack the fragments into the mold for you.$B$BDid Dithers imbue them already? Excellent... otherwise, it would have been a long trip back to the Bulwark for you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5515, 'enUS', 'The bag of horrors!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5518, 'enUS', 'Yeah, this will do the trick. Just a slip here, and tuck here... heh - this is going to work out just fine! <A broad smile beams from the chained goblin\'s face.>$B$BHere you go, you\'re all set. Remember- this isn\'t the most securely fashioned item in the world. You\'ll have ten minutes of use before it falls apart, and then you\'re not going to fool anybody! Try using it just before you approach him, and remember... it only will make him think that YOU are an ally - not your group mates!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5519, 'enUS', 'Here ya go $n - put this thing on, and you\'ll have ten minutes to do whatever. Good luck!$B$BOh, and if you happen to find a key to get me out of here, BY ALL MEANS LET ME KNOW!!!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5522, 'enUS', 'Ah, very good. You do not know the evil power for whom you have labored, but now you will know the truth. And fear not--I am no servant of that master. I am Leonid Barthalomew, known as \"the Revered\" to my brethren in the Argent Dawn.$B$BI tricked Tinkee into sending you and the dragon eggs to me. And it is good that my trick was successful, for had these eggs reached their intended destination, a great power would have fallen into the hands of the enemy.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5525, 'enUS', 'Oh, thank you for freeing me! You - ranked number one. Everyone else - ranked number two or lower.$B$BI knew my life was destined for more than just being an appetizer to some ogre!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5526, 'enUS', 'It is done, brave $C, and with that the Cenarion Circle is in your debt. Though the war is far from over, a great battle has been won against those who would bring about our doom.$B$BPlease - accept this as a token of our enduring gratitude. Know that you are welcomed here as a hero of the Cenarion Circle for your great deed, $n. Thank you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5527, 'enUS', 'Indeed, you are as resourceful as many claim. The Cenarion Circle is blessed to have you working in our stead.$B$BWith the Reliquary once more in our hands, my mind is at ease. I feel more comfortable asking you to not only face down a malfeasant demon, but also to bring back a dangerous item in the process...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5528, 'enUS', 'You da big boss now, so you get free sample of my better brews! These drinks boost you up AND give you good buzz! These are da good ones, chief... you try and tell Kreeg yourself!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5529, 'enUS', 'Well done, $n! It\'ll be a long time before they can grow another clutch of dragons.$B$B...At least, I hope it will!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5531, 'enUS', 'There they are! And in such a nice container. The perfect size!$B$BWe\'ll keep these eggs in a safe place so we can study them later, but right now we need that thing you brought them in.$B$BI have created a device with a very specific use: to destroy the undead scholars of the Scholomance. They are studying dragons in their efforts to create a plagued dragonflight, and we must halt their progress.$B$BI call the device Dawn\'s Gambit, because... I\'m not sure if it works!$B$BWill you find out for us?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5533, 'enUS', 'Well met, $C. I\'ve dedicated a fair portion of my recent studies to the Scholomance, and I believe I know a means by which you can acquire a key... one that will let you past the front door.$B$BI can only imagine what horrid sights are to be seen inside, $n. I don\'t know how you heroic types do it sometimes...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5534, 'enUS', 'Haha! Wonderful!$B$BI\'ll be sure to find out what this is for and why Magus Rimtori--she\'s the cow that hired me--wanted it. Hah! I got you now, stinkin\' Blood elves! Kim\'jael! Ha! I\'ll teach them who the rats are!$B$BYa did good, $n. Thanks a bundle. Here, take this coin and be off with ya--I\'ve got some things to figure out.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5535, 'enUS', 'Well done, $C. Thank you for your help. The spirits are at peace, and the threats that their desire for the arcane draw have been lessened.$B$BI am sure that both sides of the conflict that ravishes Azeroth will see you are above such things.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5536, 'enUS', 'Well done, $C. I see you\'re not only skilled but cunning. To defeat such creatures is a testament to your loyalty to the tenants of the Earthmother, even if you do not recognize her as your own deity. She is in all things on this planet and beyond. Her eyes, one dark and one light, travel across the skies always gazing down upon us.$B$BPlease, take this small amount of coin in appreciation for what you have done.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5537, 'enUS', 'This\'ll work, $n! My turn now - I will imbue these with a suitable temper that will allow them to survive the intense forging process they will eventually go through.$B$BThe next step will be to get an appropriate mold for the key. I know a goblin blacksmith in Tanaris that has in the past made molds for similarly macabre items. He works for whom goblins usually work for - the highest bidder.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5538, 'enUS', 'Arbington said you\'d deliver the goods, and deliver you have! A deal is a deal; let me pack the fragments into the mold for you.$B$BDid Arbington imbue them already? Excellent... otherwise, it would have been a long trip back to Chillwind Point for you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5541, 'enUS', 'Great, more ammo! Old Loslor\'s shipment finally came through! The ammo might be late, but just like my grand daddy used to tell me: late\'s better than never!$B$BThank you much, $n. I already have buyers eager for this stuff!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5542, 'enUS', 'Well done, $n. Come. Rest. Let us eat and talk of days long past!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5543, 'enUS', 'You are a shining example of decency, $R! It has been long since I have felt the blood of heroes pump through my veins. Your acts of bravery are uplifting!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5544, 'enUS', 'May the Light watch over you! Bless you, young one.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5545, 'enUS', 'Excellent! Thanks to you, I should be able to complete the order in time. To show my gratitude, I would like to offer some coin as compensation for your troubles.$B$BThank you and farewell.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5561, 'enUS', '<Smeed Scrabblescrew begins counting.>$B$BThree, excellent, I ask for three tamed kodos and I get three tamed kodos... I am going to be rich... yes, very rich! I hope to see the look on Bibbly\'s green face when he hears about my success!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5581, 'enUS', 'Your efforts have helped to slow down the enemy, $C.$B$BThank you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5582, 'enUS', 'Excellent! This will be invaluable in my continued studies of the machinations of the Scourge. Thank you, $n, and if you find more of these scales please bring them to me!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5601, 'enUS', 'Where is my sister Jessica? She left so long ago, when I was just a little girl. She cried so much when she went away. I don\'t know why she cried! Maybe she fell and hit her toe.$B$BWhen Jessica was here we used to play games and dance and pick pretty flowers around the house. And sometimes we\'d hide when we saw daddy coming home, then jump out and surprise him!$B$BCome back, Jessica! If you come back then maybe daddy will too and we can all play together again...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5713, 'enUS', 'You bring most welcome news $C. It is good to hear that Aynasha is alive and well thanks to you. I would hope that you might accept a reward for your valor. May Elune watch over you and light your path.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5721, 'enUS', 'The fighting is over! I heard my daddy whisper to me. He scared away all the ghosts and he says he\'s coming home! I\'m so happy!$B$BHe also said I should welcome you if came to our house, so I made some tea!$B$BI hope you like lots of sugar!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5722, 'enUS', 'The body looks seriously beaten by clubs or fists, but most of the corpse\'s clothing and equipment still looks present. It seems the troggs do not overly care much for material wealth.$B$BYou begin to search the body for anything of interest that might help Magatha.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5723, 'enUS', 'I am glad to see you took Magatha\'s task seriously. Thank you, $n. I\'m sure the troggs will have a harder time coming to the surface with their numbers so greatly reduced.$B$BPerhaps in the future we can take time to figure out where such creatures came from, and what they really want.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5724, 'enUS', 'Excellent, $n. Thank you.$B$BMagatha told me that if you were successful in finding her clansman that you be given this. Please, take it in thanks.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5726, 'enUS', 'Good, $n! The spirits be praised, perhaps you are the one who will finally put my greatest fears to rest! Who would suspect someone so young and so brave would rise up and champion our cause? You remind me of myself when I was younger. I will ensure that you are justly rewarded for your efforts if we both survive the coming storm.$B$BBut there is time for more praise later. You\'ve not accomplished anything in comparison to what you will face... but this is a good start.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5727, 'enUS', 'Excellent! Most excellent, $C!$B$BWhat you have done this day is only the first step in a much broader foundation--a foundation that we will build the destruction of the Shadow Council on, once and for all.$B$BTell me all he said... and leave not one word out--it may be more important than you realize.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5728, 'enUS', 'I am glad you\'ve returned, $n. Some of those loyal to me brought word immediately that the caverns below Orgrimmar were in disarray now that their leaders have been slain. I even heard reports that Neeru was more than agitated. It seems we\'ve put a dent in his armor. I can\'t say I\'m displeased... even with such a minor victory.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5729, 'enUS', 'What is it?! Oh, you, $C... my apologies. My anger rivals that of a rabid kodo bull... but perhaps it is my own fault. In sending travelers into Ragefire Chasm, I should have seen the possibility that some harm would come from it. It seems both Bazzalan and Jergosh were taken unaware and slain by some of Thrall\'s do-gooders. A most inopportune time, but there is nothing that can be done about it now.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5730, 'enUS', 'Ashenvale? Hmm, I had not heard of any presence of the Council or the Burning Blade in Ashenvale. My spies will investigate, $n. You have done well.$B$BFor now, rest, and keep yourself occupied with other tasks. I will call upon you again soon.$B$BLok-Tar Ogar!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5741, 'enUS', 'The elusive Sceptre of Light! Your acquisition of it has removed a grave threat to the world. Well done, $C, well done.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5742, 'enUS', 'The Fordring redemption will be a difficult task. Are you prepared?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5761, 'enUS', 'Ha! You\'ve done it! Thrall will be so pleased.$B$BI will ensure this heart is taken care of properly.$B$BFor now though, you must celebrate your victory. I will inform Thrall of your success.$B$BThank you for your aid, $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5762, 'enUS', 'Great, my package from Koalbeard! Thank you $n. This crate holds special-gauge ammunition - my father swears by it - and it\'ll serve us well out in the jungle!$B$BHere\'s your pay, and tell me, how is crusty old Kravel these days? Up to no good I\'d wager...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5763, 'enUS', 'Ah, this horn belongs to a $R, Roon Wildmane. My father spoke often of the good times they had together hunting the beasts of Desolace. So Roon is inviting me to join him, is he?$B$BWe\'re neck deep in the jungle right now, but thank you, $n. Perhaps my next expedition will take me to Desolace, the land of the centaurs.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5781, 'enUS', '<Tirion blinks to hold back tears.>$B$BIt was destiny that we meet, $n. You have blessed me with your kindness.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5801, 'enUS', 'We\'ve got quite the costly skeleton key there, don\'t we $n? Still, it remains unfinished; the time has now come to put some focus behind our creation. To that end, you\'re going to have to face the ultimate foe we\'ve been working towards facing in Andorhal... the nefarious being that runs the show within the ruined city walls.$B$BI\'m speaking of Araj the Summoner - a lich, and one of the most powerful beings the Scourge calls as its own.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5802, 'enUS', 'We\'ve got quite the costly skeleton key there, don\'t we $n? Still, it remains unfinished; the time has now come to put some focus behind our creation. To that end, you\'re going to have to face the ultimate foe we\'ve been working towards facing in Andorhal... the being that runs the show within the ruined city walls.$B$BI\'m speaking of Araj the Summoner - a lich, and one of the most powerful beings the Scourge calls as its own.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5803, 'enUS', 'Yes, this is it! I... again, I\'m absolutely amazed at the depths of your heroism. It simply boggles the mind the lengths you are willing to go to achieve victory over the forces of darkness.$B$BAllow me now to play my small part in this. I\'ve prepared a special solvent that should allow me to secure the scarab on as the head of the key, infusing it with the power to breach the locked door of Scholomance.$B$BHere goes nothing...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5804, 'enUS', 'Yes, this is it! I... again, I\'m absolutely amazed at the depths of your cunning and bravery. It simply boggles the mind the lengths you are willing to go in order achieve victory.$B$BAllow me now to play my small part in this. I\'ve prepared a special solvent that should allow me to secure the scarab on as the head of the key, infusing it with the power to breech the locked door of Scholomance.$B$BThis should take just a moment...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5805, 'enUS', 'You are indeed a special hero, $n. We welcome you to the world of Azeroth, and offer you one of these unique gifts!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5821, 'enUS', 'You know, it doesn\'t seem prosperous to be paying the likes of you to guard Gizelton Caravan. $C, if I were a bit taller I could take on a second job and sit kodo-side-saddle with the biggest gun my hands could carry... and I\'d show them blasted centaur who\'s their papa.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5841, 'enUS', 'You are indeed a special hero, $n. We welcome you to the world of Azeroth, and offer you one of these unique gifts!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5843, 'enUS', 'You are indeed a special hero, $n. We welcome you to the world of Azeroth, and offer you one of these unique gifts!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5845, 'enUS', 'It is as glorious now - even in its tattered state - as the day I looked upon it and took my oath of allegiance.$B$BHis redemption comes and perhaps mine with it...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5846, 'enUS', 'How could I forget? Many of the spirits on this island are cursed to relive their last happy memory before they met their tragic end.$B$BI too am cursed, but not as they - I am one of the few that remembers all...$B$BPerhaps this thing that you ask me is why I haunt these ruins. Perhaps this thing I tell you shall set me free.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5848, 'enUS', '<Tirion nearly collapses when he sees the painting.>$B$B<He begins to sob in an almost frenetic manner.>', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5861, 'enUS', 'I was Tirion\'s most trusted advisor when he held rank as Lord of Mardenholde. I openly dissented the verdict passed by the Order of the Silver Hand and was banished for my insolence.$B$BHe has been through much heart ache and disappointment in his life, $R. Are you prepared to right the wrongs set upon the Fordrings?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5862, 'enUS', 'What is this, soldier?$B$B<Taelan opens the box and begins to examine its contents.>$B$BThis... this cannot be true. But all that you have shown me...$B$B<Taelan falls to one knee, his heartbeat quickens. The fury of ages is about to be loosed upon this town.>', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5863, 'enUS', 'I\'m so glad you were able to do this for me, $n! Thanks for your help!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5881, 'enUS', 'This is great news; I have been sitting here guarding this hunk of wood for too long. Now I can spend some time with my family... Yippee!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5882, 'enUS', 'Very well then, here are your Cenarion plant salves. Scour Felwood for corrupted plants, $n! Wherever they are, use the salve I have given you to purify them.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5883, 'enUS', 'Very well then, here\'s some Cenarion plant salve. If you find more vitriol when you mine in Felwood, remember to hold on to it for me! The more of it you find, the more salve I will be able to make.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5884, 'enUS', 'Excellent work $n - every bit of this, once we purify it, will help us take back Felwood. Here, take some doses of Cenarion plant salve and head into the forest. The reclaiming of the land from the corruption waits for no $R!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5885, 'enUS', 'This is exactly what I need. Here - take these plant salves and use them to reclaim Felwood from the corruption. We must persevere, $n!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5886, 'enUS', 'Here are some Cenarion plant salves - I only need one lesser nether essence for this, $n. Essence is by far the most potent item I can make use of to turn swords into plowshares, as it were. If you need more salve, then find me here!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5887, 'enUS', 'Very well then, here are your Cenarion plant salves. Scour Felwood for corrupted plants, $n! Wherever they are, use the salve I have given you to purify them.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (5888, 'enUS', 'Very well then, here\'s some Cenarion plant salve. If you find more vitriol when you mine in Felwood, remember to hold on to it for me! The more of it you find, the more salve I will be able to make.', 0);
