INSERT INTO `quest_offer_reward_locale` VALUES (7652, 'enUS', 'Aright, mister, I got a hot deal for ya.$B$BI\'m lookin\' to offload some of these here Imperial plate armor recipes and need someone willing to take on the burden. All I want in exchange is some thorium bars. Sound good?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (7653, 'enUS', 'A pleasure doin\' business with ya.$B$BAnd $n, wipe that look of disgust off yer face.', 0);
