INSERT INTO `quest_offer_reward_locale` VALUES (8768, 'enUS', 'This festively-wrapped present has your name on it.$B$BWait, did it just move?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (8769, 'enUS', 'Have a joyous Feast of Great-Winter from your friends at Smokeywood Pastures.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (8770, 'enUS', 'Excellent work, $n. Your diligence is as noteworthy as always.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (8771, 'enUS', 'Excellent work, $n. Your part in the attack on Hive\'Ashi has been noted.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (8772, 'enUS', 'Excellent work, $n. Your part in the attack on Hive\'Zora has been noted.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (8773, 'enUS', 'I\'ve heard good things about your part in the Hive\'Zora attack. Keep up the good work and victory will soon be ours.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (8774, 'enUS', 'Excellent work, $n. Your part in the attack on Hive\'Regal has been noted.', 0);
