INSERT INTO `quest_offer_reward_locale` VALUES (13023, 'enUS', 'It is good to know the people of this land still pay homage to the olden races. I bid you well, $n, and offer you this token...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (13024, 'enUS', 'Your spirit burns with life, young $C. I accept the homage you pay, and offer in return this token...', 0);
