INSERT INTO `quest_offer_reward_locale` VALUES (13712, 'enUS', 'Gorka\'s death was not in vain, $n.$B$BSplintertree would have certainly fallen if we hadn\'t come when we did.$B$BNow we must get to work shoring up her defenses.$B$BThis isn\'t over....', 0);
