INSERT INTO `quest_offer_reward_locale` VALUES (13763, 'enUS', 'This is a fine weapon that will help me crush the competition. Thank you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (13764, 'enUS', 'The blade is as fine as the stories say. It would seem there\'s little reason to complete the repairs on my customary blade. Winter\'s Edge is far superior and I will continue to carry it. You have my thanks, $n.', 0);
