INSERT INTO `quest_offer_reward_locale` VALUES (13766, 'enUS', 'With their gates closed and no further reinforcements coming, it is only a matter of time now before the demons are no more.$B$BYou are a hero of the forest, $n. I give you thanks on behalf of all of Ashenvale\'s creatures!', 0);
