INSERT INTO `quest_offer_reward_locale` VALUES (28779, 'enUS', 'I thank you, $n. Even after I had escaped, I did not imagine I\'d be able to return home.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (28780, 'enUS', 'It\'s true, we were ambushed. I don\'t dare send any more soldiers out there and risk losing them too. I need a volunteer. Someone willing to risk their life!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (28781, 'enUS', 'Excellent work, $n. Your reputation is well-deserved.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (28782, 'enUS', 'Hell-Hoot is slain, and her meat is just as succulent as you\'d expect.$B$BToday, we eat a legend. Thank you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (28783, 'enUS', 'It is true, then? The great blight upon our race will soon end now that the Neferset have lost the ability to corrupt any more of our people.$B$BName your reward, $C. It is the least we can do for you.', 0);
