INSERT INTO `quest_offer_reward_locale` VALUES (28806, 'enUS', 'I think you now understand the power of the Light. The Light giveth hope, brother and the Light taketh from the darkness! BLESSED BE THE LIGHT!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (28807, 'enUS', 'Anduin has been investigating the Twilight\'s Hammer? Weren\'t you supposed to keep him OUT of trouble?$B$BHmmm. Let me see those badges you\'ve discovered.', 0);
