INSERT INTO `quest_offer_reward_locale` VALUES (28814, 'enUS', 'A high priestess, you say? And a member of the Twilight Council?$B$BExcellent work. I hope you find your rewards suitable.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (28815, 'enUS', '<You open the documents and look for any noteworthy information.>', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (28816, 'enUS', 'Lok\'tar, brother! You have arrived just in time.', 0);
