INSERT INTO `quest_offer_reward_locale` VALUES (31375, 'enUS', 'Ah, have you come to join the Order?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (31376, 'enUS', 'You\'ve arrived just in time, $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (31377, 'enUS', 'You\'ve arrived just in time, $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (31378, 'enUS', 'Welcome, $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (31379, 'enUS', 'Welcome, $C.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (31380, 'enUS', 'So you wish to test your skills? Very good, $R.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (31381, 'enUS', 'So you wish to test your skills? Very good, $R.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (31382, 'enUS', 'Well met, $C! We need your help.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (31383, 'enUS', 'Well met, $C! We need your help.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (31384, 'enUS', 'Well met, $R. You look capable. Glad to know we have a decent $C on our side.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (31385, 'enUS', 'Well met, $R. You look capable. Glad to know we have a decent $C on our side.', 0);
