INSERT INTO `quest_offer_reward_locale` VALUES (32673, 'enUS', 'Don\'t forget to buy your mount while you are here! You can buy one from Kall Worthaton over there.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (32674, 'enUS', 'Ah, good to see you. You look like you are ready to take to the skies!$B$BRemember, you can buy a flying mount from Drakma, just behind me.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (32675, 'enUS', 'Ah, good to see you. You look like you are ready to take to the skies!$B$BRemember, you can buy a flying mount from Tannec Stonebeak over there.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (32676, 'enUS', 'This so-called \"Thunder King\" is surrounded and cut off from his allies. No tyrant is strong enough to stand alone.$B$BYour efforts have not gone unnoticed. Take this, with my thanks.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (32677, 'enUS', 'Ah, I know a powerful artifact when I see one. You were wise to bring this here. I will see that Aethas examines it right away - perhaps we can put it to work in our favor.$B$BIn the meantime, take this in trade.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (32678, 'enUS', 'We must move quickly, $n! The others have gone ahead of us, but I will brief you in the flight over.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (32679, 'enUS', 'We must move quickly, $n! The others have gone ahead of us, but I will brief you in the flight over.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (32680, 'enUS', 'You have arrived just in time, $R. It is crucial we gain control of these shores before the Kirin Tor.$B$BThere is much to explain.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (32681, 'enUS', 'You have arrived just in time, $n. This land needs to be controlled by the Kirin Tor for everyone\'s safety. This will not be an easy task.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (32682, 'enUS', 'Why I just knew you could convince him, $n. You\'ve set my heart at ease, knowing the Tillers will be in good hands.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (32683, 'enUS', 'Well done. You are ready to become a zen master blacksmith.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (32684, 'enUS', 'Ah! Have you come to learn how to fish? You\'re talking to the right person. First, here - have some fish!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (32685, 'enUS', 'Let me see... Yes, these are nice. Good job! Here, have some more.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (32686, 'enUS', 'This will aid lorewalkers and archaeologists in understanding the history of Pandaria.', 0);
