INSERT INTO `quest_offer_reward_locale` VALUES (32707, 'enUS', 'Perfect. I will show you how to use these stones, and then you can spring the ambush.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (32708, 'enUS', 'Very impressive.$B$BShould you find more stones, you should continue this ruse. It is sure to be quite some time before they catch on to our ploy.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (32709, 'enUS', 'Welcome, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (32718, 'enUS', 'May the stars smile upon you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (32719, 'enUS', 'May the stars smile upon you, $n.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (32722, 'enUS', 'Good work. What you did today brings us one step closer to our ultimate goal.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (32724, 'enUS', 'Good work. What you did today brings us one step closer to our ultimate goal.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (32726, 'enUS', 'Well done. You are ready to become a zen master blacksmith.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (32728, 'enUS', 'Yes, good, come... there is work to be done.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (32729, 'enUS', 'One more to join the Zandalari hunt? Good. Very good.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (32730, 'enUS', 'You have good timing. I am quite in need of your assistance.', 0);
