INSERT INTO `quest_offer_reward_locale` VALUES (36650, 'enUS', 'Nothing teaches a bully better than a bloody nose! Good fight, Commander. It\'s an honor to stand with you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (36653, 'enUS', 'Is this what a peon feels like? Chopping down trees and pruning unruly hedges all day? And I thought their job was a bore. Hah!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (36655, 'enUS', 'I don\'t know what\'s worse, the brutality of this Iron Horde or the tempered menace of our old foes. It doesn\'t matter. Neither measures against our strength, Commander.', 0);
