INSERT INTO `quest_offer_reward_locale` VALUES (36702, 'enUS', 'MEATBALL FEELING BETTER NOW!$B$BMEATBALL FIGHT FOR AKIHIKÓ NOW!$B$BMEATBALL FOLLOW AKIHIKÓ WHEREVER AKIHIKÓ GO!!!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (36705, 'enUS', 'You did it. You really did it! You\'re the new lord of the ring!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (36706, 'enUS', 'Commander! I am humbled to be able to serve you. The Lieutenant requests your presence.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (36707, 'enUS', 'Throm-Ka, commander. I am pleased to finally meet you in person.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (36708, 'enUS', 'Thank you again for taking the time to visit, commander. You have no idea how much it means to us.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (36709, 'enUS', 'So, you are the commander? Sylvanas sent me to find you.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (36711, 'enUS', 'Ya brought my gear! Well it be lookin\' like my luck might be turnin\' around after all!$B$BAbu\'gar will be returnin\' the favor, just ya wait and see!$B$BRight after I hook myself a lunker, of course...', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (36741, 'enUS', 'And here\'s your potion, $n! If you don\'t have any use for it, maybe gift it to a friend who does?', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (36742, 'enUS', 'It is made. If you don\'t have any use for it, give it to one of our melee.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (36790, 'enUS', 'The lives of all the flock are precious.$BI\'m glad to know that his murderers have been brought to justice.$B$BThank you, $n, you have done us a great honor.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (36791, 'enUS', 'Here she is! Careful with her, these potions have a reputation for mysteriously disappearing.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (36800, 'enUS', 'I will take any lunkers you like find like this off your hands. Come back and see me anytime you catch one and I will pay you top dollar!', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (36801, 'enUS', 'Ah, Commander $n. Good to see you. Now our party is complete.', 0);
