INSERT INTO `quest_offer_reward_locale` VALUES (41394, 'enUS', '<Johnny raises his arm exaggeratedly, glancing at his naked wrist.>$B$BMy, my, quite the LETHARGIC adventurer, aren\'t you? That just might cover my repair bills from the many dungeons I\'ve plundered.', 0);
