INSERT INTO `quest_offer_reward_locale` VALUES (41409, 'enUS', 'Fjolrik died in honor of Jandvik. We will remember his bravery.', 0);
INSERT INTO `quest_offer_reward_locale` VALUES (41410, 'enUS', '<Fjolrik looks at you weakly.>$B$BYou made it back in time...', 0);
