INSERT INTO `quest_request_items` VALUES (10486, 1, 0, 0, 0, 'Our survival here depends upon our ability to concentrate our efforts against those that truly matter, not the mere bladewing bloodletters.$B$BYou are here again, so this must mean that you have dealt with the matter at hand?', 0);
INSERT INTO `quest_request_items` VALUES (10487, 1, 0, 0, 0, 'It would seem that the Alliance is determined to follow us wherever we go. Let us teach them that we are not intimidated by their presence.$B$BDo you have that which I have requested?', 0);
