INSERT INTO `quest_request_items` VALUES (10502, 1, 0, 0, 0, 'Our scouts have reported that the Bloodmaul ogres have begun to creep up the switchback, which leads up to the Living Grove from their hold.$B$BIt is unfortunate that we cannot find a way to live together in peace with the ogres, but then, when have we ever been able to do so?', 0);
INSERT INTO `quest_request_items` VALUES (10503, 1, 0, 0, 0, 'Our scouts have reported that the Bladespire ogres have established a camp even closer to the switchback, which leads from their hold up to us here.$B$BIt is unfortunate that we cannot simply subjugate these ogre scum to our will, but if it\'s a fight that they want, they will find the Horde more than accommodating.', 0);
INSERT INTO `quest_request_items` VALUES (10504, 1, 0, 0, 0, 'Bloodmaul or Bladespire, it doesn\'t matter. All of the ogre clans fight against each other, vying for the dominance that comes with having the favor of Gruul the Dragonkiller.$B$BEach is still our enemy, regardless of who that favor has fallen to.', 0);
INSERT INTO `quest_request_items` VALUES (10505, 1, 0, 0, 0, 'Bladespire or Bloodmaul, it matters not. All of the ogre clans fight against each other, vying for the dominance that comes with having the favor of Gruul the Dragonkiller.$B$BEach is still our enemy, regardless of who that favor has fallen to, and we will drive them before us!', 0);
INSERT INTO `quest_request_items` VALUES (10506, 1, 0, 0, 0, 'Any luck out there? I know that it\'s dangerous work to be doing this amongst the Bloodmaul ogres, but if we can avoid the killing of any more animals, I\'ll be happy.', 0);
INSERT INTO `quest_request_items` VALUES (10507, 1, 0, 0, 0, 'Is it done, $N?', 0);
