INSERT INTO `quest_request_items` VALUES (10526, 1, 0, 0, 0, 'The significance of these artifacts eludes you, I am sure.$B$BWhen we have all five, our right to the ownership of Thunderlord Stronghold will be secured through the appeasement of its dead spirits.$B$BDo you begin to grasp now why this is so important? Do you understand why I have little patience for niceties?', 0);
