INSERT INTO `quest_request_items` VALUES (10620, 1, 0, 0, 0, 'Those stalkers cleared out yet? We\'ve had a few of our people who headed north disappear.$B$BIf it\'s because of the stalkers, I want to make sure that it doesn\'t happen again.', 0);
