INSERT INTO `quest_request_items` VALUES (11050, 1, 1, 0, 0, 'We don\'t need those eggs to get into the wrong hands, $N. Bring me any additional eggs that you may find.$B$BJust remember to keep a low profile and avoid raising suspicion!', 0);
INSERT INTO `quest_request_items` VALUES (11051, 1, 1, 0, 0, 'A seasoned pro such as yourself shouldn\'t be having any difficulty banishing demons. You\'re not having any difficulties, right, $R?', 0);
