INSERT INTO `quest_request_items` VALUES (11058, 1, 0, 0, 0, 'Have you learned how to use the apexis relic yet? Both we and the Sha\'tari Skyguard just to the north are interested in what you learn about them.', 0);
INSERT INTO `quest_request_items` VALUES (11059, 1, 1, 0, 0, 'We abhor violence, but at least the construct that we have sent you against isn\'t a living being.$B$BSpeaking of which, do you have the head?', 0);
INSERT INTO `quest_request_items` VALUES (11060, 1, 1, 0, 0, 'Da demons are making da Forge Camp: Wrath bigger. Soon, dey be too big and crush us. You crush dem first!$b$bShartuul\'s transporter is da key, but it protected by a magic shield and a demon called a degrader. Us powerful $c, and make special darkrune dat give you control of da degrader. $r give Gahk darkrune, us give $r crystalforged darkrune back.$b$bTake control of da degrader and possess as many of da demons dat come through da gate as you can ta stop dem!', 0);
INSERT INTO `quest_request_items` VALUES (11061, 1, 1, 0, 0, 'That sorcerer\'s flask is sure to make my boys more intelligent than they already are, and that should help immensely with what they\'ll need to do once they\'re in the Sha\'tari Skyguard.', 0);
