INSERT INTO `quest_request_items` VALUES (11153, 0, 0, 0, 0, 'We\'re talking again are we? That must mean that you\'ve done it! You\'ve shown those rotten Northsea pirates a thing or two!$b$bRight?', 0);
INSERT INTO `quest_request_items` VALUES (11154, 6, 0, 0, 0, 'Please tell me that you have the darkclaw guano?!', 0);
INSERT INTO `quest_request_items` VALUES (11155, 0, 0, 0, 0, 'There\'s nothing as sumptuous as the tender meat carved from the flanks of a shoveltusk!', 0);
