INSERT INTO `quest_request_items` VALUES (11157, 0, 0, 0, 0, 'How go your efforts?', 0);
INSERT INTO `quest_request_items` VALUES (11158, 1, 1, 0, 0, '<Brogg looks at you hopefully.>$B$BYou bring Brogg feathers?', 0);
INSERT INTO `quest_request_items` VALUES (11159, 1, 1, 0, 0, 'Brogg must know, have you brought peace to the violent spirits of the Stonemaul?', 0);
