INSERT INTO `quest_request_items` VALUES (11161, 1, 1, 0, 0, 'Do you have the essences? Brogg must have them to avenge his friends!', 0);
INSERT INTO `quest_request_items` VALUES (11162, 1, 1, 0, 0, 'Brogg will have his revenge!', 0);
INSERT INTO `quest_request_items` VALUES (11163, 1, 1, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (11164, 1, 1, 0, 0, 'Hurry, mon!$B$BBudd be sendin\' me into Zul\'Aman alone to test his mask if I don\'t be givin\' him those tusks soon!', 0);
INSERT INTO `quest_request_items` VALUES (11165, 1, 1, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (11166, 1, 1, 0, 0, 'I trust that my hand-crafted disguises have given you adequate protection in Zul\'aman.$B$BHave you scouted those areas for me? Or shall I begin looking for your replacements?', 0);
INSERT INTO `quest_request_items` VALUES (11167, 0, 0, 0, 0, 'Have you obtained the containers, $N?', 0);
