INSERT INTO `quest_request_items` VALUES (11169, 1, 1, 0, 0, 'Have you had any luck testing the totem?', 0);
INSERT INTO `quest_request_items` VALUES (11170, 0, 0, 0, 0, 'You\'re back!', 0);
INSERT INTO `quest_request_items` VALUES (11171, 1, 1, 0, 0, 'I hope you appreciate the magnitude of the opportunity I\'ve given you, Diedthreexs.$B$BHave you taken care of old Malacrass yet?', 0);
INSERT INTO `quest_request_items` VALUES (11172, 1, 1, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (11173, 1, 1, 0, 0, 'Did you get that venom? Morlann\'s going to discover that it\'s gone any moment now...', 0);
INSERT INTO `quest_request_items` VALUES (11174, 1, 1, 0, 0, 'Did you make any progress against those oozes?', 0);
INSERT INTO `quest_request_items` VALUES (11177, 1, 1, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (11178, 1, 1, 0, 0, '<Budd tries to mask his displeasure at your presence.>$B$BYou again....$B$BI don\'t know how else to say it - I\'m simply too busy with my preparations to hold your hand any longer.$B$BTrust me, $N, it\'s all for the best. Perhaps, by some miracle, you might eventually build a name for yourself.$B$BIf so, you\'ll be able to say that you got your start from the incomparable Budd Nedreck!', 0);
INSERT INTO `quest_request_items` VALUES (11180, 1, 1, 0, 0, 'What have you learned from the spirits haunting this place?', 0);
INSERT INTO `quest_request_items` VALUES (11181, 1, 1, 0, 0, 'Were you able to gather any of the herb? We must work quickly, before whatever evil is behind this infestation consolidates its hold on this area.', 0);
INSERT INTO `quest_request_items` VALUES (11182, 0, 0, 0, 0, 'How fare your efforts against the meddlesome handlers, little one?', 0);
INSERT INTO `quest_request_items` VALUES (11183, 1, 1, 0, 0, 'Is it over?', 0);
INSERT INTO `quest_request_items` VALUES (11184, 1, 1, 0, 0, 'You look pretty satisfied with yourself. Come to claim the bounty on that monstrous raptor?', 0);
INSERT INTO `quest_request_items` VALUES (11185, 1, 1, 0, 0, 'What is that you\'re carrying, $C?', 0);
INSERT INTO `quest_request_items` VALUES (11186, 1, 1, 0, 0, 'What is that you\'re carrying, $c?', 0);
INSERT INTO `quest_request_items` VALUES (11188, 0, 0, 0, 0, 'It is unfortunate that the captain had to have you go rile up the proto-drakes. Though they seem to lack the intelligence of some of the dragons we know, they still seem to be noble creatures. Not to mention the situation it places us in.$B$BDare I ask if you have dealt with the situation?', 0);
INSERT INTO `quest_request_items` VALUES (11190, 0, 0, 0, 0, 'We\'re runnin\' dangerously low on rounds \'round here, $c. Our enemies need poundin\', especially Skorn!$B$BYa got more balls fer us?', 0);
INSERT INTO `quest_request_items` VALUES (11191, 1, 1, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (11192, 1, 1, 0, 0, 'Were you able to get any of that thresher oil?', 0);
INSERT INTO `quest_request_items` VALUES (11193, 1, 1, 0, 0, 'What\'ll it be today? The snook is quite good.', 0);
INSERT INTO `quest_request_items` VALUES (11194, 1, 1, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (11195, 1, 1, 0, 0, 'Hey, $N! I hear ya been workin\' yer way into Budd\'s crew. Ya won\'t live to regret that move, friend, I can promise ya that.$B$BWhatcha got there, mon?', 0);
INSERT INTO `quest_request_items` VALUES (11196, 1, 1, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (11198, 1, 1, 0, 0, 'Make your report.', 0);
