INSERT INTO `quest_request_items` VALUES (11205, 1, 1, 0, 0, 'Have you put that torch to good use?', 0);
INSERT INTO `quest_request_items` VALUES (11206, 1, 1, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (11207, 1, 1, 0, 0, '<Moxie looks frantic with worry.>$B$BDid the cargo survive? Did you find any of it?', 0);
INSERT INTO `quest_request_items` VALUES (11208, 1, 1, 0, 0, 'Yes?', 0);
