INSERT INTO `quest_request_items` VALUES (11217, 1, 1, 0, 0, 'Did you find any of the wyrmtail?', 0);
INSERT INTO `quest_request_items` VALUES (11218, 1, 0, 0, 0, 'Construction on this place seems to be a never-ending task. I just hope that you\'re able to get us what we need to continue.', 0);
INSERT INTO `quest_request_items` VALUES (11219, 1, 1, 0, 0, 'The Horde\'s children need you! Do not abandon them!', 0);
