INSERT INTO `quest_request_items` VALUES (11224, 0, 0, 0, 0, 'This is quite a blow to our efforts. I don\'t even want to know what they discovered down there.$B$BIt\'s enough to keep me awake at night just trying to imagine what it could be.', 0);
INSERT INTO `quest_request_items` VALUES (11225, 1, 1, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (11227, 0, 0, 0, 0, 'Did you feed this little rascal here yet?', 0);
INSERT INTO `quest_request_items` VALUES (11231, 0, 0, 0, 0, 'With the prisoners at Gjalerbron freed, the vrykul will be unable to continue their unholy rites.$B$BYou\'re our only hope of stopping them.', 0);
INSERT INTO `quest_request_items` VALUES (11232, 0, 0, 0, 0, 'The cannons, $n. Is someone going to take care of them?', 0);
INSERT INTO `quest_request_items` VALUES (11233, 0, 0, 0, 0, 'Are the Alliance leaders dead, $N? Do not return until they are.', 0);
INSERT INTO `quest_request_items` VALUES (11236, 0, 0, 0, 0, 'Are you sure that you killed Mezhen and his necrolords, $c?', 0);
INSERT INTO `quest_request_items` VALUES (11237, 5, 0, 0, 0, 'What?! Plans for an attack upon the keep?$B$BA FROST WYRM?!!!', 0);
INSERT INTO `quest_request_items` VALUES (11238, 6, 0, 0, 0, 'Is the attack averted? Are the frost wyrm and its master destroyed?$B$BDid you retrieve the horn?', 0);
INSERT INTO `quest_request_items` VALUES (11239, 0, 0, 0, 0, 'The Holy Light moves through your actions and in all things.$B$BNo creature, no place, no matter how wicked, can escape its wrath!', 0);
INSERT INTO `quest_request_items` VALUES (11242, 1, 1, 0, 0, 'You have something for me, good $N?', 0);
INSERT INTO `quest_request_items` VALUES (11243, 0, 0, 0, 0, 'The fate of the world may very well rest with us.', 0);
INSERT INTO `quest_request_items` VALUES (11244, 0, 0, 0, 0, 'Find those scouts!', 0);
INSERT INTO `quest_request_items` VALUES (11245, 0, 0, 0, 0, 'Getting too close to those towers is certain death as long as those casters are up there.', 0);
INSERT INTO `quest_request_items` VALUES (11246, 0, 0, 0, 0, 'One thing that I know for certain is that I don\'t want to see any of these bigguns standing back up after we\'ve knocked \'em down!', 0);
INSERT INTO `quest_request_items` VALUES (11247, 0, 0, 0, 0, 'May I suggest that when we set the buildings ablaze, we not tarry too long inside of them?', 0);
INSERT INTO `quest_request_items` VALUES (11249, 6, 0, 0, 0, 'Yes? How may I help you, my child?', 0);
INSERT INTO `quest_request_items` VALUES (11250, 6, 0, 0, 0, 'I gather that since you\'re here, Skorn is no more?', 0);
INSERT INTO `quest_request_items` VALUES (11252, 0, 0, 0, 0, 'Good to see you\'re well, $n.$b$bWe\'ve had about as many casualties as we can handle.', 0);
INSERT INTO `quest_request_items` VALUES (11254, 0, 0, 0, 0, 'You\'ve something to show me, $c?', 0);
INSERT INTO `quest_request_items` VALUES (11255, 0, 0, 0, 0, 'Put some backbone into it, $n! People\'s lives are at stake here!', 0);
INSERT INTO `quest_request_items` VALUES (11257, 0, 0, 0, 0, 'These vrykul have strange customs, would you not agree, $n?', 0);
INSERT INTO `quest_request_items` VALUES (11258, 0, 0, 0, 0, 'I suggest that when we set the buildings ablaze, we not stay inside of them. The fire spirit will not spare us in its thirst to destroy.', 0);
INSERT INTO `quest_request_items` VALUES (11259, 0, 0, 0, 0, 'Getting too close to those towers is certain death as long as those casters are up there.', 0);
INSERT INTO `quest_request_items` VALUES (11260, 0, 0, 0, 0, 'Yes? How may I help you, child?', 0);
INSERT INTO `quest_request_items` VALUES (11261, 0, 0, 0, 0, 'What news do you bring of Skorn, $N?', 0);
INSERT INTO `quest_request_items` VALUES (11262, 0, 0, 0, 0, 'Gird up, $r.$b$bThere\'s much work to be done. This is just the tip of the iceberg, so to speak.', 0);
INSERT INTO `quest_request_items` VALUES (11263, 0, 0, 0, 0, 'How is the fight at Gjalerbron coming?', 0);
INSERT INTO `quest_request_items` VALUES (11265, 0, 0, 0, 0, 'With the prisoners at Gjalerbron freed, the vrykul will be unable to continue their rituals.', 0);
INSERT INTO `quest_request_items` VALUES (11266, 0, 0, 0, 0, 'What?! Plans for an attack upon our camp?$B$BFROM A GREAT WYRM IN THE SKY?!!!', 0);
INSERT INTO `quest_request_items` VALUES (11267, 0, 0, 0, 0, 'Is the attack averted? Is the great death wyrm and its master destroyed?$B$BDid you retrieve the horn?', 0);
INSERT INTO `quest_request_items` VALUES (11268, 0, 0, 0, 0, 'Have you destroyed the walking dead at Gjalerbron?', 0);
INSERT INTO `quest_request_items` VALUES (11269, 0, 0, 0, 0, 'Have you brought any of that down? I could really use it...', 0);
INSERT INTO `quest_request_items` VALUES (11270, 0, 0, 0, 0, 'I don\'t care how many giants, dragons or kobolds you\'ve killed before. Get this job done and come back when you\'re finished.', 0);
