INSERT INTO `quest_request_items` VALUES (24702, 0, 0, 0, 0, 'Sturdy mount and trusted friend.$B$BDied shortly after the Cataclysm. He always hated loud noises.$B$BIn lieu of flowers, please drop off a few Bloodpetal sprouts. In life, he loved nothing more.', 0);
