INSERT INTO `quest_request_items` VALUES (24717, 0, 0, 0, 0, 'I never know what style is in vogue, so I\'ll want a few different colors. Try to get hides from each different type of gorilla there.', 0);
INSERT INTO `quest_request_items` VALUES (24718, 0, 0, 0, 0, 'Red fur? What\'re you waiting for! Go back and get that red gorilla! I\'ll pay you double!', 0);
