INSERT INTO `quest_request_items` VALUES (24720, 0, 0, 0, 0, 'I just know I\'m onto something here, $n!', 0);
INSERT INTO `quest_request_items` VALUES (24730, 0, 0, 0, 0, 'Think whatever you want of me, but they\'re not going to care if the bones they get to research are from ancient stegodons and diemetradons or the ones that almost devoured us wholesale when we were trying to set up our camps. I\'ll just be happy if we get out of here alive...', 0);
INSERT INTO `quest_request_items` VALUES (24731, 0, 0, 0, 0, 'Do not take my words lightly, $n. If you truly wish to face this creature, then you must be careful... and smart. It will take a great deal of strength and insight to draw out Lar\'korwi and defeat him.', 0);
INSERT INTO `quest_request_items` VALUES (24732, 0, 0, 0, 0, 'Be careful, $n. Getting close to any nest of eggs surely will provoke the mothers... even if you\'ve already secured a gland from one. The mothers can be just as deadly as Lar\'korwi himself when they fight to protect their young. But, if you are successful, then we shall have a definitive way of bringing Lar\'korwi out from hiding.', 0);
INSERT INTO `quest_request_items` VALUES (24733, 0, 0, 0, 0, 'Do not let fear overpower you, $n. Fear is the great betrayer... you must remain strong!', 0);
INSERT INTO `quest_request_items` VALUES (24734, 0, 0, 0, 0, 'Ringo\'s sack rests on the rock in front of you.', 0);
INSERT INTO `quest_request_items` VALUES (24736, 0, 0, 0, 0, 'Every great vehicle needs a name... I was thinking I\'d call it... Pwned!', 0);
