INSERT INTO `quest_request_items` VALUES (24740, 0, 0, 0, 0, 'Do you think the volcano is active? I wonder why it didn\'t explode when the cataclysm hit.', 0);
INSERT INTO `quest_request_items` VALUES (24741, 0, 0, 0, 0, 'How many did you get? I can just taste the massive omelet we\'re going to cook up now!', 0);
