INSERT INTO `quest_request_items_locale` VALUES (8, 'enUS', 'Yes? Yes? What is it?$B$BThe forsaken; they certainly do have huge appetites. Make yourself at home... yes, rest those weary bones. My name\'s Renee if you need anything at all.', 0);
