INSERT INTO `quest_request_items_locale` VALUES (30, 'enUS', 'Have you formed your pendant for me to inspect, $n? If you need help in locating the parts, try asking the local populace for information. Not only will you find what you seek, but it will give you a reason to explore this wondrous and sacred glade.', 0);
INSERT INTO `quest_request_items_locale` VALUES (32, 'enUS', 'I\'m sorry, I don\'t know you... but from the look of things you have at least heard something of me. Now, what can I help you with? Do you have something for me?', 0);
