INSERT INTO `quest_request_items_locale` VALUES (8059, 'enUS', 'Have you returned with the Paragons of Power? By defeating the diabolic minions of the Hakkar, you will not only prove yourself to us, but also your willingness to learn the ways of the demoniac.', 0);
