INSERT INTO `quest_request_items_locale` VALUES (8074, 'enUS', 'The Paragons of Power are what I need mon - we\'ll tap the power right out of them in our fight against the evil from Zul\'Gurub. Do ya have them for me?', 0);
INSERT INTO `quest_request_items_locale` VALUES (8076, 'enUS', 'Retrieved the Paragons of Power from Zul\'Gurub? While we will take the latent power from these items and make them our own, we will give you your own reward of power should you be successful...', 0);
INSERT INTO `quest_request_items_locale` VALUES (8077, 'enUS', 'The Paragons of Power... you have returned with them? Only once we have them will we share the ultimate symbol of demoniac power with you, $n.', 0);
INSERT INTO `quest_request_items_locale` VALUES (8078, 'enUS', 'Have you brought the Paragons of Power? Have you struck down the enemies of the tribe? Hakkar must know the wrath of the vindicator!', 0);
