INSERT INTO `quest_request_items_locale` VALUES (26344, 'enUS', 'Drizzlik\'s Excelsior leather boots, finest workmanship, one-hundred percent high quality saltwater crocolisk skin boots. None finer!$B$BCan\'t beat a glowing description like that, can you?$B$BExcept, maybe, if you have those skins for me?', 0);
