INSERT INTO `quest_template_locale` VALUES (74, 'deDE', 'Die Legende von Stalvan', 'Die zerrissene Tagebuchseite, die Ihr gefunden habt, gibt Euch den nächsten Hinweis.', 'In der Kiste findet Ihr verschiedene Dinge: muffige Erbstücke, ein Familienportrait, einige Jagdtrophäen und ein paar alte Bücher. Fast schon am Boden der Kiste findet Ihr unter einer Keramikvase eine zerrissene Tagebuchseite.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (74, 'esES', 'La leyenda de Stalvan', 'La página arrancada del diario que descubriste es tu siguiente pista.', 'Dentro de la caja encuentras varios objetos: reliquias familiares mohosas, unos trofeos de caza, un retrato de familia y algunos libros antiguos. Al fondo, debajo de un jarrón, descubres una página arrancada de un diario.', '', '', '', '', '', '', 0);
